import{_ as e,a as s,o,b as t}from"./chunks/framework.Cao6T-Ti.js";const D=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"README.md","filePath":"README.md"}'),n={name:"README.md"},r=t("p",null,"Новая версия сайта OOLEY.ru в формате статического ресурса на основе Vitepress.",-1),a=t("p",null,"Это часть постепенного переезда с Wordpress на более простую и современную инфраструктуру – файловую базу данных и генератор статического сайта. С одной стороны это шаг назад к истокам Интернета, с другой - применение новеших наработок в JS сообществе. Интерактивность, которая была заложена в основе проекта, не пригодилась: все статьи для сайта пишет и публикует один человек. Да, несколько комментариев на страничках мастерских за все время кто-то добавил, но в противовес этому – тысячи отфильтрованных спам-комментов от ботов.",-1),_=t("p",null,"Плоская файловая база данных – уникальной простоты система. Папки и файлы в проекте превращаются в пути на самом сайте. Только не нужно писать HTML – данные хранятся в MD файлах в виде YAML frontmatter и Markdown текста. Рядом же лежат картинки. Для файлов для скачивания есть публичные директории.",-1),c=t("p",null,"Редактировать файлы предельно просто, двигать между разделами – тоже. Затем запускаем сборку и скрипты соберут из всего этого статичный сайт с прогрессивной гидратацией. Так можно добавить сколько угодно интерактивности на страницы и быть уверенным, что поисковики их все равно смогут проиндексировать.",-1),d=t("p",null,"Работы проделано много, ведь данные из wordpress экспортировались не все и не в идеальной форме. Пришлось удалять шорткоды, заново вставлять картинки, задавать иконки и обложки, заполнять данные о мастерских. Что-то удалось автоматизировать, но по каждой странице пройтись пришлось. А каждую мастерскую в каталоге проверяли и обновляли инфо. Если новостей от мастерской давно нет или известно, что она закрылась - она отправляется в архив. Их странички останутся доступны, для истории.",-1),l=[r,a,_,c,d];function p(i,u,h,m,E,f){return o(),s("div",null,l)}const A=e(n,[["render",p]]);export{D as __pageData,A as default};
