import{_ as o}from"./chunks/theme.BBnZaxT7.js";import{_ as r,a as s,f as n,b as e,G as a,H as c,o as i}from"./chunks/framework.Cao6T-Ti.js";const T=JSON.parse('{"title":"Центр открытых мастерских","description":"OOLEY [улей] – проект развития творческих возможностей человека и общества","frontmatter":{"home":true,"title":"Центр открытых мастерских","description":"OOLEY [улей] – проект развития творческих возможностей человека и общества","date":"2015-08-17","cover":"cover.jpg"},"headers":[],"relativePath":"index.md","filePath":"index.md"}'),_={name:"index.md"},d=e("h2",{id:"мастера-делают-мастерские-открытыми-открытые-мастерские-делают-мастеров-свободными",tabindex:"-1"},[a("Мастера делают мастерские открытыми, открытые мастерские делают мастеров свободными "),e("a",{class:"header-anchor",href:"#мастера-делают-мастерские-открытыми-открытые-мастерские-делают-мастеров-свободными","aria-label":'Permalink to "Мастера делают мастерские открытыми, открытые мастерские делают мастеров свободными"'},"​")],-1),l=e("h3",{id:"наша-цель-cделать-свободное-творчество-понятным-доступным-и-распространенным-занятием",tabindex:"-1"},[a("Наша цель - cделать свободное творчество понятным, доступным и распространенным занятием "),e("a",{class:"header-anchor",href:"#наша-цель-cделать-свободное-творчество-понятным-доступным-и-распространенным-занятием","aria-label":'Permalink to "Наша цель - cделать свободное творчество понятным, доступным и  распространенным занятием"'},"​")],-1),p=e("p",null,"OOLEY исследует, создает и поддерживает творческие процессы и пространства, для формирования среды, в которой люди смогут легко находить возможности для развития, сотрудничества и самовыражения.",-1),h=c('<h3 id="чем-занимается-ooley" tabindex="-1">Чем занимается OOLEY? <a class="header-anchor" href="#чем-занимается-ooley" aria-label="Permalink to &quot;Чем занимается OOLEY?&quot;">​</a></h3><p>Мы внимательно следим за развитием открытых мастерских и аналогичных начинаний в Москве, в России и во всем мире. Все известные нам проекты мы вносим <a href="/workshop/catalog/">в общий каталог</a> и отмечаем на нашей карте общественных мастерских. По возможности посещаем эти места сами: наблюдаем, изучаем, много снимаем и делимся собранными знаниями в форме подробных <a href="/workshop/tour/">фото-</a> и <a href="/workshop/video/">видеообзоров</a>.</p><p>Весь собранный опыт мы обобщаем в форме общей Теории устойчивых открытых мастерских и настойчиво пробуем реализовывать её положения на практике. Так мы проверяем и уточняем ролевую модель, распорядок и технику организации открытых сессий, оттачиваем административные и педагогические навыки, разрабатываем подробные инструкции-навыки к инструментам и оборудованию, вырабатываем подходы к учёту и хранению проектов и материалов. Для этих экспериментов мы используем нашу передвижную мастерскую, а также сотрудничаем с пространствами, желающими запустить открытые мастерские у себя.</p><p>Собранные и проверенные на собственном опыте знания мы свели в Руководстве по устройству и организации устойчивой открытой мастерской. Мы проводим лекции и беседы со всеми заинтересованными в создании подобных пространств. OOLEY старается донести эти наработки до всех организаторов открытых мастерских, настоящих и будущих, а также помогает в адаптации данных рекомендаций к особенностям условий каждого конкретного проекта.</p><p>Мы стараемся никогда не прекращать создавать вещи, постоянно реализуя всё новые творческие проекты. Мы считаем, что каждый человек — творец, достаточно только начать. Пусть неподдельный интерес и естественная тяга к самореализации укажут направление, а наши наработки и путеводители помогут быстро освоиться в бескрайнем мире свободного творчества.</p>',5);function m(f,u,O,x,b,k){const t=o;return i(),s("div",null,[d,l,p,n(t,{class:"mt-8",link:"https://youtu.be/5aSf6ik05aY"}),h])}const Y=r(_,[["render",m]]);export{T as __pageData,Y as default};
