import{_ as t,o as e,c as n,e as s}from"./chunks/framework.mYMQfzZG.js";const r="/assets/4JocTNgId8o.f-V9B7DQ.jpg",k=JSON.parse('{"title":"Делай Саммит VI: Открытие ДК Делай Сам","description":"","frontmatter":{"title":"Делай Саммит VI: Открытие ДК Делай Сам","date":"2016-06-23","cover":"4gJm4SP5q0M.jpg"},"headers":[],"relativePath":"practice/event/delaj-sammit-vi-otkrytie-dk-delaj-sam/index.md","filePath":"practice/event/delaj-sammit-vi-otkrytie-dk-delaj-sam/index.md"}'),o={name:"practice/event/delaj-sammit-vi-otkrytie-dk-delaj-sam/index.md"},a=s('<p>25-26 июня 2016 года в <a href="https://www.facebook.com/BusinessParkPravda/?fref=ts" class="external-link" target="_blank" rel="noreferrer">Центре городской культуры «Правда»</a> в Москве пройдет <a href="http://delaisam.org/cities/moscow/2909" class="external-link" target="_blank" rel="noreferrer">шестая неформальная конференция о городской самоорганизации «Делай Саммит»</a>. Конференция приурочена к открытию <a href="http://delaisam.org/cities/moscow/2964" class="external-link" target="_blank" rel="noreferrer">Дома Культуры «Делай Сам/а»</a>, который после окончания Делай Саммита будет постоянно действовать на постоянной основе.</p><p>На этой странице вы можете ознакомиться с программой Делай Саммита. 25 июня (первый день) мероприятия пройдут в пространстве «ЗАЛ». 26 июня (второй день) события пройдут на четырех локациях: «ЗАЛ», «МАСТЕРСКАЯ», «КАФЕ», «ДВОР», «ГОРОД».</p><p>Вход свободный. Для участия обязательна регистрация.</p><p><strong>Первый день. 25 июня</strong></p><p><strong>10.45 — 11.00</strong> Регистрация участников</p><p>**11.00 ** Открытие Делай Саммита. Короткая презентация ДК|ДС от организаторов</p><p><strong>«ЗАЛ»</strong></p><p><strong>11.20 — 12:00</strong></p><p>Презентация первого в мире шоу-проекта о городских активистах. «Шоу «Город» / Надежда Илькевич и Анастасия Куриленко, продюсеры шоу / Беларусь</p><p><strong>12:00</strong> <strong>— 12:40</strong> Лекция «Het Circus: веселый и занятный способ просвещения и внутреннего укрепления сообществ» / Джей Наварро Овьедо, социальный предприниматель и эксперт в области социального маркетинга, основатель организации <a href="https://www.facebook.com/DenHaaginTransitie/" class="external-link" target="_blank" rel="noreferrer">«Гаага in Transition»</a>,  организатор эко-социальных районных фестивалей <a href="http://www.hetcircus.me/" class="external-link" target="_blank" rel="noreferrer">Het Circus</a>  / Нидерланды</p><p><strong>12:40 — 14:00</strong> Экономика желания / Круглый стол Союза выздоравливающих на платформе фестиваля «МедиаУдар» / Выступают: Павел Митенко, Александра Ненько, Егор Софронов…</p><p><strong>14:00 — 14:40</strong> Презентация творческой лаборатории «Вокруг да около». Спектакль-маршрут «Идиота кусок», передвижное театральное действие по тексту романа М. Достоевского «Идиот»/ Санкт-Петербург</p><p><strong>14:40 — 16:00</strong> Опыт городских инициатив разных стран «Не обогреваем ли мы космос?»/ Дискуссия</p><p>Участники: Саша Щеряков, проект «Уличное кино», Москва/Владивосток, Артем Филатов, организатор фестиваля уличного искусства «Новый город: Древний», художник, Нижний Новгород, Надя Илькевич, автор проекта «Школа супергероев/Креативный город», продюсер «Шоу «Город», Минск, Саша Старков, директор арт-центра «Грифон», Ижевск, Аня Доброва, организация «Місто Дія» («Город действия»), Киев</p><p><strong>16:00 — 16:40</strong> На-казание / Катрин Ненашева и Дима Жданов об акции На-казание и опытах низовой самоорганизации выпускников детских домов / Москва</p><p><strong>16:40 — 17:20</strong> Опыт культурно-просветительских инициатив на постсоветском пространстве. Доклад и дискуссия. Идея и модерация: проект HUMRA / Зоя Лукьянова, представитель Объединения MitOst и фонда BMW Herbert Quandt в России и Восточной Европе</p><p><strong>17:20 — 18:00</strong> Архитектура счастья в деструктивной среде / Дана Космина и Макс Попов, коллектив <a href="https://www.facebook.com/pylorama.wood/?fref=ts" class="external-link" target="_blank" rel="noreferrer">«Пилорама»</a>/ Киев</p><p><strong>18:00 — 18:40</strong> Разговоры о политике. Политическое в современном беларусском искусстве. Мечты и реальность монетизации / Алексей Толстов, художник и писатель / Беларусь</p><p><strong>18:40 — 19:20</strong> Защищай себя сам: как активисты могут обезопасить себя от угроз будущего? / Алексей Сидоренко, руководитель Теплицы социальных технологий</p><p><strong>19:20 — 20:00</strong> Ъ для гражданского общества: День журналистики решений / #ImpactJournalism и платформа +1 / Спикеры: Алексей Шаповалов (экономический обозреватель ИД «Коммерсантъ») и Анастасия Попова (CEO «Один за всех и все за одного»)</p><p><strong>20:00 — 22:00</strong> Лаборатория «Sharing economy для России: сегодня|завтра» в формате «мирового кафе». В рамках цикла партнерских событий ДК|ДС и Общероссийского гражданского форума «Город обмена, или городская экономика совместного пользования»</p><p>Спикеры: Алексей Шаповалов, экономический обозреватель Ъ, Дина Жук и Коля Спесивцев, художники, участники группировки eeefff и Летучей Кооперации, соорганизаторы ДК «Делай Сам|а», Дарья Федотова и Марийка Семененко, соорганизаторы ДК «Делай Сам|а», партнеры организации городских коммуникаций «Публичные Пространства», Павел Акимов, урбанист, исследователь технологии блокчейн, Татьяна Честина, управляющий партнер «Велес», директор по развитию Движения ЭКА, Таня Эфрусси, художница и исследовательница архитектуры советского авангарда.</p><p><strong>22:00 — 06:00 утра</strong> <a href="https://www.facebook.com/events/506381482882491/" class="external-link" target="_blank" rel="noreferrer">Криптовечеринка</a></p><p><strong>Второй день. 26 июня</strong></p><p><strong>13:00 — 15:00 («ЗАЛ»)</strong></p><p>Воркшоп «Стратегическое внедрение социального проектирования и социального маркетинга для создания, мобилизации и внутреннего укрепления сообществ» / Джей Наварро Овьедо,  социальный предприниматель и эксперт в области социального маркетинга, основатель организации <a href="https://www.facebook.com/DenHaaginTransitie/" class="external-link" target="_blank" rel="noreferrer">«Гаага in Transition»</a>,  организатор эко-социальных районных фестивалей <a href="http://www.hetcircus.me/" class="external-link" target="_blank" rel="noreferrer">Het Circus</a> / Нидерланды</p><p><strong>13:00 — 15:00 («МАСТЕРСКАЯ»)</strong></p><p>Мастерская <strong>«<strong>Ситуативное использование бумажных материалов в создании безместных памятников</strong>»</strong>. Мастерская будет посвящена конструированию мобильных (не)памятников из картона и бумаги, антагонистичных по своему характеру монументальным и долговечным памятникам, закостеневшим в официальном нарративе городской среды. Ведущие: Надя Дегтярева и Ник Дегтярев</p><p><strong><img src="'+r+'" alt="Зеленый зал"></strong></p><p><strong>13:00 — 16:00 («КАФЕ»)</strong></p><p>Презентация проекта «Фудшеринг – движение по спасению еды от выбрасывания». Мастер-класс по переработке просроченной еды</p><p><strong>13:00 — 21:00 («ДВОР»)</strong></p><p>Воркшоп по микроархитектуре от <a href="https://www.facebook.com/pylorama.wood/?fref=ts" class="external-link" target="_blank" rel="noreferrer"><strong>«<strong>Пилорамы</strong>»</strong></a></p><p>Мастер-класс по эксплуатации и первичной починке велосипеда</p><p>Мастер-класс по эксплуатации и первичной починке велосипеда от веломастерской <strong>«<strong>Другое Дело</strong>»</strong></p><p>Гаражная распродажа в поддержку ДК|ДС</p><p><strong>15:00 — 16:00 («ЗАЛ»)</strong></p><p>Проект HUMRA – Human Rights Animation. Презентация и дискуссия. Команда проекта HUMRA – Human Rights Animation приглашает на показ мультфильмов, сделанных в рамках воркшопов по анимации молодыми людьми под руководством профессиональных художников. В гостях  — представители гражданских инициатив из Украины и Грузии</p><p><strong>15:00 — 16:00 («МАСТЕРСКАЯ»)</strong></p><p>Воркшоп по шелкографии на старых футболках от сооснователя бренда FOR KINGS ONLY Кристины Ли</p><p>** 15:00 — 18:00**</p><p>Экспедиция в город: сквозь дата-центры к технобудущему / группировка eeefff (данное событие состоится вне ДК|ДС). Предлагаем всем присоединиться к вылазке в места, где сейчас пока еще незаметно, но рождается новый урбанистический порядок</p><p>** 16:00 — 17:00 («ЗАЛ»)**</p><p>Мастер-класс «Как наладить раздельный сбор: дома, в офисе, во дворе» / Валерия Коростелева, координатор общероссийского движения «РазДельный сбор» в Москве</p><p>** 16:00 — 18:00 («МАСТЕРСКАЯ»)**</p><p>Воркшоп от мастерской Ooley по переработке вторсырья в новые дизайн-вещи</p><p><strong>17:00 — 19:00 («ЗАЛ»)</strong></p><p>Мастер-класс «Вокруг да около». Этюдный метод. Работа с текстом. Работа с партнером. На этом мастер-классе участники познакомятся с методами работы с актером. Техника направлена на раскрытие творческой свободы. Участие могут принимать все, вне зависимости от наличия актерского опыта</p><p><strong><img src="'+r+'" alt="Зеленый зал">  18:00 — 19:00  («КАФЕ»)</strong></p><p>Приготовление совместного ужина</p><p><strong><img src="'+r+'" alt="Зеленый зал"> 19:00 — 20:00  («КАФЕ»)</strong></p><p>Презентация нового формата для дискуссий в ДК|ДС — бар-БЫТ. «Как термин «благоустройство» стал синонимом чего-то зловещего?» / Петр Иванов, социолог, ВШУ</p><hr><p><a href="https://docs.google.com/spreadsheets/d/15Jng41EOurY4CC9KjYeqRbs2LNmlR96XwJSyv3dYK1Q/pubhtml" class="external-link" target="_blank" rel="noreferrer">Полная программа в виде таблицы.</a></p>',54),p=[a];function g(l,i,c,f,m,_){return e(),n("div",null,p)}const h=t(o,[["render",g]]);export{k as __pageData,h as default};
