import{_ as t,o as r,c as o,b as e}from"./chunks/framework.dd5ab16e.js";const m=JSON.parse('{"title":"Дворовая мастерская в Открытом павильоне Фестиваля Московского урбанистического форума","description":"","frontmatter":{"title":"Дворовая мастерская в Открытом павильоне Фестиваля Московского урбанистического форума","date":"2015-10-01","cover":"16012113911_65d50ecefa_o.jpg"},"headers":[],"relativePath":"practice/event/dvorovaya-masterskaya-v-otkrytom-pavilone-festivalya-moskovskogo-urbanisticheskogo-foruma/index.md","filePath":"practice/event/dvorovaya-masterskaya-v-otkrytom-pavilone-festivalya-moskovskogo-urbanisticheskogo-foruma/index.md"}'),a={name:"practice/event/dvorovaya-masterskaya-v-otkrytom-pavilone-festivalya-moskovskogo-urbanisticheskogo-foruma/index.md"},s=e('<p><strong>Открытый павильон «Активистские практики освоения города»</strong></p><p><strong>17 октября 2015 с 10:00 до 18:00</strong>, на <a href="http://fest2015.mosurbanforum.ru/" class="external-link" target="_blank" rel="noreferrer">Фестивальном дне</a> <strong><a href="http://fest2015.mosurbanforum.ru/" class="external-link" target="_blank" rel="noreferrer">5-го Московского Урбанистического Форума</a></strong> будет работать <strong><a href="http://fest2015.mosurbanforum.ru/open/" class="external-link" target="_blank" rel="noreferrer">Открытый павильон</a></strong> – многофункциональная площадка для представления активистских практик освоения города.</p><p>Постиндустриальный город в силу своей динамичности, непредсказуемости и постоянно меняющегося образа жизни горожан требует новых подходов к изучению, программированию и управлению. Эксперты предлагают новые методы исследования города, архитекторы – новые модели для его проектирования, администрация разрабатывает новые системы управления. В этом же поле действуют и городские активисты. Они изучают и меняют город тактически, экспериментируют и пробуют разные сценарии его использования, тем самым влияя на формирование официальной стратегии развития.</p><p>Таким образом, основной вопрос Открытого павильона – <strong>как городские активисты и сообщества адаптируются к процессам, происходящим в городе, а потом адаптируют город «под себя»?</strong> В рамках Открытого павильона будет представлен срез «не-формальных» практик освоения города – повседневных, художественных, экстремальных и других. Под «не-формальным» в данном контексте подразумевается стиль подхода: не экспертный, а в большей степени спонтанный, ситуативный.</p><p>Открытый павильон - это интерактивная мультижанровая площадка, демонстрирующая идею вовлечения в процесс изменения города через различные виды практических активностей. В формате выставок, дискуссий, открытых презентаций, воркшопов жители города, художники, танцоры, музыканты, документалисты, представители разных городских инициатив и сообществ покажут участникам примеры своих проектов в городе и предложат поучаствовать в них.</p><p>Подобный формат был впервые опробован в 2014 году на Фестивальном дне 4-го Московского урбанистического форума. На этот раз Открытый павильон будет функционировать в виде «pop-up school» (в дословном переводе - «временная школа»), что соответствует задаче презентовать существующие практики и научить жителей экспериментальным тактикам улучшения города.</p><p>Среди множества <a href="http://fest2015.mosurbanforum.ru/open/adult/" class="external-link" target="_blank" rel="noreferrer">разнообразных секций Павильона</a> команда <strong>OOLEY</strong> принимает активное участие в реализации <strong>дворовой мастерской</strong>, которая наполнится всевозможной творческой активностью и поможет распространить в обществе &quot;не-формальные&quot; активистские практики.</p><p><strong>Дворовая мастерская</strong></p><p>Зона ручного труда, в рамках которой у посетителей будет возможность создать практические инструменты для «низового» преобразования города и реализации различных социально-культурных инициатив.</p><p>Планируемые воркшопы:</p><ul><li><strong>Изготовление «Павильона общественника»</strong> -   конструкции для проведения общественных собраний локального значения</li><li><strong>Изготовление указателей и вывесок для городских инициатив</strong></li><li><strong>Изготовление ящиков для рассады, контейнеров для макулатуры, защитных</strong></li></ul><p><strong>конструкций на деревья, миниголубятни, бельчатника</strong></p><ul><li><strong>Изготовление мобильной и стационарной уличной мебели</strong></li></ul><p>Приглашаем всех заинтересованных в совместном творческом переосмыслении города приходить на Фестиваль, заглядывать в Открытый павильон и включаться в творческий активизм вместе с нами. Вход свободный.</p>',14),n=[s];function p(l,i,g,c,_,f){return r(),o("div",null,n)}const k=t(a,[["render",p]]);export{m as __pageData,k as default};
