import{_ as t,a as A,J as o,o as a}from"./chunks/framework.BWf-PbcG.js";const p="data:image/png;base64,",r="data:image/png;base64,",g="data:image/png;base64,",e="/assets/6PHVhjyWh3o.CucTcW9w.jpg",i="/assets/8uSCMgNSjII.BT5JoNw3.jpg",c="/assets/AH5SVqZCOTY.Dy0s7vG7.jpg",O="/assets/B0ys7O7ZkJw.F8RQxzbg.jpg",n="/assets/b6IxELbAFZ0.DyRVOJN-.jpg",q="/assets/bLe6IcCq11g.Cu4VN9oS.jpg",m="/assets/c3nTxpzVElo.Sg1afG2C.jpg",l="/assets/cIMQ_HxaJJY.CGVoFc-T.jpg",B="/assets/3XBrsl6Ffxc.DDVmTASN.jpg",E="/assets/eahZ9J01ORo.D8ODK4nW.jpg",C="/assets/ef3bK-i7KpY.kIKgJc-P.jpg",f="/assets/FXUOY5Igo0c.Ct1CSWLM.jpg",h="/assets/FzwW2Bky6Bk.D2caMGD1.jpg",v="/assets/G9TpXRdmn0.Cnhn-FOH.jpg",Q="/assets/g46FC8hao2s.D4ldwqaA.jpg",u="/assets/GbWKgqZ0dSI.DX9kJhXx.jpg",R="/assets/go_MF4jhJWo.Ce67IKV9.jpg",d="/assets/gq8PggEyrM8.NDXCU5vj.jpg",D="/assets/gq8PggEyrM8.NDXCU5vj.jpg",k="/assets/HabIkUNHasc.B0mLpiQ6.jpg",j="/assets/hw1Yii87RxM.CqpEwY8p.jpg",X="/assets/I-Zu33bDo30.0n2qPdk2.jpg",P="/assets/IMG_1902.SfHmuSfk.jpg",w="/assets/k8LFU1u0sak.BQGwuejW.jpg",x="/assets/KBek7P8fZv0.D4U21HFC.jpg",U="/assets/kIZErru0hPQ.6PXA4XNE.jpg",b="/assets/L1Y7zaMj8PE.VguUOSCj.jpg",H="/assets/l4AfXdugYxI.B3jrxCBM.jpg",S="/assets/l4TXhOK4QoM.DJigi1KC.jpg",W="/assets/Le6oo_ZC4lM.DqhvM8yJ.jpg",N="/assets/nY6yJNMyz98.3L75T474.jpg",I="/assets/P72M_O1Rmo4.b4P86cGv.jpg",J="/assets/Py6vaZK94Eo.BRXpd5bh.jpg",F="/assets/ReyYvAaVVjc.C0fZfJqD.jpg",V="/assets/RlnvWpIoANQ.B63bok3N.jpg",z="/assets/RvB9L4R-Cfo.CaCVfwBx.jpg",K="/assets/s8t-YUu2D74.JW-nyIfm.jpg",L="/assets/sfrUFQk49Rw.nlZ8XQf9.jpg",M="/assets/siS51liS_bI.DxqGFP0Y.jpg",G="/assets/tEcN3JAUI-0.Da8rhuIf.jpg",y="/assets/WJWtnRj_LfY.BYcyYtxM.jpg",T="/assets/xNm7GrGrpx8.dvShsvSM.jpg",Y="/assets/yU0DgmsxoWg.D0Ukepiq.jpg",ps=JSON.parse('{"title":"Приглашение","description":"на Фестиваль свободного творчества","frontmatter":{"title":"Приглашение","description":"на Фестиваль свободного творчества","date":"2017-07-10","youtube":"https://youtu.be/S24ljzGZSLA","icon":"surf-logo.png","cover":"fon.jpg"},"headers":[],"relativePath":"practice/lom/2017/lomfest/index.md","filePath":"practice/lom/2017/lomfest/index.md"}'),Z={name:"practice/lom/2017/lomfest/index.md"};function _($,s,ss,ts,As,os){return a(),A("div",null,s[0]||(s[0]=[o('<p>Спасибо всем, кто принял участие в фестивале ЛОМ 2017. Благодаря вам мы поняли, что делаем большое и важное дело. Теперь мы знаем, что для настоящего погружения в творчество пары дней недостаточно и поэтому теперь ЛОМ — настоящий творческий лагерь для взрослых. Присоединяйтесь к нам!</p><p>ЛОМ — уникальная образовательная лаборатория, где каждый может попробовать свои силы в разнообразии творческих направлений. Это пять открытых мастерских с ежедневными мастер-классами и возможностью воплощать свои собственные проекты.</p><p>Открытая мастерская — это процесс совместного творчества и всё необходимое для него. Крыша над головой, инструменты, материалы и люди, готовые делиться своими знаниями и опытом. Мастера проведут авторские классы, администратор подскажет, где что лежит, а кураторы площадок помогут в освоении разнообразных ремёсел.</p><p>Мы разглядели в природе творчества пять стихий и будем изучать методы работы с каждой из них. Мы понимаем, что самое интересное всегда происходит на стыке, будь то вода и ветер, дерево и металл, творчество и йога, поэтому создаем условия для игры и сочетания всех творческих направлений. С самого рождения каждый человек — творец. Мы впитываем в себя силы природы и превращаем их во что-то совершенно новое. Ещё не научившись говорить, дети находят способы для самовыражения, а затем смело реализуют свои идеи. Взрослые люди нередко забывают ту искренность и непосредственность, с которой сами когда-то лепили, рисовали, пели и придумывали новые игры. Однако стоит хоть ненадолго отвлечься от своих привычных ролей, как внутри вдруг снова просыпается любознательный ребёнок, готовый на практике исследовать мир и самого себя.</p><p>Каждый, кто хоть однажды открыл в себе заново этот неувядающий источник радости, знает пути обращения к нему. Кто-то рисует картины, а кто-то — электрические схемы. Одни лепят посуду из глины, другие вырезают узоры по дереву. Можно выражать себя в мелодиях и ритмах или высвобождать внутренний потенциал в движениях танца и асанах йоги. Главное здесь — не результат, а осознание процесса.</p><p>Свободное творчество — живой процесс раскрытия себя и своих возможностей. В нём человек осознанно выражает видение совершенства окружающего мира. Это доступная каждому человеку возможность почувствовать себя свободным.</p><p><strong>Механическая и химическая обработка органических материалов.</strong></p><p>Резьба по дереву Столярное дело Плотничество Конструирование Переработка</p><p><strong>Тихая кропотливая работа с тонкими материалами</strong></p><p>Рисование Роспись Окраска Декорирование Шитьё Вышивание Плетение</p><p><strong>Работа с электричеством, высокими температурами, металлами и механизмами</strong></p><p>Паяние Резка Сварка Литьё Гнутьё Чеканка</p><p><strong>Придание формы твердым и твердеющим массам</strong></p><p>Лепка из глины Литьё гипса Литьё бетона Формовка силикона Мозаика Фигуры из песка</p><p><strong>Взаимодействие со звуком и тело как инструмент</strong></p><p>Выступления Перформансы Джем-сессии Пение Танец Йога</p><p>Каждый сможет освоиться с несколькими стихиями и обнаружить бесконечный потенциал сочетания различных материалов, творческих техник и приёмов на стыке ремёсел.</p><p>Вдумчивое созерцание природы — неиссякаемый источник вдохновения, столь важного для свободного творчества. Но не менее важна осознанность действий и дисциплина ума. Помочь справиться с прокрастинацией и недостатком концентрации помогут занятия по хатха-йоге, а лекции по основам йоги помогут «распаковать» ваш внутренний потенциал. Начинаясь с телесности, йога восходит к практикам управления вниманием и умом, которые могут быть гармонично интегрированы как в работу в лесных открытых мастерских, так и в любые задачи повседневной жизни.</p><p>Летом хочется вырваться куда-нибудь на побережье, зарядиться чистыми вибрациями свободных стихий. Дышать лесным воздухом, впитывать лучи солнца, ощущать прохладу воды и вглядываться в бездонное небо. Вдохновиться изяществом природы.</p><p>В сосновом лесу на берегу Горьковского водохранилища мы проявим пространство, где каждый сможет раскрыть в себе творца. Обстановка - важная грань творческого процесса. Естественный ритм жизни, созерцание бесконечной игры стихий и вольный ветер, выдувающий суетливые мысли из головы, закладывают красивую основу для восприятия новых идей, дарят силы для плодотворной деятельности.</p><p>Нас приютит живописный песчаный берег широко разлившейся Волги в полутора часах езды от Нижнего Новгорода. Здесь у “моря” уже девятый сезон существует Сёрфлагерь — место, где расцветают люди. Гостеприимное пространство, где есть всё необходимое для гармоничной жизни на природе: домики с печками, просторный кемпинг для палаток, баня, душ и туалеты, щедрая и большая вегетарианская кухня, крытые беседки, сцены, площадки. Тут легко можно забыть о городе и ощутить себя жителем леса.</p><p>Каждый участник лесных открытых мастерских сможет по достоинству оценить то, какие ресурсы дарит работа на природе.</p><h3 id="ищем-мастеров-и-кураторов-площадок" tabindex="-1">Ищем мастеров и кураторов площадок <a class="header-anchor" href="#ищем-мастеров-и-кураторов-площадок" aria-label="Permalink to &quot;Ищем мастеров и кураторов площадок&quot;">​</a></h3><p>Приглашаем мастеров и умельцев принять участие в создании и проведении фестиваля. Предложите ваши услуги, авторские мастер-классы или экопродукцию на продажу на ярмарке. Волонтёрам гарантируется бесплатное проживание и питание в рабочие дни. Мы открыты к партнерству!</p><p><img src="'+p+'" alt=""><img src="'+r+'" alt=""><img src="'+g+'" alt=""></p><h3 id="проживание" tabindex="-1">Проживание <a class="header-anchor" href="#проживание" aria-label="Permalink to &quot;Проживание&quot;">​</a></h3><p>Размещение на одной из подготовленных площадок в лесу.</p><p>2, 3 и 4-хместные двуслойные палатки с ковриками и, при необходимости, со спальным мешком.</p><p>4 домика с 3 двуспальными местами, печкой, газовой плитой, умывальником.</p><p><strong>Кухня Сёрфлагеря готовит исключительно вегетарианские блюда. Трехразовое питание не входит в стоимость билета и оплачивается отдельно.</strong></p><p><strong>На территории Сёрфлагеря запрещёно употребление алкоголя, табака и других наркотиков.</strong></p><h4 id="на-общественном-транспорте-из-нижнего-новгорода" tabindex="-1">На общественном транспорте из Нижнего Новгорода <a class="header-anchor" href="#на-общественном-транспорте-из-нижнего-новгорода" aria-label="Permalink to &quot;На общественном транспорте из Нижнего Новгорода&quot;">​</a></h4><ol><li><p>Доехать до Заволжья автобусом с автостанции &quot;Канавинская&quot; или электричкой с Московского вокзала.</p></li><li><p>С автостанции &quot;Заволжье&quot; доехать на такси (300—350 р.) или на автобусе до пансионата &quot;Буревестник&quot;.</p></li></ol><p>От &quot;Буревестника&quot; пешком 10-20 минут. Можно заплатить 100 рублей на входе в Буревестник, чтобы пройти напрямую, а не обходить через лес.</p><h4 id="на-машине" tabindex="-1">На машине <a class="header-anchor" href="#на-машине" aria-label="Permalink to &quot;На машине&quot;">​</a></h4><p>450 км от Москвы, 75 км от Нижнего Новгорода, 15 км от Городца. Ориентир — пансионат Буревестник. Прямо перед въездом на базу сворачиваем направо на лесную дорогу и едем по стрелкам на деревьях.</p><p>Точные координаты для навигаторов:</p><p><strong>N56°45,33&#39; E43°21,31&#39;</strong></p><p>Если у вас есть свободное место в автомобиле или вы хотели бы найти попутчиков в своем городе — <a href="https://vk.com/topic-150174855_35656036" class="external-link" target="_blank" rel="noreferrer">напишите об этом в обсуждении</a>.</p><p><img src="'+e+'" alt=""><img src="'+i+'" alt=""><img src="'+c+'" alt=""><img src="'+O+'" alt=""><img src="'+n+'" alt=""><img src="'+q+'" alt=""><img src="'+m+'" alt=""><img src="'+l+'" alt=""><img src="'+B+'" alt=""><img src="'+E+'" alt=""><img src="'+C+'" alt=""><img src="'+f+'" alt=""><img src="'+h+'" alt=""><img src="'+v+'" alt=""><img src="'+Q+'" alt=""><img src="'+u+'" alt=""><img src="'+R+'" alt=""><img src="'+d+'" alt=""><img src="'+D+'" alt=""><img src="'+k+'" alt=""><img src="'+j+'" alt=""><img src="'+X+'" alt=""><img src="'+P+'" alt=""><img src="'+w+'" alt=""><img src="'+x+'" alt=""><img src="'+U+'" alt=""><img src="'+b+'" alt=""><img src="'+H+'" alt=""><img src="'+S+'" alt=""><img src="'+W+'" alt=""><img src="'+N+'" alt=""><img src="'+I+'" alt=""><img src="'+J+'" alt=""><img src="'+F+'" alt=""><img src="'+V+'" alt=""><img src="'+z+'" alt=""><img src="'+K+'" alt=""><img src="'+L+'" alt=""><img src="'+M+'" alt=""><img src="'+G+'" alt=""><img src="'+y+'" alt=""><img src="'+T+'" alt=""><img src="'+Y+'" alt=""></p>',40)]))}const rs=t(Z,[["render",_]]);export{ps as __pageData,rs as default};
