import{_ as t}from"./chunks/vimeo-embed.l7WPTDEa.js";import{_ as i,o as a,c as o,b as n,e as r}from"./chunks/framework.7WwFwkJk.js";const T=JSON.parse('{"title":"СНежный ЛОМ 2018","description":"Новогодние лесные открытые мастерские","frontmatter":{"title":"СНежный ЛОМ 2018","description":"Новогодние лесные открытые мастерские","date":"2017-12-30T00:00:00.000Z","end_date":"2018-01-07T00:00:00.000Z","cover":"winter-lom.png","vimeo":"https://vimeo.com/287449460"},"headers":[],"relativePath":"practice/lom/2018-winter/index.md","filePath":"practice/lom/2018-winter/index.md"}'),l={name:"practice/lom/2018-winter/index.md"},c=r('<h3 id="снежныи-лом-—-это-зимнии-творческии-лагерь-для-людеи-всех-возрастов" tabindex="-1">Снежный ЛОМ — это зимний творческий лагерь для людей всех возрастов <a class="header-anchor" href="#снежныи-лом-—-это-зимнии-творческии-лагерь-для-людеи-всех-возрастов" aria-label="Permalink to &quot;Снежный ЛОМ — это зимний творческий лагерь для людей всех возрастов&quot;">​</a></h3><h3 id="_8-днеи-30-участников-2-смены" tabindex="-1">8 дней, 30 участников, 2 смены <a class="header-anchor" href="#_8-днеи-30-участников-2-смены" aria-label="Permalink to &quot;8 дней, 30 участников, 2 смены&quot;">​</a></h3><p>ЛОМ — проект для творческих людей и тех, кто мечтает таковыми стать. Тех, кто давно хочет прикоснуться к миру мастерских или озарен креативными идеями, но в городском ритме не находит возможностей. Здесь найдутся.</p><p>За одну смену СНежного ЛОМа можно:</p><ul><li>Научиться делать вещи по своему проекту для близких или на продажу</li><li>Встретить много новых созвучных друзей и партнеров по творчеству</li><li>Реализовать задумки, попробовать инструменты, посоветоваться с мастерами</li><li>Отдохнуть от города и успокоить ум, гуляя по зимнему лесу и ледяной глади Волги</li><li>Снять фильм, написать музыку, испечь пирог, построить снежную крепость, покататься на кайте</li><li>Удивиться раскрытым талантам и созданным произведениям</li></ul><p>Как на настоящей фабрике подарков Деда Мороза мы будем делать подарки своим близким и друзьям в мастерских. Шагнем в новый год с коллекцией самостоятельно выполненных изделий и багажом новых умений.</p><p>Для вас у нас есть теплые дома с печками и баней, несметное количество идей для творчества в мастерских, заботливые мастера, которые помогут освоить и отработать новые навыки, а также вкусная еда, огромный лес, и широкое дружелюбное Горе-море!</p><p>Пожить на природе зимой в комфортных условиях с веселой компанией и веером интересных занятий на выбор – не это ли новогодние праздники мечты! Приглашаем вас подарить себе такую радость.</p>',8);function _(p,s,d,m,h,u){const e=t;return a(),o("div",null,[c,n(e,{link:"https://vimeo.com/248853248"})])}const v=i(l,[["render",_]]);export{T as __pageData,v as default};
