import{_ as t,o as a,c as o,a as e,d as c}from"./chunks/framework.6c3c27db.js";const v=JSON.parse('{"title":"ЛОМ: Лесные Открытые Мастерские","description":"Лагерь совместного свободного творчества на природе","frontmatter":{"title":"ЛОМ: Лесные Открытые Мастерские","description":"Лагерь совместного свободного творчества на природе","date":"2017-05-01T00:00:00.000Z","status":"request","cover":"lom.jpg","url":"https://lomcamp.ooley.ru","vk":"lomcamp","instagram":"lomcamp","facebook":"lomcamp","vimeo":"https://vimeo.com/317435831"},"headers":[],"relativePath":"practice/lom/index.md","filePath":"practice/lom/index.md"}'),s={name:"practice/lom/index.md"},r=e("h3",{id:"лом-—-это-возможность-зарядиться-природои-общением-с-единомышленниками-обучиться-новым-умениям-и-воплотить-вдохновение-в-красивых-и-полезных-произведениях",tabindex:"-1"},[c("ЛОМ — это возможность зарядиться природой, общением с единомышленниками, обучиться новым умениям и воплотить вдохновение в красивых и полезных произведениях. "),e("a",{class:"header-anchor",href:"#лом-—-это-возможность-зарядиться-природои-общением-с-единомышленниками-обучиться-новым-умениям-и-воплотить-вдохновение-в-красивых-и-полезных-произведениях","aria-label":'Permalink to "ЛОМ — это возможность зарядиться природой, общением с единомышленниками, обучиться новым умениям и воплотить вдохновение в красивых и полезных произведениях."'},"​")],-1),i=e("p",null,"Лесные открытые мастерские — новый формат совместного отдыха на природе. Это лагерь для людей всех возрастов, где вдали от городской суеты можно расслабиться, наполниться общением с живой природой и единомышленниками и реализовать вдохновение в разнообразных творческих проектах.",-1),n=e("p",null,"Мы придумали и воплотили новый формат отдыха — недельный творческий лагерь, охватывающий все основные ремесленные направления. Мастера выезжают на природу со своими мастерскими и там делятся опытом с гостями лагеря. На занятиях преподаватели преподают курсы своего искусства, а на открытых сессиях кураторы помогают умельцам реализовывать личные и совместные проекты. И всё это на свежем воздухе, с зарядками, йогой, вегетарианской кухней и простором для полетов фантазии.",-1),l=[r,i,n];function d(m,p,_,h,u,f){return a(),o("div",null,l)}const k=t(s,[["render",d]]);export{v as __pageData,k as default};
