import{_ as s,o as t,c as r,e as a}from"./chunks/framework.7WwFwkJk.js";const e="/assets/IMG_2345.Q3kcLmOs.jpg",_="/assets/IMG_2343.6OyrG9Y1.jpg",p="/assets/21748823874_62a684544b_k.Lck2cPJA.jpg",c="/assets/22183580670_a6186fb3b6_k.wIbwaoBL.jpg",o="/assets/IMG_2341._UOhnxa5.jpg",i="/assets/IMG_2344.K4c_JKPZ.jpg",n="/assets/22183838348_078c72c2c1_k.80v0njhz.jpg",l="/assets/21748791874_ac694c4cd1_k.DCS6E7q6.jpg",g="/assets/21748796354_0a06aa8b5e_k.fRE7S7fa.jpg",m="/assets/22345549576_d613e5d24c_k.D1ke1-n3.jpg",k="/assets/22345556956_75dcc2e339_k.yHAt3AGR.jpg",j="/assets/22358475452_cbd4cf0b6f_k.H5KCPHZr.jpg",f="/assets/22371562285_7d3c2ee341_k.7RWp24N0.jpg",d="/assets/22382279541_4c0fac3ac7_k.WXOrnX0Z.jpg",b="/assets/22382295681_e00e1bf1de_k.ox3RqSox.jpg",h="/assets/22358503222_d37f227bd8_k.qkrwWBWA.jpg",u="/assets/22183853738_15a8988641_k.6dGfKi7U.jpg",I="/assets/22183862838_4775e552bc_k.p2bpMCpb.jpg",M="/assets/22184725479_08885e0134_k.gbICHStb.jpg",G="/assets/21748818604_43484c1509_k.Z3BK5PSf.jpg",w="/assets/21750485893_e31a1d9494_k.0rM_ohUV.jpg",x="/assets/IMG_2349.EIz7d6r2.jpg",v="/assets/21748814924_e9618acbdf_k.yCGLpf6A.jpg",S="/assets/22358493072_70d652cb05_k.Pu-bNEly.jpg",y="/assets/22183849828_a6cd0b9794_k.DKqYCn2Z.jpg",q="/assets/IMG_2351.pEwVR-5c.jpg",P="/assets/IMG_2352.TEVp6ZvW.jpg",T="/assets/22184736079_c9eb26ae52_k.CjHfynih.jpg",C="/assets/IMG_7900.1Op0USmp.jpg",E="/assets/IMG_7901.Gzd73NJv.jpg",L="/assets/IMG_2354.xT4eYs1w.jpg",Z="/assets/IMG_2355.aDyfmB70.jpg",O="/assets/IMG_2356.hOJg1j0C.jpg",A="/assets/IMG_2346.UC05lXDp.jpg",D="/assets/IMG_2347.Q4gSj-Sk.jpg",N="/assets/IMG_2348.nOfLuTl7.jpg",K="/assets/22382310941_cc9b427860_k.g15Za_2w.jpg",B="/assets/22184742289_fa8af1d599_k.aXHNxTSM.jpg",H="/assets/N7QCoZCYSiQ._G3u2v5q.jpg",J="/assets/u7jocn8q4XU.lLbIosGO.jpg",U="/assets/JVnHQMj8T1U.nfP8eZ5M.jpg",W="/assets/MAf6nRWPWvI.MoEmSDWr.jpg",R="/assets/IMG_2357.DjExsEIX.jpg",V="/assets/IMG_2353.nqvJrreu.jpg",Y="/assets/vQlNJ3nsEIA.qsIunk2M.jpg",Q="/assets/2s960tvMsZo.BLe5ZUsZ.jpg",X="/assets/22183555270_a622b0bbe5_k.Yga02PY3.jpg",os=JSON.parse('{"title":"Дворовая мастерская в Открытом павильоне","description":"Фестиваль Московского урбанистического форума 2015","frontmatter":{"title":"Дворовая мастерская в Открытом павильоне","description":"Фестиваль Московского урбанистического форума 2015","date":"2015-10-17","cover":"cover.jpg"},"headers":[],"relativePath":"practice/ooley42/city-workshop/index.md","filePath":"practice/ooley42/city-workshop/index.md"}'),$={name:"practice/ooley42/city-workshop/index.md"},z=a('<p>В субботу 17 октября 2015 года -2 этаж Московского Манежа уже во второй раз превратился в Открытый павильон — прообраз общественного центра, сочетающего в себе пространство для представления локальных городских проектов, конструктивного общения городских активистов и их совместного творчества. Каждый посетитель Фестиваля Московского урбанистического форума имел возможность не только узнать о различных социальных инициативах горожан, но и непосредственно включиться в неформальные городские практики. Центром творческой активности Павильона стала секция &quot;Дворовая мастерская&quot;, в подготовке и реализации которой команда OOLEY приняла активное участие. Совместно с <a href="http://vverh.su/" class="external-link" target="_blank" rel="noreferrer">Центром равных возможностей &quot;Вверх!&quot;</a> и их мастерской <a href="http://vverh.su/posts/masterskaya-rukiottuda-na-festivalnom-dne-urbanisticheskogo-foruma-17-oktyabrya/" class="external-link" target="_blank" rel="noreferrer">&quot;РукиОттуда&quot;</a>, а также с ребятами из <a href="http://www.108lab.ru/" class="external-link" target="_blank" rel="noreferrer">проекта 108Lab</a> мы подготовили насыщенную программу воркшопов, посвященных неформальным практикам освоения города.</p><p><img src="'+e+'" alt=""><img src="'+_+'" alt=""><img src="'+p+'" alt=""><img src="'+c+'" alt=""></p><p>В 10 утра Павильон открыл свои двери для посетителей, ожила и Дворовая мастерская. Каждый желающий мог включиться в коллективную сборку деревянного Павильона общественника вокруг одной из технических колонн зала. Всего за пару часов в строительстве павильона поучаствовали не менее 8 человек и каждый получил новый опыт и впечатления. Хрупкие девушки, впервые взявшие в руки шуруповёрт, ловко скрепили саморезами и уголками нежно розовые брусья каркаса, покрытого специальным огнезащитным составом. Ребята, вызвавшиеся им на помощь, придали жесткости конструкции, прикрутив на углы синие треугольники из фанеры. Крышу собрали из небольших деревянных реек и для дополнительной прочности натянули над потолком веревку. Конструкция получилась устойчивой и прочной, вполне способной некоторое время выдержать суровые условия городского двора. Защитой от непогоды может стать накинутый сверху старый рекламный баннер или просто полиэтиленовая плёнка. Внутри можно разместить одну-две скамейки и стол для обсуждения проблем локального сообщества. Также под таким нехитрым навесом возможно организовать полки для бук-кроссинга или ящики и стеллажи для взаимного обмена ненужными вещами.</p><p><img src="'+o+'" alt=""><img src="'+i+'" alt=""></p><p><img src="'+n+'" alt=""><img src="'+l+'" alt=""><img src="'+g+'" alt=""><img src="'+m+'" alt=""><img src="'+k+'" alt=""><img src="'+j+'" alt=""><img src="'+f+'" alt=""><img src="'+d+'" alt=""><img src="'+b+'" alt=""><img src="'+h+'" alt=""><img src="'+u+'" alt=""><img src="'+I+'" alt=""><img src="'+M+'" alt=""><img src="'+G+'" alt=""><img src="'+w+'" alt=""></p><p>Через два часа после открытия начался следующий воркшоп, представленный нашим полиграфическим проектом <a href="http://www.skillpad.ru" class="external-link" target="_blank" rel="noreferrer">SKILLPAD</a>. Мы предложили гостям мастерской придумать и воплотить в жизнь свои собственные идеи вывески или указателя для различных городских инициатив. Уже действующие активисты воспользовались возможностью сделать вывески своих инициатив, а остальные смогли дать волю своей фантазии.</p><p><img src="'+x+'" alt="IMG_2349"><img src="'+v+'" alt=""><img src="'+S+'" alt=""><img src="'+y+'" alt=""><img src="'+q+'" alt=""><img src="'+P+'" alt=""></p><p>Мы предложили участникам воркшопа нарисовать рисунки на самоклейке и вырезать из неё стикеры — элемент сложной системы неформального уличного общения, но посетителям Форума намного интереснее оказалось взаимодействие с деревом. Мы дали возможность выпилить из фанеры заготовку вывески, нанести на неё эскиз карандашом, а затем выжечь изображение и текст специальным аппаратом. Волны ностальгии накатывали на каждого, кто вдруг вспоминал, как выжигал что-нибудь на дереве в далёком детстве. Возможно именно воспоминания прошлого опыта и простота базовых техник работы с выжигательным аппаратом стали причиной особенной популярности этого воркшопа, затянувшегося вместо положенных двух на все четыре часа. Все более изобретательные идеи участников не позволили нам остановить их творческий процесс и приятный запах жженой березы разносился по павильону до самого вечера.</p><p><img src="'+T+'" alt=""><img src="'+C+'" alt=""><img src="'+E+'" alt=""><img src="'+L+'" alt=""><img src="'+Z+'" alt=""><img src="'+O+'" alt=""><img src="'+A+'" alt=""><img src="'+D+'" alt=""><img src="'+N+'" alt=""><img src="'+K+'" alt=""></p><p><img src="'+B+'" alt=""></p><p>Кстати, нам удалось продемонстрировать на практике небольшую хитрость с переносом изображения на поверхность с помощью планшета и карманного проектора — таким приёмом уже давно пользуются уличные граффити-художники и мы были рады поделиться этой неформальной уличной практикой с окружающими.</p><p>Несмотря на то, что посетители Павильона продолжали рисовать и выжигать всё новые вывески и указатели, команда проекта 108Lab начала свой воркшоп, посвященный необычным методам озеленения. За полтора часа участники воркшопа собрали из ПВХ-труб и геотекстиля четыре <a href="http://www.liveinternet.ru/users/5129556/post355244070" class="external-link" target="_blank" rel="noreferrer">полноценных вертикальных грядки</a>. Ребята <a href="https://drive.google.com/file/d/0B6KamMnlu188dkplTGlTeHZacmc/view?usp=sharing" class="external-link" target="_blank" rel="noreferrer">составили удобную инструкцию</a>  и участники могли пройти все этапы практически самостоятельно. Просверлить множество небольших отверстий в тонкой трубке для полива, замотать её специальной тканью и зафиксировать веревкой оказалось совершенно несложно. Немного повозиться пришлось со сверлением крупных отверстий в толстой трубе — для этого использовались сверла-коронки, требующие повышенной осторожности при работе. Дальше всё просто. Затыкаем трубки крышками, вставляем одну в другую и заполняем их сначала гравием, а потом и землёй. Высаживаем в отверстия саженцы и наливаем воду в трубку для полива. Готово! Менее чем через час после начала воркшопа уже готовый Павильон общественника украсила первая полноценная вертикальная грядка клубники. К сожалению, наполненные грунтом, такие грядки оказались слишком тяжёлыми чтобы участники могли забрать их с собой, но если проводить такой воркшоп где-нибудь на улице результатом может послужить настоящее преображение пространства.</p><p><img src="'+H+'" alt=""><img src="'+J+'" alt=""><img src="'+U+'" alt=""><img src="'+W+'" alt=""><img src="'+R+'" alt=""><img src="'+V+'" alt=""><img src="'+Y+'" alt=""><img src="'+Q+'" alt=""></p><p>Завершал программу работы Дворовой мастерской воркшоп, посвященный созданию мобильной уличной мебели. Мы предложили участникам самостоятельно изучить и воспроизвести конструкцию <a href="/practice/project/stool/">нашего складного табурета</a>, предоставив все необходимые инструменты и материалы, но предоставив возможность самостоятельно спланировать и осуществить сборку. Мы намеренно старались не давать ребятам советов и подсказок, предоставив им возможность объединиться в команды и самостоятельно решить эту технологическую головоломку. Четыре пары участников взялись за дело и в течение двух часов активно нарабатывали опыт совместного решения производственных задач. Каждый из коллективов столкнулся с множеством неожиданных трудностей и нестыковок, осознал свои ошибки и промахи и, конечно, получил свой результат. Нельзя сказать, что в результате получилась полноценная складная мебель, но все участники воркшопа оценили по достоинству возможность полного погружения в процесс совместного решения творческих задач. И, несмотря на то, что табуреты получились довольно странного вида, все до единого участники забрали с собой. Будем надеяться, они восприняли этот воркшоп как вызов и однажды смогут довести начатое дело до конца.</p><p>Не обошлось и без незапланированных проектов. Отец с маленьким сыном практически целый день старательно расчерчивали и пилили фанеру, часов за 5 смастерив огромный герб группы <a href="http://www.slipknot1.com/" class="external-link" target="_blank" rel="noreferrer">SLIPKNOT</a>. Уже под конец дня в мастерскую буквально ворвались несколько бородатых парней и, показав эскиз высокого стула, получили в своё распоряжение запас материалов и инструменты и примерно за час смогли собрать целый трон, который благополучно забрали с собой. Мы стараемся проводить все наши события в формате открытой мастерской, где каждый умелец, имеющий достаточно навыков, может самостоятельно воспользоваться нашими возможностями для реализации своего творческого проекта.</p><p>Хочется выразить отдельную благодарность инициативе <a href="http://delaisam.org/" class="external-link" target="_blank" rel="noreferrer">Делай Сам</a> и агентству <a href="http://publicspaces.pro/" class="external-link" target="_blank" rel="noreferrer">Публичные пространства</a> за возможность принять участие в таком важном для городского активизма событии. Несмотря на неотрывную работу в Дворовой мастерской, нам удалось познакомиться со многими активистами из различных городов, наладить контакты и увидеть очертания дальнейших форм сотрудничества в нелегком деле преображения наших городов. До новых встреч!</p><p><img src="'+X+'" alt=""></p>',17),F=[z];function ss(ts,rs,as,es,_s,ps){return t(),r("div",null,F)}const is=s($,[["render",ss]]);export{os as __pageData,is as default};
