import{_ as t,a as o,o as s,b as e}from"./chunks/framework.CKEhg_8n.js";const a="/assets/YKYrWwEyNEQ.DmJs4ZQz.jpg",y=JSON.parse('{"title":"Дворовые дети и сторож","description":"Местные жители","frontmatter":{"title":"Дворовые дети и сторож","description":"Местные жители","date":"2015-07-01","cover":"cctL3aesNEQ.jpg"},"headers":[],"relativePath":"practice/ooley42/dvorovye-deti/index.md","filePath":"practice/ooley42/dvorovye-deti/index.md"}'),c={name:"practice/ooley42/dvorovye-deti/index.md"},r=e("p",null,"Передвижная мастерская не стоит без дела, а используется местными жителями по назначению. Пускай пока еще большинство только интересуется и размышляет о творческих идеях, некоторые уже активно осваивают наши возможности и воплощают свои проекты. Наиболее активный интерес, конечно, проявляет дворовая ребятня, бездальничающая на каникулах в городе. Приобщение молодёжи к самостоятельному творчеству — замечательное дело, однако работа с детьми требует больше сил и времени куратора мастерской. Спасает только то, что сторож с соседней автостоянки тоже заинтересовался проектом и занялся с ребятами азами столярного дела.",-1),i=e("p",null,"Мы активно обдумываем то, как можно оптимизировать взаимодействие с детворой. Пока идеи сводятся к выделению отдельных дней/часов для работы с детьми, на которые заранее набирать взрослых волонтёров, готовых уделить время обучению заинтересованных ребят. Изредка вот так поучить детишек каким-нибудь умениям и навыкам для многих может показаться крайне полезным и приятным занятием, в особенности для умелых, но в какой-то степени отрезанных от общества, пенсионеров и безработных. Будем размышлять и формулировать это дальше. Будем рады услышать любые комментарии, идеи и предложения!",-1),n=e("p",null,[e("img",{src:a,alt:"YKYrWwEyNEQ"})],-1),d=[r,i,n];function p(_,l,m,v,f,h){return s(),o("div",null,d)}const x=t(c,[["render",p]]);export{y as __pageData,x as default};
