import{_ as t,a as i,J as o,o as r}from"./chunks/framework.BWf-PbcG.js";const a="/assets/propavshee.Cx3sgAz6.jpg",s="/assets/IMG_6724.BQO0PUDU.jpg",g=JSON.parse('{"title":"Кража из прицепа","description":"Уроки городской жизни","frontmatter":{"title":"Кража из прицепа","description":"Уроки городской жизни","date":"2015-07-30","cover":"IMG_1264.jpg"},"headers":[],"relativePath":"practice/ooley42/krazha/index.md","filePath":"practice/ooley42/krazha/index.md"}'),l={name:"practice/ooley42/krazha/index.md"};function p(n,e,c,m,d,_){return r(),i("div",null,e[0]||(e[0]=[o('<p>Однажды это должно было произойти. Обычный туристический прицеп-автодом с надписью &quot;открытая мастерская&quot; — не только замечательное место встреч и творческих начинаний, но и лакомый кусочек для готовых украсть чего-нибудь ценного. Проходной двор, стоянка у помойки, темнота. Неизвестному хватило времени расковырять и каким-то хитрым способом открыть входную дверь прицепа и со толком и расстановкой поковыряться в его содержимом. Пропало, конечно, не все — похититель унес столько, сколько смог. Взломанную дверь он смог приладить на место и она, хоть и не без повреждений, даже осталась в работоспособном состоянии.</p><p><strong>Итого пропало:</strong></p><ul><li>швейная машинка Brother Innov&#39;IS 450,</li><li>бормашинка DREMEL 3000 и наборы насадок к ней,</li><li>набор слесарного инструмента в чемодане и множество гаечных ключей,</li><li>электролобзик Sturm,</li><li>содержимое ящика для даров и еще мелочи.</li></ul><p>Потери, конечно, ощутимые, но вполне восполнимые. Эксперимент продолжается!</p><p>Однако уроки эти учтём. Есть несколько направлений <strong>усовершенствования проекта OOLEY42</strong>:</p><ul><li>установить сигнализации в прицепе <em>(над этим уже работаем)</em>,</li><li>поставить прицеп-мастерскую на территории парка или другой охраняемой площадке, где люди могут собираться днём <em>(это рассматриваем на будущее)</em>,</li><li>использовать прицеп-мастерскую в качестве площадки для обмена и совместного использования простого недорогого инструмента, а ценный инструмент хранить как-то отдельно <em>(по домам, например)</em> и приносить по договорённости.</li></ul><p><img src="'+a+'" alt="пропавшее"></p><p><img src="'+s+'" alt=""></p>',8)]))}const f=t(l,[["render",p]]);export{g as __pageData,f as default};
