import{_ as s,o as t,c as a,b as _}from"./chunks/framework.dd5ab16e.js";const p="/assets/IMG_1584.f8a67294.jpg",c="/assets/IMG_1586.81d4e041.jpg",o="/assets/IMG_1591.6d554a4d.jpg",r="/assets/IMG_1594.d35cbf1c.jpg",e="/assets/IMG_1595.e3b14e2c.jpg",i="/assets/IMG_1596.457d259f.jpg",m="/assets/IMG_15971.92b3b7aa.jpg",g="/assets/IMG_1598.47f8e94b.jpg",n="/assets/IMG_1599.4d39f6aa.jpg",l="/assets/IMG_1601.64a9829a.jpg",I="/assets/IMG_1608.afac87b5.jpg",j="/assets/IMG_1609.3bad46c6.jpg",d="/assets/IMG_1618.2a1caa2c.jpg",G="/assets/IMG_1619.b2348935.jpg",M="/assets/IMG_1628.9b90afbd.jpg",f="/assets/IMG_1629.3be2ac2f.jpg",b="/assets/IMG_1635.fcc786d2.jpg",u="/assets/IMG_1636.cb089f35.jpg",O="/assets/IMG_1607.2ea99d9b.jpg",h="/assets/IMG_1603.165b480e.jpg",q="/assets/IMG_1606.317f308f.jpg",E="/assets/IMG_1610.4517537e.jpg",k="/assets/IMG_1616.610ad2fd.jpg",x="/assets/IMG_1617.b887ea75.jpg",L="/assets/IMG_1620.7f40271e.jpg",S="/assets/IMG_1633.73289228.jpg",T="/assets/IMG_1634.f94105a1.jpg",v="/assets/IMG_1626.5727496b.jpg",y="/assets/IMG_1640.ed4ea50e.jpg",z="/assets/IMG_1638.4798db37.jpg",A="/assets/IMG_1642.d864d3d2.jpg",P="/assets/IMG_1602.aa371c5d.jpg",Y="/assets/IMG_1611.4cdb67c6.jpg",w="/assets/IMG_1623.4cab7a96.jpg",N="/assets/IMG_1641.a49e3566.jpg",$="/assets/IMG_1632.d2efc3d8.jpg",B="/assets/IMG_1637.f16b8250.jpg",D="/assets/IMG_1614.1bfcfa83.jpg",J="/assets/HPrl3vMoIvA.4e005694.jpg",V="/assets/vJNizGTfJO8.bf6eda21.jpg",C="/assets/IMG_1639.6ca7ac67.jpg",H="/assets/4Tk6nEwk8A.c0404f30.jpg",_s=JSON.parse('{"title":"Целый день творчества в открытой мастерской OOLEY42 в Музеоне","description":"","frontmatter":{"title":"Целый день творчества в открытой мастерской OOLEY42 в Музеоне","date":"2015-08-04","cover":"IMG_1622.jpg"},"headers":[],"relativePath":"practice/ooley42/muzeon/index.md","filePath":"practice/ooley42/muzeon/index.md"}'),K={name:"practice/ooley42/muzeon/index.md"},R=_('<p>&quot;Отличная возможность представить наши проекты московской публике, да еще и в парке Музеон!&quot; — подумали мы и команда OOLEY включилась в организацию зоны мастерских <a href="/practice/ooley42/muzeon/">фестиваля Планета людей</a>. <a href="/practice/ooley42/">Прицеп-мастерская OOLEY42</a> — замечательная творческая база для открытых проектов и проведения классов на свежем воздухе и даже несмотря на <a href="/practice/ooley42/krazha/">недавние потери ценных инструментов</a>, мы решили провести довольно сложные и многоэтапные мастер-классы с деревом, красками и тканью.</p><p><img src="'+p+'" alt=""><img src="'+c+'" alt=""><img src="'+o+'" alt=""><img src="'+r+'" alt=""><img src="'+e+'" alt=""><img src="'+i+'" alt=""></p><p>2 августа, 7 утра, собираем прицеп в дорогу. Учитывая опыт летающего по салону и крушащего все подряд ящика с гаечными ключами, складываем все тяжёлые инструменты в машину, закрепляем и складываем всё остальное в прицепе — можно выезжать. Уже к 10 часам мы в парке с наполненной инструментами и материалами мастерской. В программе фестиваля OOLEY значится как &quot;Мастерская авторской ткани и бумаги&quot; и мы ставим перед собой довольно смелую задачу: провести целых три междисциплинарных мастер-класса, в ходе которых ученики пройдут все этапы изготовления сумки из ткани с рисунком авторской ручной набойки.</p><p>Ученикам класса предстояло освоить азы резьбы по дереву электрической бормашинкой с различными насадками, вытачивая из брусков лиственницы узорные штампы. Не только из-за того, что на класс из пяти человек у нас было всего 3 бормашинки, но и по причине удобства и эффективности мы подготовились и к изготовлению пластиковых трафаретов для печати. Технология крайне проста: рисунок на обычной бумаге ламинируется и затем вырезается канцелярским ножом. Получается стойкий пластиковый трафарет, подходящий для многократной печати. Мы подготовили несколько тюбиков яркой акриловой краски, нарезали губок для её нанесения на штампы и ткань. Для завершающей стадии мы привезли нашу новенькую швейную машину Brother и еще машинку Singer одного нашего участника. Отрезы ткани для сумок и лямок мы сделали заранее.</p><p><img src="'+m+'" alt=""><img src="'+g+'" alt=""><img src="'+n+'" alt=""><img src="'+l+'" alt=""><img src="'+I+'" alt=""><img src="'+j+'" alt=""><img src="'+d+'" alt=""><img src="'+G+'" alt=""><img src="'+M+'" alt=""><img src="'+f+'" alt=""><img src="'+b+'" alt=""><img src="'+u+'" alt=""></p><p>В 11 утра фестиваль официально открылся и уже в 12 мы, быстро набрав группу заинтересованных учеников, начали первый класс. Раздав очки и респираторы, мы рассказали как включать и регулировать вращение бормашинки, как менять и закреплять насадки. Короткая демонстрация, несколько советов, преподавательского внимания и корректировок и ученики принимаются за самостоятельную работу над своими проектами. Несмотря на то, что резьба по дереву, даже с использованием электроинструмента — не самое быстрое дело, люди резали штампы, в зависимости от сложности, от 30 до 75 минут. За весь день никто не поранился и сильно не испортил свой рисунок ошибками при резьбе, а те помарки, что случались, можно было легко исправить. Листочки, &quot;огурцы&quot;, звездочка, сова, облачко, сердечко, какой-то символ из Гарри Поттера, руна &quot;есть&quot; в виде штампов — отличные результаты для первого дня работы нашей импровизированной мастерской печати! Вырезание трафаретов поначалу может вызывать некоторые трудности, но, воспользовавшись ценными советами преподавателей, ученики смогли придумать массу собственных творческих приёмов в этой технике.</p><p><img src="'+O+'" alt=""><img src="'+h+'" alt=""><img src="'+q+'" alt=""><img src="'+E+'" alt=""><img src="'+k+'" alt=""><img src="'+x+'" alt=""><img src="'+L+'" alt=""><img src="'+S+'" alt=""><img src="'+T+'" alt=""></p><p>Когда первый этап завершён наступает пора набойки. На лист пластика или бумаги выдавливаем немного краски, набираем ровный слой на губку и переносим легкими ударами на штамп или непосредственно на ткань с использованием трафарета. Важно не переборщить с краской — бороздки штампа забиваются и оттиск получается размазанным. К тому же рисунок будет долго сохнуть, что может сильно растянуть класс по времени. Мы нашли способ ускорить процесс сушки: мы применили наш технический фен в I режиме (80°С) — отличный способ высушить и немного закрепить рисунок. Ученики легко обнаружили огромный потенциал сочетания различных техник, охотно делились штампами и трафаретами друг с другом.</p><p>Когда рисунок высох, можно приступать к шитью. Задача не сложная, но требует внимания и усидчивости, ведь это работа со сложным механизмом, тонкой нитью и иглой. К сожалению мы не смогли справиться с морально устаревшим вертикальным челноком машинки Singer и большинство сумок пришлось сшивать на нашей единственной машинке, у которой к тому же сразу сломался механизм вставления нитки в иглу. Даже несмотря на это нам удалось уложиться в расписание и все классы довольно точно уместились в отведённые двухчасовые интервалы.</p><p><img src="'+v+'" alt=""><img src="'+y+'" alt=""><img src="'+z+'" alt=""><img src="'+A+'" alt=""><img src="'+P+'" alt=""></p><p>Для удобства работы с группой учеников мы выдавали карточки &quot;Ученик&quot; по количеству мест в классе. Первый класс привлёк внимание посетителей фестиваля, к началу второго класса в 14 часов желающих уже было столько, что многим пришлось отказать, а 6 мест в третьем классе с 18 до 20 часов нам пришлось разыграть среди окружающих жребием. В мешочек с заветными бумажками тянулось более двух десятков рук, причём с дальних рядов тянулись уже руки тех, кто и не знал, что там разыгрывается. Стоит отметить, что этот способ показался публике справедливым и однозначным, а в результате набралась ровная группа способных учениц.</p><p><img src="'+Y+'" alt=""></p><p>Сделано более дюжины штампов, вырезаны десятки трафаретов, сшито 15 уникальных сумок — и это еще не все результаты!</p><p>С самого утра к нашей команде присоединился в роли куратора волонтёр Слава, что дало возможность мастерской быть по-настоящему открытой. В промежутках между классами Слава осваивал гравировку камней бормашинкой и охотно подключал заинтересованных в этот процесс. Днём к нам пришла умелица Алиса с обрезками кухонного ДСП, веревкой, клеем и некоторой фурнитурой. Куратор Слава помог ей в освоении электролобзика и шуруповёрта и часа через три приятного совместного труда на свежем воздухе, Алиса практически самостоятельно изготовила изящный столик для комнатных растений. Тест-драйв столика на колёсах выявил недостатки конструкции, которые были устранены тут же на месте. Открытая мастерская в действии!</p><p><img src="'+w+'" alt=""><img src="'+N+'" alt=""></p><p><img src="'+$+'" alt=""><img src="'+B+'" alt=""><img src="'+D+'" alt=""><img src="'+J+'" alt=""><img src="'+V+'" alt=""></p><p>Также прицеп OOLEY42 стал площадкой для презентации посетителям парка Музеон <a href="http://www.skillpad.ru" class="external-link" target="_blank" rel="noreferrer">проекта SKILLPAD</a> — грамотных блокнотов в точку, яркие обложки и необычная линовка которых привлекли живой интерес публики. К сожалению в этот раз нам не удалось провести мастер-класс по самостоятельной сборке блокнотов, но мы обязательно устроим такое событие в будущем.</p><p><img src="'+C+'" alt=""></p><p>В целом мы получили колоссальный опыт работы с широкой публикой, отточили наши преподавательские навыки, обнаружили некоторые простые, но важные правила организации мастерской, рассказали и показали людям, что такое открытая мастерская и как можно поучаствовать в её работе. Мы практически не акцентировали внимания на добровольческом характере нашей затеи и не удивлены тем, что в нашем ящике для даров собралась совсем незначительная сумма. Однако это не значит, что мы откажемся от такого формата работы. Наработан огромный массив практического опыта и настаёт время его осмысления. Ждите наших новых выводов и размышлений!</p><p><img src="'+H+'" alt=""></p>',20),F=[R];function Q(U,W,X,Z,ss,ts){return t(),a("div",null,F)}const ps=s(K,[["render",Q]]);export{_s as __pageData,ps as default};
