import{_ as s,o as t,c as a,b as c}from"./chunks/framework.6c3c27db.js";const e="/assets/IMG_0740.807c529a.jpg",p="/assets/IMG_0746.a24cbf67.jpg",r="/assets/IMG_0749.dd33adc0.jpg",o="/assets/IMG_0750.f4ed6cac.jpg",_="/assets/IMG_0752.ec03313d.jpg",i="/assets/IMG_0759.47bc027b.jpg",m="/assets/P39RMkPvdEU.7411eaf3.jpg",g="/assets/NC_K8bHXiE8.359c544d.jpg",l="/assets/IMG_0761.9b7e0b7e.jpg",n="/assets/hQ8X97uC1Ug.18c0ac6b.jpg",j="/assets/FgivdT-Ts7M.b29ee2b9.jpg",d="/assets/B_q4vnzAGeA.aaf7ba05.jpg",f="/assets/9LCk67LWGFc.8e30b508.jpg",b="/assets/1LZ0ipa2A1A.5e3bc170.jpg",u="/assets/RcgOGX8BPe0.9e986c41.jpg",I="/assets/rD6MUFlJuA.3c40555b.jpg",G="/assets/Crrv-QovUrQ.21fd64da.jpg",h="/assets/phYcZjzSGHk.905695d8.jpg",M="/assets/2-6rmI1cfOQ.24a03e3b.jpg",y="/assets/3Dw8tt-Oj_w.c7ad92f7.jpg",O="/assets/TbgPyfcnCzI.5c129cd3.jpg",x="/assets/xVewA71h46I.189c2d02.jpg",k="/assets/PElBphwmWB0.9d2856e3.jpg",A="/assets/F7yVdb8GIus.aed3fe90.jpg",E="/assets/LiHjxOzuMx8.9359cc86.jpg",L="/assets/JGyz2PnjL7I.7f8220c6.jpg",P="/assets/kgwAeBUczzg.b623a387.jpg",S="/assets/4cSwNN04PJs.87f58246.jpg",v="/assets/A2ZudJ1oHWc.32be9f38.jpg",B="/assets/keVuoBDxKLU.169b2f1b.jpg",T="/assets/IMG_0760.9d5d832e.jpg",C="/assets/9HBSqItqqAs.39f0cc34.jpg",H="/assets/ce-EmXSCS94.aeabe80d.jpg",U="/assets/h5R1Uat65_c.3d3bfc85.jpg",w="/assets/3jou0HDeeyE.b6079faf.jpg",z="/assets/15JpbBN_cSA.e7811dbe.jpg",N="/assets/oIe6dKjyXbk.198a29b3.jpg",V="/assets/ojtltPF9HGk.13f1cb26.jpg",X="/assets/x4W4vjZgiE8.556dfac9.jpg",q="/assets/rbmSZTXjBdc.d9fb08da.jpg",D="/assets/LIHvXGLcbVU.2fdbd185.jpg",J="/assets/IMG_0778.9c4ded42.jpg",Y="/assets/IMG_0779.5a21e44a.jpg",F="/assets/IMG_0780.38a98402.jpg",Z="/assets/IMG_0793.fa537ec1.jpg",Q="/assets/IMG_0794.3e9834f8.jpg",os=JSON.parse('{"title":"Делай Саммит 2015","description":"Двухдневная открытая мастерская OOLEY42 на Флаконе","frontmatter":{"title":"Делай Саммит 2015","description":"Двухдневная открытая мастерская OOLEY42 на Флаконе","date":"2015-06-04","cover":"pre.jpg","youtube":"https://youtu.be/ympeMMrv_4w"},"headers":[],"relativePath":"practice/ooley42/om-at-delay-summit-2015/index.md","filePath":"practice/ooley42/om-at-delay-summit-2015/index.md"}'),R={name:"practice/ooley42/om-at-delay-summit-2015/index.md"},W=c('<p>30—31 мая 2015 года на <a href="http://flacon.ru" class="external-link" target="_blank" rel="noreferrer">дизайн-заводе Флакон</a> состоялся <a href="/practice/ooley42/om-at-delay-summit-2015/">Делай Саммит 2015</a>, ставший уже традиционным весенним поводом для встречи всевозможных городских активистов. В насыщенной программе мероприятия приняли участие как российские, так и специально приехавшие из-за рубежа художники и городские мыслители. Были подняты многие острые вопросы и найдено множество точек соприкосновения.</p><p><img src="'+e+'" alt=""><img src="'+p+'" alt=""><img src="'+r+'" alt=""><img src="'+o+'" alt=""></p><p>OOLEY принял активное участие в организации и реализации зоны мастерских Саммита. Утром субботы мы прикатили нашу передвижную прицеп-мастерскую на Флакон и расположили прямо у входа в пространство &quot;Куб&quot; в 12-м корпусе. Все выступления Делай Саммита распределились по трём этажам конференц-залов пространства, в коридоре разместились стойки регистрации посетителей, книжная лавка и замечательное вегетарианское кафе. А на улице прицеп OOLEY42 стал центром живой творческой активности, давший множество возможностей для самовыражения не только участникам Саммита, но и просто посетителям территории Флакона.</p><p><img src="'+_+'" alt=""><img src="'+i+'" alt=""></p><p>Мы успели хорошо подготовиться к событиям такого масштаба. Пополнили парк электрических и ручных инструментов, подготовили средства защиты и собрали гору различных материалов. Мы даже придумали и сделали <a href="/practice/project/stool/">складной стол-верстак</a>, ставший не только полезной рабочей площадью мастерской, но и прототипом для творческих порывов наших гостей.</p><p>Уже к 12:00 мы раскрыли встроенный в прицеп тент, собрали на территории Флакона несколько массивных столов, построили из раскрашенных паллет импровизированную стойку, расставили повсюду всевозможный инструмент и удобно разложили имеющиеся материалы. Сразу же появились первые гости мастерской, желающие воспользоваться нашим оборудованием для реализации своих проектов. Первую вещью, сделанной в этот день был вырезанный ребёнком лет 8 на лобзиковом станке игрушечный меч. Один из гостей с помощью лобзикового станка, мощного фена и клея смог произвести из дешевых разделочных досок прототипы накладок-слайдеров для перчаток для лонгбординга.</p><p>Мы представили наши наработки в теории устойчивых открытых мастерских и получили отличный отклик у гостей саммита. Но, все-таки, творческая практика куда привлекательнее любой теории.</p><p><img src="'+m+'" alt=""><img src="'+g+'" alt=""><img src="'+l+'" alt=""><img src="'+n+'" alt=""></p><p>В первый день мы провели 2 мастер-класса: по шитью и деревообработке. Участники классов сумели изготовить 3 складные табуретки по предложенному прототипу, начав с обмеров, подбора и разметки материалов. Двое участников, объединив усилия, смогли вместо табуретки собрать копию нашего <a href="/practice/project/stool/">складного стола</a>. Их так захватил процесс совместного творчества, что после того, как они подарили получившийся стол другой участнице мастер-класса, они той же командой еще и вырезали и собрали полку-этажерку для ещё одной гостьи.</p><p><img src="'+j+'" alt=""><img src="'+d+'" alt=""><img src="'+f+'" alt=""><img src="'+b+'" alt=""><img src="'+u+'" alt=""><img src="'+I+'" alt=""><img src="'+G+'" alt=""><img src="'+h+'" alt=""><img src="'+M+'" alt=""><img src="'+y+'" alt=""><img src="'+O+'" alt=""><img src="'+x+'" alt=""><img src="'+k+'" alt=""><img src="'+A+'" alt=""><img src="'+E+'" alt=""><img src="'+L+'" alt=""><img src="'+P+'" alt=""><img src="'+S+'" alt=""><img src="'+v+'" alt=""></p><p>Мастер-класс по шитью подарил возможность попробовать себя в шитье небольших аксессуаров более чем 10 людям. И, как обычно, не все довольствовались готовыми решениями: гости шили и чехлы для телефонов и простые кармашки для мелочи.</p><p><img src="'+B+'" alt=""><img src="'+T+'" alt=""><img src="'+C+'" alt=""><img src="'+H+'" alt=""><img src="'+U+'" alt=""><img src="'+w+'" alt=""><img src="'+z+'" alt=""></p><p>Второй день мы полностью посвятили открытому формату работы мастерской, готовой становиться площадкой для реализации любых задумок посетителей. Мы старались пробудить в людях их внутренний творческий порыв, предоставляя инструменты и примеры их использования. Сочетание лобзикового станка и бормашинки позволили родиться в этот день симпатичному цветку, аккуратной резной ложке (которая, к сожалению, сломалась при обработке маслом), штампам для ручной набойки тканей, мыльнице, фигурке зайца и еще нескольким поделкам из дерева. Швейные машинки перешивали старую одежду, предоставленную гостями. Кто-то пробовал гравировать металл, распиливать клёпки. Из предоставленного организаторами Саммита ненужного баннера мы смогли сшить удобную сумку и предложили продолжить экспериментировать с этим материалом. Один из гостей спроектировал и полностью самостоятельно сшил из него себе поясную сумку на липучке.</p><p><img src="'+N+'" alt=""><img src="'+V+'" alt=""><img src="'+X+'" alt=""><img src="'+q+'" alt=""><img src="'+D+'" alt=""><img src="'+J+'" alt=""><img src="'+Y+'" alt=""><img src="'+F+'" alt=""><img src="'+Z+'" alt=""><img src="'+Q+'" alt=""></p><p>За эти два дня нам удалось на практике изучить возможности прицепа-мастерской OOLEY42 и применить разрабатываемые нами принципы открытой мастерской в полной мере. Мы предоставляли свободный доступ к оборудованию мастерской, делились материалом и старались переиспользовать всё, что только можно. Крайне интересно и важно было практически исследовать общность и различия в ролях администратора и куратора мастерской. Гости Саммита поделились своими мыслями и идеями, а также придали уверенности в том, что мы не одни пытаемся делать этот мир лучше. Надеемся, нам ещё не раз выпадет удача посотрудничать с командой <a href="http://delaisam.org/" class="external-link" target="_blank" rel="noreferrer">Делай Сам</a> и международным активистским сообществом, а город и его окрестности продолжат быть столь гостеприимными для творческих вылазок передвижной мастерской.</p>',15),$=[W];function K(ss,ts,as,cs,es,ps){return t(),a("div",null,$)}const _s=s(R,[["render",K]]);export{os as __pageData,_s as default};
