import{_ as s,a as o,J as p,o as a}from"./chunks/framework.BWf-PbcG.js";const r="/assets/IMG_2400.DQBS2n6U.jpg",e="/assets/IMG_2404.D02uK9gV.jpg",c="/assets/IMG_2405.Cu187PZ9.jpg",i="/assets/IMG_2406.BOdX4fsn.jpg",n="/assets/IMG_2820.DjGt8-VV.jpg",_="/assets/IMG_2822.DR0hXF49.jpg",m="/assets/IMG_2827.BjkKmSPk.jpg",M=JSON.parse('{"title":"Тенденция: второй этаж в столярной мастерской","description":"","frontmatter":{"title":"Тенденция: второй этаж в столярной мастерской","date":"2015-12-28","cover":"IMG_2824.jpg"},"headers":[],"relativePath":"practice/process/second-floor/index.md","filePath":"practice/process/second-floor/index.md"}'),d={name:"practice/process/second-floor/index.md"};function l(g,t,f,u,j,G){return a(),o("div",null,t[0]||(t[0]=[p('<p>Мы стараемся внимательно следить за развитием общественных мастерских, регулярно наведываемся к ним и наблюдаем заметные изменения. Московские столярные коворкинги набирают популярность и сразу две таких мастерских уже столкнулись с проблемой нехватки пространства для всё нарастающего количества посетителей и их творческих проектов. Решение не заставило себя долго ждать.</p><p><img src="'+r+'" alt=""></p><p>И <a href="/workshop/catalog/dar-truda/">&quot;Дар Труда&quot; на Преображенке</a> и <a href="/workshop/tour/crafts-station/">&quot;Crafts Station&quot; на Кристалле</a> пришли к одному и тому же решению этой насущной проблемы — надстроить второй ярус мастерской, используя значительную высоту потолков старых промышленных помещений.</p><p><img src="'+e+'" alt=""></p><p><img src="'+c+'" alt=""></p><p>В Даре труда второй уровень используется для хранения материалов и заготовок, а достроенный недавно дополнительный пролёт над зоной станков планируется сдавать мастерам под личные выделенные зоны и верстаки.</p><p><img src="'+i+'" alt=""></p><p><img src="'+n+'" alt=""></p><p>В коворкинге Crafts Station, по мере наполнения новыми станками и прочим крупным оборудованием, просто стало не хватать места для пустых парт-верстаков, где производится основная работа с ручным электроинструментом и склеиваются заготовки. Второй ярус стал закономерным решением этой проблемы, заодно позволив создать под лестницей уютную каморку для отдыха умельцев с раздевалкой, чайником и микроволновкой.</p><p><img src="'+_+'" alt=""></p><p><img src="'+m+'" alt=""></p><p>Мы рады, что общественные мастерские не только справляются с трудностями существования в Москве, но и растут и расширяются прямо на глазах. И несмотря на то, что выполнение заказов для этих мастерских по-прежнему является основной строкой дохода, а коворкинги с трудом окупают затраты на собственное содержание, можно говорить о постепенном становлении общественной столярки как части городской среды. Продолжаем наблюдать и ждём новых &quot;расширений&quot; мастерских.</p>',12)]))}const h=s(d,[["render",l]]);export{M as __pageData,h as default};
