import{_ as s,a as e,J as r,o as p}from"./chunks/framework.BWf-PbcG.js";const a="/assets/3rd_01.Dy04INji.jpg",o="/assets/3rd_09.BVItYRxQ.jpg",_="/assets/3rd_10.C7u_qWJV.jpg",i="/assets/3rd_11.y7gp_K42.jpg",c="/assets/5IMG_6937_1500.C_Wto9BW.jpg",n="/assets/5IMG_6938_1500.B7i1ZwZF.jpg",m="/assets/3IMG_6999_1500.DDFvAM36.jpg",l="/assets/3IMG_7008_1500.Bmc2OLxT.jpg",g="/assets/1IMG_7017_1500.Da6HQeP-.jpg",d="/assets/2IMG_6987_1500.BrxUrxlX.jpg",u="/assets/2IMG_6992_1500.gCZGqUTe.jpg",j="/assets/IMG_1996_OK.BnxuEdE4.jpg",x="/assets/IMG_2041_OK.PtU4CeKl.jpg",b=JSON.parse('{"title":"Вертикальное рабочее место SitUp","description":"","frontmatter":{"title":"Вертикальное рабочее место SitUp","date":"2014-12-05","cover":"4IMG_6996_1500.jpg"},"headers":[],"relativePath":"practice/process/vertikal-noe-rabochee-mesto-situp/index.md","filePath":"practice/process/vertikal-noe-rabochee-mesto-situp/index.md"}'),I={name:"practice/process/vertikal-noe-rabochee-mesto-situp/index.md"};function G(M,t,f,k,h,B){return p(),e("div",null,t[0]||(t[0]=[r('<blockquote><p>Мы публикуем присланные вами творческие результаты. Вот, например, продуманное компактное рабочее место от Романа Бондарева из Москвы.</p></blockquote><p><img src="'+a+'" alt=""><img src="'+o+'" alt=""><img src="'+_+'" alt=""><img src="'+i+'" alt=""></p><p>Комплект оригинальной мебели для работы на компьютере, принципиальная особенность заключается в возможности использования не только привычным образом – сидя за столом, но и стоя перед ним. Высота столешницы, реализующая «принцип барной стойки», создает единое коммуникативное поле – сидящий находится на уровне глаз тех, кто стоит. Площадь столешницы составляет необходимый и достаточный минимум и не позволяет захламлять рабочее пространство. Компактный стол оснащен колёсами и мобилен – работу легко перенести в любое доступное место. Ещё одна особенность SitUp – кресло, выполняющее также функцию мини-стеллажа, является стационарным и не задвигается под стол, а наоборот – мобильный стол придвигается к нему.</p><p><img src="'+c+'" alt=""><img src="'+n+'" alt=""><img src="'+m+'" alt=""><img src="'+l+'" alt=""></p><p>Вертикальная посадка максимально комфортна, соответствует ортопедическим нормам, полностью мобилизует и стимулирует к эффективному труду. Работа на компьютере в положении стоя также полезна для здоровья, а кроме того, делает удобными «короткие подходы» к компьютеру.</p><p><img src="'+g+'" alt=""><img src="'+d+'" alt=""><img src="'+u+'" alt=""></p><p>В комплект входят: подушки сидения и спинки кресла, 2 колеса на ножках стола, электропровод с 5-ю розетками для обеспечения всех внутренних энергетических нужд. Для людей разного роста предусмотрено 2 размерности, различающиеся высотой основных поверхностей.</p><p>Материал – ясеневый брусок, высококачественная фанера 18 мм, окраска – по выбору заказчика. Подробнее на <a href="http://sit-up.ru/" class="external-link" target="_blank" rel="noreferrer">http://sit-up.ru/</a></p><p><img src="'+j+'" alt=""><img src="'+x+'" alt=""></p>',9)]))}const U=s(I,[["render",G]]);export{b as __pageData,U as default};
