import{_ as t,a as r,J as o,o as a}from"./chunks/framework.BWf-PbcG.js";const p="/assets/IMG_0476.DPbDrknk.jpg",e="/assets/0B03lnYKEbY.yNwK0J-1.jpg",n="/assets/IMG_0340.Dra3_GCn.jpg",i="/assets/IMG_0346.BR358WAu.jpg",g="/assets/IMG_0355.C7UnVub7.jpg",c="/assets/IMG_0367.Bnesfb4f.jpg",l="/assets/IMG_0369.Cp3zX2mW.jpg",_="/assets/IMG_0379.BKBrBOxZ.jpg",m="/assets/IMG_0381.Dd5qW7zm.jpg",h="/assets/IMG_0390.DzywCHTp.jpg",u="/assets/IMG_0392.zrb8DXdy.jpg",d="/assets/hD96WsvLsQs.wqa-apGV.jpg",j="/assets/IMG_0399.B3A_CKU2.jpg",q="/assets/IMG_0401.Bxn5pdjU.jpg",G="/assets/IMG_0426.BSrX96MI.jpg",I="/assets/IMG_0431.BwV0u7JT.jpg",M="/assets/IMG_0434.DIgUNx2z.jpg",f="/assets/IMG_0438.BEanwyVr.jpg",k="/assets/IMG_0440.BqBDJkXg.jpg",b="/assets/IMG_0441.Ck3lJb0g.jpg",x="/assets/IMG_0444.ChaoXjmw.jpg",D="/assets/IMG_0445.yyVvhC4s.jpg",w="/assets/IMG_0448.Cf_qsdok.jpg",B="/assets/IMG_0450.Bbj1wknN.jpg",y="/assets/icHZvmtDCww.CXKDS0yk.jpg",C="/assets/IMG_0451.Bipfeaam.jpg",v="/assets/IMG_0455.DVaciVX6.jpg",V="/assets/IMG_0458.Bybt0D3v.jpg",P="/assets/IMG_0460.Ohmhm878.jpg",X="/assets/IMG_0463.DmwaiDjV.jpg",K="/assets/jtoYXctG2fU.D0S8GOkX.jpg",O="/assets/IMG_0465.ROcqlnDG.jpg",J="/assets/IMG_0475.CIjiEyVM.jpg",z="/assets/RKMor3KTDQY.7gsGovIQ.jpg",$=JSON.parse('{"title":"Изготовление навигационных указателей","description":"Для арт-усадьбы \\"Гуслица\\"","frontmatter":{"title":"Изготовление навигационных указателей","description":"Для арт-усадьбы \\"Гуслица\\"","date":"2016-05-05","cover":"IMG_0471.jpg"},"headers":[],"relativePath":"practice/project/guslitsa/index.md","filePath":"practice/project/guslitsa/index.md"}'),E={name:"practice/project/guslitsa/index.md"};function N(S,s,U,W,Y,A){return a(),r("div",null,s[0]||(s[0]=[o('<p><a href="/workshop/catalog/guslitsa/">Творческая усадьба &quot;Гуслица&quot;</a> — международный культурно-образовательный центр в <strong>80 км от Москвы</strong>. Окружённая живописными природными пейзажами, более чем столетняя промышленная усадьба общей площадью <strong>10 000 кв.м.</strong> с прилегающей территорией в <strong>4 га</strong> — настоящий простор для вдохновения и творчества. Художники и музыканты, танцоры и актёры, скульпторы и архитекторы приезжают в арт-резиденцию чтобы создавать здесь свои произведения. В просторных залах регулярно проходят самые разнообразные выставки и выступления, от камерных показов и спонтанных спектаклей до масштабных фестивалей и крупных галерейных проектов.</p><p>Здание усадьбы — сложный архитектурный коктейль из смелых дореволюционных кирпичных построек, утилитарных советских пристроек к ним, а также преимущественно деревянных перегородок и перекрытий уже нового времени. В первый раз здесь не сложно заблудиться, но даже давние резиденты &quot;Гуслицы&quot; не знают всех закоулков этого обширного пространства. Несмотря на неоднократные попытки организации удобной навигации по усадьбе и её территории, чёткой и понятной системы наименования помещений и указателей к ним здесь так и не сложилось. Когда OOLEY откликнулся на призыв &quot;Гуслицы&quot; приезжать и включаться в волонтёрскую работу по обновлению и развитию усадьбы, разработка и создание навигационных табличек стало нашим первым совместным проектом.</p><p>Суть проекта заключалась в соединении элементов уже давно существующего пазла. <strong>Фотограф Андрей Колосов</strong> подготовил <a href="https://www.facebook.com/profile.php?id=1836861050&amp;sk=photos&amp;collection_token=1836861050%3A2305272732%3A69&amp;set=a.10203796223626172.1073741917.1836861050&amp;type=3" class="external-link" target="_blank" rel="noreferrer">более пятисот прекрасных снимков окружающих ландшафтов</a>, <strong>художник и каллиграф Александр Желонкин</strong> уже разработал уникальный <a href="https://www.facebook.com/photo.php?fbid=1518767961751813&amp;set=a.1478078882487388.1073741833.100008558714232&amp;type=3&amp;theater" class="external-link" target="_blank" rel="noreferrer">заголовочный шрифт &quot;Алхимический устав&quot;</a>, а руководство уже определилось с примерным видом итоговых табличек. Оставалось только составить список необходимых указателей и продумать всю технологическую цепочку их производства. Мы старались по-максимуму использовать уже имеющиеся у нас и у &quot;Гуслицы&quot; возможности, сделав этот процесс максимально простым и повторяемым.</p><p>Тщательно изучив все альтернативы, мы пришли к, пусть и немного кропотливой, но всё-таки простой и легко повторяемой технологической цепочке. Уже в первый заезд за <strong>два дня</strong> нам удалось создать сразу <strong>14 указателей</strong> для центральной навигационной стойки напротив ресепшена, а за второй подход мы подготовили все ингредиенты для создания десятков новых табличек. Мы собрали все необходимые материалы в одной большой коробке, познакомили волонтёров пространства с производственной технологией, а теперь делимся этими знаниями здесь в попытке создать подробную инструкцию, позволяющую сохранять единство оформления постранства даже при полной смене волонтёрской команды.</p><h3 id="набор" tabindex="-1">Набор <a class="header-anchor" href="#набор" aria-label="Permalink to &quot;Набор&quot;">​</a></h3><p><img src="'+p+'" alt="IMG_0476"></p><p>В одной большой коробке мы собрали все ключевые инструменты и материалы для создания навигационных указателей. В ней содержатся:</p><ul><li>конверт с нарезанными на плоттере трафаретами названий помещений усадьбы, а также комплектом прописных букв &quot;Алхимического устава&quot;, строчных букв <a href="http://mishapanfilov.ru/font_tsarevich.html" class="external-link" target="_blank" rel="noreferrer">бесплатного шрифта &quot;Tsarevich&quot;</a>, цифрами и стрелками,</li><li>глянцевый акриловый лак,</li><li>клей ПВА,</li><li>белая акриловая краска,</li><li>кисти для лака,</li><li>кухонные губки</li><li>нарезанные и отшлифованные фанерные заготовки,</li><li>отпечатки фотографий на лазерном принтере,</li><li>гвозди для степлера.</li></ul><p>А теперь обо всём порядку.</p><h3 id="дощечки" tabindex="-1">Дощечки <a class="header-anchor" href="#дощечки" aria-label="Permalink to &quot;Дощечки&quot;">​</a></h3><p><img src="'+e+'" alt=""><img src="'+n+'" alt=""><img src="'+i+'" alt=""><img src="'+g+'" alt=""><img src="'+c+'" alt=""><img src="'+l+'" alt=""><img src="'+_+'" alt=""><img src="'+m+'" alt=""><img src="'+h+'" alt=""><img src="'+u+'" alt=""></p><p>Основой табличек мы выбрали <strong>тонкую фанеру 4 мм толщиной</strong>, куски которой удалось отыскать, порывшись в завалах материалов в ещё нерасчищенном зале мастерских. Размер каждой таблички не должен превышать размера отпечатка на листе формата А4, поэтому фанера нарезана прямоугольниками <strong>длиной 29 см</strong>. Творческая атмосфера &quot;Гуслицы&quot; не выносит ровных линий и форм и края дощечек намеренно вырезаны электролобзиком слегка волнистыми. Так каждая табличка получает некоторую индивидуальность и вместе они создают приятную текучую композицию. Доски шириной <strong>12-15 см</strong> предназначены для надписей в одну строку, более широкие доски (<strong>20 см</strong>) — для двустрочных надписей.</p><p>Для аккуратного перевода изображений на доски важно последовательно отшлифовать их поверхность шлифовальной бумагой зернистостью <strong>Р100, Р180 и Р240</strong>, а края слегка скруглить грубой шкуркой. Тщательно очищаем доски от древесной пыли сухой кисточкой и можно приступать к следующему этапу.</p><h3 id="трансфер-фотографии" tabindex="-1">Трансфер фотографий <a class="header-anchor" href="#трансфер-фотографии" aria-label="Permalink to &quot;Трансфер фотографий&quot;">​</a></h3><p>Мы отобрали наиболее подходящие для переноса фотографии из коллекции и подготовили их <a href="https://yadi.sk/d/MxtIwLqdrWnFc" class="external-link" target="_blank" rel="noreferrer">для печати на черно-белом лазерном принтере</a>, повысив контраст и детализацию в фотошопе. Для переноса таких изображений на дерево как правило используется специальный состав под названием <strong>гель-медиум</strong>, а также особый клей <strong>&quot;Mod podge&quot;</strong>, но это довольно редкие и дорогие ингредиенты и, как оказалось, их нетрудно заменить вполне доступным составом. Для этого в небольшой емкости вроде пластикового стаканчика смешиваем в пропорции <strong>1:1</strong> обычный клей <strong>ПВА</strong> и акриловый <strong>лак</strong>. Наносим кисточкой получившуюся смесь на доски и аккуратно прикладываем к ним отпечатки фотографий изображением вниз. Для надёжного сцепления тонера с доской важно равномерно прижать бумагу гибким шпателем или небольшим резиновым валиком. Оставляем заготовки сохнуть <strong>минимум на 3 часа</strong>, а лучше на всю ночь.</p><p><img src="'+d+'" alt=""><img src="'+j+'" alt=""><img src="'+q+'" alt=""><img src="'+G+'" alt=""><img src="'+I+'" alt=""><img src="'+M+'" alt=""></p><p>Следующим этапом идёт оттирание лишних слоёв бумаги влажной губкой. Нам понадобится ёмкость с водой, обычная кухонная губка и запас терпения. Техника довольно простая, но требует аккуратности и внимательности. Слегка влажной губкой нужно смочить бумагу и легкими круговыми движениями счищать её слоями, превращая в небольшие катышки. Важно <strong>не переборщить с количеством воды</strong> и не дать ей размочить слой клея с тонером — для этого оттираем бумагу в несколько заходов, каждый раз снимая всё более тонкий слой. Влажные волокна бумаги могут быть почти незаметны, но при промежуточной сушке доски рисунок снова становится почти не виден за белой пеленой — ничего страшного в этом нет. После покрытия лаком они будут выглядеть так же, как ещё влажные. За два-три подхода с промежуточной сушкой, аккуратно работая влажной губкой и, по необходимости, просто подушечками пальцев, удаётся очистить почти всю лишнюю бумагу, оставив лишь тонкий полупрозрачный слой с тонером. Тщательно высушиваем доски и приступаем к нанесению надписей.</p><p><img src="'+f+'" alt=""><img src="'+k+'" alt=""><img src="'+b+'" alt=""><img src="'+x+'" alt=""><img src="'+D+'" alt=""><img src="'+w+'" alt=""><img src="'+B+'" alt=""></p><h3 id="надписи" tabindex="-1">Надписи <a class="header-anchor" href="#надписи" aria-label="Permalink to &quot;Надписи&quot;">​</a></h3><p>Мы полностью отрисовали шрифт <strong>&quot;Алхимический устав&quot;</strong> и адаптировали его и бесплатный шрифт <strong>&quot;Царевич&quot;</strong> для применения в трафаретах. Большинство названий помещений &quot;Гуслицы&quot; выполнены в виде готовых надписей на отдельных листах плотной <strong>ПЭТ-плёнки</strong>, однако некоторые надписи придётся набирать побуквенно с использованием листов с алфавитом. Важно тщательно выровнять надпись относительно краёв доски, а при побуквенном наборе ещё и отслеживать <strong>равномерность межбуквенных интервалов</strong>. Важно не ошибиться и с расположением и направлением стрелок.</p><p><img src="'+y+'" alt=""><img src="'+C+'" alt=""><img src="'+v+'" alt=""></p><p>Белая акриловая краска наносится на доску половинкой обычной кухонной губки. Для этого нужно взять немного краски из банки, перенести на кусок картона или фанеры и тщательно набить ею губку лёгким постукиванием. Если краски на губке будет слишком много, она затечёт под трафарет и надпись расплывётся, поэтому лучше не пытаться добиться сплошного цвета при нанесении первого слоя. <strong>Технический фен</strong> поможет просушить краску за считанные минуты и, не смещая трафарета, можно сразу нанести финальный слой краски. Аккуратно поднимаем плёнку трафарета и даём надписи хорошенько просохнуть.</p><p><img src="'+V+'" alt=""><img src="'+P+'" alt=""><img src="'+X+'" alt=""><img src="'+K+'" alt=""></p><p>Если краска всё-таки затекла под плёнку и надпись стала нечёткой, огрехи можно <strong>закрасить простым карандашом</strong>. Также можно исправить мелкие повреждения перенесённого рисунка.  При необходимости дорисовываем кавычки или другие элементы кисточкой. Всё, осталось покрыть получившиеся указатели глянцевым акриловым лаком в два слоя и готово. Технический фен <strong>в I режиме (80°С)</strong> поможет значительно ускорить этот процесс без потери в качестве работы.</p><h3 id="крепление" tabindex="-1">Крепление <a class="header-anchor" href="#крепление" aria-label="Permalink to &quot;Крепление&quot;">​</a></h3><p>Лучший вариант крепления указателей — найти деревянную основу и прибить их мелкими гвоздями молотком или используя строительный степлер с гвоздиками длиной <strong>12—16 мм</strong>. Два-три гвоздя надёжно закрепят доску и позволят при необходимости аккуратно её перенести или заменить.</p><p><img src="'+O+'" alt=""><img src="'+J+'" alt=""></p><h3 id="резюме" tabindex="-1">Резюме <a class="header-anchor" href="#резюме" aria-label="Permalink to &quot;Резюме&quot;">​</a></h3><p>Нам удалось собрать полный комплект всего необходимого для создания полноценной навигационной системы в огромном пространстве арт-усадьбы, собственноручно наполнить указателями центральный холл здания, а также передать знания этой технологии волонтёрам &quot;Гуслицы&quot;. Надеемся, эта инструкция окажется полезной и поможет сделать пространство культурно-образовательного центра чуть более удобным и понятным для всех.</p><p><img src="'+z+'" alt=""></p>',30)]))}const L=t(E,[["render",N]]);export{$ as __pageData,L as default};
