import{_ as t,o as a,c,a as e,d as o}from"./chunks/framework.mYMQfzZG.js";const j=JSON.parse('{"title":"Проекты","description":"Пройденные пути от идей до их воплощения","frontmatter":{"title":"Проекты","description":"Пройденные пути от идей до их воплощения","date":"2019-01-01T00:00:00.000Z","cover":"utencils.jpg"},"headers":[],"relativePath":"practice/project/index.md","filePath":"practice/project/index.md"}'),r={name:"practice/project/index.md"},n=e("h3",{id:"проектныи-подход-в-творчестве",tabindex:"-1"},[o("Проектный подход в творчестве "),e("a",{class:"header-anchor",href:"#проектныи-подход-в-творчестве","aria-label":'Permalink to "Проектный подход в творчестве"'},"​")],-1),s=e("p",null,"Более 6 лет мы исследовали самые различные грани творчества. Изучали как это делают другие: читали книги психологов и педагогов, перенимали опыт различных систем организации труда. Творили сами: рисовали картины, шили одежду, мастерили мебель. Помогали творить другим: проводили мастер-классы и воркшопы, устраивали лесной творческий лагерь и помогали наполнять творчеством программы фестивалей. В этом процессе мы выработали, опробовали и усовершенствовали новый максимально простой способ разбить любое творческое начинание на серию небольших понятных этапов.",-1),i=e("p",null,"Этот подход помогает легче начинать, увереннее продолжать и решительнее завершать любые творческие проекты. От вышивки до ювелирных изделий, от мебели до целых зданий — проектный подход помогает не застрять на пути, не потерять состояние потока и не бояться тишины, возникающей когда все начатое завершено, а новому еще предстоит начаться.",-1),d=[n,s,i];function p(l,_,h,m,f,x){return a(),c("div",null,d)}const $=t(r,[["render",p]]);export{j as __pageData,$ as default};
