import{_ as p}from"./chunks/vimeo-embed.l7WPTDEa.js";import{_ as o,o as r,c as a,b as c,e as s}from"./chunks/framework.7WwFwkJk.js";const _="/assets/rasp-3d.gU_sAk28.png",i="/assets/01McAg1babk.Tf1HnHLa.jpg",g="/assets/3LvL857QS_8.SDGKqGv2.jpg",m="/assets/3XBrsl6Ffxc.w1ZkwEjQ.jpg",e="/assets/4vU2_W4W58A.NIZJ3DNl.jpg",n="/assets/6Nz1YHyir0M.DF5zIaZx.jpg",l="/assets/B9tQTyl1Eno.MFy_G9ig.jpg",j="/assets/B_4upLkwPWQ.n4snuknH.jpg",d="/assets/bTsbVDADs04.tBn7fecQ.jpg",h="/assets/bzF9qBU2TVc.7qFcPIEe.jpg",f="/assets/CVXJusCh76k.aoh4eeGY.jpg",x="/assets/dNgapsggxCc.aamMqZYK.jpg",u="/assets/eD-Dfwrxww0.2st3xwJ1.jpg",v="/assets/fDZmkUvXlcs.Qq7xdmNs.jpg",A="/assets/FSp-095P6gc.SzUcPVYX.jpg",I="/assets/FuDvlgr6k4s.XuNRCJzl.jpg",T="/assets/h1v5vmsIIos.GE3DdTML.jpg",k="/assets/IerQma0Irqw.U2NWxShU.jpg",y="/assets/iiI9xNK3nmM.WNzp5HjC.jpg",P="/assets/JYDmvoED7VE.JQpJsomk.jpg",S="/assets/KWJCWKIsYhw.2lqbkHAI.jpg",V="/assets/llJucKJh-x4.2bOtSLcQ.jpg",N="/assets/LTxooLPuQ5I.yYbOE1Pi.jpg",Y="/assets/M1RWjn-jeXU.foDoFzGI.jpg",D="/assets/NfYVymN4XCE.YkS91Sym.jpg",E="/assets/pA_oZQ1Gljk.A0HsAl3e.jpg",G="/assets/PslA1vEyVyE.G6Mlzssj.jpg",b="/assets/Qo2nLRRlGyg.-U402VoP.jpg",J="/assets/s1tFwJYSWfM.gGKcM9nI.jpg",L="/assets/slB48rblPqg.4gQ3pm0e.jpg",Q="/assets/Ur-GWnYABz0.I3CPyTxr.jpg",w="/assets/UU_SWYKkjeM.ExcyQ7ul.jpg",U="/assets/uwo6RJqQl80.sGUgQ_A1.jpg",q="/assets/v5x4G5qcjYI.VhXXBIAs.jpg",M="/assets/VmlPL8wiGx0.vNeaA025.jpg",O="/assets/wGvbyi2ehxM.VrzZOuN1.jpg",W="/assets/WkhOIgHJ8Oo.R172Lhu2.jpg",z="/assets/WnlfTv_UR_o.dJO9uEd_.jpg",B="/assets/WnmwdzPwrfc.YqVVhL8z.jpg",C="/assets/X1DfmwoOPmY.g-MDGKXt.jpg",X="/assets/yuJQkNAIKyM.demdAPe5.jpg",K="/assets/Yuy9W5dcnac.dAvOtY96.jpg",R="/assets/LogoLOM-copy-2x.xAVp8nBl.png",F="/assets/9rTYKuxmrlY.3XTB3Xqp.jpg",H="/assets/a3zSmSgL7iE.XhJb8yx2.jpg",Z="/assets/ARg5ynhcCn4._UGODUqs.jpg",$="/assets/b1IjSN7c9vg.NiPAHGsa.jpg",ss="/assets/B6nC-TBlVS0.Vvz8fmtd.jpg",ls=JSON.parse('{"title":"Творческий лагерь для взрослых ЛОМ: Лесные открытые мастерские","description":"","frontmatter":{"title":"Творческий лагерь для взрослых ЛОМ: Лесные открытые мастерские","date":"2017-11-08","cover":"cover.jpg","icon":"logo-2018.png","vimeo":"https://vimeo.com/321969896"},"headers":[],"relativePath":"practice/project/lomcamp/index.md","filePath":"practice/project/lomcamp/index.md"}'),ts={name:"practice/project/lomcamp/index.md"},ps=s('<h3 id="лом-—-новыи-формат-совместного-продуктивного-отдыха-на-природе" tabindex="-1">ЛОМ — новый формат совместного продуктивного отдыха на природе. <a class="header-anchor" href="#лом-—-новыи-формат-совместного-продуктивного-отдыха-на-природе" aria-label="Permalink to &quot;ЛОМ — новый формат совместного продуктивного отдыха на природе.&quot;">​</a></h3><p><strong>Это лагерь для людей всех возрастов, где вдали от городской суеты можно расслабиться, наполниться общением с живой природой и единомышленниками и реализовать вдохновение в разнообразных творческих проектах.</strong></p><p><img src="'+_+'" alt=""></p><p>В проекте OOLEY мы исследуем и развиваем новые подходы к творчеству с 2012 года. Мы не только собираем доступную информацию о существующих открытых мастерских и прочих креативных пространствах, но и обобщаем её в теории устройства и организации устойчивых творческих процессов.</p><p>Во многих городах есть пространства и мастерские, где регулярно проходят мастер-классы. Краткие ознакомительные занятия с известным результатом — отличная возможность ненадолго отвлечься от привычных забот и позаниматься чем-то новым и интересным. А если уделить ремеслу побольше времени и внимания: пройти образовательный курс или взяться воплощать свои исследовательские и творческие проекты в ремесленном коворкинге, можно радикально поменять род деятельности и весь образ жизни.</p><p><img src="'+i+'" alt=""><img src="'+g+'" alt=""><img src="'+m+'" alt=""><img src="'+e+'" alt=""><img src="'+n+'" alt=""></p><p>Каким бы ни был удобным город, каждому человеку порой хочется уехать подальше от пыли, шума, спешки и побыть хоть немного в контакте с природой. Кому-то достаточно выбраться на дачу, кто-то отправляется в дальние страны, а можно вместе поехать с рюкзаками и палатками в лес. Массовые летние фестивали под открытым небом становятся все популярнее.</p><p>Так хорошо хоть немного пожить под открытым небом. Постепенно приходя к гармонии с природой, человек очищается, наполняется энергией и шире открывается новому опыту. Ранние подъемы, зарядка, здоровая пища и активное взаимодействие со стихиями дарит невероятный прилив сил и вдохновения. Так можно глубже изучить свои возможности, обнаружить и раскрыть новые грани своей личности. Это хорошо понимают те, кто, отправляясь на природу, берут с собой разнообразный спортинвентарь, а также музыкальные, художественные и прочие инструменты для творчества.</p><p><img src="'+l+'" alt=""><img src="'+j+'" alt=""><img src="'+d+'" alt=""><img src="'+h+'" alt=""><img src="'+f+'" alt=""><img src="'+x+'" alt=""><img src="'+u+'" alt=""></p><p>Мастер-классы давно стали неотъемлемой частью практически любого летнего фестиваля. Они подразумевают разовые занятия, на которых группа учеников, выполняя указания мастера, из заготовок получает поделку, которую можно забрать с собой. Такой подход даёт возможность прикоснуться к ремеслу практически каждому. Пара часов занимательного рукоделия без лишних трудностей и тонкостей процесса — отличный формат отдыха. Но что делать, если этого кажется мало? Как не просто чуть прикоснуться, а по-настоящему погрузиться в новое для себя ремесло?</p><p>Размышляя над этим, мы разглядели новый формат, позволяющий как вдохновиться природой, так и реализовать это состояние в творчестве. К настоящему моменту у нас накопилось достаточно инструментов и опыта в организации открытых мастерских, чтобы всерьез заняться созданием универсального творческого пространства в походных условиях. Нам посчастливилось познакомиться и найти единомышленников в уникальном природном пространстве — <a href="https://vk.com/surf2free" class="external-link" target="_blank" rel="noreferrer"><strong>Сёрфлагере на Горьковском море</strong></a>. Так перед нами открылась возможность применить эти идеи и наработки на практике.</p><p><img src="'+v+'" alt=""><img src="'+A+'" alt=""><img src="'+I+'" alt=""><img src="'+T+'" alt=""><img src="'+k+'" alt=""><img src="'+y+'" alt=""></p><p>Мы знаем, что открытая мастерская — это не какое-то определенное место, а живой процесс совместного творчества, который можно развернуть где угодно. Конечно, для этого нужны разнообразные инструменты и материалы, но самое важное — созидательный настрой участников. Совсем не нужно ждать, пока кто-то специально организует для этого пространство: чтобы начать достаточно самого малого. Открытую мастерскую можно запустить с простым карандашом и листком бумаги, творческий процесс сам подскажет пути дальнейшего развития.</p><p>У многих пылятся дома редко используемые инструменты, а современное оборудование и материалы как никогда доступны в широко раскинувшихся торговых сетях. В глобальной сети постоянно пополняется невероятный объем полезной информации практически на любую тему — с каждым днём все легче самостоятельно осваивать творческие технологии. Все ингредиенты уже есть, осталось соединить их вместе в правильной пропорции.</p><p>Основные компоненты любой открытой мастерской — это рабочее пространство, инструменты, материалы и, конечно, взаимодействующие друг с другом люди. И, кажется, мы, наконец, нашли возможность соединить это все воедино. Сёрфлагерь гостеприимно предоставляет свои обустроенные лесные сцены и домики для организации мастерских, а также предлагает комфортные условия размещения для участников кэмпа.</p><p><img src="'+P+'" alt=""><img src="'+S+'" alt=""><img src="'+V+'" alt=""></p><p><img src="'+N+'" alt=""><img src="'+Y+'" alt=""><img src="'+D+'" alt=""></p><p>Лагерь свободного творчества — это наш рецепт продуктивного взаимодействия человека с природой. Сочетание естественного расслабления тела и ума, фокуса на взаимном обучении и сотрудничестве с широкими созидательными возможностями дает человеку шанс увидеть себя в совершенно новой перспективе. <strong>Лесные открытые мастерские</strong> — это творческий процесс, который разворачивают люди, вдохновленные природой и возможностями совместной самореализации.</p><p>Мы обнаружили ключевые роли, которые могут сыграть участники общего творческого процесса. <strong>Мастера</strong> делятся своими инструментами, навыками и умениями. <strong>Кураторы</strong> поддерживают и развивают творческий процесс. <strong>Гости</strong> внимательно слушают и получают навыки, а <strong>умельцы</strong> с благодарностью используют возможности лагеря для реализации личных и совместных проектов.</p><p><strong><a href="/theory/system/projectapp/">Проектный подход</a></strong> — основа осознанного творчества. И это — первое, что стоит освоить и начать применять каждому участнику открытой мастерской. Конечно, не попробовав себя в чем-то, сложно проработать полноценный проект. Мы знаем, что нужно демонстрировать новичкам различные образцы и примеры проектов, для формирования представления о возможностях мастерской. Необходимо также уметь обеспечить плавное погружение человека в непривычное для него ремесло.</p><p>Вживую рассмотреть образцы произведений в различных техниках, понаблюдать за рабочим процессом в качестве зрителя, а затем, наметив приоритеты, попробовать себя на вводных мастер-классах — вот прозрачный метод постепенного погружения в практику. Затем внимательно слушать мастеров и оттачивать умения в ходе их углубленных курсов. А затем можно и воспользоваться приобретенной в этом процессе творческой свободой. Ведь творчество — это нечто большее, чем применение отдельных навыков. Подлинная свобода самовыражения обнаруживается в смелом сочетании различных приёмов и технологий.</p><p><img src="'+E+'" alt=""><img src="'+G+'" alt=""><img src="'+b+'" alt=""><img src="'+J+'" alt=""><img src="'+L+'" alt=""></p><p>Мы видим открытые мастерские кульминацией процесса знакомства с творчеством. Здесь наступает тот бесценный момент, когда человек впервые понимает, что он сам осознанно прошёл все стадии проекта от придумывания и проработки идеи до завершенного произведения и его представления. И не видит проблемы повторить этот путь снова. Так запускается продуктивный цикл, в котором человек всегда знает, каким будет его следующий шаг навстречу совершенству. Творчество — это процесс воплощения человеком его собственных представлений о прекрасном. Открытая мастерская предоставляет творческие возможности, оставляя человеку максимум свободы для самовыражения.</p><p>Умельцы, обладающие достаточными умениями, самостоятельно работают в мастерской над  проектами с одобрения куратора. Это могут быть любые задумки от личных творческих начинаний с целью исследования или самовыражения до крупных совместных инженерных проектов. Можно предложить любую интересную идею. Куратор скорректирует проект с учётом возможностей мастерской и компетенций автора, а также поможет спланировать этапы его реализации. Куратор также помогает умельцам не застревать на пути к самостоятельной реализации идей.</p><p><img src="'+Q+'" alt=""><img src="'+w+'" alt=""><img src="'+U+'" alt=""><img src="'+q+'" alt=""></p><p>Такая методология позволяет человеку, даже вовсе не разбирающемуся в творчестве, широко осмотреться и плавно погрузиться в заинтересовавшие его направления. Но ведь даже окинуть взглядом все многообразие созидательных практик — не такая уж простая задача. В этом поможет выработанное нами <a href="/theory/system/elements/">деление творчества на пять стихий</a>, аналогичных тем, которые выделяли в природе древние. Каждая из стихий включает в себя несколько ключевых мастерских, охватывающих все основные технологические процессы этого направления.</p><ul><li>Элемент <strong>Земля</strong> включает гончарную и скульптурную мастерскую, открывающие широкий спектр приёмов в работе с твёрдыми и твердеющими массами.</li><li>Стихия <strong>Вода</strong> — это столярная мастерская, работающая с древесиной (водопроводящей тканью сосудистых растений), а также мастерская «переработка», предназначенная для работы с резиной, пластиками, смолами и прочими синтетическими органическими материалами.</li><li>Элемент <strong>Огонь</strong> сочетает в себе работу с высокими температурами, металлами и неорганической химией в кузнице и слесарной мастерской.</li><li><strong>Воздух</strong> — это все техники работы с тонкими материалами, доступные в изостудии, макетной и швейной мастерских.</li><li><strong>Эфир</strong> — пятый элемент творчества, включающий исследование слова и текста в литературе, звука и ритма в музыке и выразительных возможностей жеста в театральных постановках.</li></ul><p>Конечно, границы между мастерскими легко размываются, однако такое логическое деление помогает создавать действительно многогранное творческое пространство.</p><p><img src="'+M+'" alt=""><img src="'+O+'" alt=""><img src="'+W+'" alt=""><img src="'+z+'" alt=""></p><p>Для того, чтобы окинуть всё это взглядом, попробовать свои силы в интересующих направлениях и освоиться с выбранными техниками необходимо время. Трансформация из гостя и начинающего ученика в умельца, самостоятельно воплощающего своё вдохновение — путь, который мы стараемся сделать максимально быстрым и простым. Однако, вряд ли для этого хватит пары дней. Необходим день на осмотреться, второй — немного прикоснуться к ремеслу. Ещё пару дней на набор ключевых навыков и умений. Плюс время для свободной работы над проектами. Так образуются недельные смены, которыми живёт лагерь свободного творчества. Оставляя время для себя, природы и общения, каждый день в лагере включает две трёхчасовые сессии работы в мастерских, а также вечернюю лекцию, музыкальный джем, кинопросмотр или другое интересное общее занятие, которое может предложить каждый.</p><p>Когда времени достаточно, появляется возможность расслабиться, отбросить городскую спешку и сонастроиться с естественными ритмами природы. Чистый воздух и зарядка по утрам, вегетарианское питание и прогулки по лесу. Виндсёрфинг и солнечные ванны летом или снежные битвы и катание на сноукайте зимой — для этого в Сёрфлагере просто идеальные условия. А Лесные открытые мастерские позволяют преобразовать полученный от общения с природой и единомышленниками заряд энергии в реальные творческие произведения. От небольших ювелирных и художественных изделий до масштабных произведений искусства и инженерных проектов — результаты лагеря свободного творчества непредсказуемы и способны удивить каждого участника, в том числе и его организаторов.</p><p><img src="'+B+'" alt=""><img src="'+C+'" alt=""><img src="'+X+'" alt=""><img src="'+K+'" alt=""></p><p>Доверие и открытость к живому совместному творчеству делает лагерь ЛОМ уникальным проектом, где каждый сможет найти для себя неповторимый способ раскрыть свой внутренний потенциал. Мастера смогут взглянуть на свои умения под новым углом, а также, обмениваясь друг с другом опытом, обнаружить перспективные направления развития и сотрудничества. Кураторы отточат свои педагогические навыки и получат возможность, работая в мастерской, вдохновлять и поддерживать окружающих умельцев. Гости смогут познакомятся с множеством новых подходов к творчеству, а умельцы погрузятся в это многообразие, чтобы научиться применять его для решения личных и коллективных задач. ЛОМ — живое творческое сообщество и чем плотнее сотрудничество всех участников лагеря, тем более глубокой и наполненной оказывается смена и её результаты. Серфлагерь предоставит прекрасные условия жизни в лесу, OOLEY оборудует универсальное творческое пространство, а что из этого получится, зависит от каждого из участников этого коллективного процесса.</p>',33),os=s('<p><img src="'+R+'" alt=""></p><p><img src="'+F+'" alt=""><img src="'+H+'" alt=""><img src="'+Z+'" alt=""><img src="'+$+'" alt=""><img src="'+ss+'" alt=""></p>',2);function rs(as,cs,_s,is,gs,ms){const t=p;return r(),a("div",null,[ps,c(t,{link:"https://vimeo.com/299208902"}),os])}const js=o(ts,[["render",rs]]);export{ls as __pageData,js as default};
