import{_ as a}from"./chunks/theme.C06NILu8.js";import{_ as e,a as p,b as o,f as i,J as r,o as c}from"./chunks/framework.BWf-PbcG.js";const l="/assets/IMG_6770.BwdYkHGY.jpg",_="/assets/IMG_6771.y-YFfDu7.jpg",n="/assets/IMG_6772.BBPB8n9L.jpg",m="/assets/IMG_6773._Jc10eu7.jpg",g="/assets/IMG_6774.DiBa1IuI.jpg",u="/assets/IMG_6776.D6bIFnvs.jpg",h="/assets/IMG_6777.CQwSx788.jpg",d="/assets/IMG_6778.BipTiZ5D.jpg",b="/assets/IMG_6779.CMi_hMky.jpg",q="/assets/IMG_6780.BSYFNgeC.jpg",j="/assets/IMG_6781.XjPLuDXr.jpg",I="/assets/IMG_6782.fH_YDGyS.jpg",G="/assets/IMG_6783.HizpAO1S.jpg",M="/assets/IMG_6785.Cqi5KskX.jpg",k="/assets/IMG_6786.D6-Dwb0R.jpg",f="/assets/IMG_6787.BVR7oIli.jpg",x="/assets/IMG_6788.DTWt25WN.jpg",B="/assets/IMG_6789.Csc8_wVP.jpg",P="/assets/IMG_6791.D2sXP035.jpg",D="/assets/IMG_6792.BsxejitU.jpg",C="/assets/IMG_6793.CO2peBgo.jpg",O="/assets/IMG_6794.BT7jBpeQ.jpg",Y="/assets/IMG_6795.Be_uWVzA.jpg",v="/assets/IMG_6799.CqKFR1QT.jpg",S="/assets/IMG_7403.CJKDQS1J.jpg",V="/assets/IMG_7415.KofRRFqc.jpg",w="/assets/IMG_7423.BoWWOUww.jpg",y="/assets/IMG_7426.B8PTIC8v.jpg",K=JSON.parse('{"title":"Шьем мини-кошельки из кожи и резинки","description":"","frontmatter":{"title":"Шьем мини-кошельки из кожи и резинки","date":"2014-11-11","cover":"IMG_6803.jpg","vimeo":"https://vimeo.com/62260868","toc":true},"headers":[],"relativePath":"practice/project/mini-purse/index.md","filePath":"practice/project/mini-purse/index.md"}'),F={name:"practice/project/mini-purse/index.md"};function N(J,t,L,Q,R,T){const s=a;return c(),p("div",null,[t[0]||(t[0]=o("p",null,"Мы увидели эту кампанию на Кикстартере … и не стали ждать её завершения. Поразмышляли, поскладывали бумажки по-всякому и поняли как сшить такой кошелек самим. Это оказалось вовсе нетрудно и мы решили поделиться этим навыком с вами. Попробуйте шить кошельки самостоятельно.",-1)),i(s,{link:"http://youtu.be/YadICbFV1eQ"}),t[1]||(t[1]=r('<p><em>Время указано из расчета шитья 1—2 кошельков.</em></p><h2 id="инструменты-и-материалы" tabindex="-1">Инструменты и материалы <a class="header-anchor" href="#инструменты-и-материалы" aria-label="Permalink to &quot;Инструменты и материалы&quot;">​</a></h2><p><img src="'+l+'" alt=""></p><p>Подойдет любая линейка, мягкий карандаш или ручка и ножницы, желательно поострее. Можно использовать и ножи, в том числе круговые.</p><h3 id="материалы" tabindex="-1">Материалы <a class="header-anchor" href="#материалы" aria-label="Permalink to &quot;Материалы&quot;">​</a></h3><p>Из расчета на один кошелек, вам понадобятся:</p><ul><li>кожа или кожзам (6х18 см/шт),</li><li>широкая резинка ~6 см шириной (8 см/шт),</li><li>двухсторонний скотч <em>(нужен для временной сборки перед шитьем),</em></li><li>нитка,</li><li>клей для кожи или любой универсальный.</li></ul><h3 id="инструменты" tabindex="-1">Инструменты <a class="header-anchor" href="#инструменты" aria-label="Permalink to &quot;Инструменты&quot;">​</a></h3><p>Чтобы шитье не было пыткой, стоит подготовиться и иметь:</p><ul><li>карандаш или ручку,</li><li>линейку,</li><li>ножницы или острый нож,</li><li>молоток,</li><li>швейную машинку.</li></ul><p>Все это есть в OOLEY42  и вы можете записаться и воспользоваться возможностью пошить в <a href="/practice/ooley42/" title="Прицеп OOLEY42">прицепе-мастерской OOLEY42</a>.</p><h2 id="размечаем-и-режем-10-минут" tabindex="-1">Размечаем и режем [10 минут] <a class="header-anchor" href="#размечаем-и-режем-10-минут" aria-label="Permalink to &quot;Размечаем и режем [10 минут]&quot;">​</a></h2><p><img src="'+_+'" alt=""><img src="'+n+'" alt=""></p><blockquote><p>Эффективно используйте материал. Продумайте разметку и учтите ньюансы материала.</p></blockquote><p>Размечаем прямоугольники 6 х 18 см. Никакого запаса или отступов не нужно, так что смело размечайте встык, например, если хотите сделать несколько кошельков.</p><p><img src="'+m+'" alt="Нарезанные кусочки"></p><h2 id="проклеиваем-края-20-минут" tabindex="-1">Проклеиваем края [20 минут] <a class="header-anchor" href="#проклеиваем-края-20-минут" aria-label="Permalink to &quot;Проклеиваем края [20 минут]&quot;">​</a></h2><p><img src="'+g+'" alt=""></p><blockquote><p>Чтобы сделать аккуратным внешний край кармашка, его подвернем и проклеим.</p></blockquote><p>Как вы уже могли догадаться, мы вырезали будущие кармашки, которые получатся сгибанием этих кусочков пополам. По длинным сторонам прямоугольники будут зашиты и остается две свободных стороны, будущее отверстие кармана.</p><p>Чтобы его немного облагородить, мы наносим клей с каждой из этих сторон шириной примерно 1,5 см. Далее, в соответствии с инструкцией к конкретному клею, выдерживаем некоторое время. Затем сгибаем пополам проклеенные участки, получая ровный загиб ~7 мм шириной.</p><p>Чтобы край был ровным и менее рыхлым, аккуратно пробиваем его молотком. Складываем кусок пополам и пробиваем молотком место сгиба будущего кармашка.  Советуем делать это на черновой поверхности вроде какой-нибудь доски. Молоток на удивление легко оставляет вмятины на многих покрытиях.</p><h2 id="собираем-25-минут" tabindex="-1">Собираем [25 минут] <a class="header-anchor" href="#собираем-25-минут" aria-label="Permalink to &quot;Собираем [25 минут]&quot;">​</a></h2><p><img src="'+u+'" alt=""></p><blockquote><p>Резинки могут быть самыми разными, какие найдете. Желательна ширина от 4 до 6 см, но есть простор для экспериментов.</p></blockquote><p>Нарезаем резинку на нужное количество отрезков чуть шире, чем ширина кармашков.  Казалось бы, можно уже садиться за машинку и сшивать кошельки, но сшить два слоя кожи с резинкой — не такая уж тривиальная задачка! Можно, конечно, наметать нашу конструкцию нитками, но тогда потом скорее всего в коже останутся дырочки от намёточного шва. Мы придумали другое решение, использовать секретный ингредиент!</p><p><img src="'+h+'" alt=""></p><blockquote><p>Секретный ингредиент — двухсторонний скотч</p></blockquote><p>Чтобы точно разместить и временно закрепить все элементы конструкции, мы используем полоски двухстороннего скотча шириной ~1 см и длиной ~6см.</p><p><img src="'+d+'" alt=""><img src="'+b+'" alt=""><img src="'+q+'" alt=""><img src="'+j+'" alt=""><img src="'+I+'" alt=""><img src="'+G+'" alt=""></p><p>Наклеиваем и открываем две полоски вдоль длинной стороны на будущей <strong>внешней</strong> стороне кармашка. Поперек этих линий кладем резинку, следя за перпендикулярностью и расстоянием от края и сгиба кармана. Затем клеим скотч на резинку в тех же местах и накрываем получившуюся конструкцию свободной стороной кармана.</p><p><img src="'+M+'" alt="IMG_6785"></p><blockquote><p>Получается вот такой бутерброд</p></blockquote><h2 id="прошиваем-15-минут" tabindex="-1">Прошиваем [15 минут] <a class="header-anchor" href="#прошиваем-15-минут" aria-label="Permalink to &quot;Прошиваем [15 минут]&quot;">​</a></h2><p><img src="'+k+'" alt=""></p><blockquote><p>Мы предусмотрели отступы по краям кармана</p></blockquote><p>Теперь намечаем линии швов с отступом 3—4 мм, и можно садиться за швейную машинку.  Советуем убедиться заранее в том, чтобы машинка справлялась с используемыми материалами, сложенными слоями. Не используйте слишком толстую кожу, а то прийдется шить руками и с использованием специальных техник и инструментов.</p><p>Проверьте наличие верхней нитки в иголке и нижней нитки на шпульке, выставьте прямой шов средней длины (~2-3 мм). Начинаем шить от нижнего края кармана, стараясь не тянуть и не толкать наш бутерброд, а помогать машинке самой его проглотить и прошить край до самого верха. При приближении к концу кармана будьте внимательны: постарайтесь остановиться как можно ближе к краю,  а затем сделайте несколько шагов машинкой назад (на них бывает кнопка реверса, ну или крутите колесо в обратную сторону руками.), а затем еще раз вперед, тем самым закрепляя шов.</p><p><img src="'+f+'" alt=""><img src="'+x+'" alt=""><img src="'+B+'" alt=""><img src="'+P+'" alt=""><img src="'+D+'" alt=""></p><p>Отрежьте нитки и  у вас в руках уже готовый кошелек. Осталось только его вывернуть.</p><h2 id="выворачиваем-5-минут" tabindex="-1">Выворачиваем [5 минут] <a class="header-anchor" href="#выворачиваем-5-минут" aria-label="Permalink to &quot;Выворачиваем [5 минут]&quot;">​</a></h2><p><img src="'+C+'" alt=""></p><blockquote><p>Кошелек наизнанку</p></blockquote><p>Как вы уже, наверняка, догадались, мы сшивали кошелек с изнанки. Теперь вам нужно постараться вывернуть получившийся кожаный кармашек. Это может оказаться затруднительным если вы переборщили со скотчем или он получился у вас слишком узким, но у вас наверняка это получится.</p><p><img src="'+O+'" alt=""></p><blockquote><p>Осталось отклеить скотч</p></blockquote><p>Удалите остатки скотча и кошелек готов! Для улучшения формы стоит интенсивно пробить молотком верхний край кармашка и его сгиб.</p><h3 id="готово" tabindex="-1">Готово! <a class="header-anchor" href="#готово" aria-label="Permalink to &quot;Готово!&quot;">​</a></h3><p><img src="'+Y+'" alt=""><img src="'+v+'" alt=""></p><p>У нас ушло около трех часов, чтобы сшить не один, а целых <a href="/practice/project/mini-purse/" title="Ультракомпактные кожаные кошельки">десять кошельков с разными резинками</a>.</p><p>Если этот класс оказался для вас чем-то полезен — не молчите, пишите комментарии! Также пишите здесь если вам интересно было бы посетить мастер-класс по шитью кошельков, мы организуем!</p><p><img src="'+S+'" alt=""><img src="'+V+'" alt=""><img src="'+w+'" alt=""><img src="'+y+'" alt=""></p>',52))])}const X=e(F,[["render",N]]);export{K as __pageData,X as default};
