import{_ as s,o as t,c as a,e as p}from"./chunks/framework.78e651f5.js";const c="/assets/F8WPDL7FDWI2KFF.MEDIUM.30d86f4f.jpg",r="/assets/FH7W5TYFDWI2KFK.LARGE_.0e8c5156.jpg",o="/assets/FJDCM0RFDWI2KR7.LARGE_.ed42fff0.jpg",e="/assets/FOS87FLFDWI2KFJ.LARGE_.d37d2080.jpg",i="/assets/FVVZLKCFDWI2KFE.LARGE_.28ac8c62.jpg",_="/assets/ADS1743.3fe4a0aa.jpg",m="/assets/ADS1745.94283621.jpg",g="/assets/ADS1746.d2ccc04f.jpg",l="/assets/ADS1776.c2b81733.jpg",n="/assets/ADS1778.745d6cf3.jpg",d="/assets/ADS1788.eb72bd97.jpg",j="/assets/ADS1794.209eb141.jpg",b="/assets/ADS1824.f810d100.jpg",f="/assets/ADS1853.052ea132.jpg",D="/assets/ADS1857.7bab433d.jpg",A="/assets/ADS1858.42b56cc4.jpg",S="/assets/ADS1871.7906654d.jpg",h="/assets/ADS1887.e648daa0.jpg",u="/assets/ADS1892.7a0033f3.jpg",I="/assets/ADS1903.5ce37740.jpg",q="/assets/ADS2178.5cf28ac8.jpg",x="/assets/ADS2193.b61b0f1a.jpg",F="/assets/ADS2194.c9670fc6.jpg",M="/assets/ADS2197.66ed4a50.jpg",k="/assets/diHlI2ZrQP8.16677b35.jpg",w="/assets/ADS1909.659cc3d1.jpg",G="/assets/ADS1910.48ef5f6d.jpg",P="/assets/ADS1919.eb099f1d.jpg",E="/assets/ADS1929.fa0a2ff5.jpg",R="/assets/ADS1930.8760d411.jpg",T="/assets/gwVQpN4Rg7A.66da61b0.jpg",K="/assets/KZXarsnnwbw.7e2193d1.jpg",V="/assets/ADS1899.3b3b29f2.jpg",y="/assets/UmIrQC10Leo.5042682e.jpg",L="/assets/uHkp885k1Q.f4084bc3.jpg",W="/assets/UdbMH5aij3Y.d7ae84c0.jpg",C="/assets/gN72llzagPE.78eed393.jpg",J="/assets/IMG_3035.51c87185.jpg",N="/assets/IMG_3038.b5927c50.jpg",H="/assets/IMG_3044.521bfdd4.jpg",Q="/assets/IMG_3045.8ee21cec.jpg",v="/assets/JV2rMJp4yDg.ba328f11.jpg",O="/assets/OT3Xp1bH9RE.3edcc566.jpg",U="/assets/qgv1Wz3yUyM.4deb98a3.jpg",$="/assets/ECAJO71o9zo.93daad1d.jpg",z="/assets/ah7mpiQArec.fc64b443.jpg",B="/assets/IMG_3060.8d923ca8.jpg",Y="/assets/IMG_3061.4a2e271c.jpg",Z="/assets/IMG_3062.7304c168.jpg",X="/assets/IMG_3067.c2904dfd.jpg",ss="/assets/IMG_3069.6c85a10d.jpg",ts="/assets/IMG_3071.c7c42147.jpg",as="/assets/a3big4sViVA.c0c02d6c.jpg",ps="/assets/uKpBwNixR3s.02c10183.jpg",cs="/assets/ADS2186.08c57fac.jpg",rs="/assets/ADS2149.7b42fef6.jpg",os="/assets/ADS2153.26dcb40e.jpg",es="/assets/ADS2154.f03e5eeb.jpg",is="/assets/ADS2156.435b29b9.jpg",_s="/assets/ADS2142.1c8eb817.jpg",ms="/assets/ADS2143.80d9287f.jpg",gs="/assets/ADS2144.156238b7.jpg",ls="/assets/ADS2204.d03c793f.jpg",ns="/assets/ADS2215.04655770.jpg",ds="/assets/ADS2217.4ef2d642.jpg",js="/assets/ADS2223.7d551437.jpg",bs="/assets/ADS2234.a44eeb8d.jpg",fs="/assets/ADS2243.94617753.jpg",Ds="/assets/ADS2253.b5c85752.jpg",As="/assets/ADS2260.129a9fc5.jpg",Ss="/assets/ADS2268.50e8050a.jpg",hs="/assets/ADS2146.db55c182.jpg",us="/assets/ADS2135.4b4c1adf.jpg",Is="/assets/ADS2141.d5dab260.jpg",qs="/assets/k13TKn3AD60.7bf7ef7b.jpg",Vs=JSON.parse('{"title":"Два стола из одного листа фанеры","description":"Для соседского центра ЖК Новомолоково","frontmatter":{"title":"Два стола из одного листа фанеры","description":"Для соседского центра ЖК Новомолоково","date":"2016-03-12","cover":"tables.jpg"},"headers":[],"relativePath":"practice/project/plywood-table/index.md","filePath":"practice/project/plywood-table/index.md"}'),xs={name:"practice/project/plywood-table/index.md"},Fs=p('<p>Изучая зарубежный DIY-опыт и наработки, мы часто находим различные открытые инструкции и схемы создания самых разнообразных штук. Один из полезнейших ресурсов в сфере рукоделия, в широком смысле этого слова, — портал Instructables.com, где можно найти подробные иллюстрированные пошаговые инструкции к творческим проектам. Именно там мы однажды наткнулись на занимательные чертежи столов сделанных из одного листа фанеры.</p><p><a href="http://www.instructables.com/id/8x42-2-Tables-from-1-sheet-of-plywood/" class="external-link" target="_blank" rel="noreferrer">www.instructables.com/8x42-2-Tables-from-1-sheet-of-plywood/</a></p><p><img src="'+c+'" alt=""><img src="'+r+'" alt=""><img src="'+o+'" alt=""><img src="'+e+'" alt=""><img src="'+i+'" alt=""></p><p>Изящное дизайн-решение позволяет всего из одного стандартного листа фанеры 1500х3000 мм толщиной 24 мм вырезать все необходимые детали для сборки сразу двух больших рабочих столов. К инструкции приложены схемы для программирования фрезерного или лазерного ЧПУ-станка, а в комментариях отмечено, что раскрой можно выполнить и ручными электроинструментами. Изящный дизайн привлёк наше внимание и мы решили попробовать сделать такие столы самостоятельно. К сожалению эта инструкция предлагается лишь как идея, отрисованная на компьютере, то есть ни фотографий готовых столов, ни описания опыта их использования там нет. Не проблема, пройдём этот путь и сами!</p><p><img src="'+_+'" alt=""><img src="'+m+'" alt=""><img src="'+g+'" alt=""><img src="'+l+'" alt=""><img src="'+n+'" alt=""><img src="'+d+'" alt=""></p><p>Проект, конечно, кажется простым только на первый взгляд. Да, у нас уже есть достаточный для этого инструмент, но перед передвижной мастерской постоянно встаёт серьезный вопрос о пространстве для воплощения подобных проектов, особенно зимой. Шуметь и пылить в своей квартире — не самая лучшая идея. На улице редко бывают достаточно тёплые дни, чтобы проработать не замёрзнув хотя бы час. Да и для инструмента это могут быть не самые подходящие условия. Но мы не унываем, ведь знаем немало московских общественных столярных мастерских, где можно без особых трудностей реализовать даже самые смелые проекты. К тому же вполне возможно, что материал можно будет приобрести прямо на месте.</p><p><img src="'+j+'" alt=""><img src="'+b+'" alt=""><img src="'+f+'" alt=""><img src="'+D+'" alt=""></p><p>Однако на этот раз получилось немного иначе. К нам обратилась команда Культурного центра нового подмосковного жилого комплекса Ново-Молоково с предложением провести серию мастер-классов для жителей района. К нашему удивлению, Центру как раз не хватало рабочих столов для организации полноценного коворкинга в отведённом для него помещении. После пробного мастер-класса по резьбе и выжиганию, мы предложили прямо на месте выпилить и собрать полноценные рабочие столы в ходе серии открытых воркшопов для всех желающих. К сложному инструменту можно допускать людей от 16 и старше, что совпадает с целевой аудиторией коворкинга. Мы запланировали справиться с этим проектом за три выходных четырёхчасовых сессии работы открытой мастерской.</p><p><img src="'+A+'" alt=""><img src="'+S+'" alt=""><img src="'+h+'" alt=""><img src="'+u+'" alt=""><img src="'+I+'" alt=""></p><p>За время переговоров мы успели собрать макет стола из тонкой фанеры в масштабе примерно 1:3 и обдумали технологию распила и сборки. Макет показал некоторую шаткость конструкции, что заставило задуматься об использовании более толстой фанеры. Оставалось только приобрести все необходимые расходники, крепежи и доставить на площадку эти огромные листы фанеры. Один лист большеформатной фанеры толщиной 24 мм весит около 75, а 27 мм — все 85 килограмм. Мы заказали доставку двух листов фанеры на сайте <a href="http://fanerra.ru/" class="external-link" target="_blank" rel="noreferrer">Fanerra.ru</a> и дополнительно попросили распилить их на 4 квадрата 1500х1500 мм — по одному на стол. Оплатили заказ электронно и назначили доставку на день перед воркшопом. В качестве крепежа решили использовать саморезы и мебельные винты-конфирматы. Мы готовы начинать!</p><h2 id="первыи-воркшоп" tabindex="-1">Первый воркшоп <a class="header-anchor" href="#первыи-воркшоп" aria-label="Permalink to &quot;Первый воркшоп&quot;">​</a></h2><p><img src="'+q+'" alt=""></p><p>Четыре часа — не так уж много времени для столярного дела. Не так давно столярничество было длительным процессом кропотливого труда исключительно ручным инструментом. Современный электроинструмент ускоряет и упрощает базовые механические операции, однако это далеко не всё. В ЧПУ-станок загружается непосредственно программа, а для ручной работы необходима ответственная стадия разметки контуров реза на заготовке. Автор инструкции приложил схему со всеми необходимыми размерами и нам оставалось лишь бдительно перенести эти размеры на поверхность фанеры с помощью угольника, длинной линейки, циркуля и простого карандаша.</p><p>Развёртка передвижной мастерской в стенах культурного центра занимает, ориентировочно, 45 минут, и к моменту начала воркшопа мы успели нанести разметку для резки первого листа 24-миллиметровой фанеры. Для выпиливания заготовок мы применили электролобзик с пилками для чистого прямого реза. Радиус поворота с таким полотном составляет около 100 мм, что совпадает с радиусом скругления углов столешницы. Для вырезания пазов в перемычках ножек, а также для вырезания отверстий для их вставки в столешницу, мы предварительно сверлили отверстия шуруповёртом.</p><p><img src="'+x+'" alt=""><img src="'+F+'" alt=""><img src="'+M+'" alt=""><img src="'+k+'" alt=""></p><p>При создании стола, особенно с такой конструкцией, крайне важна точность в размерах заготовок и пазов. Ручной электроинструмент не даёт такой точности, как ЧПУ-станок, особенно когда человек пользуется электролобзиком впервые в жизни. Вместе нам удалось выпилить заготовки достаточно аккуратно, а вот пазы все-таки пришлось дорабатывать потом молотком и стамеской, что потребовало дополнительных сил и времени. Скруглив кромку столешницы кромочным фрезером с радиусной фрезой 6 мм, мы приступили к сборке стола. Силой забив перекрестие ножек в пазы в столешнице, мы притянули их к ней с помощью мебельных стяжек — &quot;конфирматов&quot;. Для сверления пробовали применить специальные конфирматные свёрла с изменяемой глубиной сверления, однако они не смогли справиться с трудностями сверления торцов фанеры: центральное сверло застревало и сворачивало крепивший его винтик. Попытки затянуть его посильнее привели только к порче идущего в комплекте тонкого шестигранного ключа. Видимо, такие свёрла предназначены исключительно для ДСП, ДВП и подобных материалов. Пришлось сверлить отверстия в два подхода:</p><ul><li>сверлим отверствие сверлом 6 мм на грубину вкручивания конфирмата,</li><li>расширяем его держателем для конфирматных свёрел без центрального сверла</li></ul><p><img src="'+w+'" alt=""><img src="'+G+'" alt=""><img src="'+P+'" alt=""><img src="'+E+'" alt=""><img src="'+R+'" alt=""><img src="'+T+'" alt=""><img src="'+K+'" alt=""></p><p>Стяжка помогла сделать конструкцию крепче, однако это не сильно повлияло на всё-таки явно недостаточную устойчивость готового стола. Конечно, сказался не везде перпендикулярный ход пилки лобзика, однако основной недостаток обнаружился в самой конструкции. Изящность дизайна привела к потере конструктивной жесткости изделия. Стол неплохо держит продольные толчки и не трясётся. Однако ножки оказались слишком тонкими и гибкими, стол заметно качается при его &quot;скручивании&quot;. Мы не стали проверять на практике, но было очевидно, что если поставить на такой стол чашку чая и попытаться за ним поработать, неприятности неизбежны. Понимая, что такой стол вряд ли будет использоваться в коворкинге, мы отдали его на растерзание местным детишкам, позволив им начать рисовать на нём всё, что им заблагорассудится. На этом первый воркшоп завершился и мы отправились размышлять над полученным опытом.</p><h2 id="переосмысление-конструкции" tabindex="-1">Переосмысление конструкции <a class="header-anchor" href="#переосмысление-конструкции" aria-label="Permalink to &quot;Переосмысление конструкции&quot;">​</a></h2><p><img src="'+V+'" alt=""><img src="'+y+'" alt=""><img src="'+L+'" alt=""><img src="'+W+'" alt=""><img src="'+C+'" alt=""></p><p>Исходной конструкции не хватает перекрестий и перпендикулярных плоскостей, придающих жёсткости. Мы решили переработать структуру стола, оставаясь в рамках использования одного целого листа фанеры. В процессе размышлений и проверки гипотез мы создали два макета из картона, малярного скотча и термоклея, а затем и более масштабный макет стола из 10-миллиметровой фанеры. Уже на этой стадии стало очевидно, что использование двухэлементных ножек, соединённых под прямым углом, с использованием даже нешироких царг (верхнего пояса подстолья, соединяющего ножки между собой) даёт отличную жёсткость и прочность конструкции. Собранный на морозе буквально за полчаса макет, даже без царг и соединённый лишь гвоздями спокойно выдерживает вес взрослого человека и не качается. Высоту стола решили оставить прежней, 750 мм — это чуть выше обеденного стола и идеально для работы с ноутбуком и бумагами. Захватив с собой небольшой лист тонкой фанеры для царг, мы готовы строить столы новой конструкции.</p><p><img src="'+J+'" alt=""><img src="'+N+'" alt=""><img src="'+H+'" alt=""><img src="'+Q+'" alt=""></p><h2 id="второи-воркшоп" tabindex="-1">Второй воркшоп <a class="header-anchor" href="#второи-воркшоп" aria-label="Permalink to &quot;Второй воркшоп&quot;">​</a></h2><p>В обновлённой конструкции мы учли неточность пиления электролобзиком и сделали линии отпила прямыми и навылет, что позволило нам задействовать ручную циркулярную пилу, дающую намного более качественные спилы. Безусловно, циркулярка — еще более сложный и опасный инструмент, чем электролобзик, и мы внимательно отнеслись к работе и обучению обращению с ним. Участники воркшопа начали работу с разметки сразу двух столов, отмечая не только линии пила, но и смещённые на ширину отступа подошвы пилы линии для выставления направляющей. Для аккуратных прямых отпилов пиление циркулярной производится только вдоль направляющей (мы использовали ровный кусок фанеры) и с использованием пильного диска с частыми зубьями для чистого реза. Важно всегда помнить о существенной ширине пропила, составляющей примерно 3 мм и учитывать это при разметке расстояний до направляющей. Также стоит помнить, что циркулярная пила даёт достаточно много опилок и выбрасывает их довольно мощной струёй — без очков не обойтись.</p><p><img src="'+v+'" alt=""><img src="'+O+'" alt=""><img src="'+U+'" alt=""><img src="'+$+'" alt=""><img src="'+z+'" alt=""></p><p>Совместными усилиями мы достаточно быстро выпилили два комплекта заготовок для столов. Мы постарались эффективно сочетать работу по доработке ножек и царг с обработкой столешниц. Лобзиком привычно скруглили углы столешниц, а их кромки со всех сторон обработали радиусной фрезой. В это время началась активная шлифовка деталей. В ход пошли шлифовальные листы на тканевой основе, эксцентриковая и ленточная шлифмашинки. К горстке сравнительно крупной стружки от пиления добавилось плотное облако мелкой пыли от шлифования грубой, а затем и более тонкой шкуркой. Все участники работали в очках и респираторах, так что это было довольно безопасно, однако пространство культурного центра еще долго будет помнить об этом моменте. В особенности сложно это будет забыть уборщице, которой еще не один день придётся собирать оседающую на все поверхности пыль.</p><p><img src="'+B+'" alt=""><img src="'+Y+'" alt=""><img src="'+Z+'" alt=""><img src="'+X+'" alt=""><img src="'+ss+'" alt=""><img src="'+ts+'" alt=""></p><p>Подготовленные детали ножек мы скрепляли с использованием угловых и прямых струбцин, столярного клея и саморезов. После отладки процесса сборка одной ножки занимает не более 10 минут. Намазываем обе поверхности клеем, совмещаем и закрепляем в угловой струбцине и стягиваем всё это прямой. Напротив стыка засверливаем отверстия для саморезов и скрепляем ножку в трёх точках. Всё, можно снимать струбцины и переходить к следующей ножке.</p><p>За второй воркшоп мы смогли полностью подготовить 2 столешницы, 8 ножек и 8 царг, готовых к покраске и сборке. К тому же мы нашли возможность разобраться и с самым первым шатким столом. Как оказалось, маленькие дети — завсегдатаи культурного центра и для них требуется свой стол для рисования и прочего творчества. Мы укоротили ножки примерно на 200 мм, сделав удобный для детей от 5 до 15 лет &quot;детский&quot; стол для работы стоя или сидя на диване.</p><p><img src="'+as+'" alt=""><img src="'+ps+'" alt=""></p><h2 id="дизаин-отделки" tabindex="-1">Дизайн отделки <a class="header-anchor" href="#дизаин-отделки" aria-label="Permalink to &quot;Дизайн отделки&quot;">​</a></h2><p>Немаловажная часть любого столярного проекта — финишная отделка готового изделия. Здесь всегда рождается множество разнообразных идей и их сочетаний, однако важно всегда проверять соответствие внешнего вида предмета его функциональному назначению. Размышляя над оформлением столов, мы рассматривали варианты приклеивания различных карт, схем и прочих бумажек методом декупажа, пробовали сделать набор трафаретов для нанесения всевозможных декоративных элементов на дерево, однако вовремя осознали, что для работы в коворкинге необходимы наименее отвлекающие поверхности, позволяющие сконцентрироваться на работе. Мы решили обратиться к эффектному сочетанию гладких крашеных поверхностей с фактурой необработанного дерева. Вариант обработки фанеры масло-воском пришлось отбросить в связи с ограниченностью времени на сушку, ведь повторный слой масла можно наносить только через сутки впитывания и сушки первого слоя. В очередной раз нас выручили акриловые вододисперсионные краски и матовый лак. Мы разработали несложную композицию из крашеных в белый ножек, ярких царг и покрытых прозрачным лаком столешниц. Осталось воплотить это всё за один четырёхчасовой воркшоп.</p><h2 id="третии-воркшоп" tabindex="-1">Третий воркшоп <a class="header-anchor" href="#третии-воркшоп" aria-label="Permalink to &quot;Третий воркшоп&quot;">​</a></h2><p><img src="'+cs+'" alt=""><img src="'+rs+'" alt=""><img src="'+os+'" alt=""><img src="'+es+'" alt=""><img src="'+is+'" alt=""><img src="'+_s+'" alt=""><img src="'+ms+'" alt=""><img src="'+gs+'" alt=""></p><p>Для того, чтобы начать красить детали, необходимо определить их расположение относительно друг друга и подготовить все крепёжные отверстия. После пробной примерной сборки, мы снова разобрали все на детали и приступили к активной покраске. Для хорошего укрытия фанеры требуется 2—3 слоя акриловой краски. Слои, в зависимости от толщины, сами по себе сохнут не менее часа, поэтому нам пришлось прибегнуть к хитрости. Для ускорения сушки мы применили строительный фен, в первом режиме выдувающий струю воздуха температурой 80°С. Нанести слой краски или лака, просушить феном, отшлифовать тонкой шкуркой поднявшийся деревянный ворс (краска же на водяной основе), протереть слегка влажной тряпкой от образовавшейся пыли — вот этапы непрерывного покрасочного процесса, позволившего уложиться в имеющиеся сроки. Конечно, любые подтёки краски даже с использованием фена сохнут намного дольше и мы то и дело пачкали и пачкались еще влажной краской. Соединив два листа самоклейки и вырезав из них ровный круг, мы сделали трафарет для фирменной голубой точки из логотипа девелоперской компании RDI. Обычная губка для мытья посуды позволила за один раз нанести краску плотным ровным слоем. Переходим к сборке!</p><p><img src="'+ls+'" alt=""><img src="'+ns+'" alt=""><img src="'+ds+'" alt=""><img src="'+js+'" alt=""><img src="'+bs+'" alt=""><img src="'+fs+'" alt=""><img src="'+Ds+'" alt=""><img src="'+As+'" alt=""><img src="'+Ss+'" alt=""></p><p>Царги присоединяются к внутренним сторонам ножек с помощью саморезов с плоскими шляпками. Затем кладём столешницу сверху на собранное подстолье и выравниваем её по угольнику. В столешницах мы заранее разметили и просверлили отверстия для вкручивания конфирматов. Сверлим отверстия для конфирмата в ножках и вкручиваем винты шестигранными ключами. Для стяжки стола нам хватит всего 8 таких соединений, по два на каждую ножку. В очередной раз мы убедились в том, что конфирмат не очень-то подходит к работе с фанерой, когда во время затяжки один из них сломался, а в другом начал стёсываться шестигранный шлиц. В следующий раз в подобных ситуациях использование толстых оцинкованных саморезов представляется более предпочтительным. Финишное покрытие столешниц лаком, тщательная уборка за собой и всё, мы уложились в срок!</p><p><img src="'+hs+'" alt=""><img src="'+us+'" alt=""><img src="'+Is+'" alt=""><img src="'+qs+'" alt=""></p><h2 id="выводы" tabindex="-1">Выводы <a class="header-anchor" href="#выводы" aria-label="Permalink to &quot;Выводы&quot;">​</a></h2><p>Столы получились жёсткими и устойчивыми, фетровые наклейки на ножках убрали даже самый малый люфт, а также позволили нивелировать вес столов — по наливному полу такие ножки скользят бесшумно и легко. За три воркшопа мы вместе с заинтересованными местными жителями вырезали и собрали три стола. Участники этих событий попробовали свои силы в новых для себя формах деятельности, обучившись базовым приёмам разметки, пиления, шлифовки, сверления и соединения деревянных деталей. Мы привнесли новый формат событий культурного центра, хотя, конечно, ценой заполонения всего его пространства мелкой древесной пылью. Там ещё остался целый квадрат фанеры толщиной 24 мм и мы, возможно, еще завершим серию четвертым столом, однако на этот раз мы лучше дождёмся весны и будем пилить и шлифовать деревяшки на улице.</p>',41),Ms=[Fs];function ks(ws,Gs,Ps,Es,Rs,Ts){return t(),a("div",null,Ms)}const ys=s(xs,[["render",ks]]);export{Vs as __pageData,ys as default};
