import{_ as a}from"./chunks/youtube-embed.ab0856e3.js";import{_ as i,o as r,c as l,e as s,a as o,d as e,b as c}from"./chunks/framework.dd5ab16e.js";const p="/assets/IMG_0729.d3645efe.jpg",n="/assets/IMG_0724.fa47f24d.jpg",_="/assets/IMG_0727.269a4556.jpg",g="/assets/IMG_0728.afa853a0.jpg",m="/assets/IMG_0731.6da7fe88.jpg",d="/assets/F7yVdb8GIus.aed3fe90.jpg",u="/assets/hQ8X97uC1Ug.18c0ac6b.jpg",h="/assets/kgwAeBUczzg.b623a387.jpg",f="/assets/LiHjxOzuMx8.9359cc86.jpg",j="/assets/PElBphwmWB0.9d2856e3.jpg",b="/assets/TbgPyfcnCzI.5c129cd3.jpg",A=JSON.parse('{"title":"Собираем простой складной табурет из деревянного бруска","description":"","frontmatter":{"title":"Собираем простой складной табурет из деревянного бруска","date":"2015-06-05","cover":"pre1.jpg"},"headers":[],"relativePath":"practice/project/stool/index.md","filePath":"practice/project/stool/index.md"}'),x={name:"practice/project/stool/index.md"},y=o("p",null,[e("Складная мебель — прекрасное средство расширить возможности любого помещения, например, мастерской, в особенности — передвижной. Мы уже разобрались с созданием "),o("a",{href:"/practice/project/stol-x/"},"складного стола-верстака"),e(" и усвоили конструкцию и соотношения его складного механизма. Теперь этот принцип можно применить и для иных задач.")],-1),I=o("p",null,"Конечно, мы не изобрели колесо, решив сделать складные табуретки по такому принципу. Одна украинская фирма продаёт такие табуретки в комплектах с такими же столами, а на ютьюбе есть даже несколько уроков по их самостоятельному изготовлению.",-1),k=c('<p>Впервые мы воплотили эту идею в <a href="/workshop/catalog/dar-truda/">производственном коворкинге &quot;Дар Труда&quot;</a>, в рамках подготовки к совместному мастер-классу на <a href="/practice/ooley42/om-at-delay-summit-2015/">Делай Саммите 2015</a>. В качестве исходного сырья администратор Роман предоставил нам неплохие метровые обрезки соснового мебельного щита толщиной 18 мм и шириной от 25 до 75 мм. Нам это подошло идеально. Все детали крепежа удалось закупить разом в ближайшем Леруа Мерлен.</p><p><img src="'+p+'" alt="IMG_0729"></p><h3 id="материалы" tabindex="-1">Материалы <a class="header-anchor" href="#материалы" aria-label="Permalink to &quot;Материалы&quot;">​</a></h3><ul><li>брус 100х25х18 общей длиной 3000 мм</li><li>доска 100х75х18 общей длиной 2000 мм</li><li>болты М6  длиной 35 мм (чуть меньше двух толщин используемого бруска)</li><li>мебельные гайки (или просто гайки)</li><li>шайбы 6 мм внутренний и 16 мм внешний диаметр</li><li>саморезы</li></ul><p>Широкий ассортимент инструментов производственного коворкинга позволил нам выбрать наиболее подходящие для наших задач инструменты, однако стоит помнить о том, что задачи пиления и сверления дерева могут быть выполнены многими способами.</p><h3 id="инструменты" tabindex="-1">Инструменты <a class="header-anchor" href="#инструменты" aria-label="Permalink to &quot;Инструменты&quot;">​</a></h3><ul><li>рулетка, линейка и карандаш</li><li>торцовочная пила</li><li>планетарная и ленточная шлифмашинки</li><li>наждачная бумага</li><li>сверлильный станок</li><li>шуруповёрт</li><li>свёрла по дереву 2, 6 и 8 мм, а также перовое сверло 16 мм</li><li>гаечный ключ с головкой 12 мм</li><li>2 струбцины</li></ul><p>Отталкиваясь от размеров сырья, мы построили несложные соотношения размеров деталей. Мы разметили все линии реза и напилили на торцовочной пиле все необходимые заготовки.</p><ul><li>4 бруска 500х25х18 для ножек</li><li>4 бруска 250х25х18 для опор сиденья</li><li>6 досок 330х75х18 для сиденья и рёбер жесткости</li></ul><p>Лучше всего уже на этом этапе скруглить оба конца ножек и опоры с одной стороны и отшлифовать заготовки. Далее сверлим отверстия на концах ножек и с отступом <strong>от края в 200 мм</strong> сверлом <strong>6 мм</strong>. Со стороны, где будут размещаться мебельные гайки, немного рассверливаем отверстия сверлом побольше. Со стороны шляпки болта нужно сделать <strong>углубление ~5 мм</strong> с помощью перьевого сверла. Аналогично подготавливаем отверстия с одного края каждой опоры сиденья.</p><p>Собираем каркас, прокладывая места трения <strong>шайбами</strong>. Расположив основания строго параллельно друг другу, кладём сверху первую доску сиденья и надёжно прижимаем эту конструкцию к столу <strong>струбцинами</strong>. Теперь можно наметить тонким сверлом отверстия для саморезов и скрепить ими каждую из половинок сиденья. Табурет можно уже собрать и протестировать правильность расчётов и отпилов. Остаётся только правильно прикрепить к ножкам собранной табуретки <strong>рёбра жёсткости</strong> и конструкция собрана полностью!</p><p><img src="'+n+'" alt=""><img src="'+_+'" alt=""><img src="'+g+'" alt=""><img src="'+m+'" alt=""></p><p>В производственном коворкинге нам потребовалось на все эти операции не более 40 минут, учитывая наличие мощных инструментов и соответствующих компетенций. Теперь можно еще раз пройтись шлифовальной бумагой по углам и поверхностям табуретки и приступить к нанесению <strong>масловоска или лака.</strong></p><p>Мы не собрали не просто табуретку, но <strong>прототип</strong>, по которому и провели наш совместный мастер-класс на <a href="/practice/ooley42/om-at-delay-summit-2015/">Делай Саммите 2015</a>. Участники справились с задачей в условиях открытой передвижной мастерской в течение  1—2 часов, командно и по одиночке собрав <strong>3 полноценных складных табуретки.</strong></p><p><img src="'+d+'" alt=""><img src="'+u+'" alt=""><img src="'+h+'" alt=""><img src="'+f+'" alt=""><img src="'+j+'" alt=""><img src="'+b+'" alt=""></p>',15);function P(M,T,V,q,G,B){const t=a;return r(),l("div",null,[y,I,s(t,{link:"https://youtu.be/X15QlVUdK10"}),s(t,{link:"https://youtu.be/O9lb30ZtpAc"}),s(t,{link:"https://youtu.be/sq6b240PzvM"}),k])}const S=i(x,[["render",P]]);export{A as __pageData,S as default};
