import{_ as s}from"./chunks/theme.BRWDm4A_.js";import{_ as l,a as o,f as e,b as r,K as p,L as t,o as i}from"./chunks/framework.CKEhg_8n.js";const n="/assets/par.D990EpSN.jpg",h="/assets/start0.4OuI4fim.jpg",c="/assets/start1.BYWrBYOz.jpg",_="/assets/start2.COVMm-ko.jpg",d="/assets/start3.CHZ6wpDR.jpg",m="/assets/enter.DXjLs78j.jpg",u="/assets/ipad.CMbW9bQQ.jpg",k="/assets/sketch.c82Q637c.jpg",b="/assets/far.Dr_MAl6z.jpg",g="/assets/lobby.CssLHJ4L.jpg",f="/assets/place.BhkakrHH.jpg",w="/assets/room.Cg30GT5K.jpg",x="/assets/steam.CqSW7UvC.jpg",v="/assets/table.4YfoCKUT.jpg",q="/assets/tea.ChTCzYdH.jpg",y="/assets/tight.4crlSG3F.jpg",j="/assets/top.CNEl5hBk.jpg",T="/assets/room.o1LkTyTi.webp",P="/assets/steam.DBn1EWRL.jpg",C="/assets/window.BXE3xYEY.jpg",z="/assets/plan.CLuHOcCJ.webp",R=JSON.parse('{"title":"Workplace fashion","description":"История развития коворкинга для дизайнеров одежды","frontmatter":{"title":"Workplace fashion","description":"История развития коворкинга для дизайнеров одежды","date":"2022-02-28T00:00:00.000Z","cover":"workplace-things-big.webp","toc":true},"headers":[],"relativePath":"theory/repost/workplace/index.md","filePath":"theory/repost/workplace/index.md"}'),S={name:"theory/repost/workplace/index.md"},V=r("p",null,[p("Автор проекта: "),r("a",{href:"https://volklab.ru/fashion/",class:"external-link",target:"_blank",rel:"noreferrer"},"Евгений Волк")],-1),W=t('<h2 id="задача" tabindex="-1">Задача <a class="header-anchor" href="#задача" aria-label="Permalink to &quot;Задача&quot;">​</a></h2><p>Придумать идею для пустующего лофта, задизайнить пространство, сверстать сайт и пошуметь в прессе.</p><h2 id="идея" tabindex="-1">Идея <a class="header-anchor" href="#идея" aria-label="Permalink to &quot;Идея&quot;">​</a></h2><p>Создать новый вид коворкингов, где дизайнеры одежды смогут использовать наше оборудование, большие столы и встречать клиентов в красивых примерочных, а не дома.</p><h2 id="решение" tabindex="-1">Решение <a class="header-anchor" href="#решение" aria-label="Permalink to &quot;Решение&quot;">​</a></h2><p>Закупить оборудование, создать фотоконтент и пригласить дизайенров переехать из квартирных ателье к нам, чтобы работать с коллегами в рабочей обстановке дома моды.</p><p><img src="'+n+'" alt=""></p><h2 id="_2013-—-2015-старт-проекта" tabindex="-1">2013 — 2015 Старт проекта <a class="header-anchor" href="#_2013-—-2015-старт-проекта" aria-label="Permalink to &quot;2013 — 2015 Старт проекта&quot;">​</a></h2><h4 id="место-рождения-концепции-—-москва-бауманская-переведеновскии-переулок-18-строение-7" tabindex="-1">Место рождения концепции — Москва, Бауманская, Переведеновский переулок 18, строение 7 <a class="header-anchor" href="#место-рождения-концепции-—-москва-бауманская-переведеновскии-переулок-18-строение-7" aria-label="Permalink to &quot;Место рождения концепции — Москва, Бауманская, Переведеновский переулок 18, строение 7&quot;">​</a></h4>',9),B=t('<p><img src="'+h+'" alt=""></p><p><img src="'+c+'" alt=""></p><p><img src="'+_+'" alt=""></p><p><img src="'+d+'" alt=""></p><p><img src="'+m+'" alt=""></p><p><img src="'+u+'" alt=""></p><p><img src="'+k+'" alt=""></p><h2 id="_2016-—-2018-второи-выход" tabindex="-1">2016 — 2018 Второй выход <a class="header-anchor" href="#_2016-—-2018-второи-выход" aria-label="Permalink to &quot;2016 — 2018 Второй выход&quot;">​</a></h2><h4 id="москва-бауманская-переведеновскии-переулок-18-строение-2" tabindex="-1">Москва, Бауманская, Переведеновский переулок 18, строение 2 <a class="header-anchor" href="#москва-бауманская-переведеновскии-переулок-18-строение-2" aria-label="Permalink to &quot;Москва, Бауманская, Переведеновский переулок 18, строение 2&quot;">​</a></h4><p><img src="'+b+'" alt=""><img src="'+g+'" alt=""><img src="'+f+'" alt=""><img src="'+w+'" alt=""><img src="'+x+'" alt=""><img src="'+v+'" alt=""><img src="'+q+'" alt=""><img src="'+y+'" alt=""><img src="'+j+'" alt=""></p><h2 id="_2018-—-2022-воркплеис-в-наши-дни" tabindex="-1">2018 — 2022 Воркплейс в наши дни <a class="header-anchor" href="#_2018-—-2022-воркплеис-в-наши-дни" aria-label="Permalink to &quot;2018 — 2022 Воркплейс в наши дни&quot;">​</a></h2><h4 id="москва-новыи-вернисаж-в-измаиловском-кремле" tabindex="-1">Москва, Новый Вернисаж в Измайловском Кремле <a class="header-anchor" href="#москва-новыи-вернисаж-в-измаиловском-кремле" aria-label="Permalink to &quot;Москва, Новый Вернисаж в Измайловском Кремле&quot;">​</a></h4><p>Далее - цитаты из <a href="https://vc.ru/tribuna/170511-workplace-fashion-prostranstvo-feshn-klaster-gde-mozhno-nayti-srazu-27-dizaynerov-odezhdy-shvey-i-portnyh" class="external-link" target="_blank" rel="noreferrer">текста нового владельца пространства Кирилла Мельчакова</a></p><h4 id="workplace-fashion-—-пространство-фешн-кластер-где-можно-наити-сразу-27-дизаинеров-одежды-швеи-и-портных" tabindex="-1">Workplace Fashion — пространство, фешн-кластер, где можно найти сразу 27 дизайнеров одежды, швей и портных <a class="header-anchor" href="#workplace-fashion-—-пространство-фешн-кластер-где-можно-наити-сразу-27-дизаинеров-одежды-швеи-и-портных" aria-label="Permalink to &quot;Workplace Fashion — пространство, фешн-кластер, где можно найти сразу 27 дизайнеров одежды, швей и портных&quot;">​</a></h4><p>В ноябре 2018 года Евгений Волк передал мне свой бизнес — воркплейс «Дом моды» в Москве, которому исполняется 9 лет в ноябре. Раннее я работал в нем в качестве администратора и с первых месяцев работы просто влюбился в проект.</p><p>Когда вся ответственность за существование бизнеса встала на мои плечи, я был очень заинтересован, чтобы проект жил и приносил деньги в качестве пассивного заработка. Главная задача, которая у меня стояла — это осуществить 100% нагрузку. Процессы все настроены, внутри создана своего рода экосистема, которая работает без стороннего вмешательства. Могу не появляться в студии 2-4 недели, при этом администратора внутри нет. Есть большие планы и амбиции по развитию, только давай по порядку.</p><h3 id="концепция" tabindex="-1">Концепция <a class="header-anchor" href="#концепция" aria-label="Permalink to &quot;Концепция&quot;">​</a></h3><p>Мало отличается от стандартного понимания коворкинга кроме направления и отсутствия часовой аренды. У нас только ежемесячные абонементы для открытия своей мастерской индивидуального пошива/бренда, мини-цеха, лаборатории бренда с минимальными затратами денег и времени.</p><p>С переездом в 2017 году в новое помещение мы отошли от полного формата опен-спейса. Помещение позволило сделать отдельные мастерские (кабинеты), которые дизайнеры оборудуют под себя, и одну общую зона для раскроя и ВТО.</p><p>Такой формат позволяет дизайнерам долго сотрудничать с нами и расти внутри коворкинга, переезжая в кабинеты большего размера.</p><p><img src="'+T+'" alt=""></p><p><img src="'+P+'" alt=""></p><h3 id="как-у-нас-сеичас-дела" tabindex="-1">Как у нас сейчас дела <a class="header-anchor" href="#как-у-нас-сеичас-дела" aria-label="Permalink to &quot;Как у нас сейчас дела&quot;">​</a></h3><p>Пережили пандемию с минимальными потерями: в начале марта 2020 года заполняемость стала 100%, а к концу марте съехало только 3 дизайнера. Мы предоставляли скидки пропорциональные тем, которые арендатор давал мне.</p><p>Сложно заполнить коворкинг снова, поменялось мышление насчет организации своей мастерской (многие работают дома) и также низкая ставка коммерческой недвижимости переманивает потенциальных клиентов.</p><p>Сейчас в коворкинге 14 дизайнеров и 13 швей (сотрудников у дизайнеров). Кто-то занимается индивидуальным пошивом, кто-то запустил бренд, кто-то открыл мини-цех, у кого-то корсеты, нижнее белье, повседневная одежда, кто-то шьет костюмы для бальных танцев или гимнастов, а кто-то сотрудничает со звездами.</p><p>После переезда мы перестали предоставлять резидентам швейные машинки (раньше давали бытовые), они не окупались, а в новом формате все привозят свои, так как на своем оборудовании работать лучше. Однако в 2020 году решил протестировать за доп.стоимость в месяц предоставлять промышленные машинки, появился спрос: сейчас у меня 10 собственных машин и они все заняты. С декабря тестирую сдачу оверлока, правда с ним сложнее в обслуживании.</p><p><img src="'+C+'" alt=""></p><p><img src="'+z+'" alt=""></p><h3 id="конкуренция" tabindex="-1">Конкуренция <a class="header-anchor" href="#конкуренция" aria-label="Permalink to &quot;Конкуренция&quot;">​</a></h3><p>Швейных коворкингов в Москве я знаю около 5-ти, большая часть заточена под любителей шить для себя или желающих научиться шитью. Есть один коворкинг с промышленным оборудованием и почасовой оплатой.</p><p>Я не привык считать кого-то конкурентами, мы коллеги и я с радостью рекомендую другие коворкинги, если не могу закрыть потребность клиента.</p><p>Из-за пандемии главным конкурентом стала коммерческая недвижимость в центре, которая освободилась и к настоящему моменту подешевела.</p><blockquote><p>Диалог дизайнера и впервые пришедшего клиента: —Вау, это все ваше (про студию и работающих дизайнеров в общей зоне) —Да, это все мое! Проходите, пожалуйста, в примерочную</p></blockquote><h3 id="швеиное-коммьюнити" tabindex="-1">Швейное коммьюнити <a class="header-anchor" href="#швеиное-коммьюнити" aria-label="Permalink to &quot;Швейное коммьюнити&quot;">​</a></h3><p>Внутри коворкинга образовалось свое сообщество: Все друг другу помогают — приехал курьер, за тебя встретят или отдадут заказ, если ты не успеваешь; Случаются выгодные сотрудничества — бренд заказывает образцы у другого резидента; Работают совместно над одним заказом клиента; Подсказывают где купить ткани, какую машинку купить, как обработать лучше края и тд.</p><p>Со своей стороны мы организуем застолья на 23 февраля и 8 марта, День Рождения студии, Новый год, пикники — это дни, когда большинство резидентов могут за одним раскройным столом поговорить и познакомиться.</p><p>Организуем тематические кинопросмотры и бизнес-бранчи весной и осенью, куда приглашаем экспертов по темам и наших подписчиков. Финансы</p><p>Расходы от 220 тыс до 251 тыс:</p><ul><li>Аренда: 171 тыс</li><li>Коммунальные услуги: летом — 10 тыс, зимой: 31 тыс</li><li>Уборщица: 14 тыс</li><li>Интернет: 1, 5 тыс</li><li>Реклама: 10 — 20 тыс</li><li>Расходные материалы: 5 тыс</li><li>Бухгалтерия: 9 тыс</li></ul><p><strong>Доход: 373 600 руб</strong></p><h3 id="планы-развития" tabindex="-1">Планы развития <a class="header-anchor" href="#планы-развития" aria-label="Permalink to &quot;Планы развития&quot;">​</a></h3><p>Два года хожу с мыслью, что хочу открыть бОльшего размера фэшн-кластер и с бОльшим спектром услуг и возможностей: фотостудия, услуга по изготовлению лукбуков, коворкинг с емежесячными абонементами и часовой оплатой, пространство для мастер-классов и лекций, мини-цех для отшива маленьких партий, шоу-рум для работы с байерами.</p><p>Воркплейс станет не только пространством для работы и встреч с клиентами, но и экспертом в том, как упаковать свой бизнес и расти в нем, как выстраивать процессы, как и где продавать, как укрепиться в своей нише, как начать работу с оптом. Воркплейс - это о предпринимательстве, историях предпринимателей в швейной индустрии, об историях успеха и провалов.</p><p>Мы создадим комплекс, в котором можно будет использовать нужные дизайнеру инструменты на разных этапах развития бренда, мастерской или ателье.</p><p>Вечный вопрос — найти подходящее помещение и инвестора, который также любит фэшн-индустрию и готов ее развивать.</p><p>Пусть это будет посылом в космос и все найдется.</p><h2 id="пресса-о-проекте" tabindex="-1">Пресса о проекте <a class="header-anchor" href="#пресса-о-проекте" aria-label="Permalink to &quot;Пресса о проекте&quot;">​</a></h2><p>«<a href="https://www.the-village.ru/village/business/management/222571-kovorkingi-masterskie" class="external-link" target="_blank" rel="noreferrer">Заняться делом: Мастерские в Москве, где можно поработать</a>», The-village.ru, 02.10.15</p><p>«<a href="https://www.intermoda.ru/cit/proekt-workplace-dlya-dizaynerov-odezhdy-v-peterburge.html" class="external-link" target="_blank" rel="noreferrer">Проект Workplace для дизайнеров одежды в Петербурге</a>», intermoda.ru, 16.07.2015</p><p>«<a href="https://zvzda.ru/interviews/a03b0379aad6" class="external-link" target="_blank" rel="noreferrer">Настал декабрь, и у всех разом сократились заказы</a>», журнал «Звезда», 28.05.15</p><p>«<a href="https://youtu.be/nIaxI_Re6yc" class="external-link" target="_blank" rel="noreferrer">Телепередача «Хэштег: чем отличаются коворкинги от воркплейсов»</a>», Хэштег Хэштегов, 5.11.14</p><p>«<a href="https://www.sostav.ru/publication/artemij-lebedev-rabota-nad-oshibkami-12960.html" class="external-link" target="_blank" rel="noreferrer">Лебедев создал новый лого для Workplace</a> », Sostav.ru, 28.10.14</p><p>«<a href="https://issuu.com/muzalewsky/docs/t_man_01_saratov_issuu/13" class="external-link" target="_blank" rel="noreferrer">Евгений Волк и история создания сети воркплейсов</a>», terra-sar.ru, 04.10.14</p><p>«<a href="https://brother-friends.ru/?p=38400" class="external-link" target="_blank" rel="noreferrer">Воркплейс для начинающих модельеров и дизайнеров</a>», Brother friends, 21.07.14</p><p>«<a href="https://broidery.ru/?p=38950" class="external-link" target="_blank" rel="noreferrer">Воркплейсы — профессиональные центры для людей творческих профессий</a>», broidery.ru, 11.03.14</p><p>«<a href="https://www.youtube.com/watch?v=itOqoob_X3w" class="external-link" target="_blank" rel="noreferrer">Телеканал Мир о воркплейсах меняющие ход событий людей</a>», Телеканал МИР, 18.02.14</p><p>«<a href="https://youtu.be/GraZ3CX19wY" class="external-link" target="_blank" rel="noreferrer">Стартап: Евгений Волк</a>», Телеканал ТНТ-Новый регион, 12.02.14</p><p>«<a href="https://siliconrus.com/2014/02/skandal-s-logo-lebedeva-nayti-i-peredelat/" class="external-link" target="_blank" rel="noreferrer">Скандал с лого Лебедева: найти и переделать</a>», siliconrus.com, 05.02.14</p><p>«<a href="https://www.the-village.ru/village/hopesandfears/news/154875-workplace-logo" class="external-link" target="_blank" rel="noreferrer">Артемий Лебедев признался в плагиате</a>», The-Village, 31.01.14</p><p>«<a href="https://tjournal.ru/paper/lebedev-workplace" class="external-link" target="_blank" rel="noreferrer">Студия Лебедева уволила дизайнера за плагиат</a>», Tjournal.ru, 31.01.14</p><p>«<a href="https://www.sostav.ru/publication/studiya-lebedeva-sozdala-logo-dlya-work-place-8081.html" class="external-link" target="_blank" rel="noreferrer">Студия Лебедева создала лого для workplace</a>», Sostav.ru, 30.01.14</p><p>«<a href="https://www.artlebedev.ru/workplace/logo/" class="external-link" target="_blank" rel="noreferrer">Анонс логотипа WORKPLACE от Студии Артемия Лебедева</a>», artlebedev.ru, 2014</p><p>«<a href="https://www.m24.ru/audios/1222" class="external-link" target="_blank" rel="noreferrer">Евгений Волк об открытии в Москве первого воркплейса</a>», МоскваFM, 23.01.14</p><p>«<a href="https://www.the-village.ru/village/service-shopping/service/137849-vork" class="external-link" target="_blank" rel="noreferrer">В Москве открылся воркплейс для дизайнеров</a>», The-Village, 22.01.14</p><p>«<a href="https://www.the-village.ru/village/hopesandfears/management/153747-wolf-like-a-phoenix" class="external-link" target="_blank" rel="noreferrer">Евгений Волк: Как превратить портных и парикмахеров в предпринимателей</a>», The Village, 09.12.13</p>',66);function E(N,A,D,L,O,Y){const a=s;return i(),o("div",null,[V,e(a,{class:"my-6",link:"https://youtu.be/itOqoob_X3w"}),W,e(a,{class:"my-6",link:"https://youtu.be/GraZ3CX19wY"}),B])}const X=l(S,[["render",E]]);export{R as __pageData,X as default};
