import{_ as e}from"./chunks/youtube-embed.pGRHW7C2.js";import{_ as s,o,c as p,b as r,e as a}from"./chunks/framework.nzJD8CNM.js";const _="/assets/Bert-1989.Tu_qtB7m.png",c="/assets/AS220-Empire-St-Elevation-Pre-1st-Renovation-LG.dLtZ8yf-.jpg",n="/assets/mercantile-block-photocredit-heidi-gumula-dbvw-architects.6ipgp5xN.jpg",i="/assets/ri_pvd_empire_as220_1_6.TDWQgPhJ.jpg",k=JSON.parse('{"title":"Общественный творческий кластер AS220, Род Айленд","description":"","frontmatter":{"title":"Общественный творческий кластер AS220, Род Айленд","date":"2016-06-07","cover":"20140401180643-IMG_1618.jpeg","youtube":"https://youtu.be/Gs-MBHwzy_4"},"headers":[],"relativePath":"theory/research/as220/index.md","filePath":"theory/research/as220/index.md"}'),m={name:"theory/research/as220/index.md"},l=a('<blockquote><p>AS220 представляет справедливый мир, где все люди могут реализовать свой полный творческий потенциал.</p></blockquote><p><img src="'+_+'" alt=""></p><p>AS220 – некоммерческий общественный художественный центр, расположенный в районе Даунтаун в городе Провиденс, штат Род Айленд, США. Он был образован в 1985 году группой художников, обеспокоенных состоянием искусства и творческого сообщества в городе: доступ к необходимым пространствам и инструментам оказался ограничен для всех, кто не поступил в коммерческую школу дизайна или не подошёл для участия в местных государственных инициативах. В ответ на сложившуюся тревожную ситуацию, Умберто &quot;Берт&quot; Кренка вместе с Сьюзан Клаузен и Скоттом Сиболтом написали манифест, призывающий всех местных творческих людей объединиться и создать свой собственный ни от кого не зависимый художественный центр.</p><p>Начавшись с небольшого творческого пространства (буквы AS расшифровываются как Artist&#39;s Space) в помещении на втором этаже дома 220 по Вейбоссет стрит, инициатива быстро выросла до семи студий, галереи, сцены для выступлений и потребовала дальнейшего расширения. В 1992 году растущее сообщество смогло приобрести своё собственное здание площадью 2000 кв. м. на Эмпайр стрит неподалёку, которое было превращено в центр искусств, совмещающий резиденции художников, галерею, пространство для выступлений и представительства нескольких танцевальных и театральных компаний. К Умберто Кренка, взявшему на себя обязанности художественного руководителя пространства, присоединились не только многие деятели искусства, но и умелые администраторы и менеджеры, сумевшие применить свои навыки для развития некоммерческого независимого проекта. Их главной задачей стало защитить свободу творчества и искусства от диктата государственных программ и сложившихся рыночных моделей.</p><p><img src="'+c+'" alt=""></p><p>В период с 2003 по 2006 год была проведена кампания по сбору средств, которая принесла целых 2 миллиона долларов на обновление комплекса зданий на Эмпайр стрит, включая открытие бара и ресторана. В 2006 году AS220 купили ещё одно здание — бывший отель площадью более 2300 кв. м., который уже к маю 2007 года превратился в творческий центр с 14 студиями для художников-резидентов, 4 арендными студиями, офисом администрации AS220 и просторной галереей. Приобретённое в 2008 году еще одно большое здание на Вашингтон стрит в июне 2011 года открыло свои двери под названием AS220 Industries, где под одной крышей объединились общественная типография, фаблаб и студии для цифрового искусства, а также множество жилых и рабочих студий художников, офисное пространство для местных некоммерческих инициатив и несколько магазинов местных предпринимателей на первом этаже.</p><p>Таким образом, являясь в первую очередь организацией, занимающейся искусством, AS220 оказала значительное влияние на город и в качестве девелопера, занявшего и преобразившего множество ветшавших зданий в деловой части города. Имеющиеся пространства используются для нужд сообщества, сдаются местным художникам, творческим коллективам и малому бизнесу, часто по ценам ниже рыночных, или по принципу &quot;доступного жилья&quot; для большинства. Общая стоимость недвижимости, принадлежащей проекту, оценивается по меньшей мере в 25 миллионов долларов. На первый взгляд проект может показаться сугубо инвестиционным, но если бы это было так, то за более чем 30 лет своего существования он давно бы скатился к коммерции и профанации искусства, а этого не произошло. Ответственность перед сообществом и забота о культурном развитии города помогли руководству проекта найти золотую середину между деньгами и творчеством. Использование доходов от управления недвижимостью для развития пространства и снижение стоимости его услуг открывает двери в мир творчества для тех, кому это иначе было бы просто недоступно.</p><p><img src="'+n+'" alt=""></p><p>Вокруг этого значительного городского начинания, направленного в первую очередь на популяризацию искусства и защиту его от корпоративного и государственного влияния, сплотилось крупное сообщество творцов, умельцев и ценителей искусства. На главной сцене каждую неделю проводятся десятки самых разных музыкальных, танцевальных и театральных представлений, в шести галереях постоянно идут выставки и происходят самые различные перформансы. С самого начала центр принял правило &quot;никаких каверов&quot; и допускает исполнение музыки и других произведений, обязательно находящихся в открытом доступе и под одной из открытых лицензий вроде Creative commons. Такой подход помог развиться целым новым направлениям музыкальной сцены Род Айленда и подготовил почву для появления множества независимых лейблов и студий звукозаписи.</p><p><img src="'+i+'" alt=""></p><p>Отдельное внимание уделяется здесь молодёжи, для которой при поддержке различных правительственных программ регулярно проводятся бесплатные классы по танцам, музыке, визуальным искусствам, фотографии и другим видам творчества. Большая общественная типография с массивными прессами и станками для шелкографии в комплексе с лабораторией цифрового моделирования и студиями медиа-искусств значительно расширяют творческий потенциал сообщества художников, умельцев и начинающих предпринимателей через проведение обучающих классов и курсов, аренду рабочих мест и события со свободным доступом.</p><p>Трудно охватить целиком это масштабное культурное начинание, преобразившее увядающий деловой центр города и открывающее широкие горизонты творческой самореализации для самых разных людей из самых разных слоёв общества. Сложно сказать, что оказалось важнее для столь успешного роста и развития некоммерческого общественного проекта: усилия смелых инициаторов, наличие доступных площадей или готовность людей сплотиться в живые самоорганизующиеся сообщества. Однако можно смело утверждать, что даже без одного из этих ингредиентов, такой крупный центр искусств был бы невозможен.</p><p>Официальный сайт: <a href="http://www.as220.org/" class="external-link" target="_blank" rel="noreferrer">http://www.as220.org/</a></p>',13);function u(d,h,g,S,A,f){const t=e;return o(),p("div",null,[l,r(t,{link:"https://youtu.be/4qBzWcTkJfI"})])}const x=s(m,[["render",u]]);export{k as __pageData,x as default};
