import{_ as o,a,b as t,o as r}from"./chunks/framework.BWf-PbcG.js";const u=JSON.parse('{"title":"Проектный подход в творчестве","description":"Цикл самомотивированной деятельности","frontmatter":{"title":"Проектный подход в творчестве","description":"Цикл самомотивированной деятельности","date":"2017-10-25","cover":"Project-wheel.png","youtube":"https://www.youtube.com/watch?v=5aSf6ik05aY"},"headers":[],"relativePath":"theory/system/projectapp/index.md","filePath":"theory/system/projectapp/index.md"}'),s={name:"theory/system/projectapp/index.md"};function p(n,e,c,i,d,l){return r(),a("div",null,e[0]||(e[0]=[t("p",null,"Более 6 лет мы исследовали самые различные грани творчества. Изучали как это делают другие: читали книги психологов и педагогов, перенимали опыт различных систем организации труда. Творили сами: рисовали картины, шили одежду, мастерили мебель. Помогали творить другим: проводили мастер-классы и воркшопы, устраивали лесной творческий лагерь и помогали наполнять творчеством программы фестивалей. В этом процессе мы выработали, опробовали и усовершенствовали новый максимально простой способ разбить любое творческое начинание на серию небольших понятных этапов.",-1),t("p",null,"Этот подход помогает легче начинать, увереннее продолжать и решительнее завершать любые творческие проекты. От вышивки до ювелирных изделий, от мебели до целых зданий — проектный подход помогает не застрять на пути, не потерять состояние потока и не бояться тишины, возникающей когда все начатое завершено, а новое еще не начиналось.",-1)]))}const f=o(s,[["render",p]]);export{u as __pageData,f as default};
