import{_ as o,o as t,c as n,a as e}from"./chunks/framework.mYMQfzZG.js";const g=JSON.parse('{"title":"Колокол","description":"","frontmatter":{"title":"Колокол","date":"2019-03-06","coverImage":"logo.png"},"headers":[],"relativePath":"workshop/archive/kolokol-voronezh/index.md","filePath":"workshop/archive/kolokol-voronezh/index.md"}'),s={name:"workshop/archive/kolokol-voronezh/index.md"},r=e("p",null,"Главным смыслом открытия школы гончарного искусства «Колокол» стало создание места, где каждый человек может прикоснуться к древнему гончарному искусству в непринужденной домашней атмосфере. Мы хотим, чтобы наши ученики научились создавать красивую керамику для себя и своих близких, следуя вековым традициям и современным тенденциям дизайна.",-1),a=e("p",null,"При создании интерьера мастерской мы старались руководствоваться принципом того, что помещение должно быть не только функциональным, а еще и вдохновлять наших учеников на творчество.",-1),l=e("p",null,"Мы долго и кропотливо изучали технологию создания керамики, шаг за шагом закупали необходимые инструменты и материалы для достижения высокого качества ученических работ.",-1),c=e("p",null,"В итоге мы сами являемся производителями гончарных кругов и муфельных печей!",-1),i=e("p",null,"Одним из главных показателей профессионального уровня нашей школы являются работы учеников. На курсах и мастер-классах мы обучаем большому количество техник лепки, глазурования изделий и их обжига. А еще все изделия покрываются пищевыми глазурями и полностью пригодны для использования в быту.",-1),_=e("p",null,[e("a",{href:"https://youtu.be/R-3kzOGoJJE",class:"external-link",target:"_blank",rel:"noreferrer"},"https://youtu.be/R-3kzOGoJJE")],-1),d=[r,a,l,c,i,_];function h(p,k,u,f,m,v){return t(),n("div",null,d)}const z=o(s,[["render",h]]);export{g as __pageData,z as default};
