import{_ as o,a as t,o as n,b as e}from"./chunks/framework.Cao6T-Ti.js";const v=JSON.parse('{"title":"Промзона","description":"Столярная школа","frontmatter":{"title":"Промзона","description":"Столярная школа","date":"2017-05-18","icon":"zxfPR5b_WYE-e1495106277215.jpg","cover":"zYctV7BzfEA.jpg","address":"ул. Фабричная 17А","city":"г. Новосибирск","tel":"+7 (962) 838-58-48","vk":"promzona_nsk","email":"promzonansk@gmail.ru","status":"closed"},"headers":[],"relativePath":"workshop/archive/promzona/index.md","filePath":"workshop/archive/promzona/index.md"}'),s={name:"workshop/archive/promzona/index.md"},a=e("p",null,"➡ Столярные мастер-классы. ➡ Курсы столярного дела. ➡ Индивидуальное обучение. ➡ Общественная столярная площадка.",-1),r=e("hr",null,null,-1),c=e("p",null,"А вот в Новосибирске горожане увлеклись столярным делом. Местный энтузиаст на собственные деньги организовал мастерскую, в которую может прийти любой желающий научиться работать с деревом.",-1),i=e("p",null,"А также опытный мастер, у которого есть инструменты, но нет места для работы. Несмотря на то, что столярное дело считается тяжелым трудом, 70% учеников организатора центра — девушки. Специалисты говорят, вещи ручной работы из дерева могут стоить как неплохая иномарка, но для самого столяра — эксклюзив выходит дешевле чем в магазине.",-1),l=e("blockquote",null,[e("p",null,"Мы живем в эру потребительской экономики. Купил выкинул- снова купил. Я придерживаюсь абсолютно другого мнения, мне нравятся вещи длительной службы, потому что это постоянство это стабильность, купить выкинуть для меня это что-то такое непонятное. Деревянные вещи сделанные самостоятельно служат очень долго и передаются по наследству, это дух это характер у изделия, ну это круто")],-1),d=e("p",null,"Александр Моисеенко — организатор рабочего пространства «Промзона»",-1),p=[a,r,c,i,l,d];function _(h,m,u,f,k,x){return n(),t("div",null,p)}const g=o(s,[["render",_]]);export{v as __pageData,g as default};
