import{_ as e}from"./chunks/vimeo-embed.6b341309.js";import{_ as t,o as s,c as o,e as r,b as c}from"./chunks/framework.dd5ab16e.js";const p="/assets/16.21fd568e.jpg",i="/assets/1410365623381-X5CDV169REVNELNTYY1X.5e8ef346.jpg",l="/assets/23.2403331a.jpg",x=JSON.parse('{"title":"Академия стекла","description":"Творческое пространство","frontmatter":{"title":"Академия стекла","description":"Творческое пространство","date":"2015-03-31","cover":"Czv3YST8Zoc.jpg","icon":"logo.png","youtube":"https://youtu.be/PkTffwL5trQ","tel":"+7 (926) 065-64-57","instagram":"Glass_academy_russia","email":"glassacademyrussia@gmail.com","url":"https://glassacademyrussia.com","city":"г. Москва","address":"ул. Балтийская 13","place":"м. Сокол","youtube-channel":"UCA0BOL4SPPHjbsUIDBmuI8A","vk":"glassacademy","facebook":"GlassAcademyMoscow","hours":"с 10.00 до 22.00 по предварительной записи","coord":[55.810009514588685,37.51328325379455],"status":"open"},"headers":[],"relativePath":"workshop/catalog/akademiya-stekla/index.md","filePath":"workshop/catalog/akademiya-stekla/index.md"}'),n={name:"workshop/catalog/akademiya-stekla/index.md"},m=c('<p>&quot;Академия стекла&quot; - творческое пространство, в котором мы открываем для Вас волшебный мир работы со стеклом (лэмпворк) и самыми новыми ювелирными техниками (Metalclay, живой металл, гальванопластика). Наши мастера находят индивидуальный подход к каждому ученику и гостю, помогают найти свой уникальный вход в мир безграничного творчества, авторских коллекций, показов, брендов.</p><p>В Академии Вы пройдете обучение и узнаете весь путь от формирования простых бусин в огне горелки - до создания уникальных авторских украшений и арт объектов. Мы поможем вам реализовать самые смелые и сложные дизайнерские задумки, любые проекты связанные с муранским, чешским и другими видами стекла и ювелирными техниками.</p><p>В нашей уютной мастерской вы сможете арендовать рабочее место для работы со стеклом или металлом и заниматься любимым делом в удобное для вас время.</p><p>Академия Стекла открыта ежедневно с 10 утра до 10 вечера. На студии собрана богатая коллекция литературы, инструментов от российских и муранских мастеров и гамма самого разного стекла!</p><p>А главное - в мастерской работают удивительные люди и хранители знаний, готовые поддержать, посоветовать и помочь вам найти &quot;ТО САМОЕ&quot;, чем бы оно ни было.</p><h3 id="аренда-рабочих-мест" tabindex="-1">АРЕНДА РАБОЧИХ МЕСТ <a class="header-anchor" href="#аренда-рабочих-мест" aria-label="Permalink to &quot;АРЕНДА РАБОЧИХ МЕСТ&quot;">​</a></h3><p>Часы работы с 10.00 до 22.00 по предварительной записи. Абонемент на 10 часов и более: 250 р. час. Абонемент на 100 часов и более: 220 р. час. Разовое посещение 300 руб</p><p>Для мечтающих, начинающих и посвященных в Lampwork в московской Академии Стекла созданы самые роскошные условия! Великолепное оборудование, отличная вентиляция и огромный выбор инструментов и стекла!</p><p><img src="'+p+'" alt=""></p><h3 id="уроки-ведущих-мастеров" tabindex="-1">УРОКИ ВЕДУЩИХ МАСТЕРОВ <a class="header-anchor" href="#уроки-ведущих-мастеров" aria-label="Permalink to &quot;УРОКИ ВЕДУЩИХ МАСТЕРОВ&quot;">​</a></h3><p>В академии регулярно проводятся уроки ведущих российских из зарубежных мастеров по Lampwork и смежным техникам. <a href="http://www.glassacademyrussia.com/classes/" class="external-link" target="_blank" rel="noreferrer">Расписание ближайших занятий.</a></p><p><img src="'+i+'" alt=""></p><h3 id="широкии-выбор-стекла" tabindex="-1">ШИРОКИЙ ВЫБОР СТЕКЛА <a class="header-anchor" href="#широкии-выбор-стекла" aria-label="Permalink to &quot;ШИРОКИЙ ВЫБОР СТЕКЛА&quot;">​</a></h3><p>В распоряжении гостей студии огромный выбор стекла от разных производителей, богатейшая гамма оттенков и эффектов.</p><p><img src="'+l+'" alt=""></p>',15);function d(_,u,h,k,g,f){const a=e;return s(),o("div",null,[m,r(a,{link:"https://vimeo.com/97861528"})])}const q=t(n,[["render",d]]);export{x as __pageData,q as default};
