import{_ as i,a,J as e,o as t}from"./chunks/framework.BWf-PbcG.js";const r="/assets/cer.pKtr0YlI.jpg",o="/assets/plakat.BZXF2VHJ.jpg",k=JSON.parse('{"title":"The Clay Studio","description":"Студия Академии Керамики","frontmatter":{"title":"The Clay Studio","description":"Студия Академии Керамики","date":"2018-09-19","icon":"logo.jpg","cover":"table.jpg","city":"г. Санкт-Петербург","address":"ул. Заставская д. 11 кор. 2, лит. Б","place":"м. Московские ворота","url":"http://dhc-tvorec.ru","email":"info@akademiakeramiki.ru","vk":"dhc_tvorec","tel":"+7 (800) 600-26-55","hours":"Пн-Пт  11:00 — 20:30 Сб-Вс  11:00 — 19:30","coord":[59.88930218786389,30.33102937000775],"status":"open"},"headers":[],"relativePath":"workshop/catalog/clay-studio/index.md","filePath":"workshop/catalog/clay-studio/index.md"}'),p={name:"workshop/catalog/clay-studio/index.md"};function s(n,l,c,d,h,u){return t(),a("div",null,l[0]||(l[0]=[e('<p>Студия гончарного мастерства «Творец» открылась в Петербурге в декабре 2009. На данный момент в городе это единственное место, где любой желающий может пройти курс обучения гончарному искусству в полном объёме. Занятия могут посещать дети от четырех лет и взрослые всех возрастов.</p><p><img src="'+r+'" alt=""></p><h4 id="может-быть-есть-где-то-еще" tabindex="-1">Может быть есть где-то ещё? <a class="header-anchor" href="#может-быть-есть-где-то-еще" aria-label="Permalink to &quot;Может быть есть где-то ещё?&quot;">​</a></h4><p>Есть. Керамику изучают в художественных школах, но в них не принимают всех желающих в любом возрасте. Также есть компании, предлагающие сделать поделку на гончарном круге. Как правило, они даже не обжигают изделие и тем более не покрывают глазурью – то есть чашечки и вазочки абсолютно не пригодны к использованию.</p><p>Есть творческие студии, предлагающие обжиг с покрытием глазурями или другими красками для керамики. Однако, мало где Вас смогут обучить всем тонкостям работы с керамикой от подготовки глиняной массы для лепки до загрузки готовых изделий в печь.</p><p>«Творец» – это студия, специализирующаяся исключительно на гончарном деле, поэтому здесь можно освоить большинство техник:</p><ul><li>работа на гончарном круге</li><li>ручная лепка</li><li>лепка из пластов</li><li>декорирование керамики глиной</li><li>роспись изделий красками</li><li>роспись разноцветными ангобами и глазурями</li><li>обжиг изделий (обычный и раку).</li></ul><h4 id="что-конкретно-предлагаем" tabindex="-1">Что конкретно предлагаем? <a class="header-anchor" href="#что-конкретно-предлагаем" aria-label="Permalink to &quot;Что конкретно предлагаем?&quot;">​</a></h4><p>Предлагаем прикоснуться к древнему ремеслу, которое зародилось на заре человечества и своими руками создать шедевр. В «Творце» каждый может выбрать подходящий вид деятельности:</p><ul><li>разовые занятия по лепке или работе на гончарном круге</li><li>базовый курс обучения индивидуально или в группе, разработанный для разных возрастов</li><li>дополнительные углублённые занятия по выбранным темам (например, обжиг, подготовка глины)</li><li>специализированные курсы и мастер-классы (обучение педагогов  керамике, лепка бижутерии)</li></ul><h4 id="к-чему-стремимся" tabindex="-1">К чему стремимся? <a class="header-anchor" href="#к-чему-стремимся" aria-label="Permalink to &quot;К чему стремимся?&quot;">​</a></h4><p>Стремимся объединить традиции древнего искусства с современными тенденциями. В Европе уже давно популярная система workshop`ов  - человек может прийти и  купить всё необходимое для работы, арендовать рабочее место и творить в своё удовольствие. Мы надеемся, что с нашим участием подобные места станут популярны и в Санкт-Петербурге.</p><p>В начале марта 2010 года мы открыли специализированный магазин  <a href="http://ceramistam.ru/" title="Товары для керамистов и гончаров" class="external-link" target="_blank" rel="noreferrer"><strong>Керамистам.ру</strong></a>.  Теперь у нас есть возможность не только обучать гончарному искусству, но и полноценно обеспечивать всем необходимым для самостоятельного творчества.</p><p><img src="'+o+'" alt=""></p><p><a href="http://www.akademiakeramiki.ru/" class="external-link" target="_blank" rel="noreferrer">Академия Керамики</a></p><p>Наверное каждый керамист мечтает о своей мастерской, в которой он мог бы отдыхать душой, свободно творить и экспериментировать. Ценность мастерской заключается не только в ее уединенности, но и в ее оснащении. К сожалению, не у всех есть возможности вкладывать силы, время и средства в создание своего идеального творческого места. И тут на помощь приходят коворкинги! Тут есть все: любое необходимое оборудование, компания единомышленников, совет и помощь мастера, теплая и спокойная атмосфера! И да, это все о нашем коворкинге на базе Академии керамики.</p><p>Коворкинга Академии керамики даст Вам:</p><ul><li>Доступ к материально-технической базе Академии.Дополнительно к обычному инструментарию, Вы получите доступ и к особому (сушильный шкаф, пульфон, экструдер, раскатчик);</li><li>Возможность обжига в том числе крупных партий. Всего в Академии 5 печей (200, 150, 60, 48 и 30 литров). Можно подобрать индивидуальную программу для обжига*;⠀</li><li>Помощь специалиста Академии керамики, дежурный по коворкингу, придёт на помощь в нужный момент, подскажет более лёгкий путь или поделится секретом, коих у него в запасе великое множество;</li><li>Отдельный рабочий стол;</li><li>Гончарный круг;</li><li>Место на стеллаже;</li><li>Бесплатная закрытая парковка;</li><li>Безлимитный WiFi;</li><li>Чай, печенье.</li></ul><p>Приятный бонус:</p><ul><li>Скидки на коворкинг для учеников и выпускников Академии.</li><li>Для резидентов коворкинга скидки в интернет-магазине Керамистам.ру</li></ul>',20)]))}const f=i(p,[["render",s]]);export{k as __pageData,f as default};
