import{_ as a,o as e,c as o,e as t}from"./chunks/framework.mYMQfzZG.js";const u=JSON.parse('{"title":"DIY академия","description":"Общественная столярная мастерская","frontmatter":{"title":"DIY академия","description":"Общественная столярная мастерская","date":"2014-11-19","icon":"diy-logo-color.png","cover":"cover.jpg","tel":"+7 (925) 483-56-90","url":"https://diy-academy.ru","email":"mail@diy-academy.ru","address":"Малая Семеновская, 5с1","place":"м. Семеновская","city":"г. Москва","coord":[55.784906234562854,37.70755917002208],"facebook":"diyacademybosch","instagram":"diyacademy","status":"open"},"headers":[],"relativePath":"workshop/catalog/diy-academy/index.md","filePath":"workshop/catalog/diy-academy/index.md"}'),r={name:"workshop/catalog/diy-academy/index.md"},i=t('<p>DIY (do it yourself) – это современное движение тех, кто любит работать руками. Мы - столярная школа с курсами и мастерская для ваших экспериментов/хобби. DIY Академия — комфортная мастерская с практикующими мастерами, качественным инструментом, материалами и проработанными программами для новичков и тех, кто практикует столярное дело время от времени. Помогаем сделать хобби профессиональным хендмейдом. Отличный старт, если вы хотите испытать себя, понять, насколько близок вам шум фрезера и шлифмашинки.</p><blockquote><p>Здесь каждый может взять в аренду верстак и воплотить свою мечту в дереве при поддержке дизайнеров и столяров. Мечты у всех, конечно, разные. Кто-то просто хочет сделать стеллаж, кому-то давно снится рабочий стол, за которым легко сосредотачиваться, кто-то мечтает построить во дворе рампу для скейтборда, а кто-то конструирует витрину фуд стартапа.</p><p>Мы с радостью поможем вам создать дизайн желанного объекта и вместе подумаем над конструкцией. А если у вас не хватает навыков, то можно записаться на «Курс столярного мастерства».</p></blockquote><h3 id="инструменты" tabindex="-1">Инструменты <a class="header-anchor" href="#инструменты" aria-label="Permalink to &quot;Инструменты&quot;">​</a></h3><p>Вся линейка инструментов Бош.</p><h3 id="материалы" tabindex="-1">Материалы <a class="header-anchor" href="#материалы" aria-label="Permalink to &quot;Материалы&quot;">​</a></h3><p>На площадке всегда можно купить по рыночной себестоимости стандартный набор материалов, элементов крепежа, красок и лаков.</p><h3 id="условия" tabindex="-1">Условия <a class="header-anchor" href="#условия" aria-label="Permalink to &quot;Условия&quot;">​</a></h3><ul><li><strong>БАЗОВЫЙ ТАРИФ – 1000 рублей 3 часа</strong> Это оптимальный промежуток, чтобы сделать небольшой стеллаж для книг или конструктор для ребенка. А за несколько блоков по 3 часа уже можно сделать серьезный объект.</li><li><strong>КСМ – 10 000 рублей за 8 занятий</strong> Курс столярного мастерства в DIY Академии — это пошаговый курс, в ходе которого вы познакомитесь со всеми этапами создания интерьерного объекта от эскиза до сборки и отделки.</li><li><strong>АБОНЕМЕНТ НА МЕСЯЦ – 15 000 рублей</strong> Серьезные проекты требуют серьезного погружения. За месяц вдумчивой работы можно сделать авторский шкаф, необычную двуспальную кровать, разработать систему хранения под особенности своего пространства.</li><li><strong>ТЕМАТИЧЕСКИЕ КУРСЫ</strong> В Академии постоянно проходят курсы для взрослых и детей, начинающих и продвинутых, по техникам столярного мастерства и теории дизайна. Мы знаем, что DIY применим везде.</li><li><strong>ПРИСМОТР ЗА ДЕТЬМИ – 300 рублей в час</strong> Если вы пришли вместе с ребенком, то у вас всегда есть выбор поработать вместе или оставить под присмотром в детском пространстве. Мы принимаем детей от 4 лет.</li></ul>',8),s=[i];function c(d,l,n,p,_,h){return e(),o("div",null,s)}const g=a(r,[["render",c]]);export{u as __pageData,g as default};
