import{_ as t,o as s,c as a,a as e,d as o}from"./chunks/framework.78e651f5.js";const S=JSON.parse('{"title":"FLAPPERS","description":"","frontmatter":{"title":"FLAPPERS","date":"2019-04-26","icon":"rUuibb1GTZE.jpg","cover":"flappers_photo_002.jpg","url":"https://flappers.ru","instagram":"flappersworkshop","vk":"vk.flappers","status":"open","tel":"+7 (999) 095-41-20","city":"г. Москва","address":"Новодмитровская Б. ул., 36","place":"Дизайн-завод Флакон","hours":"Пн-Вс — с 10 до 22","coord":[55.80592183152701,37.584330214198715]},"headers":[],"relativePath":"workshop/catalog/flappers/index.md","filePath":"workshop/catalog/flappers/index.md"}'),r={name:"workshop/catalog/flappers/index.md"},p=e("h5",{id:"flappers-лучшие-курсы-шитья-в-москве",tabindex:"-1"},[o("FLAPPERS - лучшие курсы шитья в Москве "),e("a",{class:"header-anchor",href:"#flappers-лучшие-курсы-шитья-в-москве","aria-label":'Permalink to "FLAPPERS - лучшие курсы шитья в Москве"'},"​")],-1),l=e("p",null,"Мы любим моделировать, конструировать, кроить и шить одежду. И еще больше любим это делать в хорошей компании! Наши курсы и мастер-классы для начинающих помогут даже при нулевом уровне подготовки научиться шить качественную одежду.",-1),n=e("p",null,"Мастерская FLAPPERS оснащена современным французским и итальянским оборудованием. Простые в использовании швейные машинки, оверлоки и разнообразные лекала станут вашими лучшими помощниками в шитье и моделировании одежды.",-1),c=e("p",null,"Мы предлагаем удобный график обучения. Мастерская работает ежедневно, занятия в группах проходят в утренние, дневные или вечерние часы. Возможно выбрать наиболее подходящее для себя время, а также записаться на индивидуальные занятия.",-1),d=e("p",null,"Научитесь разбираться в тканях, выбирать подходящий к индивидуальным особенностям фигуры фасон одежды, овладеете тонкостями пошива юбок, платьев, брюк, блузок и даже нижнего белья. По окончании любой из наших программ мы гарантируем готовое, созданное твоими руками изделие, а также настроение увлечённо творить, создавая новую одежду. У тебя останутся выкройки, методические рекомендации и ценные навыки, чтобы шить самостоятельно.",-1),i=e("p",null,"Преподаватели-наставники индивидуально подходят к каждому участнику, терпеливо и вдохновенно обучая всем секретам шитья. Их цель — раскрыть творческий потенциал и научить создавать уникальную одежду, которая понравится и тебе, и окружающим.",-1),_=e("p",null,"Мастерская шитья FLAPPERS находится в самом эпицентре культурной жизни столицы — Дизайн-Заводе Флакон. Креативная атмосфера стильного кластера в центре Москвы объединяет единомышленников и способствует вдохновенному творчеству. Приходите, здесь тебе будут рады!",-1),h=[p,l,n,c,d,i,_];function f(u,P,m,k,x,E){return s(),a("div",null,h)}const A=t(r,[["render",f]]);export{S as __pageData,A as default};
