import{_ as a,a as e,o as t,H as r}from"./chunks/framework.Cao6T-Ti.js";const o="/assets/256626064_117303780751794_5997958676714867303_n.CtHpTHNq.jpg",i="/assets/bz1zdmosdiw.CZmfgs_C.jpg",s="/assets/r-abauwtm0e.OtLL-WT4.jpg",b=JSON.parse('{"title":"Глазурь","description":"Гончарная мастерская","frontmatter":{"title":"Глазурь","description":"Гончарная мастерская","date":"2020-05-06","icon":"logo.png","cover":"cover.jpg","instagram":"glazur.ceramics","vk":"gm_glasur","url":"http://masterskaya-glazur.ru","address":"ул. Савушкина, д. 121, корп. 2","city":"г. Санкт-Петербург","place":"м. Беговая","tel":"+7 (812) 642-15-08","coord":[59.98712297737678,30.225268914305918],"status":"open"},"headers":[],"relativePath":"workshop/catalog/glazur/index.md","filePath":"workshop/catalog/glazur/index.md"}'),l={name:"workshop/catalog/glazur/index.md"},n=r('<p>Гончарная мастерская &quot;Глазурь&quot;, проводит мастер-классы по лепке и гончарному мастерству. У нас можно провести свидание, день рождения, корпоратив, а так же есть коворкинг для керамистов и гончарный курс для начинающих. Утильный и политой обжиг.</p><p><img src="'+o+'" alt=""></p><h5 id="роспись-керамики-new" tabindex="-1">Роспись керамики (new) <a class="header-anchor" href="#роспись-керамики-new" aria-label="Permalink to &quot;Роспись керамики (new)&quot;">​</a></h5><p>В нашей студии можно не только, лепить или крутить на гончарном круге, но и расписать готовое изделие. С помощью необходимых инструментов вы сможете покрасить и декорировать свое изделие.</p><h5 id="самостоятельное-творчество" tabindex="-1">Самостоятельное творчество <a class="header-anchor" href="#самостоятельное-творчество" aria-label="Permalink to &quot;Самостоятельное творчество&quot;">​</a></h5><p>Воплотите любую Вашу творческую идею в комфортных условиях оборудованной студии, где есть все необходимое оборудование и материалы для работы</p><h5 id="индивидуальное-занятие-с-мастером" tabindex="-1">Индивидуальное занятие с мастером <a class="header-anchor" href="#индивидуальное-занятие-с-мастером" aria-label="Permalink to &quot;Индивидуальное занятие с мастером&quot;">​</a></h5><p>Если вы новичок и только знакомитесь с гончарным искусством, можно выбрать занятия под наблюдением мастера, который поможет Вам с изготовлением желаемого изделия</p><h5 id="занятие-с-мастером-в-группе-свободная-лепка" tabindex="-1">Занятие с мастером в группе ( свободная лепка ) <a class="header-anchor" href="#занятие-с-мастером-в-группе-свободная-лепка" aria-label="Permalink to &quot;Занятие с мастером в группе ( свободная лепка )&quot;">​</a></h5><p>Интересный формат мастер-класса по работе с глиной, который позволит не только создать собственное изделие (чашки, тарелки, блюдца, блюда и пиалы), но и найти друзей по интересам.</p><h5 id="день-рождения-в-гончарнои-мастерскои" tabindex="-1">День рождения в гончарной мастерской <a class="header-anchor" href="#день-рождения-в-гончарнои-мастерскои" aria-label="Permalink to &quot;День рождения в гончарной мастерской&quot;">​</a></h5><p>Если вы не знаете где отметить памятную дату (день рождения, девичник, корпоратив, 14 февраля, 23 февраля 8 марта и т.д.), а может быть у вас намечается своё мероприятие, тогда мы приглашаем вас к нам в мастерскую.</p><h5 id="романтическое-свидание" tabindex="-1">Романтическое свидание <a class="header-anchor" href="#романтическое-свидание" aria-label="Permalink to &quot;Романтическое свидание&quot;">​</a></h5><p>Хочешь удивить свою половинку необычным подарком? Тогда приходите к нам в гончарную мастерскую на романтическое свидание. В нашей студии вы сможете создать памятную вещицу которая будет напоминать вам об этом моменте.</p><h5 id="в-стоимость-1-часа-включено" tabindex="-1">В стоимость 1 часа включено: <a class="header-anchor" href="#в-стоимость-1-часа-включено" aria-label="Permalink to &quot;В стоимость 1 часа включено:&quot;">​</a></h5><ul><li>500 грамм базовой глины/базовой глазури</li><li>Утильный или политой обжиг работ до 1 000 градусов</li><li>Комплект необходимых инструментов и оборудования</li><li>Чай, кофе, печенье</li></ul><p><img src="'+i+'" alt=""><img src="'+s+'" alt=""></p><h3 id="приходите-с-близкими-получите-море-эмоции-от-совместнои-работы-за-гончарным-кругом" tabindex="-1">Приходите с близкими, получите море эмоций от совместной работы за гончарным кругом <a class="header-anchor" href="#приходите-с-близкими-получите-море-эмоции-от-совместнои-работы-за-гончарным-кругом" aria-label="Permalink to &quot;Приходите с близкими, получите море эмоций от совместной работы за гончарным кругом&quot;">​</a></h3>',18),c=[n];function h(d,p,_,u,m,g){return t(),e("div",null,c)}const f=a(l,[["render",h]]);export{b as __pageData,f as default};
