import{_ as t}from"./chunks/theme.BBnZaxT7.js";import{_ as p,a as o,f as a,H as l,o as e}from"./chunks/framework.Cao6T-Ti.js";const k=JSON.parse('{"title":"Гуслица","description":"Творческая усадьба","frontmatter":{"title":"Гуслица","description":"Творческая усадьба","date":"2016-01-25","icon":"logo1.jpg","cover":"cover.jpg","email":"info@art-guslitsa.com","url":"https://art-guslitsa.ru","city":"Московская область","place":"Орехово-Зуевский район","address":"Ильинский Погост, ул. Митрохинская, д. 8","tel":"+7 (929) 599-32-98","hours":"Круглосуточно","youtube":"https://youtu.be/M10pyeBHO2A","facebook":"Guslitsa","vk":"guslitsa","youtube-channel":"guslitsa","coord":[55.481292815352525,38.923144498338566]},"headers":[],"relativePath":"workshop/catalog/guslitsa/index.md","filePath":"workshop/catalog/guslitsa/index.md"}'),s={name:"workshop/catalog/guslitsa/index.md"},r=l("<p>Творческая усадьба «Гуслица» - это арт-резиденция, международный культурно-образовательный центр.</p><p>Гуслица - это благодатная почва для всестороннего развития. Здесь создаются творческие, социальные, технические, экологические и образовательные проекты.</p><p>Художники, скульпторы, мозаисты создают произведения искусства и выставляют свои работы как в галереях, так и в просторных залах Гуслицы.</p><p>Певцы, музыканты и актеры устраивают творческие вечера, перформансы и выступают на мероприятиях, организуемых творческой усадьбой.</p><p>В мастерских производят керамику, мебель и соломенные блоки для строительства домов.</p><p>Творческая усадьба «Гуслица» - это уникальное живое пространство для проведения мероприятий любого формата в любое время года - от выставок, ретритов, концертов, театральных представлений и фестивалей до образовательных семинаров, конференций и бизнес-тренингов.</p><p>Здесь по-особенному празднуют дни рождения и свадьбы. Помимо девяти аутентичных залов, у нас есть гостевой дом на 80 мест, летний кемпинг для 300 гостей, охраняемая парковка, столовая со вкусной домашней едой, баня с купелью, чайная, кинозал, детская комната и Wi-Fi.</p><p>Территория Гуслицы — это пространство для разностороннего отдыха или удаленной работы вдали от городской суеты: хвойный лес с пешими и лыжными тропами, река и озеро, свежий воздух, старообрядческое село с церковью конца XIX века, а поблизости расположены исторические города.</p><p>Гуслица развивается в согласии с принципами пермакультуры и выстраивает свою деятельность, основываясь на сотрудничестве, экономической целесообразности и экологичности. Что позволяет, творчески проектируя окружающего человека пространство, проявлять заботу о Земле и людях.</p><p>Тот, кто однажды побывал в Гуслице, возвращается сюда снова и снова. Потому что это место для всех, кто хочет светить и созидать.</p>",10),n=l('<hr><p>«Гуслица» — творческое сообщество нового типа, которое объединяет представителей всех творческих профессий — архитекторов, музыкантов, художников, кураторов, режиссёров, актеров, продюсеров, журналистов и др.</p><p>Как проект, «Гуслица» — это сообщество, арт-институция и культурно-просветительский туристический центр.</p><p>Сообщество создается с целью создания среды общения для возрождения культуры края путем вовлечения местного населения в культурные международные процессы.</p><p>Творческая усадьба «Гуслица» является также Международной арт-резиденцией и реализует ряд программ в сфере искусства.</p><p>Проект «Гуслица» реализуется некоммерческим негосударственным фондом развития культуры и духовности «Преображение».</p><p>Усадьба находится в живописнейшем месте в окружении соснового леса, реки и озера. На территории Творческой усадьбы практикуются общепринятые международные законы экологии.</p><h3 id="в-распоряжении-гостеи-и-резидентов" tabindex="-1">В распоряжении гостей и резидентов: <a class="header-anchor" href="#в-распоряжении-гостеи-и-резидентов" aria-label="Permalink to &quot;В распоряжении гостей и резидентов:&quot;">​</a></h3><ul><li>арт-резиденция</li><li>выставочные залы</li><li>художественные мастерские</li><li>производственные мастерские</li><li>кинозал</li><li>конференц зал</li><li>гостевой дом</li><li>баня</li></ul><h3 id="здесь-функционируют" tabindex="-1">Здесь функционируют: <a class="header-anchor" href="#здесь-функционируют" aria-label="Permalink to &quot;Здесь функционируют:&quot;">​</a></h3><ul><li>выставки</li><li>фестивали</li><li>мастер-классы</li><li>конференции</li><li>исследовательские лаборатории</li><li>студия звукозаписи</li><li>интерактивный музей</li><li>библиотека</li></ul><p>Творческая усадьба «Гуслица» расположена в историческом  здании старинной промышленной усадьбы, построенной братьями Патрешовыми в конце ХIХ века — начале XX века. Усадьба общей площадью 10 000 кв.м, на территории более 4 га, которая огорожена и охраняется, расположена в 77 километрах от Москвы.</p><p>Деятельность усадьбы посвящена практическому изучению и поддержке современного искусства в различных его проявлениях (изобразительное искусства, танец, музыка, кино, экспериментальный театр и пр.). В просторных залах Творческой Усадьбы «Гуслица» открыта возможность реализоваться множеству идей:</p><ul><li>создание художественных, музыкальных и танцевальных студий, скульптурных, дизайнерских и архитектурных мастерских, театральных площадок и съёмочных павильонов;</li><li>организация и проведение выставок, фестивалей и т.д.;</li><li>поддержка талантливых людей – известных мастеров  и начинающих молодых дарований; открытие новых имен в современном искусстве;</li><li>образовательная и исследовательская деятельность, проведение мастер-классов, конференций, создание творческих лабораторий  и т.д.;</li><li>создание информационного банка;</li><li>создание библиотеки (искусствоведение классическое и современное искусство, психология и т.д.).</li></ul><p><strong>Философия проекта</strong></p><p>В бесконечных пространствах Вселенной возникла едва заметная точка. Идея. Сверкнули молнии, шевельнулись гигантские шестеренки судеб, и материализовались стены. Благодаря творческой энергии родился новый мир.</p><p>Пространство вдохновения!</p><p><strong>Многие творческие проекты нуждаются в герметичной среде для полного и глубокого погружения в процесс. Удаленность  от вибраций мегаполиса и бытовых необходимостей , близость к природе и дух старинного сооружения создают уникальную комбинацию для обретения вдохновения в реализации проектов .</strong></p><p>Система взаимодействия настраивается таким образом, чтобы убрать бытовые помехи из жизни. Размещение  c возможностью организации питания, сервис и техническая поддержка, хороший подъезд и удобный трансфер из Москвы – необходимые условия для творческих сессий и мастер-классов. В пространствах Усадьбы создается дружественная, интеллигентная и вдохновляющая атмосфера, способствующая рождению и развитию всевозможных форм сотрудничества творческих сил.</p><h3 id="подробнее-об-арт-резиденции" tabindex="-1">Подробнее об арт-резиденции <a class="header-anchor" href="#подробнее-об-арт-резиденции" aria-label="Permalink to &quot;Подробнее об арт-резиденции&quot;">​</a></h3><p>На момент 1 января 2013 г. Усадьба позволяет комфортно разместить 50 резидентов в Гостевом доме — демократичной гостинице, расположенной в корпусе усадьбы.</p><p>В усадьбе имеются мастерские от 20 до 40 кв. метров, высота потолка от 5 до 10 метров.</p><p><strong>Продолжительность пребывания резидентов</strong></p><p>Срок пребывания каждого арт-резидента до двух месяцев с последующей выставкой выполненных работ в Галерее Гуслицы. Прибыть для участия в программе резидент может в любое удобное для него время по согласованию с организаторами.</p><p><strong>Предоставление материалов, необходимых для участия в арт-резиденции</strong></p><p>Резиденты приглашаются после предоставления резюме, заявлении о намерениях, резюме, фотографии работ, по желанию — обзоры прессы, профессиональные рекомендации и любые другие соответствующие материалы в единый документ (MS Word или PDF), что дает нам представление о широте работа и карьеры.</p><p><strong>Варианты стоимости проживания в арт-резиденции:</strong></p><ul><li>Полная оплата за проживание</li><li>Частичная оплата за проживание</li><li>Бесплатное проживание при наличии международных грантов</li></ul><p>В стоимость проживания входит: проживание в номере (от 2-х до 15-ти человек), бельё, удобства, электричество, отопление, холодная и горячая вода, уборка. Все прочие расходы не включены.</p><p>Резиденты могут проживать с семьями. Проживание дополнительного человека оплачивается отдельно.</p><p><strong>Дополнительные услуги:</strong></p><ul><li>Бесплатный беспроводной доступ в Интернет.</li><li>Есть русская баня с купелью за небольшую плату.</li></ul><p><a href="https://www.youtube.com/watch?v=suUPoFzW3jc" class="external-link" target="_blank" rel="noreferrer">https://www.youtube.com/watch?v=suUPoFzW3jc</a></p>',33);function u(c,_,h,d,g,m){const i=t;return e(),o("div",null,[r,a(i,{link:"https://youtu.be/FRQUVs7a0-s"}),n])}const P=p(s,[["render",u]]);export{k as __pageData,P as default};
