import{_ as o,o as t,c as s,a as e}from"./chunks/framework.6c3c27db.js";const x=JSON.parse('{"title":"Легко-Легко","description":"Пространство","frontmatter":{"title":"Легко-Легко","description":"Пространство","date":"2015-03-30","icon":"legko-map.png","cover":"logo_ver_20_07_16.png","tel":"+7 (812) 779-14-91","email":"store@legko-legko.ru","address":"Большая Пушкарская, 10","place":"Петроградский район","city":"г. Санкт-Петербург","hours":"каждый день с 11:00 до 21:00","coord":[59.95521924392703,30.29718782732458],"status":"open"},"headers":[],"relativePath":"workshop/catalog/legko-legko/index.md","filePath":"workshop/catalog/legko-legko/index.md"}'),a={name:"workshop/catalog/legko-legko/index.md"},l=e("p",null,"«Помогать легко» — проект фонда AdVita («Ради жизни»). Покупая сувениры или приходя в пространство Легко-Легко на мастер-класс или кинопросмотр, Вы помогаете собрать средства на лечение больных раком. Подарите себе радость подарить жизнь!",-1),n=e("p",null,"ООО «Легко-Легко» производит и продает товары с социальной составляющей. В одном месте вы можете выбрать и социальную идею, которую хотите поддержать, и товар. Деньги от покупок идут на развитие инклюзивных мастерских, в которых производятся вещи, или поступают в благотворительные организации. Мы тщательно отбираем социальные бренды и товары социальных предприятий, которые у нас представлены, и по качеству и по эффективности социального воздействия. «Легко-Легко» – это широкий ассортимент уникальных товаров и безупречная ценностная составляющая.",-1),c=e("p",null,"«Легко-Легко» не только продает сувениры. Первое в мире благотворительное пространство «Легко-Легко» создано в 2009 году по инициативе группы волонтеров благотворительного фонда AdVita («Ради жизни»). И вот уже почти десять лет крупнейший на Северо-Западе фонд, помогающий людям с онкологическими заболеваниями, остается нашим основным благополучателем. В год мы проводим более сотни больших и малых мероприятий в пользу БФ AdVita и за 11 лет программ «Помогать легко» передали в фонд более 18 млн пожертвований и отчислений с продаж сувениров.",-1),r=e("p",null,"В 2018 году в состав учредителей «Легко-Легко» вошла Ассоциация социальных предпринимателей. В этом союзе с другими социальными проектами мы создаем новую модель бизнеса, новые формы кооперации, продвижения и поддержки социальных предпринимателей. делаем товары социальных брендов и благотворительных фондов конкурентоспособными и доступными на рынке.",-1),d=e("p",null,"Основное направление нашей работы – привлечение корпоративных покупателей к покупкам с социальной и благотворительной составляющей. Мы рады зафиксировать, что количество корпоративных социальных инициатив с нашим участием растет год от года. Присоединяйтесь к нам!",-1),i=[l,n,c,r,d];function _(p,g,h,k,u,m){return t(),s("div",null,i)}const v=o(a,[["render",_]]);export{x as __pageData,v as default};
