import{_ as a,o as e,c as o,e as t}from"./chunks/framework.78e651f5.js";const r="/assets/dress.742ffabe.jpg",s="/assets/VbmAmA2IjUE.5a5e641d.jpeg",b=JSON.parse('{"title":"Сад гранат Москва","description":"","frontmatter":{"title":"Сад гранат Москва","date":"2018-09-19","icon":"logo.jpg","cover":"cover.jpg","url":"https://wowmosaic.moscow/","place":"м. Дмитровская","address":"Новодмитровская улица, 1с2","coord":[55.80502048599078,37.590209998349025],"tel":"+7 (999) 711-03-83","hours":"по будням с 12 до 22, по выходным с 10 до 21","vk":"wowmosaic_moscow","instagram":"wowmosaic_moscow","status":"open"},"headers":[],"relativePath":"workshop/catalog/sad-granat-moscow/index.md","filePath":"workshop/catalog/sad-granat-moscow/index.md"}'),c={name:"workshop/catalog/sad-granat-moscow/index.md"},i=t('<p><a href="/workshop/catalog/sad-granat-spb/">Та самая Школа Мозаики</a> теперь в Москве!</p><p>Уже более 10 лет мы занимаемся мозаикой и обучаем этому ремеслу всех желающих. Мы - большие фанаты искусства мозаики. Это дело нашей мечты.</p><p>Школа основана на базе крупнейшего творческого объединения ТАВР. Соединяет методики преподавания мозаики в Академии Художеств им. Репина и Художественно-Промышленной Академии им. Барона Штиглица с реальным опытом мозаичной мастерской.</p><p>Наши ученики участвуют в выставках, делают собственные мозаичные проекты, работают в лучших мозаичных мастерских Санкт-Петербурга и Москвы, а некоторые открывают собственные студии мозаики.</p><p>Место творчества, встреч и отдыха от повседневной жизни для тех, кто верит в создание шедевров своими руками.</p><p>Студия оборудована лучшим профессиональным инструментом для мозаики. Академическая смальта из Санкт- Петербурга. Натуральный камень из Италии, Греции, Турции, Китая, Индии и даже Чили.Только итальянские харди и мартелины. Ароматный кофе. Всё это создаёт уникальную атмосферу творчества. Все необходимые материалы для занятий мы предоставляем.</p><p><img src="'+r+'" alt=""></p><h2 id="почему-мы" tabindex="-1">Почему мы? <a class="header-anchor" href="#почему-мы" aria-label="Permalink to &quot;Почему мы?&quot;">​</a></h2><h3 id="одна-из-лучших-школ-в-мире" tabindex="-1">Одна из лучших школ в мире <a class="header-anchor" href="#одна-из-лучших-школ-в-мире" aria-label="Permalink to &quot;Одна из лучших школ в мире&quot;">​</a></h3><p>К нам приезжают на обучение из Германии, Греции, Израиля и других стран</p><h3 id="удобныи-график" tabindex="-1">Удобный график <a class="header-anchor" href="#удобныи-график" aria-label="Permalink to &quot;Удобный график&quot;">​</a></h3><p>Занятия проходят в утреннее, дневное и вечернее время</p><h3 id="не-требуется-художественное-образование" tabindex="-1">Не требуется художественное образование <a class="header-anchor" href="#не-требуется-художественное-образование" aria-label="Permalink to &quot;Не требуется художественное образование&quot;">​</a></h3><p>Наши занятия подходят как для новичков, так и для профессионалов</p><h3 id="материалы" tabindex="-1">Материалы <a class="header-anchor" href="#материалы" aria-label="Permalink to &quot;Материалы&quot;">​</a></h3><p>Мы предоставляем все расходные материалы и инструменты</p><h3 id="спецпроекты" tabindex="-1">Спецпроекты <a class="header-anchor" href="#спецпроекты" aria-label="Permalink to &quot;Спецпроекты&quot;">​</a></h3><p>Устраиваем выставки работ наших выпускников и издаём каталоги по итогам</p><h3 id="команда" tabindex="-1">Команда <a class="header-anchor" href="#команда" aria-label="Permalink to &quot;Команда&quot;">​</a></h3><p>Профессиональные наставники и команда единомышленников</p><p><img src="'+s+'" alt=""></p>',21),p=[i];function n(d,h,l,_,m,u){return e(),o("div",null,p)}const g=a(c,[["render",n]]);export{b as __pageData,g as default};
