import{_ as a,o as e,c as o,e as t}from"./chunks/framework.78e651f5.js";const r="/assets/inside.7b6a00c3.jpg",i="/assets/inside-2.2ae48328.jpg",f=JSON.parse('{"title":"Второе дыхание","description":"Общественные мастерские с бесплатным посещением","frontmatter":{"title":"Второе дыхание","description":"Общественные мастерские с бесплатным посещением","date":"2015-05-28","cover":"inside-3.jpg","icon":"logo.jpg","vk":"vtd.fond","telegram":"vtoroemoscow","tel":"+7(499) 110-32-30","url":"https://vtoroe.ru/","email":"workshop@vtoroe.ru","coord":[56.02172095216836,37.60607068582383],"address":"ул. Годовикова, 6","place":"м. Алексеевская","city":"г. Москва","status":"open","youtube":"https://www.youtube.com/watch?v=57vvG4V0Iv0","hours":"вт-сб с 11-20"},"headers":[],"relativePath":"workshop/catalog/vtoroe/index.md","filePath":"workshop/catalog/vtoroe/index.md"}'),l={name:"workshop/catalog/vtoroe/index.md"},s=t('<h2 id="шеи-чини-береги-мы-научим-тому-как-дать-вещам-вторую-жизнь" tabindex="-1">Шей-чини-береги, мы научим тому, как дать вещам вторую жизнь! <a class="header-anchor" href="#шеи-чини-береги-мы-научим-тому-как-дать-вещам-вторую-жизнь" aria-label="Permalink to &quot;Шей-чини-береги, мы научим тому, как дать вещам вторую жизнь!&quot;">​</a></h2><p>Вещи заслуживают второго шанса, даже если они сломались, устарели или разонравились.</p><p><strong>Мастерские фонда Второе дыхание</strong> - это три общественных мастерских со всем необходимым оборудованием и бесплатным посещением (швейный коворкинг, столярная мастерская и творческая)</p><p>В Мастерских фонда ВТОРОЕ ДЫХАНИЕ вещи обретают вторую жизнь. Здесь можно починить сломавшиеся или устаревшие вещи, попробовать себя в дизайне и творчестве и узнать больше об осознанном потреблении.</p><h2 id="швеиная-мастерская" tabindex="-1">Швейная мастерская <a class="header-anchor" href="#швеиная-мастерская" aria-label="Permalink to &quot;Швейная мастерская&quot;">​</a></h2><p>В Мастерских можно научиться чинить или кастомизировать одежду, создавать новые предметы гардероба и интерьера из ненужных вещей, а также бесплатно пользоваться современным оборудованием для шитья и починки вещей.</p><h3 id="как-появилась" tabindex="-1">Как появилась <a class="header-anchor" href="#как-появилась" aria-label="Permalink to &quot;Как появилась&quot;">​</a></h3><p>Мы во ВТОРОМ ДЫХАНИИ верим, что вещи заслуживают второго шанса, даже если они сломались, устарели или просто разонравились. Нас вдохновляют российские и зарубежные дизайнеры, превращающие вторсырьё в объекты искусства, а никому не нужные вещи – в желанные и необычные, которыми мы можем пользоваться каждый день.</p><h3 id="какую-проблему-решает" tabindex="-1">Какую проблему решает? <a class="header-anchor" href="#какую-проблему-решает" aria-label="Permalink to &quot;Какую проблему решает?&quot;">​</a></h3><p>Миссия фонда – развивать осознанное потребление в России. В стране ежегодно на мусорных полигонах оказываются 2 млн тонн одежды, при этом часть из них ещё может быть повторно использована. Мы призываем не выкидывать вещи, а по возможности ремонтировать их – и готовы научить и предоставить оборудование для этого. ЭТО МАСТЕРСКИЕ ИНКЛЮЗИВНОСТИ</p><p>Отдельное направление проекта – создание инклюзивной среды: Мастерские открыты, а расписание адаптировано под запросы людей с ограниченными возможностями.</p><p><img src="'+r+'" alt=""></p><h3 id="два-направления" tabindex="-1">Два направления <a class="header-anchor" href="#два-направления" aria-label="Permalink to &quot;Два направления&quot;">​</a></h3><ol><li>МЕРОПРИЯТИЯ Возможность научиться починке вещей у профессионалов Мастер-классы по шитью, починке одежды, созданию аксессуаров, игрушек, лекции от стилистов и экологов, свопы и многое другое</li><li>БЕСПЛАТНЫЙ ШВЕЙНЫЙ КОВОРКИНГ Возможность бесплатно пользоваться необходимым оборудованием Есть всё необходимое оборудование – от булавок и ножниц до современных швейных машин и оверлок, которым может воспользоваться любой желающий после прохождения установочной встречи</li></ol><h2 id="форматы-мероприятии" tabindex="-1">Форматы мероприятий <a class="header-anchor" href="#форматы-мероприятии" aria-label="Permalink to &quot;Форматы мероприятий&quot;">​</a></h2><h3 id="мастер-классы" tabindex="-1">Мастер-классы <a class="header-anchor" href="#мастер-классы" aria-label="Permalink to &quot;Мастер-классы&quot;">​</a></h3><p>По апсайклингу и кастомайзингу: освойте технику тай-дай, научитесь подшивать брюки и накладывать заплатки, чинить мебель и создавать предметы интерьера и игрушки – и многое другое</p><h3 id="лекции-и-обсуждения" tabindex="-1">Лекции и обсуждения <a class="header-anchor" href="#лекции-и-обсуждения" aria-label="Permalink to &quot;Лекции и обсуждения&quot;">​</a></h3><p>С дизайнерами, представителями fashion-индустрии, экосообщества</p><h3 id="тематические-программы-на-целыи-день" tabindex="-1">Тематические программы на целый день <a class="header-anchor" href="#тематические-программы-на-целыи-день" aria-label="Permalink to &quot;Тематические программы на целый день&quot;">​</a></h3><p>Nематические программы на весь день с лекциями, мастер-классами от стилистов и специалистов по наведению порядка</p><h3 id="темы-мастер-классов" tabindex="-1">ТЕМЫ МАСТЕР-КЛАССОВ: <a class="header-anchor" href="#темы-мастер-классов" aria-label="Permalink to &quot;ТЕМЫ МАСТЕР-КЛАССОВ:&quot;">​</a></h3><ul><li>ремонт одежды под руководством мастера</li><li>кастомизация вещей с помощью различных техник</li><li>создание новых предметов одежды из вторичных материалов</li><li>создание украшений и аксессуаров, предметов интерьера из вторсырья</li></ul><p><img src="'+i+'" alt=""></p>',24),h=[s];function n(d,c,p,u,_,m){return e(),o("div",null,h)}const q=a(l,[["render",n]]);export{f as __pageData,q as default};
