import{_ as s,a as t,o as _,H as o}from"./chunks/framework.Cao6T-Ti.js";const r="/assets/IMG_9966.C_7J0zWl.jpg",a="/assets/IMG_9967.CaNbqBZV.jpg",g="/assets/IMG_9969.DlSfQbEg.jpg",p="/assets/IMG_9970.2agPD_eq.jpg",c="/assets/IMG_9972.Bg6Gbtts.jpg",i="/assets/IMG_0186.D1g3k79x.jpg",m="/assets/IMG_0189.DKigzUIK.jpg",n="/assets/IMG_0190.fVo18DWP.jpg",e="/assets/IMG_0191.vh3hjP1H.jpg",l="/assets/IMG_0192.gU9W01ZA.jpg",j="/assets/IMG_0193.CBPPiU8D.jpg",I="/assets/IMG_0194.CIZQUbRm.jpg",G="/assets/IMG_0195.Dm3RGYe5.jpg",M="/assets/IMG_0196.l6gIIt6C.jpg",D="/assets/IMG_0197.CJzbJ4pL.jpg",k="/assets/IMG_0198.H5ipzt8j.jpg",C="/assets/IMG_0199.CKc_Y8KS.jpg",d="/assets/IMG_0200.BFbjjMBj.jpg",f="/assets/IMG_0201.BQEQIk_o.jpg",h="/assets/IMG_0202.DON058H2.jpg",B="/assets/IMG_0160.BXfXLvqT.jpg",w="/assets/IMG_0161.D0QboTyi.jpg",u="/assets/IMG_0162.BgjNctP1.jpg",q="/assets/IMG_0163.Dj0z7Sab.jpg",P="/assets/IMG_0164.CfviYAj5.jpg",T="/assets/IMG_0165.Cd5TQdhp.jpg",N="/assets/IMG_0166.DqFpZKXW.jpg",U="/assets/IMG_0167.CAnhg7j4.jpg",b="/assets/IMG_0168.BYI_ugFm.jpg",V="/assets/IMG_0169.DNAEUPcI.jpg",x="/assets/IMG_0170.DGnrPqel.jpg",A="/assets/IMG_0171.BX3ZAHFE.jpg",F="/assets/IMG_0172.CDrLYNZw.jpg",S="/assets/IMG_0173.2T9CV7-T.jpg",Z="/assets/IMG_0174.CGoBnlow.jpg",v="/assets/IMG_0175.CjgSfoLh.jpg",E="/assets/IMG_0176.BzGVA-1N.jpg",K="/assets/IMG_0183.CZ1y354X.jpg",X="/assets/IMG_0184.CEZTXahX.jpg",y="/assets/IMG_0185.5eElJZcz.jpg",J="/assets/IMG_0177.OckmakOz.jpg",L="/assets/IMG_0178.PEMJk3FG.jpg",Y="/assets/IMG_0179.DTXvkWmX.jpg",z="/assets/IMG_0180.Dymsn4sr.jpg",Q="/assets/IMG_0181.tbEgF_WM.jpg",H="/assets/IMG_0182.I_UPVAyr.jpg",O="/assets/IMG_0187.D2FKRwxP.jpg",R="/assets/IMG_9986.BDt90p2G.jpg",W="/assets/IMG_9987.Co_jeO-w.jpg",$="/assets/IMG_9988.BbrqFope.jpg",ss="/assets/IMG_9989.C7kdFNrw.jpg",ts="/assets/IMG_9991.Dtg7K4ma.jpg",_s="/assets/IMG_9992.BTrktVHN.jpg",os="/assets/IMG_9993.B3v0klXb.jpg",rs="/assets/IMG_9994.DmAMcsDV.jpg",as="/assets/IMG_9995.BHPUjwKg.jpg",gs="/assets/IMG_9996.C8UfLKLY.jpg",ps="/assets/IMG_9999.Dsfxkcjs.jpg",cs="/assets/IMG_0141.CnFgF9oJ.jpg",is="/assets/IMG_0142.KchaL3gM.jpg",ms="/assets/IMG_0143.cOMtoElk.jpg",ns="/assets/IMG_0144.BJsrzV9f.jpg",es="/assets/IMG_0145._g1LkD9o.jpg",ls="/assets/IMG_0146.B474cnel.jpg",js="/assets/IMG_0147.Cx5ucvUj.jpg",Is="/assets/IMG_0148.AKiK50JY.jpg",Gs="/assets/IMG_0149.Dm_10tL9.jpg",Ms="/assets/IMG_0150.DVqBSUGh.jpg",Ds="/assets/IMG_0151.CJfuvhVT.jpg",ks="/assets/IMG_9973.C6w-QaVc.jpg",Cs="/assets/IMG_9974.B-dOonoH.jpg",ds="/assets/IMG_9976.Bf76q6PC.jpg",fs="/assets/IMG_9977.BJiXmwyt.jpg",hs="/assets/IMG_9978.CCX1Gk-Y.jpg",Bs="/assets/IMG_9979.vQFVooES.jpg",ws="/assets/IMG_9980.xf5cRwU7.jpg",us="/assets/IMG_9981.DgFo8r5t.jpg",qs="/assets/IMG_9982.DmAt4TLQ.jpg",Ps="/assets/IMG_9983.BUZhwSNA.jpg",Ts="/assets/IMG_9985.Dcy0zIo_.jpg",Ns="/assets/IMG_0152.B_b87Nrm.jpg",Us="/assets/IMG_0153.D8vPU23R.jpg",bs="/assets/IMG_0154.3r8Iv4mU.jpg",Vs="/assets/IMG_0155.Z37LNYqW.jpg",xs="/assets/IMG_0156.DyI1X7aw.jpg",As="/assets/IMG_0157.9NZg6U-S.jpg",Fs="/assets/IMG_0158.DNfeaDop.jpg",Ss="/assets/IMG_0159.D5SOaB_Y.jpg",Zs="/assets/IMG_0188.DpU-LsQs.jpg",vs="/assets/IMG_0203.DwlTDmdm.jpg",Es="/assets/IMG_0204.Dr88G4qu.jpg",Ks="/assets/IMG_0205.CFYgE3mK.jpg",Xs="/assets/IMG_0206.BZwJkGZl.jpg",st=JSON.parse('{"title":"Творческая мастерская Академия Стекла","description":"","frontmatter":{"title":"Творческая мастерская Академия Стекла","date":"2015-04-20","cover":"IMG_9998.jpg"},"headers":[],"relativePath":"workshop/tour/akademiya-stekla/index.md","filePath":"workshop/tour/akademiya-stekla/index.md"}'),ys={name:"workshop/tour/akademiya-stekla/index.md"},Js=o('<p>Формат открытой мастерской — удел не только крупных и громких дерево- и металлообработки. Мы уже рассказывали об <a href="/workshop/catalog/artefaktorium/" title="Открытая ювелирная мастерская Артефакториум">открытой ювелирной мастерской Артефакториум</a>, где мы узнали еще об одном открытом творческом направлении: <strong>работе со стеклом</strong>. Не откладывая в долгий ящик, мы отправились в <a href="/workshop/catalog/akademiya-stekla/" title="Творческая мастерская Академия стекла">творческую мастерскую &quot;Академия Стекла&quot;</a>, расположенную недалеко от <strong>метро Сокол на Балтийской улице в доме №11</strong>. Чтобы попасть в мастерскую, мы проходим мимо шлагбаума, преграждающего въезд на территорию автомобилей и обходим длинное здание справа. Завернув за угол, в торце здания видим черную дверь за невысоким заборчиком. Заходим внутрь, поднимаемся на второй этаж и мы на месте!</p><p><img src="'+r+'" alt=""><img src="'+a+'" alt=""><img src="'+g+'" alt=""><img src="'+p+'" alt=""><img src="'+c+'" alt=""></p><p>Войдя внутрь, мы попадаем в длинный коридор, соединяющий различные помещения Академии Стекла. Если быть точными, здесь под одной крышей собраны сразу несколько тесно переплетённых проектов. Первый справа проход ведёт нас в <a href="http://www.hightidespace.com/about/" class="external-link" target="_blank" rel="noreferrer">галерею High Tide</a>, где на полках и в витринах собраны избранные творческие результаты, созданные мастерами в стенах Академии, а также предоставленные другими виртуозами стекольного и ювелирного дела. В представленных украшениях и авторских композициях сочетаются и переплетаются различные техники работы со стеклом, керамикой, металлом и другими материалами, создавая причудливые образы и неожиданно натуралистичные формы. Мастера черпают своё вдохновение во внимательном наблюдении за природой, смело экспериментируют с техниками и технологиями. Галерея — пространство для вдумчивого изучения готовых изделий, проведения небольших презентаций, непринуждённого общения. Посетители могут сразу приобрести понравившиеся экземпляры.</p><p><img src="'+i+'" alt=""><img src="'+m+'" alt=""><img src="'+n+'" alt=""><img src="'+e+'" alt=""><img src="'+l+'" alt=""><img src="'+j+'" alt=""><img src="'+I+'" alt=""><img src="'+G+'" alt=""><img src="'+M+'" alt=""><img src="'+D+'" alt=""><img src="'+k+'" alt=""><img src="'+C+'" alt=""><img src="'+d+'" alt=""><img src="'+f+'" alt=""><img src="'+h+'" alt=""></p><p>Академия стекла — нынешний этап сложного творческого пути Марины и Анастасии Прибельских, начинавших с создания авторских кукол в рамках проекта <a href="http://www.glassacademyrussia.com/rowancraft/" class="external-link" target="_blank" rel="noreferrer"><strong>Rowan Craft</strong></a> и решивших не останавливаться в саморазвитии, продолжать осваивать всё новые техники и совершенствовать имеющиеся умения. Глубокие наблюдения за живой природой послужили основой для разработки тонкого авторского подхода к воссозданию отдельных живых существ и целых экосистем из сочетания различных глин, пластиков, красок и других материалов. В какой-то момент поиск новых выразительных средств привёл мастериц к <strong>лэмпворку</strong> — плавке и формованию стеклянных изделий в пламени газовой горелки — бесконечному полю для развития и всевозможных творческих экспериментов. Так формировалась Академия Стекла — поначалу как личная мастерская Марины, Анастасии и других мастеров, постепенно узнававших и присоединявшихся к проекту. Обучение в формате отдельных <strong>мастер-классов, продолжительных курсов и даже обучающих походов</strong> всегда было неотъемлемой составляющей проекта Rowan Craft и по мере освоения техники лэмпворка мастерская становилась все более открытой и направленной на обучение всех заинтересованных.</p><p><img src="'+B+'" alt=""><img src="'+w+'" alt=""><img src="'+u+'" alt=""><img src="'+q+'" alt=""><img src="'+P+'" alt=""><img src="'+T+'" alt=""><img src="'+N+'" alt=""><img src="'+U+'" alt=""><img src="'+b+'" alt=""><img src="'+V+'" alt=""><img src="'+x+'" alt=""><img src="'+A+'" alt=""><img src="'+F+'" alt=""><img src="'+S+'" alt=""><img src="'+Z+'" alt=""><img src="'+v+'" alt=""><img src="'+E+'" alt=""></p><p>Сразу при входе в галерею, слева есть дверь, ведущая в узкую комнату с длинным столом, предназначенным для финальной сборки изделий из стекла и работы с полимерными и металлическими глинами. За длинным столом свободно размещаются <strong>5—6 человек</strong> и получают возможность погрузиться в тонкости различных творческих техник и технологий. Лепка из всевозможных глин и пластиков — одно из приоритетных направлений, но это далеко не всё. Среди прочего здесь можно научиться резьбе по керамике, гравировке и печати в технике офорта, созданию цветочных композиций из шёлка или специальных пластических составов. В закромах мастерской найдётся множество всевозможных инструментов, а основные материалы можно приобрести прямо здесь же.</p><p>В дальнем углу галереи можно увидеть ещё одну дверь, ведущую в малую мастерскую. Это небольшое помещение, оборудованное двумя газовыми горелками и электрической печкой и наполненное огромным количеством различных глин, красок и всевозможного стекла. Во время мастер-классов по работе с металлическими глинами в этой мастерской происходит обжиг вылепленных изделий, а когда здесь не проводится никаких событий комната играет роль личной мастерской хозяек Академии.</p><p><img src="'+K+'" alt=""><img src="'+X+'" alt=""><img src="'+y+'" alt=""><img src="'+J+'" alt=""><img src="'+L+'" alt=""><img src="'+Y+'" alt=""><img src="'+z+'" alt=""><img src="'+Q+'" alt=""><img src="'+H+'" alt=""></p><p>Вернёмся в общий коридор и повернём в следующую после входа в галерею дверь — мы окажемся в основном рабочем помещении Академии. Это лэмпворк-мастерская с шестью полностью оборудованными рабочими местами. Здесь есть всё необходимое для свободного творчества и для проведения всевозможных обучающих мероприятий. Давайте осмотримся повнимательнее. Мы увидим два больших стеллажа заполненных множеством полезностей. За время существования Академии здесь накопилось большое количество редких книг, учебников и каталогов по работе со стеклом — вся эта <strong>библиотека доступна для каждого резидента</strong> мастерской. Уложенный в картонные тубы здесь хранится внушительный <strong>запас дротов</strong>, стеклянных стержней, <strong>и фритов</strong>, мелкой цветной стеклянной крошки, которые можно приобрести прямо на площадке. Стекло, как и книги, поступают не только от местных поставщиков, но и привозятся хозяйками мастерской из разных уголков нашей планеты, в которых они оказываются в рамках своих регулярных путешествий. В своих поездках мастерицы посещают мастерские художников и активно собирают новые знания, умения и редкие материалы. Они <strong>приглашают всех желающих отправиться в поездку</strong> вместе с ними, а по возвращении делятся впечатлениями и полученным опытом в форме новых увлекательных мастер-классов.</p><p><img src="'+O+'" alt=""><img src="'+R+'" alt=""><img src="'+W+'" alt=""><img src="'+$+'" alt=""><img src="'+ss+'" alt=""><img src="'+ts+'" alt=""><img src="'+_s+'" alt=""><img src="'+os+'" alt=""><img src="'+rs+'" alt=""><img src="'+as+'" alt=""><img src="'+gs+'" alt=""><img src="'+ps+'" alt=""></p><p>Каждому резиденту мастерской выделяется место в зоне общего хранения. В картонных тубусах сложены сотни <strong>стеклодротов</strong> и специфических инструментов. Некоторые из них подписаны, некоторые — нет, но путаницы не возникает — мастера просто запоминают что и куда положили и, конечно же, никогда не позволят себе взять чужое. Личные, сложные и особенно ценные инструменты хранятся отдельно и чтобы ими воспользоваться нужно обратиться к хозяйке мастерской или непосредственно к владельцу инструмента. В Академии стекла сложилась интересная практика маркировки личных инструментов: у каждого мастера есть <strong>свой личный цвет</strong>, которым помечены все его персональные приспособления. Для маркировки используется специальная термостойкая эмаль и такие условные обозначения несут множество плюсов. Хоть нигде и нет официальной таблицы персональных цветов, на первом же занятии вводного курса для резидентов и гостей мастерской сообщают об этих соответствиях и в последствии все вопросы собственности на инструменты решаются быстро и просто. Тем более, что подавляющее <strong>большинство инструментов — общественные и доступны каждому</strong>. А если чего-то не хватает — всегда можно попросить поделиться. Дружелюбная творческая атмосфера создает благоприятную среду для конструктивного общения и взаимовыручки: здесь всегда есть к кому обратиться за советом или помощью.</p><p><img src="'+cs+'" alt=""><img src="'+is+'" alt=""><img src="'+ms+'" alt=""><img src="'+ns+'" alt=""><img src="'+es+'" alt=""><img src="'+ls+'" alt=""><img src="'+js+'" alt=""><img src="'+Is+'" alt=""><img src="'+Gs+'" alt=""><img src="'+Ms+'" alt=""><img src="'+Ds+'" alt=""></p><p>Из чего же состоит рабочее место для лэмпворка? Нетрудно догадаться, что начинается оно с &quot;лампы&quot; — <strong>газовой горелки</strong>, работающей на смеси обычного бытового газа <strong>пропана</strong>, поступающего из баллонов, стоящих в отдельном помещении, <strong>и кислорода</strong>, получаемого на месте с помощью <strong>концентратора</strong>, такое устройство есть под каждым рабочим местом мастерской. Пропан и кислород, смешиваясь в горелке, дают ровное интенсивное пламя с температурой <strong>свыше 1300°С</strong>. Для удаления избыточного тепла и продуктов горения к каждой горелке подведена труба мощной <strong>вытяжки</strong>, а окна помещения оборудованы системой <strong>приточной вентиляции</strong>. У каждой горелки под трубой вытяжки есть специальная миска с водой, необходимой для резкого охлаждения стекла в некоторых технологических операциях. Рядом с каждой горелкой лежат в два слоя <strong>керамические одеяла</strong>, между которыми помещаются остывающие изделия. Теплоизолирующие свойства этих матов предотвращают растрескивание стекла из-за большой разницы в температуре внешних и внутренних слоёв материала. Также для каждого резидента мастерской доступен запас специальных металлических стержней — <strong>&quot;мандрелей&quot;</strong> — разного диаметра и формы наконечника. Мандрели выполняют роль оси, на которую накручивается стекольная масса и в последствии определяют диаметр отверстия в бусине или кольце. Для того, чтобы готовые изделия можно было отделить от оси, мандрели покрывают специальным составом — <strong>сепаратором</strong>, похожим на жидкую глину. Высыхая, этот состав оставляет стойкую к температуре, но водорастворимую плёнку, благодаря чему окончательно остывшие бусины снимаются со стержней под струей воды. Каждому резиденту мастерской доступны еще множество различных приспособлений от специальных графитовых площадок, на которых раскатывают и формируют бусины до всевозможных пинцетов, щипцов и лопаток. В мастерской на всех хватит и специальных очков с фиолетово-синими стёклами, блокирующими мешающие точной работе желтый и оранжевый цвета пламени. Очки доступны в оправах различных фасонов на выбор. В центре мастерской расположена общая <strong>муфельная печь для отжига изделий</strong>, поддерживающая в течение дня постоянную температуру в <strong>510°С</strong>, а ночью плавно остывающую до комнатной температуры — надёжное средство для предотвращения растрескивания стекла из-за неравномерного остывания. В случае если бусина все-таки треснула или совсем не хочет сниматься с мандрели — её нагревают в печи и резко погружают в холодную воду: стекло мгновенно растрескивается и легко осыпается, а полученные осколки нередко используются в дальнейших творческих экспериментах.</p><p><img src="'+ks+'" alt=""><img src="'+Cs+'" alt=""><img src="'+ds+'" alt=""><img src="'+fs+'" alt=""><img src="'+hs+'" alt=""><img src="'+Bs+'" alt=""><img src="'+ws+'" alt=""><img src="'+us+'" alt=""><img src="'+qs+'" alt=""><img src="'+Ps+'" alt=""><img src="'+Ts+'" alt=""></p><p>Хозяйки мастерской внимательно относятся к каждому новому гостю и к каждому мастеру-резиденту, всегда готовы помочь советом, подсказать решение творческих задач. Они регулярно проводят групповые и индивидуальные курсы по основам лэмпворка для начинающих, а также мастер-классы по продвинутым техникам для уже практикующих мастеров. Свои авторские курсы представляет сразу целая команда мастеров. Есть возможность выгодно арендовать мастерскую для проведения своих обучающих событий.</p><p>Марина и Анастасия играючи познакомили меня с основными аспектами практики лэмпворка, продемонстрировали все основные этапы работы со стеклом и дали возможность сделать свою первую <strong>стеклянную бусину самостоятельно</strong>. Процесс работы с горелкой — действительно завораживающее действо! Лэмпворк — замечательная технология, с невероятным творческим потенциалом. Я уже задумал один стеклянный проект и, надеюсь, вместе с мастерской &quot;Академия Стекла&quot; мы сможем реализовать мои смелые фантазии. Постараемся максимально подробно рассказать о нашем дальнейшем сотрудничестве, а также сформировать четкие инструкции к полученным навыкам и умениям.</p><p><img src="'+Ns+'" alt=""><img src="'+Us+'" alt=""><img src="'+bs+'" alt=""><img src="'+Vs+'" alt=""><img src="'+xs+'" alt=""><img src="'+As+'" alt=""><img src="'+Fs+'" alt=""><img src="'+Ss+'" alt=""><img src="'+Zs+'" alt=""><img src="'+vs+'" alt=""><img src="'+Es+'" alt=""><img src="'+Ks+'" alt=""><img src="'+Xs+'" alt=""></p>',18),Ls=[Js];function Ys(zs,Qs,Hs,Os,Rs,Ws){return _(),t("div",null,Ls)}const tt=s(ys,[["render",Ys]]);export{st as __pageData,tt as default};
