import{_ as s,o as t,c as _,e as o}from"./chunks/framework.nzJD8CNM.js";const r="/assets/IMG_9966.v-ydM1pV.jpg",a="/assets/IMG_9967.mjW6gWVT.jpg",g="/assets/IMG_9969.5Un0GxIO.jpg",p="/assets/IMG_9970.NmoDw_3q.jpg",i="/assets/IMG_9972.YOhm7bbP.jpg",c="/assets/IMG_0186.9YN5O_cf.jpg",m="/assets/IMG_0189.yooM1CCi.jpg",n="/assets/IMG_0190.H1aNfA1j.jpg",e="/assets/IMG_0191.L4d4Yz9R.jpg",l="/assets/IMG_0192.IFPVtNWQ.jpg",I="/assets/IMG_0193.gTz4lPA4.jpg",M="/assets/IMG_0194.iGUFG0Zs.jpg",j="/assets/IMG_0195.5t0RmHuS.jpg",G="/assets/IMG_0196.JeoCCLeg.jpg",d="/assets/IMG_0197.ic2yeKS2.jpg",f="/assets/IMG_0198.B-Yqc7fI.jpg",h="/assets/IMG_0199.inP2PCkr.jpg",k="/assets/IMG_0200.RW44zAY_.jpg",u="/assets/IMG_0201.UBECJP6E.jpg",C="/assets/IMG_0202.zjdOfB9v.jpg",P="/assets/IMG_0160.V31y76k2.jpg",B="/assets/IMG_0161.9EG6E8os.jpg",w="/assets/IMG_0162.YIzXLT9a.jpg",x="/assets/IMG_0163.49M-0mm-.jpg",L="/assets/IMG_0164.n74mAI-a.jpg",S="/assets/IMG_0165.neU0HYaR.jpg",W="/assets/IMG_0166.6haWSl1h.jpg",y="/assets/IMG_0167.gJ4YO4-E.jpg",J="/assets/IMG_0168.WCP7oBZi.jpg",V="/assets/IMG_0169.zQBFD3CG.jpg",q="/assets/IMG_0170.xp6z6npR.jpg",N="/assets/IMG_0171.V92QBxRC.jpg",T="/assets/IMG_0172.g6y2DWcF.jpg",Y="/assets/IMG_0173.Nk_Qle_k.jpg",z="/assets/IMG_0174.hqAZ5aMG.jpg",A="/assets/IMG_0175.o4En6C4S.jpg",E="/assets/IMG_0176.cxlQPtTa.jpg",O="/assets/IMG_0183.mdct-eF3.jpg",R="/assets/IMG_0184.hGU12oV4.jpg",D="/assets/IMG_0185.OXhJSWXM.jpg",H="/assets/IMG_0177.DnJJmpDs.jpg",K="/assets/IMG_0178.DxDCZNxR.jpg",X="/assets/IMG_0179.0175Fpl_.jpg",U="/assets/IMG_0180.8prJ-LK-.jpg",Q="/assets/IMG_0181.LWxIBf1j.jpg",Z="/assets/IMG_0182.CP1D1QMq.jpg",b="/assets/IMG_0187.9hSkcMTz.jpg",v="/assets/IMG_9986.Q7fdKdhp.jpg",F="/assets/IMG_9987.qP43jvsM.jpg",$="/assets/IMG_9988.W66haKXl.jpg",ss="/assets/IMG_9989.u5HRTa8A.jpg",ts="/assets/IMG_9991.7YOyuJmo.jpg",_s="/assets/IMG_9992.U65LVRzd.jpg",os="/assets/IMG_9993.d79JJV24.jpg",rs="/assets/IMG_9994.5gDHLA1S.jpg",as="/assets/IMG_9995.Rz1I8CoL.jpg",gs="/assets/IMG_9996.vFHyyi2O.jpg",ps="/assets/IMG_9999.7H8ZHI7E.jpg",is="/assets/IMG_0141.pxYBfaCQ.jpg",cs="/assets/IMG_0142.CnIWi94D.jpg",ms="/assets/IMG_0143.HDjLaBJZ.jpg",ns="/assets/IMG_0144.SbK81fX1.jpg",es="/assets/IMG_0145.P4NS5A_a.jpg",ls="/assets/IMG_0146.eO-HJ3pS.jpg",Is="/assets/IMG_0147.sebnL1I_.jpg",Ms="/assets/IMG_0148.ACoiudCW.jpg",js="/assets/IMG_0149.5v9dLS_d.jpg",Gs="/assets/IMG_0150.1agUlBoY.jpg",ds="/assets/IMG_0151.iX7r4VU1.jpg",fs="/assets/IMG_9973.usPkGlXL.jpg",hs="/assets/IMG_9974.fnTqJ6B_.jpg",ks="/assets/IMG_9976.X--qujwl.jpg",us="/assets/IMG_9977.SYl5sMrW.jpg",Cs="/assets/IMG_9978.gl9RpPmN.jpg",Ps="/assets/IMG_9979.L0BVaKBE.jpg",Bs="/assets/IMG_9980.MX-XEcFO.jpg",ws="/assets/IMG_9981.4BaPK-bf.jpg",xs="/assets/IMG_9982.5gLeEy0H.jpg",Ls="/assets/IMG_9983.VGYcEjQA.jpg",Ss="/assets/IMG_9985.3MtMyKP9.jpg",Ws="/assets/IMG_0152.f2_Oza5i.jpg",ys="/assets/IMG_0153._Lz1Nt0f.jpg",Js="/assets/IMG_0154.N6_CL-Jl.jpg",Vs="/assets/IMG_0155.Gd-yzWKl.jpg",qs="/assets/IMG_0156.8iNV-2sK.jpg",Ns="/assets/IMG_0157.PTWYOlPk.jpg",Ts="/assets/IMG_0158.zX3mg6KY.jpg",Ys="/assets/IMG_0159.A-Ujmgf2.jpg",zs="/assets/IMG_0188.6VPi7ELO.jpg",As="/assets/IMG_0203.8JUw5nZj.jpg",Es="/assets/IMG_0204.6_PBuKru.jpg",Os="/assets/IMG_0205.hWIBN5it.jpg",Rs="/assets/IMG_0206.WcCZBmZe.jpg",st=JSON.parse('{"title":"Творческая мастерская Академия Стекла","description":"","frontmatter":{"title":"Творческая мастерская Академия Стекла","date":"2015-04-20","cover":"IMG_9998.jpg"},"headers":[],"relativePath":"workshop/tour/akademiya-stekla/index.md","filePath":"workshop/tour/akademiya-stekla/index.md"}'),Ds={name:"workshop/tour/akademiya-stekla/index.md"},Hs=o('<p>Формат открытой мастерской — удел не только крупных и громких дерево- и металлообработки. Мы уже рассказывали об <a href="/workshop/catalog/artefaktorium/" title="Открытая ювелирная мастерская Артефакториум">открытой ювелирной мастерской Артефакториум</a>, где мы узнали еще об одном открытом творческом направлении: <strong>работе со стеклом</strong>. Не откладывая в долгий ящик, мы отправились в <a href="/workshop/catalog/akademiya-stekla/" title="Творческая мастерская Академия стекла">творческую мастерскую &quot;Академия Стекла&quot;</a>, расположенную недалеко от <strong>метро Сокол на Балтийской улице в доме №11</strong>. Чтобы попасть в мастерскую, мы проходим мимо шлагбаума, преграждающего въезд на территорию автомобилей и обходим длинное здание справа. Завернув за угол, в торце здания видим черную дверь за невысоким заборчиком. Заходим внутрь, поднимаемся на второй этаж и мы на месте!</p><p><img src="'+r+'" alt=""><img src="'+a+'" alt=""><img src="'+g+'" alt=""><img src="'+p+'" alt=""><img src="'+i+'" alt=""></p><p>Войдя внутрь, мы попадаем в длинный коридор, соединяющий различные помещения Академии Стекла. Если быть точными, здесь под одной крышей собраны сразу несколько тесно переплетённых проектов. Первый справа проход ведёт нас в <a href="http://www.hightidespace.com/about/" class="external-link" target="_blank" rel="noreferrer">галерею High Tide</a>, где на полках и в витринах собраны избранные творческие результаты, созданные мастерами в стенах Академии, а также предоставленные другими виртуозами стекольного и ювелирного дела. В представленных украшениях и авторских композициях сочетаются и переплетаются различные техники работы со стеклом, керамикой, металлом и другими материалами, создавая причудливые образы и неожиданно натуралистичные формы. Мастера черпают своё вдохновение во внимательном наблюдении за природой, смело экспериментируют с техниками и технологиями. Галерея — пространство для вдумчивого изучения готовых изделий, проведения небольших презентаций, непринуждённого общения. Посетители могут сразу приобрести понравившиеся экземпляры.</p><p><img src="'+c+'" alt=""><img src="'+m+'" alt=""><img src="'+n+'" alt=""><img src="'+e+'" alt=""><img src="'+l+'" alt=""><img src="'+I+'" alt=""><img src="'+M+'" alt=""><img src="'+j+'" alt=""><img src="'+G+'" alt=""><img src="'+d+'" alt=""><img src="'+f+'" alt=""><img src="'+h+'" alt=""><img src="'+k+'" alt=""><img src="'+u+'" alt=""><img src="'+C+'" alt=""></p><p>Академия стекла — нынешний этап сложного творческого пути Марины и Анастасии Прибельских, начинавших с создания авторских кукол в рамках проекта <a href="http://www.glassacademyrussia.com/rowancraft/" class="external-link" target="_blank" rel="noreferrer"><strong>Rowan Craft</strong></a> и решивших не останавливаться в саморазвитии, продолжать осваивать всё новые техники и совершенствовать имеющиеся умения. Глубокие наблюдения за живой природой послужили основой для разработки тонкого авторского подхода к воссозданию отдельных живых существ и целых экосистем из сочетания различных глин, пластиков, красок и других материалов. В какой-то момент поиск новых выразительных средств привёл мастериц к <strong>лэмпворку</strong> — плавке и формованию стеклянных изделий в пламени газовой горелки — бесконечному полю для развития и всевозможных творческих экспериментов. Так формировалась Академия Стекла — поначалу как личная мастерская Марины, Анастасии и других мастеров, постепенно узнававших и присоединявшихся к проекту. Обучение в формате отдельных <strong>мастер-классов, продолжительных курсов и даже обучающих походов</strong> всегда было неотъемлемой составляющей проекта Rowan Craft и по мере освоения техники лэмпворка мастерская становилась все более открытой и направленной на обучение всех заинтересованных.</p><p><img src="'+P+'" alt=""><img src="'+B+'" alt=""><img src="'+w+'" alt=""><img src="'+x+'" alt=""><img src="'+L+'" alt=""><img src="'+S+'" alt=""><img src="'+W+'" alt=""><img src="'+y+'" alt=""><img src="'+J+'" alt=""><img src="'+V+'" alt=""><img src="'+q+'" alt=""><img src="'+N+'" alt=""><img src="'+T+'" alt=""><img src="'+Y+'" alt=""><img src="'+z+'" alt=""><img src="'+A+'" alt=""><img src="'+E+'" alt=""></p><p>Сразу при входе в галерею, слева есть дверь, ведущая в узкую комнату с длинным столом, предназначенным для финальной сборки изделий из стекла и работы с полимерными и металлическими глинами. За длинным столом свободно размещаются <strong>5—6 человек</strong> и получают возможность погрузиться в тонкости различных творческих техник и технологий. Лепка из всевозможных глин и пластиков — одно из приоритетных направлений, но это далеко не всё. Среди прочего здесь можно научиться резьбе по керамике, гравировке и печати в технике офорта, созданию цветочных композиций из шёлка или специальных пластических составов. В закромах мастерской найдётся множество всевозможных инструментов, а основные материалы можно приобрести прямо здесь же.</p><p>В дальнем углу галереи можно увидеть ещё одну дверь, ведущую в малую мастерскую. Это небольшое помещение, оборудованное двумя газовыми горелками и электрической печкой и наполненное огромным количеством различных глин, красок и всевозможного стекла. Во время мастер-классов по работе с металлическими глинами в этой мастерской происходит обжиг вылепленных изделий, а когда здесь не проводится никаких событий комната играет роль личной мастерской хозяек Академии.</p><p><img src="'+O+'" alt=""><img src="'+R+'" alt=""><img src="'+D+'" alt=""><img src="'+H+'" alt=""><img src="'+K+'" alt=""><img src="'+X+'" alt=""><img src="'+U+'" alt=""><img src="'+Q+'" alt=""><img src="'+Z+'" alt=""></p><p>Вернёмся в общий коридор и повернём в следующую после входа в галерею дверь — мы окажемся в основном рабочем помещении Академии. Это лэмпворк-мастерская с шестью полностью оборудованными рабочими местами. Здесь есть всё необходимое для свободного творчества и для проведения всевозможных обучающих мероприятий. Давайте осмотримся повнимательнее. Мы увидим два больших стеллажа заполненных множеством полезностей. За время существования Академии здесь накопилось большое количество редких книг, учебников и каталогов по работе со стеклом — вся эта <strong>библиотека доступна для каждого резидента</strong> мастерской. Уложенный в картонные тубы здесь хранится внушительный <strong>запас дротов</strong>, стеклянных стержней, <strong>и фритов</strong>, мелкой цветной стеклянной крошки, которые можно приобрести прямо на площадке. Стекло, как и книги, поступают не только от местных поставщиков, но и привозятся хозяйками мастерской из разных уголков нашей планеты, в которых они оказываются в рамках своих регулярных путешествий. В своих поездках мастерицы посещают мастерские художников и активно собирают новые знания, умения и редкие материалы. Они <strong>приглашают всех желающих отправиться в поездку</strong> вместе с ними, а по возвращении делятся впечатлениями и полученным опытом в форме новых увлекательных мастер-классов.</p><p><img src="'+b+'" alt=""><img src="'+v+'" alt=""><img src="'+F+'" alt=""><img src="'+$+'" alt=""><img src="'+ss+'" alt=""><img src="'+ts+'" alt=""><img src="'+_s+'" alt=""><img src="'+os+'" alt=""><img src="'+rs+'" alt=""><img src="'+as+'" alt=""><img src="'+gs+'" alt=""><img src="'+ps+'" alt=""></p><p>Каждому резиденту мастерской выделяется место в зоне общего хранения. В картонных тубусах сложены сотни <strong>стеклодротов</strong> и специфических инструментов. Некоторые из них подписаны, некоторые — нет, но путаницы не возникает — мастера просто запоминают что и куда положили и, конечно же, никогда не позволят себе взять чужое. Личные, сложные и особенно ценные инструменты хранятся отдельно и чтобы ими воспользоваться нужно обратиться к хозяйке мастерской или непосредственно к владельцу инструмента. В Академии стекла сложилась интересная практика маркировки личных инструментов: у каждого мастера есть <strong>свой личный цвет</strong>, которым помечены все его персональные приспособления. Для маркировки используется специальная термостойкая эмаль и такие условные обозначения несут множество плюсов. Хоть нигде и нет официальной таблицы персональных цветов, на первом же занятии вводного курса для резидентов и гостей мастерской сообщают об этих соответствиях и в последствии все вопросы собственности на инструменты решаются быстро и просто. Тем более, что подавляющее <strong>большинство инструментов — общественные и доступны каждому</strong>. А если чего-то не хватает — всегда можно попросить поделиться. Дружелюбная творческая атмосфера создает благоприятную среду для конструктивного общения и взаимовыручки: здесь всегда есть к кому обратиться за советом или помощью.</p><p><img src="'+is+'" alt=""><img src="'+cs+'" alt=""><img src="'+ms+'" alt=""><img src="'+ns+'" alt=""><img src="'+es+'" alt=""><img src="'+ls+'" alt=""><img src="'+Is+'" alt=""><img src="'+Ms+'" alt=""><img src="'+js+'" alt=""><img src="'+Gs+'" alt=""><img src="'+ds+'" alt=""></p><p>Из чего же состоит рабочее место для лэмпворка? Нетрудно догадаться, что начинается оно с &quot;лампы&quot; — <strong>газовой горелки</strong>, работающей на смеси обычного бытового газа <strong>пропана</strong>, поступающего из баллонов, стоящих в отдельном помещении, <strong>и кислорода</strong>, получаемого на месте с помощью <strong>концентратора</strong>, такое устройство есть под каждым рабочим местом мастерской. Пропан и кислород, смешиваясь в горелке, дают ровное интенсивное пламя с температурой <strong>свыше 1300°С</strong>. Для удаления избыточного тепла и продуктов горения к каждой горелке подведена труба мощной <strong>вытяжки</strong>, а окна помещения оборудованы системой <strong>приточной вентиляции</strong>. У каждой горелки под трубой вытяжки есть специальная миска с водой, необходимой для резкого охлаждения стекла в некоторых технологических операциях. Рядом с каждой горелкой лежат в два слоя <strong>керамические одеяла</strong>, между которыми помещаются остывающие изделия. Теплоизолирующие свойства этих матов предотвращают растрескивание стекла из-за большой разницы в температуре внешних и внутренних слоёв материала. Также для каждого резидента мастерской доступен запас специальных металлических стержней — <strong>&quot;мандрелей&quot;</strong> — разного диаметра и формы наконечника. Мандрели выполняют роль оси, на которую накручивается стекольная масса и в последствии определяют диаметр отверстия в бусине или кольце. Для того, чтобы готовые изделия можно было отделить от оси, мандрели покрывают специальным составом — <strong>сепаратором</strong>, похожим на жидкую глину. Высыхая, этот состав оставляет стойкую к температуре, но водорастворимую плёнку, благодаря чему окончательно остывшие бусины снимаются со стержней под струей воды. Каждому резиденту мастерской доступны еще множество различных приспособлений от специальных графитовых площадок, на которых раскатывают и формируют бусины до всевозможных пинцетов, щипцов и лопаток. В мастерской на всех хватит и специальных очков с фиолетово-синими стёклами, блокирующими мешающие точной работе желтый и оранжевый цвета пламени. Очки доступны в оправах различных фасонов на выбор. В центре мастерской расположена общая <strong>муфельная печь для отжига изделий</strong>, поддерживающая в течение дня постоянную температуру в <strong>510°С</strong>, а ночью плавно остывающую до комнатной температуры — надёжное средство для предотвращения растрескивания стекла из-за неравномерного остывания. В случае если бусина все-таки треснула или совсем не хочет сниматься с мандрели — её нагревают в печи и резко погружают в холодную воду: стекло мгновенно растрескивается и легко осыпается, а полученные осколки нередко используются в дальнейших творческих экспериментах.</p><p><img src="'+fs+'" alt=""><img src="'+hs+'" alt=""><img src="'+ks+'" alt=""><img src="'+us+'" alt=""><img src="'+Cs+'" alt=""><img src="'+Ps+'" alt=""><img src="'+Bs+'" alt=""><img src="'+ws+'" alt=""><img src="'+xs+'" alt=""><img src="'+Ls+'" alt=""><img src="'+Ss+'" alt=""></p><p>Хозяйки мастерской внимательно относятся к каждому новому гостю и к каждому мастеру-резиденту, всегда готовы помочь советом, подсказать решение творческих задач. Они регулярно проводят групповые и индивидуальные курсы по основам лэмпворка для начинающих, а также мастер-классы по продвинутым техникам для уже практикующих мастеров. Свои авторские курсы представляет сразу целая команда мастеров. Есть возможность выгодно арендовать мастерскую для проведения своих обучающих событий.</p><p>Марина и Анастасия играючи познакомили меня с основными аспектами практики лэмпворка, продемонстрировали все основные этапы работы со стеклом и дали возможность сделать свою первую <strong>стеклянную бусину самостоятельно</strong>. Процесс работы с горелкой — действительно завораживающее действо! Лэмпворк — замечательная технология, с невероятным творческим потенциалом. Я уже задумал один стеклянный проект и, надеюсь, вместе с мастерской &quot;Академия Стекла&quot; мы сможем реализовать мои смелые фантазии. Постараемся максимально подробно рассказать о нашем дальнейшем сотрудничестве, а также сформировать четкие инструкции к полученным навыкам и умениям.</p><p><img src="'+Ws+'" alt=""><img src="'+ys+'" alt=""><img src="'+Js+'" alt=""><img src="'+Vs+'" alt=""><img src="'+qs+'" alt=""><img src="'+Ns+'" alt=""><img src="'+Ts+'" alt=""><img src="'+Ys+'" alt=""><img src="'+zs+'" alt=""><img src="'+As+'" alt=""><img src="'+Es+'" alt=""><img src="'+Os+'" alt=""><img src="'+Rs+'" alt=""></p>',18),Ks=[Hs];function Xs(Us,Qs,Zs,bs,vs,Fs){return t(),_("div",null,Ks)}const tt=s(Ds,[["render",Xs]]);export{st as __pageData,tt as default};
