import{_ as s,o as t,c as a,b as _}from"./chunks/framework.dd5ab16e.js";const o="/assets/IMG_9966.ea71464e.jpg",r="/assets/IMG_9967.abac8fe7.jpg",c="/assets/IMG_9969.b2ecea6a.jpg",g="/assets/IMG_9970.f324fc99.jpg",p="/assets/IMG_9972.a22cbf43.jpg",i="/assets/IMG_0186.5268ff73.jpg",m="/assets/IMG_0189.6c0a1db9.jpg",e="/assets/IMG_0190.fca5129a.jpg",n="/assets/IMG_0191.6b4f25fc.jpg",l="/assets/IMG_0192.c185974b.jpg",I="/assets/IMG_0193.d0287014.jpg",j="/assets/IMG_0194.dc04562d.jpg",G="/assets/IMG_0195.1d5b0612.jpg",M="/assets/IMG_0196.caa34253.jpg",d="/assets/IMG_0197.93d918b2.jpg",f="/assets/IMG_0198.482b09dc.jpg",b="/assets/IMG_0199.ba1915ae.jpg",h="/assets/IMG_0200.22096622.jpg",k="/assets/IMG_0201.73ade196.jpg",u="/assets/IMG_0202.7a95ac2d.jpg",w="/assets/IMG_0160.af12fc9d.jpg",x="/assets/IMG_0161.16d90f02.jpg",q="/assets/IMG_0162.201548e7.jpg",T="/assets/IMG_0163.6a2b2931.jpg",y="/assets/IMG_0164.19e53a4e.jpg",S="/assets/IMG_0165.db3ef084.jpg",C="/assets/IMG_0166.9341bac3.jpg",P="/assets/IMG_0167.244d5468.jpg",$="/assets/IMG_0168.6d731c63.jpg",v="/assets/IMG_0169.de8b57ea.jpg",A="/assets/IMG_0170.270d09a5.jpg",N="/assets/IMG_0171.b58b2109.jpg",R="/assets/IMG_0172.70584d7b.jpg",V="/assets/IMG_0173.649c95db.jpg",B="/assets/IMG_0174.0910a901.jpg",D="/assets/IMG_0175.82f3b94a.jpg",E="/assets/IMG_0176.29133c58.jpg",H="/assets/IMG_0183.ed252dc1.jpg",J="/assets/IMG_0184.208eec7b.jpg",O="/assets/IMG_0185.040d4613.jpg",z="/assets/IMG_0177.557954a2.jpg",F="/assets/IMG_0178.861570e2.jpg",K="/assets/IMG_0179.8485808d.jpg",L="/assets/IMG_0180.4458c728.jpg",Q="/assets/IMG_0181.101a8362.jpg",U="/assets/IMG_0182.adf64506.jpg",W="/assets/IMG_0187.5c9d3ff0.jpg",X="/assets/IMG_9986.91fab289.jpg",Y="/assets/IMG_9987.5bae2fa3.jpg",Z="/assets/IMG_9988.37f67716.jpg",ss="/assets/IMG_9989.0aa99439.jpg",ts="/assets/IMG_9991.15834353.jpg",as="/assets/IMG_9992.3e1f872a.jpg",_s="/assets/IMG_9993.6a0d3c74.jpg",os="/assets/IMG_9994.f60a0f18.jpg",rs="/assets/IMG_9995.3fc7216a.jpg",cs="/assets/IMG_9996.5df5afe6.jpg",gs="/assets/IMG_9999.c5569346.jpg",ps="/assets/IMG_0141.fbac869c.jpg",is="/assets/IMG_0142.9fdf8309.jpg",ms="/assets/IMG_0143.91d6462c.jpg",es="/assets/IMG_0144.15fcb6f6.jpg",ns="/assets/IMG_0145.d63d3d4f.jpg",ls="/assets/IMG_0146.78dcdd3d.jpg",Is="/assets/IMG_0147.b9eaa3de.jpg",js="/assets/IMG_0148.2dc7a099.jpg",Gs="/assets/IMG_0149.88d4f2ea.jpg",Ms="/assets/IMG_0150.cf482171.jpg",ds="/assets/IMG_0151.9841a4c8.jpg",fs="/assets/IMG_9973.08388c9a.jpg",bs="/assets/IMG_9974.1035f643.jpg",hs="/assets/IMG_9976.6eb7a1bc.jpg",ks="/assets/IMG_9977.e0efe6d6.jpg",us="/assets/IMG_9978.1876301d.jpg",ws="/assets/IMG_9979.74afe8f1.jpg",xs="/assets/IMG_9980.e18fc3c8.jpg",qs="/assets/IMG_9981.2b1c1a8e.jpg",Ts="/assets/IMG_9982.3a09c8a1.jpg",ys="/assets/IMG_9983.be3a31a4.jpg",Ss="/assets/IMG_9985.a79241c7.jpg",Cs="/assets/IMG_0152.dd047efc.jpg",Ps="/assets/IMG_0153.57f69237.jpg",$s="/assets/IMG_0154.c3553a11.jpg",vs="/assets/IMG_0155.5871ec9e.jpg",As="/assets/IMG_0156.c2d9ca45.jpg",Ns="/assets/IMG_0157.b864a9fa.jpg",Rs="/assets/IMG_0158.bf96e18c.jpg",Vs="/assets/IMG_0159.f6abd37f.jpg",Bs="/assets/IMG_0188.b4da478e.jpg",Ds="/assets/IMG_0203.1774c392.jpg",Es="/assets/IMG_0204.61907cfc.jpg",Hs="/assets/IMG_0205.3f03a900.jpg",Js="/assets/IMG_0206.f8436547.jpg",st=JSON.parse('{"title":"Творческая мастерская Академия Стекла","description":"","frontmatter":{"title":"Творческая мастерская Академия Стекла","date":"2015-04-20","cover":"IMG_9998.jpg"},"headers":[],"relativePath":"workshop/tour/akademiya-stekla/index.md","filePath":"workshop/tour/akademiya-stekla/index.md"}'),Os={name:"workshop/tour/akademiya-stekla/index.md"},zs=_('<p>Формат открытой мастерской — удел не только крупных и громких дерево- и металлообработки. Мы уже рассказывали об <a href="/workshop/catalog/artefaktorium/" title="Открытая ювелирная мастерская Артефакториум">открытой ювелирной мастерской Артефакториум</a>, где мы узнали еще об одном открытом творческом направлении: <strong>работе со стеклом</strong>. Не откладывая в долгий ящик, мы отправились в <a href="/workshop/catalog/akademiya-stekla/" title="Творческая мастерская Академия стекла">творческую мастерскую &quot;Академия Стекла&quot;</a>, расположенную недалеко от <strong>метро Сокол на Балтийской улице в доме №11</strong>. Чтобы попасть в мастерскую, мы проходим мимо шлагбаума, преграждающего въезд на территорию автомобилей и обходим длинное здание справа. Завернув за угол, в торце здания видим черную дверь за невысоким заборчиком. Заходим внутрь, поднимаемся на второй этаж и мы на месте!</p><p><img src="'+o+'" alt=""><img src="'+r+'" alt=""><img src="'+c+'" alt=""><img src="'+g+'" alt=""><img src="'+p+'" alt=""></p><p>Войдя внутрь, мы попадаем в длинный коридор, соединяющий различные помещения Академии Стекла. Если быть точными, здесь под одной крышей собраны сразу несколько тесно переплетённых проектов. Первый справа проход ведёт нас в <a href="http://www.hightidespace.com/about/" class="external-link" target="_blank" rel="noreferrer">галерею High Tide</a>, где на полках и в витринах собраны избранные творческие результаты, созданные мастерами в стенах Академии, а также предоставленные другими виртуозами стекольного и ювелирного дела. В представленных украшениях и авторских композициях сочетаются и переплетаются различные техники работы со стеклом, керамикой, металлом и другими материалами, создавая причудливые образы и неожиданно натуралистичные формы. Мастера черпают своё вдохновение во внимательном наблюдении за природой, смело экспериментируют с техниками и технологиями. Галерея — пространство для вдумчивого изучения готовых изделий, проведения небольших презентаций, непринуждённого общения. Посетители могут сразу приобрести понравившиеся экземпляры.</p><p><img src="'+i+'" alt=""><img src="'+m+'" alt=""><img src="'+e+'" alt=""><img src="'+n+'" alt=""><img src="'+l+'" alt=""><img src="'+I+'" alt=""><img src="'+j+'" alt=""><img src="'+G+'" alt=""><img src="'+M+'" alt=""><img src="'+d+'" alt=""><img src="'+f+'" alt=""><img src="'+b+'" alt=""><img src="'+h+'" alt=""><img src="'+k+'" alt=""><img src="'+u+'" alt=""></p><p>Академия стекла — нынешний этап сложного творческого пути Марины и Анастасии Прибельских, начинавших с создания авторских кукол в рамках проекта <a href="http://www.glassacademyrussia.com/rowancraft/" class="external-link" target="_blank" rel="noreferrer"><strong>Rowan Craft</strong></a> и решивших не останавливаться в саморазвитии, продолжать осваивать всё новые техники и совершенствовать имеющиеся умения. Глубокие наблюдения за живой природой послужили основой для разработки тонкого авторского подхода к воссозданию отдельных живых существ и целых экосистем из сочетания различных глин, пластиков, красок и других материалов. В какой-то момент поиск новых выразительных средств привёл мастериц к <strong>лэмпворку</strong> — плавке и формованию стеклянных изделий в пламени газовой горелки — бесконечному полю для развития и всевозможных творческих экспериментов. Так формировалась Академия Стекла — поначалу как личная мастерская Марины, Анастасии и других мастеров, постепенно узнававших и присоединявшихся к проекту. Обучение в формате отдельных <strong>мастер-классов, продолжительных курсов и даже обучающих походов</strong> всегда было неотъемлемой составляющей проекта Rowan Craft и по мере освоения техники лэмпворка мастерская становилась все более открытой и направленной на обучение всех заинтересованных.</p><p><img src="'+w+'" alt=""><img src="'+x+'" alt=""><img src="'+q+'" alt=""><img src="'+T+'" alt=""><img src="'+y+'" alt=""><img src="'+S+'" alt=""><img src="'+C+'" alt=""><img src="'+P+'" alt=""><img src="'+$+'" alt=""><img src="'+v+'" alt=""><img src="'+A+'" alt=""><img src="'+N+'" alt=""><img src="'+R+'" alt=""><img src="'+V+'" alt=""><img src="'+B+'" alt=""><img src="'+D+'" alt=""><img src="'+E+'" alt=""></p><p>Сразу при входе в галерею, слева есть дверь, ведущая в узкую комнату с длинным столом, предназначенным для финальной сборки изделий из стекла и работы с полимерными и металлическими глинами. За длинным столом свободно размещаются <strong>5—6 человек</strong> и получают возможность погрузиться в тонкости различных творческих техник и технологий. Лепка из всевозможных глин и пластиков — одно из приоритетных направлений, но это далеко не всё. Среди прочего здесь можно научиться резьбе по керамике, гравировке и печати в технике офорта, созданию цветочных композиций из шёлка или специальных пластических составов. В закромах мастерской найдётся множество всевозможных инструментов, а основные материалы можно приобрести прямо здесь же.</p><p>В дальнем углу галереи можно увидеть ещё одну дверь, ведущую в малую мастерскую. Это небольшое помещение, оборудованное двумя газовыми горелками и электрической печкой и наполненное огромным количеством различных глин, красок и всевозможного стекла. Во время мастер-классов по работе с металлическими глинами в этой мастерской происходит обжиг вылепленных изделий, а когда здесь не проводится никаких событий комната играет роль личной мастерской хозяек Академии.</p><p><img src="'+H+'" alt=""><img src="'+J+'" alt=""><img src="'+O+'" alt=""><img src="'+z+'" alt=""><img src="'+F+'" alt=""><img src="'+K+'" alt=""><img src="'+L+'" alt=""><img src="'+Q+'" alt=""><img src="'+U+'" alt=""></p><p>Вернёмся в общий коридор и повернём в следующую после входа в галерею дверь — мы окажемся в основном рабочем помещении Академии. Это лэмпворк-мастерская с шестью полностью оборудованными рабочими местами. Здесь есть всё необходимое для свободного творчества и для проведения всевозможных обучающих мероприятий. Давайте осмотримся повнимательнее. Мы увидим два больших стеллажа заполненных множеством полезностей. За время существования Академии здесь накопилось большое количество редких книг, учебников и каталогов по работе со стеклом — вся эта <strong>библиотека доступна для каждого резидента</strong> мастерской. Уложенный в картонные тубы здесь хранится внушительный <strong>запас дротов</strong>, стеклянных стержней, <strong>и фритов</strong>, мелкой цветной стеклянной крошки, которые можно приобрести прямо на площадке. Стекло, как и книги, поступают не только от местных поставщиков, но и привозятся хозяйками мастерской из разных уголков нашей планеты, в которых они оказываются в рамках своих регулярных путешествий. В своих поездках мастерицы посещают мастерские художников и активно собирают новые знания, умения и редкие материалы. Они <strong>приглашают всех желающих отправиться в поездку</strong> вместе с ними, а по возвращении делятся впечатлениями и полученным опытом в форме новых увлекательных мастер-классов.</p><p><img src="'+W+'" alt=""><img src="'+X+'" alt=""><img src="'+Y+'" alt=""><img src="'+Z+'" alt=""><img src="'+ss+'" alt=""><img src="'+ts+'" alt=""><img src="'+as+'" alt=""><img src="'+_s+'" alt=""><img src="'+os+'" alt=""><img src="'+rs+'" alt=""><img src="'+cs+'" alt=""><img src="'+gs+'" alt=""></p><p>Каждому резиденту мастерской выделяется место в зоне общего хранения. В картонных тубусах сложены сотни <strong>стеклодротов</strong> и специфических инструментов. Некоторые из них подписаны, некоторые — нет, но путаницы не возникает — мастера просто запоминают что и куда положили и, конечно же, никогда не позволят себе взять чужое. Личные, сложные и особенно ценные инструменты хранятся отдельно и чтобы ими воспользоваться нужно обратиться к хозяйке мастерской или непосредственно к владельцу инструмента. В Академии стекла сложилась интересная практика маркировки личных инструментов: у каждого мастера есть <strong>свой личный цвет</strong>, которым помечены все его персональные приспособления. Для маркировки используется специальная термостойкая эмаль и такие условные обозначения несут множество плюсов. Хоть нигде и нет официальной таблицы персональных цветов, на первом же занятии вводного курса для резидентов и гостей мастерской сообщают об этих соответствиях и в последствии все вопросы собственности на инструменты решаются быстро и просто. Тем более, что подавляющее <strong>большинство инструментов — общественные и доступны каждому</strong>. А если чего-то не хватает — всегда можно попросить поделиться. Дружелюбная творческая атмосфера создает благоприятную среду для конструктивного общения и взаимовыручки: здесь всегда есть к кому обратиться за советом или помощью.</p><p><img src="'+ps+'" alt=""><img src="'+is+'" alt=""><img src="'+ms+'" alt=""><img src="'+es+'" alt=""><img src="'+ns+'" alt=""><img src="'+ls+'" alt=""><img src="'+Is+'" alt=""><img src="'+js+'" alt=""><img src="'+Gs+'" alt=""><img src="'+Ms+'" alt=""><img src="'+ds+'" alt=""></p><p>Из чего же состоит рабочее место для лэмпворка? Нетрудно догадаться, что начинается оно с &quot;лампы&quot; — <strong>газовой горелки</strong>, работающей на смеси обычного бытового газа <strong>пропана</strong>, поступающего из баллонов, стоящих в отдельном помещении, <strong>и кислорода</strong>, получаемого на месте с помощью <strong>концентратора</strong>, такое устройство есть под каждым рабочим местом мастерской. Пропан и кислород, смешиваясь в горелке, дают ровное интенсивное пламя с температурой <strong>свыше 1300°С</strong>. Для удаления избыточного тепла и продуктов горения к каждой горелке подведена труба мощной <strong>вытяжки</strong>, а окна помещения оборудованы системой <strong>приточной вентиляции</strong>. У каждой горелки под трубой вытяжки есть специальная миска с водой, необходимой для резкого охлаждения стекла в некоторых технологических операциях. Рядом с каждой горелкой лежат в два слоя <strong>керамические одеяла</strong>, между которыми помещаются остывающие изделия. Теплоизолирующие свойства этих матов предотвращают растрескивание стекла из-за большой разницы в температуре внешних и внутренних слоёв материала. Также для каждого резидента мастерской доступен запас специальных металлических стержней — <strong>&quot;мандрелей&quot;</strong> — разного диаметра и формы наконечника. Мандрели выполняют роль оси, на которую накручивается стекольная масса и в последствии определяют диаметр отверстия в бусине или кольце. Для того, чтобы готовые изделия можно было отделить от оси, мандрели покрывают специальным составом — <strong>сепаратором</strong>, похожим на жидкую глину. Высыхая, этот состав оставляет стойкую к температуре, но водорастворимую плёнку, благодаря чему окончательно остывшие бусины снимаются со стержней под струей воды. Каждому резиденту мастерской доступны еще множество различных приспособлений от специальных графитовых площадок, на которых раскатывают и формируют бусины до всевозможных пинцетов, щипцов и лопаток. В мастерской на всех хватит и специальных очков с фиолетово-синими стёклами, блокирующими мешающие точной работе желтый и оранжевый цвета пламени. Очки доступны в оправах различных фасонов на выбор. В центре мастерской расположена общая <strong>муфельная печь для отжига изделий</strong>, поддерживающая в течение дня постоянную температуру в <strong>510°С</strong>, а ночью плавно остывающую до комнатной температуры — надёжное средство для предотвращения растрескивания стекла из-за неравномерного остывания. В случае если бусина все-таки треснула или совсем не хочет сниматься с мандрели — её нагревают в печи и резко погружают в холодную воду: стекло мгновенно растрескивается и легко осыпается, а полученные осколки нередко используются в дальнейших творческих экспериментах.</p><p><img src="'+fs+'" alt=""><img src="'+bs+'" alt=""><img src="'+hs+'" alt=""><img src="'+ks+'" alt=""><img src="'+us+'" alt=""><img src="'+ws+'" alt=""><img src="'+xs+'" alt=""><img src="'+qs+'" alt=""><img src="'+Ts+'" alt=""><img src="'+ys+'" alt=""><img src="'+Ss+'" alt=""></p><p>Хозяйки мастерской внимательно относятся к каждому новому гостю и к каждому мастеру-резиденту, всегда готовы помочь советом, подсказать решение творческих задач. Они регулярно проводят групповые и индивидуальные курсы по основам лэмпворка для начинающих, а также мастер-классы по продвинутым техникам для уже практикующих мастеров. Свои авторские курсы представляет сразу целая команда мастеров. Есть возможность выгодно арендовать мастерскую для проведения своих обучающих событий.</p><p>Марина и Анастасия играючи познакомили меня с основными аспектами практики лэмпворка, продемонстрировали все основные этапы работы со стеклом и дали возможность сделать свою первую <strong>стеклянную бусину самостоятельно</strong>. Процесс работы с горелкой — действительно завораживающее действо! Лэмпворк — замечательная технология, с невероятным творческим потенциалом. Я уже задумал один стеклянный проект и, надеюсь, вместе с мастерской &quot;Академия Стекла&quot; мы сможем реализовать мои смелые фантазии. Постараемся максимально подробно рассказать о нашем дальнейшем сотрудничестве, а также сформировать четкие инструкции к полученным навыкам и умениям.</p><p><img src="'+Cs+'" alt=""><img src="'+Ps+'" alt=""><img src="'+$s+'" alt=""><img src="'+vs+'" alt=""><img src="'+As+'" alt=""><img src="'+Ns+'" alt=""><img src="'+Rs+'" alt=""><img src="'+Vs+'" alt=""><img src="'+Bs+'" alt=""><img src="'+Ds+'" alt=""><img src="'+Es+'" alt=""><img src="'+Hs+'" alt=""><img src="'+Js+'" alt=""></p>',18),Fs=[zs];function Ks(Ls,Qs,Us,Ws,Xs,Ys){return t(),a("div",null,Fs)}const tt=s(Os,[["render",Ks]]);export{st as __pageData,tt as default};
