import{_ as s,o as t,c as a,e as o}from"./chunks/framework.7WwFwkJk.js";const r="/assets/IMG_0083.fRChuYXE.jpg",_="/assets/IMG_0152.fRJWsUQw.jpg",p="/assets/IMG_0076.dKT8anz7.jpg",i="/assets/IMG_0060.Jg4C7xC4.jpg",e="/assets/IMG_0003.R-gYUBI5.jpg",c="/assets/IMG_0097.I-zo-CV-.jpg",m="/assets/IMG_0037.uYXx5Hvn.jpg",g="/assets/IMG_0047.XvnW2KPw.jpg",l="/assets/IMG_0032.x2lcmSzY.jpg",n="/assets/IMG_0018.CJk1bZgG.jpg",u="/assets/IMG_9994.zFM--6ke.jpg",M="/assets/IMG_0006.2FW8yVRR.jpg",G="/assets/IMG_0008.LjoldEZh.jpg",I="/assets/IMG_0175.L4Qp3nwM.jpg",q="/assets/IMG_0088.ovsRGl40.jpg",j="/assets/IMG_0086.LulGSbu3.jpg",h="/assets/IMG_0066.6OBC1Rlo.jpg",d="/assets/IMG_0017.K3lcNvQY.jpg",f="/assets/IMG_0171.iNJuXt-q.jpg",x="/assets/IMG_0087.eavRWVqn.jpg",b="/assets/IMG_0077.rnMgIHOD.jpg",k="/assets/IMG_0061.mlOUtsfL.jpg",P="/assets/IMG_0016.fbhARq9O.jpg",S="/assets/IMG_0069.xeDeIqjT.jpg",w="/assets/IMG_0071.7xJUdcTm.jpg",R="/assets/IMG_0035.UY8Gu5ta.jpg",V="/assets/IMG_0091.s_mEyMax.jpg",Y="/assets/IMG_0048.TOq01PRr.jpg",C="/assets/IMG_0049.Swd8rz1X.jpg",T="/assets/IMG_9996.SBZUdgoK.jpg",D="/assets/IMG_9998.huC1U6EC.jpg",v="/assets/IMG_0156.p091eGu0.jpg",z="/assets/IMG_0158.9Sy-1641.jpg",B="/assets/IMG_0174.Raz7693h.jpg",N="/assets/IMG_0142.oYTOH4z2.jpg",O="/assets/IMG_0126.cFszX8lC.jpg",U="/assets/IMG_0133.LuMcSnYN.jpg",X="/assets/IMG_0115.pH8fdLZ5.jpg",L="/assets/IMG_0119.tajBWyf2.jpg",Z="/assets/IMG_0121.aPKoGYZ8.jpg",y="/assets/IMG_0123.3prIoTRu.jpg",E="/assets/IMG_0127.DMuANVxi.jpg",F="/assets/IMG_0098.VeFlqOLH.jpg",H="/assets/IMG_0101.gqY8kce6.jpg",J="/assets/IMG_0106.qw77q2Nn.jpg",K="/assets/IMG_0109.DiVi2pvt.jpg",A="/assets/IMG_0113.2dQdBYts.jpg",W="/assets/IMG_0011.2wSszV5X.jpg",Q="/assets/IMG_0043.9M94XbKV.jpg",$="/assets/IMG_0044.39ZBCp2Y.jpg",ss="/assets/IMG_0045.5huY5BPa.jpg",ts="/assets/IMG_0053.tDMsrG3D.jpg",as="/assets/IMG_0063.4i6ZySVo.jpg",os="/assets/IMG_0081.oN7mU7VS.jpg",Ms=JSON.parse('{"title":"Преображение общественной мастерской в ЦМИТ \\"Дар Труда\\"","description":"","frontmatter":{"title":"Преображение общественной мастерской в ЦМИТ \\"Дар Труда\\"","date":"2016-04-22","cover":"IMG_9994.jpg"},"headers":[],"relativePath":"workshop/tour/dar-to-cmit/index.md","filePath":"workshop/tour/dar-to-cmit/index.md"}'),rs={name:"workshop/tour/dar-to-cmit/index.md"},_s=o('<p>Общественная мастерская, производственный коворкинг, а теперь и Центр молодёжного инновационного творчества &quot;Дар Труда&quot; — стремительно развивающийся московский проект, растущий прямо на наших глазах. Маленькая столярная мастерская в подвале на Чистых прудах, просторный цех в здании бывшего НИИ и теперь уже целый комплекс производственных и образовательных проектов реализует широкий спектр разнообразных услуг.</p><p>Каждый этап развития — очередной вызов для команды проекта, требующий ответного усложнения внутренних процессов и самой организации мастерской. Небольшую открытую мастерскую может поддерживать и один мастер, инструменты в паре комнат в подвале можно помнить наизусть, а общение с посетителями может быть совершенно неформальным, однако развитие может потребовать определённых перемен и введения организационных новшеств. Команда &quot;Дара Труда&quot; постоянно исследует возможности развития проекта и старается расширять диапазон оказываемых услуг, интенсивно используя имеющиеся ресурсы.</p><p>Сочетая работу в формате коворкинга с производством различных столярных изделий на заказ, общественная мастерская всегда была точкой соединения различных направлений под общей крышей. Правильное сочетание и объединение разных модулей в единую систему — одна из важнейших задач организатора открытой мастерской. Команда &quot;Дара Труда&quot; выявила и выделила три основных направления своей деятельности, назвав их столярный коворкинг, учебный центр и отдел заказов и определив для них подходящее пространство и время. Отдел заказов обзавёлся новым современным оборудованием и обособился в углу нового профессионального цеха, общественное рабочее пространство теперь занимает всю площадь первого яруса основного цеха, а все эти помещения, где шесть дней в неделю работают над своими проектами коворкеры и резиденты мастерской, теперь каждое воскресенье превращаются в обширное образовательное пространство, вмещающее по несколько мастер-классов и занятий курсов одновременно. Но обо всём по порядку.</p><p><img src="'+r+'" alt=""><img src="'+_+'" alt=""><img src="'+p+'" alt=""><img src="'+i+'" alt=""><img src="'+e+'" alt=""></p><h3 id="главныи-цех" tabindex="-1">Главный цех <a class="header-anchor" href="#главныи-цех" aria-label="Permalink to &quot;Главный цех&quot;">​</a></h3><p>Со времени нашего прошлого <a href="/workshop/tour/dar-truda-start/">обзора мастерской &quot;Дар Труда&quot;</a>минуло чуть больше года и здесь многое изменилось с тех пор. Небольшая надстройка над входом, где хранились материалы мастерской, разрослась до полноценного второго яруса пространства, увеличив его полезную площадь почти в два раза. Два пролёта деревянных перекрытий на каркасе из бруса помогли серьезно улучшить зонирование пространства. Все отсеки мастеров-резидентов переместились на второй ярус, раздевалка и кухня переехали в самый конец цеха, а весь первый ярус стал единым общественным рабочим пространством. &quot;Дару Труда&quot; удалось так радикально расшириться, но при этом не потерять в комфортности помещения. Вертикальные доски перекрытий оставили незашитыми и их стройные ряды создают ощущение пространства над головой. Отдельно стоит отметить яркое светодиодное освещение и размещение на колоннах электрических розеток, огнетушителей и информационных материалов, что сделало эту надстройку ещё более уместной. Часть пространства второго яруса уже заселена резидентами, занимающимися производством мебели, гитар и других деревянных изделий, а свободные площади используются для расширения зоны коворкинга и проведения различных мастер-классов.</p><p><img src="'+c+'" alt=""><img src="'+m+'" alt=""><img src="'+g+'" alt=""><img src="'+l+'" alt=""><img src="'+n+'" alt=""><img src="'+u+'" alt=""></p><p>В середине цеха осталось незакрытое вторым ярусом пространство — здесь расположилась основная зона коворкинга со столами для размещения и работы посетителей, а также шкафом для хранения их проектов. Часы на колонне и центральное расположение этой зоны делают её похожей на городскую площадь, наполненную постоянной активностью. Сюда спускаются две довольно крутые, но вполне удобные лестницы, под которыми устроено пространство для хранения материалов и различных обрезков, а также разместился воздушный компрессор, шланги от которого тянутся в разные зоны мастерской.</p><p>По обе стороны от центральной площадки расположились две зоны станков, наполненные мощным столярным оборудованием. Парк инструментов за это время существенно расширился. В дополнение к большому форматно-раскроечному станку в зале появился большой циркулярный станок Felisatti. Рядом с ним стоит новый фуговально-рейсмусовый станок Hammer, а напротив красуется большой и серьезный ленточно-пильный станок Felder. Появился новый фрезерный станок, а сделанный своими руками фрезерный стол получил массу усоврешенствований. Несмотря на то, что к станкам подключены новые мощные стружкоотсосы, борьба с распространением пыли в столярной мастерской ещё только начата.</p><p><img src="'+M+'" alt=""><img src="'+G+'" alt=""><img src="'+I+'" alt=""><img src="'+q+'" alt=""><img src="'+j+'" alt=""><img src="'+h+'" alt=""><img src="'+d+'" alt=""></p><p>За дальней лестницей устроен склад листовых материалов, а также место хранения крупных проектов коворкеров, а напротив стойки администратора расположились ящики и стеллажи для сортировки производственных отходов. Мешки для чистых опилок, отдельные ящики для обрезков сосны, твёрдых пород и листовых пиломатериалов позволяют мастерской наиболее эффективно использовать имеющиеся ресурсы. Опилки по 6-8 мешков в неделю забирают садоводы и животноводы, а многие обрезки становятся источниками вдохновения и используются начинающими умельцами для тренировки и проверки производственных решений.</p><p><img src="'+f+'" alt=""><img src="'+x+'" alt=""><img src="'+b+'" alt=""><img src="'+k+'" alt=""><img src="'+P+'" alt=""><img src="'+S+'" alt=""></p><h3 id="коммуникация" tabindex="-1">Коммуникация <a class="header-anchor" href="#коммуникация" aria-label="Permalink to &quot;Коммуникация&quot;">​</a></h3><p>Существенные изменения произошли в сфере коммуникации мастерской с её посетителями. Все площадки пространства получили свои номера, пронумерованы все полки обозначенных буквами шкафов, а вся необходимая оснастка станков наглядно располагается рядом с ними на специально размеченных стендах. На стенде в самом центре мастерской расположились постоянно требующиеся для настройки оборудования ключи и приспособления, а также средства индивидуальной защиты и уборочный инвентарь. На стенах появились плакаты по безопасной работе в столярной мастерской, всё больше информации можно обнаружить на доске объявлений рядом с входом. Материалы и заготовки посетителей, хранящиеся в мастерской, отмечены специальными наклейками, а серьезные станки обзавелись крупными стикерами с обозначением необходимого для работы с ними уровня допуска.</p><p><img src="'+w+'" alt=""><img src="'+R+'" alt=""><img src="'+V+'" alt=""><img src="'+Y+'" alt=""><img src="'+C+'" alt=""><img src="'+T+'" alt=""><img src="'+D+'" alt=""></p><p>На данный момент в мастерской можно выделить 4 основных уровня допуска: базовый, основные станки, &quot;1&quot; и &quot;0&quot;. Каждый посетитель получает базовый уровень допуска, дающий возможность использовать только простые ручные и электроинструменты. Чтобы использовать основные станки понадобится пройти за одни выходные вводный курс, состоящий из двух занятий. Для допуска к высокопроизводительным профессиональным станкам понадобится отдельная аттестация на уровень &quot;1&quot;. Уровень &quot;0&quot; обозначает что к оборудованию имеют допуск только сотрудники мастерской и работа с ним производится в форме заказных услуг.</p><h3 id="стоика-администратора" tabindex="-1">Стойка администратора <a class="header-anchor" href="#стоика-администратора" aria-label="Permalink to &quot;Стойка администратора&quot;">​</a></h3><p>С расширением мастерской расширился и комплекс рабочих обязанностей администратора, который удачно сформулирован в виде должностной инструкции, описывающей распорядок дня и список его обязанностей. Этот текст мы использовали как основу при составлении соответствующего раздела нашего <a href="/theory/handbook/">Руководства по устройству и организации устойчивых открытых мастерских</a>. Должностная инструкция расположена на стойке администратора в папке вместе с описанием тарифов и графиком работы мастерской. Работу в общем пространстве можно оплачивать по часам, дням и месяцам, обзавестись своим персональным отсеком можно в двух вариантах — &quot;Мастер&quot; и &quot;Профи&quot;, отличающихся размером персональной ячейки и количеством допущенных в мастерскую человек. Мастер может привести с собой одного помощника, а тариф &quot;Профи&quot; допускает присутствие до трёх человек одновременно. Отдельно оплачивается хранение заготовок посетителей на складе, доступ в покрасочную комнату, также предусмотрена дополнительная тарификация для тех, кому нужно просто много &quot;пилить&quot; из расчёта на погонные метры пила или &quot;строгать&quot; из расчёта на баки опилок.</p><p><img src="'+v+'" alt=""><img src="'+z+'" alt=""><img src="'+B+'" alt=""><img src="'+N+'" alt=""></p><p>Помимо папки с информационными материалами на стойке администратора располагаются несколько важных для устойчивой работы журналов и тетрадей:</p><ul><li>журнал открытия-закрытия мастерской с указанием времени и ФИО пришедшего в мастерскую первым и последним покидающего её с комментариями о состоянии пространства,</li><li>тетрадь поломок и ремонта, служащая средством общения коллектива мастерской с регулярно приходящим техником-ремонтником, занимающимся наладкой и восстановлением инструментов и оборудования,</li><li>журнал прохождения инструктажа по технике безопасности содержит подписи всех посетителей, использующих возможности пространства.</li></ul><h3 id="профессиональныи-цех" tabindex="-1">Профессиональный цех <a class="header-anchor" href="#профессиональныи-цех" aria-label="Permalink to &quot;Профессиональный цех&quot;">​</a></h3><p>&quot;Дар Труда&quot; расширился не только ввысь за счёт надстройки второго яруса, но и вширь, арендовав ещё одно большое помещение в соседнем корпусе бывшего НИИДАРа. Новое пространство стало цехом для профессиональной работы столяров над крупными проектами: здесь стоят только самые необходимые высокоэффективные станки и есть достаточно места для сборки корпусной мебели и других крупногабаритных заказов. В распоряжении коворкеров здесь находится большой форматно-раскроечный станок, торцовочная пила, компрессор, серьезные рейсмусный и фуговальные станки, а также различный ручной электроинструмент.</p><p><img src="'+O+'" alt=""><img src="'+U+'" alt=""><img src="'+X+'" alt=""><img src="'+L+'" alt=""><img src="'+Z+'" alt=""><img src="'+y+'" alt=""></p><h3 id="отдел-чпу-станков-и-лазеров" tabindex="-1">Отдел ЧПУ-станков и лазеров <a class="header-anchor" href="#отдел-чпу-станков-и-лазеров" aria-label="Permalink to &quot;Отдел ЧПУ-станков и лазеров&quot;">​</a></h3><p>Часть профессионального цеха отделена перегородками, формирующими помещения цифрового отдела мастерской. &quot;Дар Труда&quot; с самого своего основания активно использовал небольшой лазерный гравер для изготовления заказов, а после получения от государства вместе с официальным статусом Центра молодёжного инновационного творчества (ЦМИТ) значительных инвестиций, парк ЧПУ оборудования мастерской стал намного разнообразнее. В небольшом помещении из двух комнат без устали трудятся большеформатные лазерный и фрезерный станки, налаживается работа ещё одного ЧПУ-фрезера и специального принтера для ультрафиолетовой печати. Мастерская установила <a href="http://www.dartruda.ru/" class="external-link" target="_blank" rel="noreferrer">на своём сайте</a> удобные калькуляторы для расчёта стоимости заказа, делая доступ к этому оборудованию вполне прозрачным. Уже сегодня здесь проходят курсы по самостоятельной работе на лазерном станке, со временем аналогичные курсы планируются по ЧПУ-фрезеровке и печати на 3D-принтере.</p><p><img src="'+E+'" alt=""><img src="'+F+'" alt=""><img src="'+H+'" alt=""><img src="'+J+'" alt=""><img src="'+K+'" alt=""><img src="'+A+'" alt=""></p><h3 id="образовательная-программа" tabindex="-1">Образовательная программа <a class="header-anchor" href="#образовательная-программа" aria-label="Permalink to &quot;Образовательная программа&quot;">​</a></h3><p>Выделение отдельного учебного дня, когда никто кроме преподавателей и учеников не может работать в мастерской, создало прозрачную среду для разработки и внедрения новых образовательных проектов. Регулярно и по известному расписанию проводится обучение основам работы в мастерской: работа на станках, отдельные классы по работе на фрезерном и токарном станках, лазерном гравере.</p><p>Есть возможность посетить разовые мастер-классы по различным техникам работы с деревом, поучаствовать в семейных воркшопах в формате “Папа—сын” или записаться на более продолжительные курсы с глубоким погружением в столярное дело. Бывают здесь и занятия по несложным техникам, рассчитанные на добровольную оплату.</p><p><img src="'+W+'" alt=""><img src="'+Q+'" alt=""><img src="'+$+'" alt=""><img src="'+ss+'" alt=""><img src="'+ts+'" alt=""><img src="'+as+'" alt=""><img src="'+os+'" alt=""></p><h3 id="резюме" tabindex="-1">Резюме <a class="header-anchor" href="#резюме" aria-label="Permalink to &quot;Резюме&quot;">​</a></h3><p>Пройдя значительный путь развития в сравнительно короткие сроки, открытая мастерская &quot;Дар Труда&quot; на данный момент — не только одна из первых, но и самая крупная и продвинутая общественная мастерская Москвы и, пожалуй, всей нашей страны. Производственно-образовательная площадка, доступная как для только начинающих свой путь в столярке, так и для профессиональных столяров, реализующих свои собственные творческие проекты, заказы и небольшие серии изделий на продажу. Диверсификация и сочетание отдельных модулей в единой системе делает её понятной и создаёт множество возможностей для проявления синергетического эффекта. Один и тот же человек может работать в коворкинге по абонементу, выполнять заказы мастерской, получая свою долю, проводить курсы, посещать различные мастер-классы, а также использовать возможности 3D-печати, цифровой гравировки и фрезерования в процессе прототипирования и производства столярных изделий.</p><p>&quot;Дар Труда&quot; — это не просто общественная мастерская, но целая производственная экосистема, предоставляющая массу разнообразных возможностей для реализации самых смелых идей. Безусловно, система ещё далека от совершенства, процесс активного переустройства на новом этапе ещё далёк от завершения, однако темпы развития мастерской внушают уверенность в успешности выбранного пути. Мы продолжим активно следить за развитием этого проекта, делясь своими наблюдениями со всеми, кто заинтересован в развитии открытых мастерских в России и мире.</p>',34),ps=[_s];function is(es,cs,ms,gs,ls,ns){return t(),a("div",null,ps)}const Gs=s(rs,[["render",is]]);export{Ms as __pageData,Gs as default};
