import{_ as s,o as t,c as _,b as a}from"./chunks/framework.6c3c27db.js";const o="/assets/IMG_9758.16dbe622.jpg",p="/assets/IMG_9763.e9186ab3.jpg",c="/assets/IMG_9764.d7dc57ca.jpg",r="/assets/IMG_9767.8004f753.jpg",e="/assets/IMG_9768.5c6abe85.jpg",i="/assets/IMG_9771.7ae755a2.jpg",m="/assets/IMG_9772.07191a8a.jpg",g="/assets/IMG_9773.bf231871.jpg",n="/assets/IMG_9775.73046517.jpg",l="/assets/IMG_9776.f1636096.jpg",I="/assets/IMG_9777.22d6f19a.jpg",j="/assets/IMG_9778.06c0749a.jpg",G="/assets/IMG_9779.7dc5be0b.jpg",M="/assets/IMG_9780.257aeea6.jpg",d="/assets/IMG_9782.689e2b0d.jpg",b="/assets/IMG_9783.1ece376e.jpg",f="/assets/IMG_9785.b50e2fc8.jpg",u="/assets/IMG_9786.40873a8e.jpg",q="/assets/IMG_9787.c7dfbf45.jpg",h="/assets/IMG_9788.dbb1912d.jpg",k="/assets/IMG_9789.789b2bd4.jpg",x="/assets/IMG_9790.6466a4f7.jpg",v="/assets/IMG_9795.6ab2a99b.jpg",w="/assets/IMG_9796.119bbe3c.jpg",S="/assets/IMG_9797.7dd8a761.jpg",T="/assets/IMG_9798.7fe6f920.jpg",P="/assets/IMG_9799.df155ccb.jpg",$="/assets/IMG_9800.7f6400c9.jpg",y="/assets/IMG_9802.88c70b9b.jpg",A="/assets/IMG_9803.955182fc.jpg",D="/assets/IMG_9804.67d979ff.jpg",N="/assets/IMG_9805.14b86027.jpg",V="/assets/IMG_9806.ebcbdb55.jpg",B="/assets/IMG_9807.2b05b3f7.jpg",C="/assets/IMG_9808.ced2bd8c.jpg",E="/assets/IMG_9809.bb033c18.jpg",J="/assets/IMG_9810.c9b3c9aa.jpg",O="/assets/IMG_9826.16dae3c0.jpg",R="/assets/IMG_9827.30a5fc15.jpg",Y="/assets/IMG_9828.7ad14e0c.jpg",z="/assets/IMG_9829.327464f0.jpg",F="/assets/IMG_9830.73b8552b.jpg",H="/assets/IMG_9811.abc652bb.jpg",K="/assets/IMG_9812.b8a77548.jpg",L="/assets/IMG_9813.8a183ca2.jpg",Q="/assets/IMG_9815.ee264534.jpg",U="/assets/IMG_9816.2c23e401.jpg",W="/assets/IMG_9817.ada2c4f6.jpg",X="/assets/IMG_9818.841f3dce.jpg",Z="/assets/IMG_9819.ecb04541.jpg",ss="/assets/IMG_9820.5e3d4f5c.jpg",ts="/assets/IMG_9821.39eac999.jpg",_s="/assets/IMG_9822.7ff1feb8.jpg",as="/assets/IMG_9823.b41ec1da.jpg",os="/assets/IMG_9824.0d298065.jpg",ps="/assets/IMG_9831.d8883b6f.jpg",cs="/assets/IMG_9834.f3ab0dd1.jpg",rs="/assets/IMG_9838.941f4bf0.jpg",es="/assets/IMG_9839.a0eb445c.jpg",is="/assets/IMG_9840.22095904.jpg",ms="/assets/IMG_9841.716d5ca2.jpg",gs="/assets/IMG_9843.2d879e5a.jpg",ns="/assets/IMG_9844.de1a5fbb.jpg",ls="/assets/IMG_9845.30a37fd6.jpg",Is="/assets/IMG_9846.d1239b08.jpg",js="/assets/IMG_9847.8812c63e.jpg",Gs="/assets/IMG_9848.14764882.jpg",Ms="/assets/IMG_9849.f6779b0a.jpg",ds="/assets/IMG_9850.c58063e2.jpg",bs="/assets/IMG_9851.2a456259.jpg",fs="/assets/IMG_9852.ea542af8.jpg",us="/assets/IMG_9853.a99fe421.jpg",qs="/assets/IMG_9854.3894d70c.jpg",hs="/assets/IMG_9855.b377d321.jpg",ks="/assets/IMG_9856.1de5e002.jpg",xs="/assets/IMG_9857.a72d99d9.jpg",vs="/assets/IMG_9858.8ea7e8c5.jpg",ws="/assets/IMG_9862.0605f3c1.jpg",Ss="/assets/IMG_9866.a25613b1.jpg",Ts="/assets/IMG_9868.2f70a831.jpg",Os=JSON.parse('{"title":"Открытая столярная мастерская \\"Делай вещь\\"","description":"","frontmatter":{"title":"Открытая столярная мастерская \\"Делай вещь\\"","date":"2016-01-25","cover":"IMG_9784.jpg","icon":"logo.jpg"},"headers":[],"relativePath":"workshop/tour/om-delay-vesch/index.md","filePath":"workshop/tour/om-delay-vesch/index.md"}'),Ps={name:"workshop/tour/om-delay-vesch/index.md"},$s=a('<p>После изучения всей доступной информации о новой московской открытой столярной мастерской &quot;Делай вещь&quot;, мы всё ещё не получили представления о её внутреннем устройстве. А ведь наполнение пространства и внутренняя механика работы открытой мастерской — наш главный интерес. Значит настало время отправляться в гости.</p><p>Мы связались с одним из основателей мастерской, креативным директором проекта Сергеем Царёвым, и он любезно согласился показать нам мастерскую и рассказать о ней поподробнее. Едем!</p><p>Буквально в трёх минутах ходьбы от метро ул. 1905 года прямо напротив парка Декабрьского восстания стоит длинное серое здание Московской газетной типографии. Звоним по телефону мастерской, получаем пятизначный пин-код, введя который на проходной, получаем карточку для прохода на территорию предприятия. Неожиданно удобная система, наверное, расположенная в этом же здании Студия Артемия Лебедева повлияла. Дойдя по территории до разгрузочного пандуса, находим там дверь с жёлтым треугольником, открывающуюся по той же карточке. Поднимаемся по крутой лестнице на третий этаж и мы почти у цели. Снова желтый треугольник на двери у грузового лифта, за которой узкий коридор, ведущий в общее помещение, соединяющее сразу три мастерских. За приоткрытыми раздвижными дверьми слева расположилось небольшое производство мягкой мебели, а также их соседи — бородатые мебельщики со своей <a href="http://wooddeed.ru/" class="external-link" target="_blank" rel="noreferrer">мастерской Вуд Дид</a>.</p><p><img src="'+o+'" alt=""><img src="'+p+'" alt=""><img src="'+c+'" alt=""><img src="'+r+'" alt=""><img src="'+e+'" alt=""><img src="'+i+'" alt=""><img src="'+m+'" alt=""><img src="'+g+'" alt=""><img src="'+n+'" alt=""></p><p>Отличное соседство для проекта &quot;Делай вещь&quot;, скрывающегося за белой дверью с иллюминатором. Уже здесь в общем вестибюле у лифтов начинаются владения открытой мастерской — запас большеформатной фанеры, разобранных поддонов, а также различных досок и брусков, сложенных на стеллажах и подвешенных на консолях к стенам. Аккуратная система хранения не мешает проходам к грузовым лифтам, разгружая при этом пространство мастерской.</p><p>Войдя, сразу оказываемся в просторном холле, сочетающем сразу несколько функций. Вдоль стены вешалки для верхней одежды, справа рядом с красной телефонной будкой — стол дежурного мастера Виктора Крутелёва. Чуть дальше — стойка регистрации гостей и сотрудников мастерской. Слева от входа расположилась общая кухня, место встреч и непринуждённого общения за чашечкой чая с печеньками. Рядом стоит большой белый стол для переговоров, за которым смогут легко разместиться человек 10-15.  В дальнем углу — флипчарт и большой экран для презентаций, а также старый кабинетный стол — рабочее место Сергея Царёва, все время ведущего какие-то важные переговоры.</p><p><img src="'+l+'" alt=""><img src="'+I+'" alt=""><img src="'+j+'" alt=""><img src="'+G+'" alt=""><img src="'+M+'" alt=""><img src="'+d+'" alt=""><img src="'+b+'" alt=""><img src="'+f+'" alt=""></p><p>В этом светлом холле сразу создаётся ощущение открытости и прозрачности, ведь весь коллектив мастерской готов приветствовать каждого гостя или сотрудника мастерской. Стеклянная стена, открывающая обзор на общественное пространство мастерской только усиливает это ощущение. За стойкой гостей встречает приветливая девушка-администратор Анна. Она подробно рассказала нам о расписании и режиме работы мастерской, а также о разнообразных форматах взаимодействия с ней.</p><p><img src="'+u+'" alt=""><img src="'+q+'" alt=""><img src="'+h+'" alt=""><img src="'+k+'" alt=""><img src="'+x+'" alt=""></p><p>На данный момент открытая мастерская работает в режиме &quot;по запросу&quot;, т.е. желающие поработать над своими проектами, сообщают о своём приезде заранее.  Как правило коворкеры собираются в мастерской вечерами будних дней, занимаясь творческой самореализацией в свободное от основной работы время. По субботам в мастерской регулярно проходят различные однодневные воркшопы &quot;Простые вещи&quot;, позволяющие на целый день погрузиться в творчество даже тем, кто от этого еще очень далёк. Для таких клубных встреч задаётся тема, готовятся все необходимые для творчества материалы и в свободном формате гости мастерской воплощают свои самые смелые тематические проекты под руководством ведущего событие мастера.  По понедельникам в мастерской нередко устраиваются <a href="https://www.facebook.com/delaivesch/posts/730291727104835" class="external-link" target="_blank" rel="noreferrer">мастер-классы для ребят из колледжа №21 для молодых людей с особенностями развития</a>.</p><p><img src="'+v+'" alt=""><img src="'+w+'" alt=""><img src="'+S+'" alt=""><img src="'+T+'" alt=""><img src="'+P+'" alt=""><img src="'+$+'" alt=""><img src="'+y+'" alt=""><img src="'+A+'" alt=""></p><p>Открытая мастерская — невероятно гибкий формат взаимодействия с заказчиками, гостями и всеми желающими поработать руками. Для каждого конкретного проекта здесь готовы подобрать наиболее оптимальный сценарий его реализации от исполнения его мастерской на заказ до полностью самостоятельного воплощения посетителями собственных идей. Для заинтересованных в базовых навыках столярного мастерства здесь регулярно проводится курс &quot;Основы мастерства&quot;, где за 5 занятий главный мастер проекта Вячеслав Швайков передаёт не только базовые столярные техники, но и дизайн-подход в реализации творческих проектов. Новички могут поработать в мастерской и вне курса, но только с персональной поддержкой мастером-ангелом, оплачивая время работы по часам или по дням. Для прошедших вводный курс или доказавших свою квалификацию любителей мастер-ангел уже не требуется, за исключением случаев использования форматно-раскроечного станка или другого серьезного оборудования. Самостоятельную работу в мастерской можно оплачивать по часам, по дням, а можно приобрести абонемент резидента сразу на 5, 10 или 20 рабочих дней (с ограничением в 8 часов работы в день).</p><p>Здесь же на стойке администратора мы увидели списки учёта, где сотрудники мастерской записывают время своего присутствия, а посетители расписываются в получении инструктажа по технике безопасности. Мы оказались в мастерской в первой половине буднего дня и через стеклянную стену мастерской можно было наблюдать несколько человек, увлечённо работающих над мебельными проектами. Как оказалось, это не коворкеры, а полноценные сотрудники и стажёры мастерской, постигающие новые профессии под руководством её основателей. Подробнее о том, как &quot;Делай вещь&quot; помогает бывшим офисным сотрудникам начать новую жизнь в столярном деле, нам рассказал Сергей Царёв.</p><p><img src="'+D+'" alt=""><img src="'+N+'" alt=""><img src="'+V+'" alt=""><img src="'+B+'" alt=""><img src="'+C+'" alt=""><img src="'+E+'" alt=""><img src="'+J+'" alt=""></p><p>В мастерской активно функционирует сразу несколько программ стажировки. Действительно, многие обращаются в &quot;Делай вещь&quot; не просто чтобы заказать или даже сделать самостоятельно какую-то вещь, но с целью попробовать себя в новых сферах деятельности. Курсы и поддержка мастеров — отличное подспорье в обучении, но для того, чтобы начать профессионально работать в столярке этого явно недостаточно. Программа стажировки позволяет плавно погрузиться в бурный рабочий процесс мастерской, достаточно лишь написать мотивационное письмо и пройти собеседование с мастерами проекта. После одного испытательного месяца работы без оплаты, но с возможностью свободно посещать все события мастерской, человек получает возможность стать настоящим подмастерьем с зарплатой и возможностью работать над собственными проектами в свободное время. Стажёры и подмастерья работают над заказами и проектами мастерской командами, гибко координируя свои усилия под руководством мастеров. Основатели проекта уделяют особое внимание возможностям такой &quot;моментальной коммутации&quot;, когда в пространстве мастерской можно в один миг обнаружить не только всё необходимое оборудование и материалы, но и людей, готовых взяться за любые, даже самые смелые проекты. Открытая мастерская в чём-то похожа на биржу, собирающую всевозможные заказы и перераспределяющую их среди независимых творческих команд. В то же время  &quot;Делай Вещь&quot; чем-то напоминает средневековый цех, где ключевую роль всегда имел мастер, принимающий заказы и перераспределяющий работу среди подмастерьев, обучая и воспитывая учеников в совместном труде. В сегодняшней открытой мастерской мастер продолжает играть важную роль.</p><p><img src="'+O+'" alt=""><img src="'+R+'" alt=""><img src="'+Y+'" alt=""><img src="'+z+'" alt=""><img src="'+F+'" alt=""></p><p>Нам удалось побеседовать с мастером Виктором Крутелёвым, одним из основателей проекта, который провёл нам экскурсию по пространству мастерской и рассказал о её становлении от самой идеи до полноценной реализации. Несколько лет назад Виктор понял, что работа в рекламе - это не то, чем ему хотелось бы заниматься  и решил радикально изменить свою жизнь, выбрав для себя профессию столяра. Он пробовал свои силы и нарабатывал столярный опыт в различных проектах под руководством опытного мастера Вячеслава Швайкова в его мастерской &quot;Механические Деревянные Шестерёнки&quot;. Через несколько лет обучения и практики, работы в рамках МДШ и над своими собственными проектами и заказами, пришла уверенность в собственных силах, а также понимание того, что трансформация из офисного сотрудника,  в человека владеющего востребованной профессией - не так уж страшна, как казалось вначале. Такая трансформация подарила ему вдохновение делиться этим опытом, причём на практике. Он уверен, что во многих из тех, кто сегодня задумывается над тем, своим ли делом занимается, просиживая в офисе неделю за неделей, и год за годом, где-то в глубине скрываются перспективные столяры и умелые дизайнеры, которым нужно лишь дать пространство и возможности для того, чтобы, наконец, раскрыться и заняться тем, чем действительно хочется заниматься. В современном столярном деле с предсказуемыми материалами и высокоэффективными электроинструментами все процессы интенсифицируются и вместе с тем ускоряется развитие самого человека. Виктор считает, что за несколько лет человек любого возраста и уровня квалификации вполне может пройти весь путь от ученичества через практику подмастерья и помощника к мастерству, открывающему безграничные возможности в жизни. Дать людям возможность такой трансформации — вот движущая идея создания открытой столярной мастерской &quot;Делай вещь&quot;.</p><p>Для того, чтобы овладеть и уверенно пользоваться всеми базовыми инструментами и приёмами работы в столярной мастерской достаточно и пары лет. Стажёры сначала получают знания по базовым операциям, затем уже в качестве подмастерьев осваивают работу со станками и сложными формами и соединениями. Затем на первый план выходит развитие дизайн-подхода, начинающегося с продумывания проекта и чертежа и заканчивающегося упаковкой готового изделия. Освоив и научившись сочетать различные виды отделки и выработав собственный стиль, в скором времени человек способен преобразиться в мастера, самостоятельно реализующего полный цикл работы над проектом. Основные материалы, используемые при работе над проектами в мастерской — фанера и массив дерева, которые обрабатывают ручным электроинструментом и на различных станках. &quot;Делай вещь&quot; развивает идеи и практики, опробованные её командой в их предыдущем проекте — общественной мастерской DIY Академия. Аккуратные стеллажи с крупными листами фанеры, полки с ручным электроинструментом, небольшие, но эффективные станки. Давайте взглянем на мастерскую повнимательнее.</p><p><img src="'+H+'" alt=""><img src="'+K+'" alt=""><img src="'+L+'" alt=""><img src="'+Q+'" alt=""><img src="'+U+'" alt=""><img src="'+W+'" alt=""><img src="'+X+'" alt=""><img src="'+Z+'" alt=""><img src="'+ss+'" alt=""><img src="'+ts+'" alt=""><img src="'+_s+'" alt=""><img src="'+as+'" alt=""><img src="'+os+'" alt=""></p><p>Светлое пространство мастерской поделено на две основных зоны: обозреваемую через стеклянную стену общественную и производственную. Общественная зона предназначена для работы коворкеров, стажёров и подмастерьев, проведения образовательных и иных событий. Здесь стоят два больших верстака с множеством встроенных ящиков и полочек, с достаточной поверхностью чтобы уверенно работать с крупногабаритными изделиями или разместить множество небольших субботних проектов. Есть компрессор, запас фанеры, ленточная пила, заточной станок, небольшой сверлильный станок, базовый электроинструмент и расходники. Здесь же у окна разместился токарный станок, стамески и заготовки еще одного мастера — француза Лионеля Роя, творящего на нём свои скульптуры и игрушки и с удовольствием обучающего этому искусству участников своих воркшопов. Участие в таких событиях — возможность не только прикоснуться к этому завораживающему ремеслу, но и заодно попрактиковаться в английском языке.</p><p><img src="'+ps+'" alt=""><img src="'+cs+'" alt=""><img src="'+rs+'" alt=""><img src="'+es+'" alt=""><img src="'+is+'" alt=""><img src="'+ms+'" alt=""><img src="'+gs+'" alt=""><img src="'+ns+'" alt=""><img src="'+ls+'" alt=""><img src="'+Is+'" alt=""><img src="'+js+'" alt=""></p><p>Производственная часть мастерской вмещает более сложное оборудование, включая форматно-раскроечный станок с системой сбора стружки, а также фрезерный стол, фуганок, рейсмус, отрезную пилу, циркулярный стол. Установлен довольно большой пресс для фанеровки и изготовления гнутых деталей из дерева. Остаётся масса свободного пространства, где сейчас удобно работать с крупногабаритными заказами, а в будущем появятся еще несколько профессиональных станков.</p><p>Еще в мастерской есть два помещения, одно из которых сейчас используется как временное хранилище проектов, а второе играет одновременно роль покрасочной комнаты и хранилища материалов <a href="/practice/process/second-floor/">на надстроенном втором этаже</a>.  &quot;Делай вещь&quot; использует в отделке преимущественно водные краски и лаки, а также масла и воски, для которых не требуются особые условия нанесения, однако отдельная комната необходима даже хотя бы для сушки окрашенных изделий в помещении без пыли и стружки.</p><p><img src="'+Gs+'" alt=""><img src="'+Ms+'" alt=""><img src="'+ds+'" alt=""><img src="'+bs+'" alt=""><img src="'+fs+'" alt=""><img src="'+us+'" alt=""><img src="'+qs+'" alt=""><img src="'+hs+'" alt=""><img src="'+ks+'" alt=""><img src="'+xs+'" alt=""><img src="'+vs+'" alt=""><img src="'+ws+'" alt=""><img src="'+Ss+'" alt=""><img src="'+Ts+'" alt=""></p><p>Мастерская &quot;Делай вещь&quot; существует всего несколько месяцев, но уже активно развивает множество различных направлений. Команда проекта уверена, что такое пространство может стать прибыльным только если будет предоставлять максимально широкие возможности и сочетать разнообразные формы деятельности. Значительную часть средств, конечно, продолжают приносить заказы производству, абонементы коворкинга дают ещё часть. Мастер-классы и разнообразные воркшопы и прочие события в пространстве — тоже немаловажный &quot;ручеёк&quot;, из которых собирается весь необходимый поток средств. Мастерская создавалась полностью на собственные средства её основателей, наполняется тоже во многом своими силами, команда активно продвигает и развивает проект, привлекая производителей оборудования к спонсорству и сотрудничеству. Такой цех современного дизайна и интенсивного профессионального образования способен раскрыть множество действительно перспективных столяров и дизайнеров. Мы всецело поддерживаем благородное стремление помочь людям вырваться из офисного и компьютерного заточения, предоставляя им возможности заняться по-настоящему плодотворной творческой деятельностью. Пожелаем им успехов в этом замечательном начинании!</p><hr><p>P.S. Кстати, в мастерской открыта вакансия: ищут опытного столяра на позицию директора производства. Задача — управление производственной группой. Требования — выполнять заказы в срок, вписываться в бюджет, иметь представление о дизайне и современных технологиях.</p><p>По всем вопросам <a href="/workshop/catalog/delaivesch/">связывайтесь непосредственно с мастерской.</a></p>',28),ys=[$s];function As(Ds,Ns,Vs,Bs,Cs,Es){return t(),_("div",null,ys)}const Rs=s(Ps,[["render",As]]);export{Os as __pageData,Rs as default};
