import{_ as s,o as t,c as o,b as _}from"./chunks/framework.dd5ab16e.js";const r="/assets/IMG_0394.08e772a7.jpg",a="/assets/IMG_0419.8c33ba4a.jpg",p="/assets/IMG_0401.f3bd4d8c.jpg",e="/assets/IMG_0395.5cd2c6e9.jpg",c="/assets/IMG_0396.a1511b68.jpg",i="/assets/IMG_0334.58039429.jpg",g="/assets/IMG_0397.8126ea44.jpg",n="/assets/IMG_0398.65d44728.jpg",m="/assets/IMG_0399.ee4913a7.jpg",d="/assets/IMG_0320.0dd7bb49.jpg",l="/assets/IMG_0418.5bd3cda1.jpg",I="/assets/IMG_0415.132dc1db.jpg",M="/assets/IMG_0416.eea6c7ef.jpg",j="/assets/IMG_0417.b6df22db.jpg",G="/assets/IMG_0412.8271745c.jpg",f="/assets/IMG_0413.69cfb640.jpg",b="/assets/IMG_0414.da46c4e9.jpg",h="/assets/IMG_0291.b5b5aaab.jpg",u="/assets/IMG_0420.38b7faee.jpg",v="/assets/IMG_0411.5c4964ff.jpg",k="/assets/IMG_0409.3d874117.jpg",x="/assets/IMG_0408.6e7487cd.jpg",T="/assets/IMG_0410.a03a9a88.jpg",S="/assets/IMG_0298.40a7ced3.jpg",w="/assets/IMG_0402.f215488e.jpg",z="/assets/IMG_0403.c3f0fbf5.jpg",C="/assets/IMG_0404.6370f1fd.jpg",P="/assets/IMG_0405.beeb06a2.jpg",$="/assets/IMG_0406.1d123e0f.jpg",A="/assets/IMG_0407.a30e48e5.jpg",H=JSON.parse('{"title":"День открытых ворот в мастерских Про.Движения","description":"","frontmatter":{"title":"День открытых ворот в мастерских Про.Движения","date":"2015-04-27","cover":"IMG_0391.jpg"},"headers":[],"relativePath":"workshop/tour/pro-dvizhenie-open/index.md","filePath":"workshop/tour/pro-dvizhenie-open/index.md"}'),D={name:"workshop/tour/pro-dvizhenie-open/index.md"},N=_('<p>Недавно мы опубликовали <a href="/workshop/catalog/pro-dvizhenie/">обзор мастерских проекта Про.Движение</a> и обещали дополнить некоторые недостающие детали. Очень кстати оказался <a href="/practice/event/den-otkrytyh-vorot/">День открытых ворот</a>, который Конфедерация успешно провела в субботу 25 апреля. Мы с удовольствием вновь оказались на территории Кристалла и собрали всю недостающую информацию о внутреннем устройстве мастерских серьезной металлообработки. Нам выпал отличный шанс увидеть мастеров и сложное оборудование за работой, делимся с вами нашими наблюдениями.</p><p><img src="'+r+'" alt=""><img src="'+a+'" alt=""><img src="'+p+'" alt=""><img src="'+e+'" alt=""><img src="'+c+'" alt=""></p><p>В прошлый раз мы лишь окинули взором одно из важнейших помещений мастерских — кузницу. Теперь мы ознакомились с наполнением этого цеха и увидели оборудование в действии. Слева у стены установлен <strong>кузнечный горн</strong>, где происходит нагрев стальных заготовок. Несложное устройство в виде массивного стола с решетчатым <strong>горновым гнездом</strong>, над которым устраивается из огнеупорного кирпича очаг. В качестве топлива используется каменный или обычный древесный уголь, а для достижения нужной интенсивности пламени снизу в гнездо подаётся поток воздуха из специального вентилятора. Такое устройство позволяет разогревать сталь до <strong>более чем 1300°С</strong>, чего вполне достаточно для её размягчения.</p><p><img src="'+i+'" alt=""><img src="'+g+'" alt=""><img src="'+n+'" alt=""><img src="'+m+'" alt=""></p><p>После того, как заготовка достаточно нагрелась, её можно ковать. Для этого здесь предусмотрено несколько наковален разного размера с различными приспособлениями для сгибания и прочей обработки горячего металла. Помимо ручного кования молотком на наковальне, резидентам мастерской доступен большой механический <strong>кузнечный молот</strong>, значительно облегчающий обработку металла. Также в кузне можно найти всевозможные щипцы, клещи, ножницы. Незаменимым помощником кузнеца оказался и пневматический молоток, работающий от воздушного компрессора.</p><p><img src="'+d+'" alt=""><img src="'+l+'" alt=""><img src="'+I+'" alt=""><img src="'+M+'" alt=""><img src="'+j+'" alt=""></p><p>Удалось увидеть в действии и многое другое. Мы увидели процесс ковки аккуратной вогнутой чаши из стального листа на кузнечных молотах общего цеха мастерской. <strong>Никита Custo</strong> не только представил велосипеды своей работы, но и продемонстрировал мастерство <strong>аргонодуговой сварки алюминия</strong> на примере треснувшей велорамы. На авторских велосипедах мог прокатиться каждый желающий, стоит признаться, ездить на них — одно удовольствие. На улице была хорошая погода и нам удалось понаблюдать за удивительно простым и эффективным процессом <strong>плазменной резки металла</strong>. Воздушный компрессор и специальный инвертор дают возможность резать довольно толстую листовую сталь почти как бумагу.</p><p><img src="'+G+'" alt=""><img src="'+f+'" alt=""><img src="'+b+'" alt=""><img src="'+h+'" alt=""></p><p>Большое помещение в дальней части мастерской теперь расчищено и подготовлено к въезду еще одного конфедерата проекта Про.Движение. Рядом оказалась открыта дверь <strong>проекта ИСБМ</strong>, занимающегося 3D-печатью и сложным ЧПУ фрезерованием дерева и других материалов. Оборудование этого проекта позволяет создавать вещи удивительной сложности, первичная разработка которых может занимать недели. Сложности в разработке программы для станка с лихвой компенсируются точностью обработки и возможностью легкого повторения изделий. Все резиденты мастерских частенько обращаются сюда с всевозможными проектами по резке дерева.</p><p><img src="'+u+'" alt=""><img src="'+v+'" alt=""><img src="'+k+'" alt=""><img src="'+x+'" alt=""><img src="'+T+'" alt=""><img src="'+S+'" alt=""></p><p>Ну и, напоследок, несколько сочных фотографий из мотомастерской <strong>CusMos</strong>, где ребята эффектно разложили разобранный мотодвигатель и можно было в деталях изучить его устройство. Да и сами мотоциклы поражают обилием неожиданных конструктивных решений.</p><p><img src="'+w+'" alt=""><img src="'+z+'" alt=""><img src="'+C+'" alt=""><img src="'+P+'" alt=""><img src="'+$+'" alt=""><img src="'+A+'" alt=""></p>',12),V=[N];function y(B,E,J,O,R,q){return t(),o("div",null,V)}const K=s(D,[["render",y]]);export{H as __pageData,K as default};
