import{_ as s,a as t,o,L as r}from"./chunks/framework.CKEhg_8n.js";const _="/assets/IMG_0394.CNkrqZ3W.jpg",p="/assets/IMG_0419.8kcTUe1U.jpg",a="/assets/IMG_0401.BRpk3RH8.jpg",c="/assets/IMG_0395.DWSJ7c8H.jpg",e="/assets/IMG_0396.CktsDa95.jpg",i="/assets/IMG_0334.Xappn9JO.jpg",g="/assets/IMG_0397.DcaIh6AU.jpg",n="/assets/IMG_0398.CbkbBROn.jpg",m="/assets/IMG_0399.CslyblxY.jpg",l="/assets/IMG_0320.DTjEX1n9.jpg",I="/assets/IMG_0418.CJeTKzOj.jpg",j="/assets/IMG_0415.wuGHk96z.jpg",G="/assets/IMG_0416.Bl7GN2hW.jpg",M="/assets/IMG_0417.newZGgfg.jpg",d="/assets/IMG_0412.CV7on110.jpg",h="/assets/IMG_0413.BX-oHYdR.jpg",f="/assets/IMG_0414.4csSFP2Y.jpg",k="/assets/IMG_0291.DZE3ig26.jpg",u="/assets/IMG_0420.CLfIXX6r.jpg",C="/assets/IMG_0411.B4cIjDcZ.jpg",B="/assets/IMG_0409.84vHNrrM.jpg",v="/assets/IMG_0408.mQuZpB9D.jpg",D="/assets/IMG_0410.Cw_2qJX3.jpg",T="/assets/IMG_0298.DJaaSVF3.jpg",J="/assets/IMG_0402.B0Nsjmqg.jpg",S="/assets/IMG_0403.Bdglc5Jq.jpg",w="/assets/IMG_0404.BG_N7nrJ.jpg",x="/assets/IMG_0405.BwgqSzhu.jpg",N="/assets/IMG_0406.CbPT47J5.jpg",z="/assets/IMG_0407.ULHs9f8Y.jpg",U=JSON.parse('{"title":"День открытых ворот в мастерских Про.Движения","description":"","frontmatter":{"title":"День открытых ворот в мастерских Про.Движения","date":"2015-04-27","cover":"IMG_0391.jpg"},"headers":[],"relativePath":"workshop/tour/pro-dvizhenie-open/index.md","filePath":"workshop/tour/pro-dvizhenie-open/index.md"}'),H={name:"workshop/tour/pro-dvizhenie-open/index.md"},P=r('<p>Недавно мы опубликовали <a href="/workshop/archive/pro-dvizhenie/">обзор мастерских проекта Про.Движение</a> и обещали дополнить некоторые недостающие детали. Очень кстати оказался <a href="/practice/event/den-otkrytyh-vorot/">День открытых ворот</a>, который Конфедерация успешно провела в субботу 25 апреля. Мы с удовольствием вновь оказались на территории Кристалла и собрали всю недостающую информацию о внутреннем устройстве мастерских серьезной металлообработки. Нам выпал отличный шанс увидеть мастеров и сложное оборудование за работой, делимся с вами нашими наблюдениями.</p><p><img src="'+_+'" alt=""><img src="'+p+'" alt=""><img src="'+a+'" alt=""><img src="'+c+'" alt=""><img src="'+e+'" alt=""></p><p>В прошлый раз мы лишь окинули взором одно из важнейших помещений мастерских — кузницу. Теперь мы ознакомились с наполнением этого цеха и увидели оборудование в действии. Слева у стены установлен <strong>кузнечный горн</strong>, где происходит нагрев стальных заготовок. Несложное устройство в виде массивного стола с решетчатым <strong>горновым гнездом</strong>, над которым устраивается из огнеупорного кирпича очаг. В качестве топлива используется каменный или обычный древесный уголь, а для достижения нужной интенсивности пламени снизу в гнездо подаётся поток воздуха из специального вентилятора. Такое устройство позволяет разогревать сталь до <strong>более чем 1300°С</strong>, чего вполне достаточно для её размягчения.</p><p><img src="'+i+'" alt=""><img src="'+g+'" alt=""><img src="'+n+'" alt=""><img src="'+m+'" alt=""></p><p>После того, как заготовка достаточно нагрелась, её можно ковать. Для этого здесь предусмотрено несколько наковален разного размера с различными приспособлениями для сгибания и прочей обработки горячего металла. Помимо ручного кования молотком на наковальне, резидентам мастерской доступен большой механический <strong>кузнечный молот</strong>, значительно облегчающий обработку металла. Также в кузне можно найти всевозможные щипцы, клещи, ножницы. Незаменимым помощником кузнеца оказался и пневматический молоток, работающий от воздушного компрессора.</p><p><img src="'+l+'" alt=""><img src="'+I+'" alt=""><img src="'+j+'" alt=""><img src="'+G+'" alt=""><img src="'+M+'" alt=""></p><p>Удалось увидеть в действии и многое другое. Мы увидели процесс ковки аккуратной вогнутой чаши из стального листа на кузнечных молотах общего цеха мастерской. <strong>Никита Custo</strong> не только представил велосипеды своей работы, но и продемонстрировал мастерство <strong>аргонодуговой сварки алюминия</strong> на примере треснувшей велорамы. На авторских велосипедах мог прокатиться каждый желающий, стоит признаться, ездить на них — одно удовольствие. На улице была хорошая погода и нам удалось понаблюдать за удивительно простым и эффективным процессом <strong>плазменной резки металла</strong>. Воздушный компрессор и специальный инвертор дают возможность резать довольно толстую листовую сталь почти как бумагу.</p><p><img src="'+d+'" alt=""><img src="'+h+'" alt=""><img src="'+f+'" alt=""><img src="'+k+'" alt=""></p><p>Большое помещение в дальней части мастерской теперь расчищено и подготовлено к въезду еще одного конфедерата проекта Про.Движение. Рядом оказалась открыта дверь <strong>проекта ИСБМ</strong>, занимающегося 3D-печатью и сложным ЧПУ фрезерованием дерева и других материалов. Оборудование этого проекта позволяет создавать вещи удивительной сложности, первичная разработка которых может занимать недели. Сложности в разработке программы для станка с лихвой компенсируются точностью обработки и возможностью легкого повторения изделий. Все резиденты мастерских частенько обращаются сюда с всевозможными проектами по резке дерева.</p><p><img src="'+u+'" alt=""><img src="'+C+'" alt=""><img src="'+B+'" alt=""><img src="'+v+'" alt=""><img src="'+D+'" alt=""><img src="'+T+'" alt=""></p><p>Ну и, напоследок, несколько сочных фотографий из мотомастерской <strong>CusMos</strong>, где ребята эффектно разложили разобранный мотодвигатель и можно было в деталях изучить его устройство. Да и сами мотоциклы поражают обилием неожиданных конструктивных решений.</p><p><img src="'+J+'" alt=""><img src="'+S+'" alt=""><img src="'+w+'" alt=""><img src="'+x+'" alt=""><img src="'+N+'" alt=""><img src="'+z+'" alt=""></p>',12),X=[P];function q(R,V,Z,b,A,E){return o(),t("div",null,X)}const Y=s(H,[["render",q]]);export{U as __pageData,Y as default};
