import{_ as s,o as t,c as o,e as _}from"./chunks/framework.7WwFwkJk.js";const r="/assets/IMG_0394.jZK6md1i.jpg",p="/assets/IMG_0419.PJHE1HtV.jpg",e="/assets/IMG_0401.UaZN0R_P.jpg",a="/assets/IMG_0395.1kie3PB9.jpg",i="/assets/IMG_0396.pLbA2vec.jpg",c="/assets/IMG_0334.F2qaZ_ST.jpg",g="/assets/IMG_0397.3GiIegFN.jpg",n="/assets/IMG_0398.m5GwUTp5.jpg",m="/assets/IMG_0399.rJcm5cWJ.jpg",l="/assets/IMG_0320.04xF9Z_Z.jpg",I="/assets/IMG_0418.iXkyszo7.jpg",G="/assets/IMG_0415.MLhh5Pes.jpg",j="/assets/IMG_0416.ZexjdoVr.jpg",M="/assets/IMG_0417.J3sGRoH4.jpg",d="/assets/IMG_0412.le6J9ddE.jpg",h="/assets/IMG_0413.V_qB2HUd.jpg",k="/assets/IMG_0414.OHLEhT9m.jpg",v="/assets/IMG_0291.2RN4oNum.jpg",x="/assets/IMG_0420.i3yF1-qy.jpg",f="/assets/IMG_0411.eHCIw3Gb.jpg",u="/assets/IMG_0409.POLxza6z.jpg",P="/assets/IMG_0408.JkLmaQfQ.jpg",z="/assets/IMG_0410.sP9qiV9x.jpg",T="/assets/IMG_0298.yWmklRd0.jpg",y="/assets/IMG_0402.dDbI5qoB.jpg",J="/assets/IMG_0403.XYJXOSam.jpg",N="/assets/IMG_0404.Rvze56yc.jpg",S="/assets/IMG_0405.cIKks4bo.jpg",V="/assets/IMG_0406.mz0-Oyee.jpg",w="/assets/IMG_0407.FCx7PX_G.jpg",A=JSON.parse('{"title":"День открытых ворот в мастерских Про.Движения","description":"","frontmatter":{"title":"День открытых ворот в мастерских Про.Движения","date":"2015-04-27","cover":"IMG_0391.jpg"},"headers":[],"relativePath":"workshop/tour/pro-dvizhenie-open/index.md","filePath":"workshop/tour/pro-dvizhenie-open/index.md"}'),C={name:"workshop/tour/pro-dvizhenie-open/index.md"},H=_('<p>Недавно мы опубликовали <a href="/workshop/archive/pro-dvizhenie/">обзор мастерских проекта Про.Движение</a> и обещали дополнить некоторые недостающие детали. Очень кстати оказался <a href="/practice/event/den-otkrytyh-vorot/">День открытых ворот</a>, который Конфедерация успешно провела в субботу 25 апреля. Мы с удовольствием вновь оказались на территории Кристалла и собрали всю недостающую информацию о внутреннем устройстве мастерских серьезной металлообработки. Нам выпал отличный шанс увидеть мастеров и сложное оборудование за работой, делимся с вами нашими наблюдениями.</p><p><img src="'+r+'" alt=""><img src="'+p+'" alt=""><img src="'+e+'" alt=""><img src="'+a+'" alt=""><img src="'+i+'" alt=""></p><p>В прошлый раз мы лишь окинули взором одно из важнейших помещений мастерских — кузницу. Теперь мы ознакомились с наполнением этого цеха и увидели оборудование в действии. Слева у стены установлен <strong>кузнечный горн</strong>, где происходит нагрев стальных заготовок. Несложное устройство в виде массивного стола с решетчатым <strong>горновым гнездом</strong>, над которым устраивается из огнеупорного кирпича очаг. В качестве топлива используется каменный или обычный древесный уголь, а для достижения нужной интенсивности пламени снизу в гнездо подаётся поток воздуха из специального вентилятора. Такое устройство позволяет разогревать сталь до <strong>более чем 1300°С</strong>, чего вполне достаточно для её размягчения.</p><p><img src="'+c+'" alt=""><img src="'+g+'" alt=""><img src="'+n+'" alt=""><img src="'+m+'" alt=""></p><p>После того, как заготовка достаточно нагрелась, её можно ковать. Для этого здесь предусмотрено несколько наковален разного размера с различными приспособлениями для сгибания и прочей обработки горячего металла. Помимо ручного кования молотком на наковальне, резидентам мастерской доступен большой механический <strong>кузнечный молот</strong>, значительно облегчающий обработку металла. Также в кузне можно найти всевозможные щипцы, клещи, ножницы. Незаменимым помощником кузнеца оказался и пневматический молоток, работающий от воздушного компрессора.</p><p><img src="'+l+'" alt=""><img src="'+I+'" alt=""><img src="'+G+'" alt=""><img src="'+j+'" alt=""><img src="'+M+'" alt=""></p><p>Удалось увидеть в действии и многое другое. Мы увидели процесс ковки аккуратной вогнутой чаши из стального листа на кузнечных молотах общего цеха мастерской. <strong>Никита Custo</strong> не только представил велосипеды своей работы, но и продемонстрировал мастерство <strong>аргонодуговой сварки алюминия</strong> на примере треснувшей велорамы. На авторских велосипедах мог прокатиться каждый желающий, стоит признаться, ездить на них — одно удовольствие. На улице была хорошая погода и нам удалось понаблюдать за удивительно простым и эффективным процессом <strong>плазменной резки металла</strong>. Воздушный компрессор и специальный инвертор дают возможность резать довольно толстую листовую сталь почти как бумагу.</p><p><img src="'+d+'" alt=""><img src="'+h+'" alt=""><img src="'+k+'" alt=""><img src="'+v+'" alt=""></p><p>Большое помещение в дальней части мастерской теперь расчищено и подготовлено к въезду еще одного конфедерата проекта Про.Движение. Рядом оказалась открыта дверь <strong>проекта ИСБМ</strong>, занимающегося 3D-печатью и сложным ЧПУ фрезерованием дерева и других материалов. Оборудование этого проекта позволяет создавать вещи удивительной сложности, первичная разработка которых может занимать недели. Сложности в разработке программы для станка с лихвой компенсируются точностью обработки и возможностью легкого повторения изделий. Все резиденты мастерских частенько обращаются сюда с всевозможными проектами по резке дерева.</p><p><img src="'+x+'" alt=""><img src="'+f+'" alt=""><img src="'+u+'" alt=""><img src="'+P+'" alt=""><img src="'+z+'" alt=""><img src="'+T+'" alt=""></p><p>Ну и, напоследок, несколько сочных фотографий из мотомастерской <strong>CusMos</strong>, где ребята эффектно разложили разобранный мотодвигатель и можно было в деталях изучить его устройство. Да и сами мотоциклы поражают обилием неожиданных конструктивных решений.</p><p><img src="'+y+'" alt=""><img src="'+J+'" alt=""><img src="'+N+'" alt=""><img src="'+S+'" alt=""><img src="'+V+'" alt=""><img src="'+w+'" alt=""></p>',12),R=[H];function Z(q,B,E,F,L,O){return t(),o("div",null,R)}const D=s(C,[["render",Z]]);export{A as __pageData,D as default};
