import{_ as s,o as t,c as o,e as r}from"./chunks/framework.mYMQfzZG.js";const _="/assets/IMG_9825.fe1anzDi.jpg",p="/assets/IMG_9826.Jfv2LRm-.jpg",g="/assets/IMG_9828.dfG-zEtH.jpg",a="/assets/Planirovka.wWecjH1Z.jpg",i="/assets/omT6PIFcfn8.MgEvB58X.jpg",n="/assets/hg_XAZhjW74.LuBRFU4B.jpg",c="/assets/bEMR-y82aQU.5h5ZFK6i.jpg",e="/assets/IMG_9874.-ToxdGeC.jpg",m="/assets/IMG_9831.SCJZiZaW.jpg",l="/assets/IMG_9829.MWhFeX0Q.jpg",j="/assets/IMG_9832.zRGrsvZK.jpg",G="/assets/IMG_9833.3-US7_PC.jpg",I="/assets/IMG_9834.3y61Jp10.jpg",M="/assets/IMG_9840.Tm38OjXF.jpg",u="/assets/IMG_9843.bHHsrwvI.jpg",h="/assets/IMG_9875.5TMVgjmQ.jpg",d="/assets/IMG_9844.OoYp0K5U.jpg",k="/assets/IMG_9845.xiNopf4y.jpg",f="/assets/IMG_9841.ze2cvbGm.jpg",v="/assets/IMG_9842.OCyV5kXF.jpg",x="/assets/IMG_9846.5jVNt2bZ.jpg",q="/assets/IMG_9848.oZmy9-Oy.jpg",y="/assets/IMG_9849.Y05d2Kdh.jpg",P="/assets/IMG_9847.KNFlTxW8.jpg",B="/assets/IMG_9904.mw3Pfxmw.jpg",b="/assets/IMG_9850.clMsf2BT.jpg",z="/assets/IMG_9851.Iigyssxl.jpg",D="/assets/IMG_9852.GRIEDN_z.jpg",S="/assets/IMG_9853.evD9u_7G.jpg",W="/assets/IMG_9854.zaSGeKsy.jpg",F="/assets/IMG_9855.oDz4vWsC.jpg",w="/assets/IMG_9856.dvr_wLW1.jpg",J="/assets/IMG_9857.M5oN7nQf.jpg",K="/assets/IMG_9858.kAk50Xup.jpg",T="/assets/IMG_9905.Ww1LGGKB.jpg",U="/assets/IMG_9906.BAV_V40C.jpg",C="/assets/IMG_9886.u07jXaxx.jpg",V="/assets/IMG_9887.SyUjguOk.jpg",X="/assets/IMG_9888.tLkI3hKC.jpg",A="/assets/IMG_9889.VN1rngmf.jpg",L="/assets/IMG_9890.Akdw55bF.jpg",R="/assets/IMG_9891.-UIAsB6u.jpg",E="/assets/IMG_9876.29cAxVgS.jpg",H="/assets/IMG_9877.SGUjqdRI.jpg",N="/assets/IMG_9878.-b3rBYfv.jpg",O="/assets/IMG_9879.ss4S0HKB.jpg",Z="/assets/IMG_9880.XNkXBHxF.jpg",Y="/assets/IMG_9881.CgWIakLw.jpg",Q="/assets/IMG_9882.P5lr4J4a.jpg",$="/assets/IMG_9883.WM7y_VFI.jpg",ss="/assets/IMG_9884.oEavMW5P.jpg",ts="/assets/IMG_9885.C8HPTasf.jpg",os="/assets/IMG_9868.y8ln0BCx.jpg",rs="/assets/Q54yi8F7DRw.nDxLgrlW.jpg",_s="/assets/r94klGlk7Jk.tipZ5iLH.jpg",ps="/assets/KjTFcPySKPk.VH_BfvL9.jpg",gs="/assets/Men2OSaFvJ0.4XxeuP87.jpg",as="/assets/IMG_9892.KbSXbFA_.jpg",is="/assets/IMG_9893.lWgVckrR.jpg",ns="/assets/IMG_9894.q8xH6uJm.jpg",cs="/assets/IMG_9895.A1tEkcEa.jpg",es="/assets/IMG_9896.UP8qz1Rk.jpg",ms="/assets/IMG_9897.gDpUeOnO.jpg",ls="/assets/IMG_9907.13d0eTeI.jpg",js="/assets/IMG_9898.3GcoQjLl.jpg",Gs="/assets/IMG_9835.WJF6HWi4.jpg",Is="/assets/IMG_9838.7ixUg2yU.jpg",Ms="/assets/IMG_9839.6iq2oKD3.jpg",us="/assets/IMG_9911.WzmkRnah.jpg",hs="/assets/IMG_9866.Mz57AES4.jpg",ds="/assets/IMG_9865.Kxfatyph.jpg",ks="/assets/IMG_9870.KeKeQ8Xj.jpg",fs="/assets/IMG_9863.xvjvRYoo.jpg",vs="/assets/IMG_9864.VJGWGP91.jpg",xs="/assets/IMG_9859.scXePmhz.jpg",qs="/assets/IMG_9860.Emy6vGu_.jpg",ys="/assets/IMG_9861.9Kh53Oj6.jpg",Ps="/assets/IMG_9862.qTPe-nBw.jpg",Bs="/assets/IMG_9867.JRsDWMR8.jpg",bs="/assets/IMG_9869.68WGP7sh.jpg",zs="/assets/IMG_9909.wjU9kmPf.jpg",Ds="/assets/IMG_9901.i7rjXX-g.jpg",Ss="/assets/IMG_9902._nDy9Ppi.jpg",Ws="/assets/ilMdhzPJJ0.7JgpsfZt.jpg",Fs="/assets/ORgUGsLagY.CGzg24Bf.jpg",ws="/assets/0_GUbIa839w.JWNfzaqR.jpg",Js="/assets/6Prt8_BmeLY.4AV4hN3E.jpg",Ks="/assets/8Bju_ADxh90.0yB4uXYd.jpg",Ts="/assets/b5ko5OxPiTI.FUdS9Ypg.jpg",Us="/assets/D0JjGJMuU9Y.ps2koJs9.jpg",Cs="/assets/IMG_9912.PesA8FS6.jpg",Vs="/assets/IMG_9910.YyStL-Ld.jpg",Xs="/assets/IMG_9899.-2PupXFK.jpg",As="/assets/IMG_9903.Nm4dbHr6.jpg",Ls="/assets/IMG_9873.L-QnDGTE.jpg",Rs="/assets/IMG_9872.xHfZyDVV.jpg",Es="/assets/IMG_9871.3DkL9E6D.jpg",_t=JSON.parse('{"title":"Мастерские проекта Про.Движение","description":"","frontmatter":{"title":"Мастерские проекта Про.Движение","date":"2015-04-15","cover":"cover.jpg"},"headers":[],"relativePath":"workshop/tour/pro-dvizhenie/index.md","filePath":"workshop/tour/pro-dvizhenie/index.md"}'),Hs={name:"workshop/tour/pro-dvizhenie/index.md"},Ns=r('<p>Мы уже рассказали, где в Москве можно поработать с деревом, настало время расширить спектр доступных материалов. На этот раз мы побываем в <a href="/workshop/archive/pro-dvizhenie/" title="Мастерские проекта Про.Движение">мастерских проекта Про.Движение</a>, основным приоритетом которых является обработка металла, как мелкая так и крупная.</p><p>Существуя в виде мобильного фестивального проекта в течение более 5 лет, проект Про.Движение решил &quot;осесть&quot; совсем недавно. Идея <a href="/theory/repost/svobodnye-masterskie/" title="Проект открытой архитектурной мастерской “Свободные мастерские”">&quot;Свободных мастерских&quot;</a> сформировалась в недрах команды примерно к концу 2013 года, а сама мастерская на территории бывшего завода &quot;Кристалл&quot; была открыта в ноябре 2014 года. За это время проект не только освоился и обустроил рабочее пространство, но и успел превратиться в целую Конфедерацию, объединив под одной крышей сразу несколько независимых мастеров и мастерских.</p><p><img src="'+_+'" alt=""><img src="'+p+'" alt=""><img src="'+g+'" alt=""></p><p>Водочный завод &quot;Кристалл&quot; прекратил свою работу в конце 2012 года, а окончательно производство переехало в подмосковное Корыстово в апреле 2013 года. Сегодня &quot;Кристалл&quot; — это объект активного <strong>редевелопмента</strong>, т.е. переустройства промышленного объекта в современный городской квартал. Производственное оборудование давно вывезено, в цехах уже не пахнет спиртом. Помещения завода активно заселяются всевозможными творческими проектами. Несмотря на это, рудименты строгого производственного контроля здесь еще сохраняются. В частности, для того, чтобы попасть внутрь вам понадобится записаться у охраны в список на проход в мастерские Про.Движение. Лучше предупредить о своём посещении заранее, чтобы вас могли встретить. Это особенно важно если вы приехали в первый раз, т.к. территория завода внушительная и заблудиться там не так уж и сложно. Чтобы этого не произошло, сразу после выхода из проходной поворачивайте направо, проходите через арку, затем еще раз поворачиваете направо. Далее нужно будет повернуть налево и пройти оставшиеся 50 метров до одноэтажного здания с большими черными воротами. Скорее всего ворота окажутся открытыми, заходим!</p><p><img src="'+a+'" alt=""></p><p>Мы сразу попадаем в <strong>зону загрузки</strong>. Здесь нас встречают два внушительных станка, один из которых режет листовой металл, а другой позволяет сгибать листы под заданным углом. Здесь происходит первичная обработка поступающих в мастерскую материалов. Повернув в дверь налево, отсюда можно попасть в <strong>комнату отдыха</strong>, оборудованную индивидуальными шкафчиками для каждого резидента, диваном, столами и стульями. Здесь работает вай-фай, можно попить воды, угоститься чаем с печеньками. Тут мы встретили замечательных сотрудниц мастерской: <strong>директора по маркетингу Асю и администратора мастерской Александру</strong>. Вместе с ними мы изучили наполнение мастерской и спешим поделиться этим знанием с вами.</p><p><img src="'+i+'" alt=""><img src="'+n+'" alt=""><img src="'+c+'" alt=""><img src="'+e+'" alt=""><img src="'+m+'" alt=""><img src="'+l+'" alt=""><img src="'+j+'" alt=""><img src="'+G+'" alt=""><img src="'+I+'" alt=""><img src="'+M+'" alt=""><img src="'+u+'" alt=""><img src="'+h+'" alt=""></p><p>Напротив двери в комнату отдыха в зале загрузки есть еще одна дверь, ведущая в <strong>макетную мастерскую Urban Garden</strong>. Небольшое помещение отлично подходит для работы с моделями и макетами. В конце комнаты стоит большое черное устройство, как-то связанное с 3D-печатью. К сожалению, точнее понять не удалось, попробуем выяснить его точное назначение в другой раз. На полках можно увидеть образцы печати вполне достойного качества. <strong>Широкоформатный принтер</strong> и <strong>терморежущий станочек Styrocut</strong> — хороший комплект для изготовления пластиковых деталей, а большие окна и мощная вытяжка создают приятную и безопасную атмосферу для кропотливой макетной работы.</p><p><img src="'+d+'" alt=""><img src="'+k+'" alt=""><img src="'+f+'" alt=""><img src="'+v+'" alt=""><img src="'+x+'" alt=""><img src="'+q+'" alt=""><img src="'+y+'" alt=""></p><p>Прямо из зоны загрузки в основную рабочую зону мастерской ведёт длинный коридор, из которого можно попасть в <strong>кузницу</strong> и в <strong>монтажную комнату</strong> с дверью на замке. Ограничение доступа в это помещение не удивительно: здесь располагается основное сварочное оборудование. Помимо нескольких <strong>сварочных инверторов</strong>, в том числе и для <strong>аргонодуговой сварки</strong>, в монтажной можно обнаружить <strong>сверлильный, точильный  и шлифовальный станки</strong>, <strong>циркулярную пилу</strong>, <strong>УШМ</strong> и другой ручной инструмент. В помещении для лучшей вентиляции устроены две вытяжки.</p><p><img src="'+P+'" alt=""><img src="'+B+'" alt=""><img src="'+b+'" alt=""><img src="'+z+'" alt=""><img src="'+D+'" alt=""><img src="'+S+'" alt=""><img src="'+W+'" alt=""><img src="'+F+'" alt=""><img src="'+w+'" alt=""><img src="'+J+'" alt=""><img src="'+K+'" alt=""><img src="'+T+'" alt=""><img src="'+U+'" alt=""></p><p>Напротив монтажной, отделенная от коридора перегородкой, расположилась <strong>кузница</strong>. Ковка и прочая формовка металла в основном производятся здесь. На момент нашего визита кузница находилась на стадии переоборудования и подробно ознакомиться с оборудованием этого зала нам в этот раз не удалось. Однако вскоре мы внимательно изучили кузницу на дне открытых ворот, где было <a href="/workshop/tour/pro-dvizhenie-open/">продемонстрировано множество возможностей мастерской в действии</a>.</p><p><img src="'+C+'" alt=""><img src="'+V+'" alt=""><img src="'+X+'" alt=""><img src="'+A+'" alt=""><img src="'+L+'" alt=""><img src="'+R+'" alt=""></p><p>Главный коридор ведёт нас в большой слесарный цех, где разместились сразу три &quot;конфедерата&quot;. Слева от входа находится уголок <a href="https://vk.com/custombicycles" class="external-link" target="_blank" rel="noreferrer">велоателье <strong>Custo Bicycles</strong></a> — творческая площадка мастера <a href="https://vk.com/id383417" class="external-link" target="_blank" rel="noreferrer">Никиты Сергеева</a>. Здесь разрабатываются и создаются не просто велосипеды, а настоящие произведения искусства, сочетающие высокие технологии и тонкий подход к деталям. Ателье берется за проекты любой сложности и доводит каждый до идеала. <a href="https://vk.com/album-50140018_196634048" class="external-link" target="_blank" rel="noreferrer">Электровелосипед &quot;Ракета&quot;</a> — знаковая работа, дающая понять возможности команды Custo Bicycles.  Сказывается наличие всего необходимого оборудования и свободное владение важными технологиями, такими как, например, <strong>гибка и сварка легких алюминиевых труб</strong> и других конструктивных элементов. Сборка рам и велосипедов производится на специальном <strong>стапеле</strong>, а мощный <strong>токарный станок</strong> позволяет изготавливать уникальные элементы конструкции. Каждому конфедерату выделяется несколько верстаков, полок, ящиков для удобной работы и хранения всего необходимого.</p><p><img src="'+E+'" alt=""><img src="'+H+'" alt=""><img src="'+N+'" alt=""><img src="'+O+'" alt=""><img src="'+Z+'" alt=""><img src="'+Y+'" alt=""><img src="'+Q+'" alt=""><img src="'+$+'" alt=""><img src="'+ss+'" alt=""><img src="'+ts+'" alt=""><img src="'+os+'" alt=""></p><p>Напротив окна всю территорию вдоль стены заняла мастерская <strong><a href="http://copperbrass.ru/" class="external-link" target="_blank" rel="noreferrer">CopperBrass</a></strong> братьев <strong>Дмитрия и Олега Зубковых</strong>, производящая множество всевозможных изделий из металла и не только. Команда мастерской прошла долгий путь в поисках своего собственного стиля, освоив огромное количество производственных процессов. Мощные <strong>кузнечные молоты, прессы</strong> и другие станки позволяют использовать в дизайн-проектах мастерской практически любые металлы и прочие материалы. Отсюда выходят уникальные светильники, стильные камины и всевозможные элементы интерьера от дверных ручек до скульптур и прочих архитектурных деталей.</p><p><img src="'+rs+'" alt=""><img src="'+_s+'" alt=""><img src="'+ps+'" alt=""><img src="'+gs+'" alt=""><img src="'+as+'" alt=""><img src="'+is+'" alt=""><img src="'+ns+'" alt=""><img src="'+cs+'" alt=""><img src="'+es+'" alt=""><img src="'+ms+'" alt=""></p><p>Здесь же размещается <strong>лаборатория индивидуального камина FireBAU</strong>, где художник <strong>Евгений Миленький</strong> создаёт настоящие произведения каминного искусства. Возможности мастерских проекта Про.Движение и высокая квалификация мастера позволяют браться за самые сложные проекты и неизменно получать потрясающие результаты.</p><p><img src="'+ls+'" alt=""><img src="'+js+'" alt=""></p><p>В дальнем углу этого большого цеха разместилось <strong>&quot;Объединение практикующих архитекторов&quot; ОПАrch</strong>, занимающееся созданием архитектурных макетов, необычной мебели и прочих предметов интерьера, преимущественно из дерева. Молодая команда смело экспериментирует с формой, используя для производства сложное ЧПУ оборудование, находя неожиданные решения для вполне обыденных задач.</p><p><img src="'+Gs+'" alt=""><img src="'+Is+'" alt=""><img src="'+Ms+'" alt=""><img src="'+us+'" alt=""><img src="'+hs+'" alt=""><img src="'+ds+'" alt=""><img src="'+ks+'" alt=""><img src="'+fs+'" alt=""><img src="'+vs+'" alt=""><img src="'+xs+'" alt=""><img src="'+qs+'" alt=""><img src="'+ys+'" alt=""><img src="'+Ps+'" alt=""><img src="'+Bs+'" alt=""></p><p>Это еще далеко не всё. Из длинного коридора, соединяющего просторные цеха мастерской можно попасть в <strong>офис архитектурного бюро А2</strong>, руководит которым основатель проекта Про.Движение <strong>Пётр Виноградов</strong>. Небольшое помещение эффектно увеличили за счёт сварной стальной надстройки второго этажа, где и расположено рабочее место руководителя. Наверх ведёт необычная крутая лестница, разработанная и созданная здесь же в мастерской.</p><p>В самом конце коридора находится комната, где постепенно расквартировывается <strong>проект ИСБМ</strong> — производственно-конструкторское бюро, берущееся за задачи любой сложности в области 3D-печати, фрезеровки на серьезных ЧПУ станках, а также в раскрое различных листовых материалов. Мастера высокой квалификации используют сложное и дорогостоящее оборудование.</p><p><img src="'+bs+'" alt=""><img src="'+zs+'" alt=""><img src="'+Ds+'" alt=""><img src="'+Ss+'" alt=""></p><p>Если здесь повернуть налево, то попадаешь в просторный зал, где разместилась мастерская еще одного конфедерата — <strong>ателье CusMos</strong>, занимающееся проектированием и непосредственным воплощением смелых кастом-проектов. Это всевозможные переделки и реконструкция старых и новых мотоциклов, мопедов, квадроциклов. Диапазон возможностей огромен: от простого тех. обслуживания до тотальной переделки практически любых транспортных средств — в планах ателье постройка уникальных автомобилей в стиле <strong>hot rod</strong>. В мастерской чисто и светло, на пяти мотоподъемниках сверкают хромированными деталями мощные мотоциклы. Отчасти помещение напоминает шоу-рум, но размещенный вдоль стен многочисленный инструмент позволяет осознать масштабы работ, которые здесь производятся. Можно увидеть внушительный <strong>токарный</strong>, а также <strong>сверлильные и точильные станки</strong>, окруженные верстаками с ящиками заполненными всеми необходимыми слесарными приспособлениями.</p><p><img src="'+Ws+'" alt=""><img src="'+Fs+'" alt=""><img src="'+ws+'" alt=""><img src="'+Js+'" alt=""><img src="'+Ks+'" alt=""><img src="'+Ts+'" alt=""><img src="'+Us+'" alt=""></p><p>Несмотря на внушительное наполнение, мастерские проекта Про.Движение на Кристалле еще только в начале своего пути. У команды проекта большие планы по развитию пространства и его возможностей. В частности в скором времени планируется постройка полноценной <strong>покрасочной комнаты</strong> для работы с распыляемыми пульверизатором и порошковыми красками. Кстати, приглашают новых конфедератов <strong>со</strong> своей покрасочной комнатой на льготных основаниях. В складском помещении, располагающемся в пристройке к основному зданию мастерской, активно строится удобная <strong>система хранения</strong>, в которой каждому резиденту и конфедерату будет отведено своё место. Скоро завершится оформление кузницы, а <strong>25 апреля в мастерских пройдет <a href="/practice/event/den-otkrytyh-vorot/" title="День открытых ворот">&quot;День открытых ворот&quot;</a></strong>, где любой желающий сможет внимательно изучить всё имеющееся оборудование, познакомиться с резидентами и конфедератами мастерской, а также <a href="/workshop/tour/pro-dvizhenie-open/">увидеть их виртуозное владение станками и инструментами вживую</a>.</p><p>Проект Про.Движение работает, как и другие подобные мастерские, <strong>в смешанном режиме</strong>. Мастерская <strong>принимает заказы</strong> на изготовление изделий практических любых масштабов и любой сложности из металла и других материалов. Сотрудники проекта готовы взяться за ваш проект еще на стадии сырой идеи и совместными усилиями довести его до реализации. Возможны и более открытые формы взаимодействия.</p><p>Есть возможность совместной разработки и <strong>воплощения идеи вместе с мастером</strong>, наблюдая за работой и по мере сил включаясь в процесс. В ходе практической реализации задумки можно овладеть большим количеством новых навыков и умений. Также можно просто <strong>&quot;арендовать&quot; время мастера</strong> для помощи и руководства в ходе самостоятельного воплощения собственных проектов в рамках аренды рабочего места в коворкинге.</p><p><img src="'+Cs+'" alt=""><img src="'+Vs+'" alt=""><img src="'+Xs+'" alt=""></p><p><img src="'+As+'" alt=""></p><p>Однако наиболее эффективным способом в деле обучения являются, конечно, мастер-классы и курсы, регулярно проводимые мастерами и сотрудниками мастерской. Так <strong>&quot;Курс кузнечного дела&quot;, состоящий из 8 занятий,</strong> позволит не только воплотить свой творческий проект, но и получить целостное представление об этом сложном и полном тайн деле. Для лучшего усвоения материала и обеспечения полной безопасности процесса, курсы и мастер-классы проводятся <strong>для малых групп</strong> участников от 2 до 6 человек. Также есть программа <strong>набора мастерами учеников</strong> для вдумчивого обучения и передачи навыков и умений во всей их полноте. Несмотря на масштабность проекта, Про.Движение сохраняет индивидуальный подход к желающим влиться в коллектив мастеров.</p><p><strong>Конфедерация</strong> — объединение независимых мастерских под одной крышей — уже приносит множество плюсов всем её участникам. Мастерская серьезно расширяет спектр возможных к реализации заказов, при необходимости <strong>перенаправляя их соответствующим конфедератам</strong> и пользуясь увеличенным парком общего оборудования. Конфедераты совместно решают общие вопросы от наполнения баллонов для аргонодуговой сварки и закупки материалов до доработки и ремонта имеющихся станков и <strong>помощи советом и делом в работе над проектами</strong>. Все общественные ресурсы от питьевой воды до подключения к Интернету финансируются совместно всеми конфедератами. Также общими силами производится ремонт и развитие пространства.</p><p>Команда проекта Про.Движение настроена не останавливаться на достигнутом. Здесь активно вынашиваются планы развития как сугубо производственной, так и образовательной сфер. В планах повышение качества и количества курсов и мастер-классов, получение соответствующих лицензий и формирование четкой образовательной программы. Сам проект отнюдь не ограничен стенами мастерской и продолжает участвовать во всевозможных выездных событиях и совместных проектах. Строятся планы дальнейшей фестивальной деятельности.</p><p>Мы будем активно следить за развитием проекта и мастерских. Конфедерация творческих проектов — отличный формат для объединения разрозненных усилий и достижения синергетических эффектов открытого сотрудничества. А формат открытой мастерской с широкой образовательной программой — прекрасный способ отыскать и развить скрытый творческий потенциал современных горожан. Верным путём идём, это радует.</p><p><img src="'+Ls+'" alt=""><img src="'+Rs+'" alt=""><img src="'+Es+'" alt=""></p>',36),Os=[Ns];function Zs(Ys,Qs,$s,st,tt,ot){return t(),o("div",null,Os)}const pt=s(Hs,[["render",Zs]]);export{_t as __pageData,pt as default};
