import{_ as s,o as t,c as o,e as r}from"./chunks/framework.78e651f5.js";const a="/assets/IMG_9825.6a20ce2b.jpg",_="/assets/IMG_9826.1638b8b7.jpg",p="/assets/IMG_9828.22accafe.jpg",g="/assets/Planirovka.e7ad37d3.jpg",c="/assets/omT6PIFcfn8.0fa4dd7b.jpg",e="/assets/hg_XAZhjW74.87f571b5.jpg",n="/assets/bEMR-y82aQU.17375f65.jpg",i="/assets/IMG_9874.5ffb0caf.jpg",m="/assets/IMG_9831.711f390c.jpg",l="/assets/IMG_9829.119a6987.jpg",j="/assets/IMG_9832.786297ff.jpg",G="/assets/IMG_9833.ec5fce01.jpg",I="/assets/IMG_9834.e4ff7ff6.jpg",M="/assets/IMG_9840.225f5de3.jpg",d="/assets/IMG_9843.cd3b812a.jpg",f="/assets/IMG_9875.3868742a.jpg",b="/assets/IMG_9844.c918ad63.jpg",u="/assets/IMG_9845.f84d6e8b.jpg",h="/assets/IMG_9841.32f02e75.jpg",k="/assets/IMG_9842.249f1ae6.jpg",q="/assets/IMG_9846.629e5755.jpg",v="/assets/IMG_9848.f031d091.jpg",x="/assets/IMG_9849.70183308.jpg",y="/assets/IMG_9847.82e5820b.jpg",P="/assets/IMG_9904.48e42ce9.jpg",T="/assets/IMG_9850.f9aeba0a.jpg",w="/assets/IMG_9851.ea80e84a.jpg",B="/assets/IMG_9852.0a32a365.jpg",S="/assets/IMG_9853.3c590805.jpg",z="/assets/IMG_9854.8776e257.jpg",D="/assets/IMG_9855.310d5dce.jpg",J="/assets/IMG_9856.59aa4025.jpg",A="/assets/IMG_9857.eded5933.jpg",C="/assets/IMG_9858.87357d98.jpg",U="/assets/IMG_9905.a52c7a6e.jpg",F="/assets/IMG_9906.752dd3be.jpg",O="/assets/IMG_9886.9869c7fd.jpg",R="/assets/IMG_9887.9e214235.jpg",$="/assets/IMG_9888.d07cb75c.jpg",E="/assets/IMG_9889.85d84659.jpg",N="/assets/IMG_9890.74904ca3.jpg",V="/assets/IMG_9891.3306894a.jpg",Y="/assets/IMG_9876.4239a580.jpg",K="/assets/IMG_9877.0c420233.jpg",L="/assets/IMG_9878.5cca3fc8.jpg",Q="/assets/IMG_9879.a68f5ac1.jpg",W="/assets/IMG_9880.697a99f8.jpg",X="/assets/IMG_9881.4606cee1.jpg",Z="/assets/IMG_9882.d7f2c44f.jpg",H="/assets/IMG_9883.6c721d6a.jpg",ss="/assets/IMG_9884.fc555af5.jpg",ts="/assets/IMG_9885.10972794.jpg",os="/assets/IMG_9868.86505e5e.jpg",rs="/assets/Q54yi8F7DRw.e78772a9.jpg",as="/assets/r94klGlk7Jk.3c65b160.jpg",_s="/assets/KjTFcPySKPk.f9140961.jpg",ps="/assets/Men2OSaFvJ0.3b387269.jpg",gs="/assets/IMG_9892.66ab887a.jpg",cs="/assets/IMG_9893.72c8a413.jpg",es="/assets/IMG_9894.c17c5b21.jpg",ns="/assets/IMG_9895.5569cb33.jpg",is="/assets/IMG_9896.1391e524.jpg",ms="/assets/IMG_9897.e2843840.jpg",ls="/assets/IMG_9907.7e593d07.jpg",js="/assets/IMG_9898.82d36ad3.jpg",Gs="/assets/IMG_9835.d762508b.jpg",Is="/assets/IMG_9838.3f5133fe.jpg",Ms="/assets/IMG_9839.abe76f27.jpg",ds="/assets/IMG_9911.1f37f63c.jpg",fs="/assets/IMG_9866.eca2b66c.jpg",bs="/assets/IMG_9865.c318ea79.jpg",us="/assets/IMG_9870.502a4308.jpg",hs="/assets/IMG_9863.d3eab331.jpg",ks="/assets/IMG_9864.accb186e.jpg",qs="/assets/IMG_9859.745d4c7c.jpg",vs="/assets/IMG_9860.33202a71.jpg",xs="/assets/IMG_9861.51550d9c.jpg",ys="/assets/IMG_9862.de90ccc5.jpg",Ps="/assets/IMG_9867.3c2761a0.jpg",Ts="/assets/IMG_9869.7a7224eb.jpg",ws="/assets/IMG_9909.f1b3d4c7.jpg",Bs="/assets/IMG_9901.22e5e3a3.jpg",Ss="/assets/IMG_9902.4356f122.jpg",zs="/assets/ilMdhzPJJ0.3193d02f.jpg",Ds="/assets/ORgUGsLagY.fbf3f1bd.jpg",Js="/assets/0_GUbIa839w.1d6ea1b8.jpg",As="/assets/6Prt8_BmeLY.e55e515d.jpg",Cs="/assets/8Bju_ADxh90.6b8e76d4.jpg",Us="/assets/b5ko5OxPiTI.d778c938.jpg",Fs="/assets/D0JjGJMuU9Y.8a65e00f.jpg",Os="/assets/IMG_9912.46b886b4.jpg",Rs="/assets/IMG_9910.797789ed.jpg",$s="/assets/IMG_9899.b64d5e98.jpg",Es="/assets/IMG_9903.bd5eb064.jpg",Ns="/assets/IMG_9873.2f17e598.jpg",Vs="/assets/IMG_9872.db7439bc.jpg",Ys="/assets/IMG_9871.0d5b8f0f.jpg",at=JSON.parse('{"title":"Мастерские проекта Про.Движение","description":"","frontmatter":{"title":"Мастерские проекта Про.Движение","date":"2015-04-15","cover":"cover.jpg"},"headers":[],"relativePath":"workshop/tour/pro-dvizhenie/index.md","filePath":"workshop/tour/pro-dvizhenie/index.md"}'),Ks={name:"workshop/tour/pro-dvizhenie/index.md"},Ls=r('<p>Мы уже рассказали, где в Москве можно поработать с деревом, настало время расширить спектр доступных материалов. На этот раз мы побываем в <a href="/workshop/catalog/pro-dvizhenie/" title="Мастерские проекта Про.Движение">мастерских проекта Про.Движение</a>, основным приоритетом которых является обработка металла, как мелкая так и крупная.</p><p>Существуя в виде мобильного фестивального проекта в течение более 5 лет, проект Про.Движение решил &quot;осесть&quot; совсем недавно. Идея <a href="/theory/repost/svobodnye-masterskie/" title="Проект открытой архитектурной мастерской “Свободные мастерские”">&quot;Свободных мастерских&quot;</a> сформировалась в недрах команды примерно к концу 2013 года, а сама мастерская на территории бывшего завода &quot;Кристалл&quot; была открыта в ноябре 2014 года. За это время проект не только освоился и обустроил рабочее пространство, но и успел превратиться в целую Конфедерацию, объединив под одной крышей сразу несколько независимых мастеров и мастерских.</p><p><img src="'+a+'" alt=""><img src="'+_+'" alt=""><img src="'+p+'" alt=""></p><p>Водочный завод &quot;Кристалл&quot; прекратил свою работу в конце 2012 года, а окончательно производство переехало в подмосковное Корыстово в апреле 2013 года. Сегодня &quot;Кристалл&quot; — это объект активного <strong>редевелопмента</strong>, т.е. переустройства промышленного объекта в современный городской квартал. Производственное оборудование давно вывезено, в цехах уже не пахнет спиртом. Помещения завода активно заселяются всевозможными творческими проектами. Несмотря на это, рудименты строгого производственного контроля здесь еще сохраняются. В частности, для того, чтобы попасть внутрь вам понадобится записаться у охраны в список на проход в мастерские Про.Движение. Лучше предупредить о своём посещении заранее, чтобы вас могли встретить. Это особенно важно если вы приехали в первый раз, т.к. территория завода внушительная и заблудиться там не так уж и сложно. Чтобы этого не произошло, сразу после выхода из проходной поворачивайте направо, проходите через арку, затем еще раз поворачиваете направо. Далее нужно будет повернуть налево и пройти оставшиеся 50 метров до одноэтажного здания с большими черными воротами. Скорее всего ворота окажутся открытыми, заходим!</p><p><img src="'+g+'" alt=""></p><p>Мы сразу попадаем в <strong>зону загрузки</strong>. Здесь нас встречают два внушительных станка, один из которых режет листовой металл, а другой позволяет сгибать листы под заданным углом. Здесь происходит первичная обработка поступающих в мастерскую материалов. Повернув в дверь налево, отсюда можно попасть в <strong>комнату отдыха</strong>, оборудованную индивидуальными шкафчиками для каждого резидента, диваном, столами и стульями. Здесь работает вай-фай, можно попить воды, угоститься чаем с печеньками. Тут мы встретили замечательных сотрудниц мастерской: <strong>директора по маркетингу Асю и администратора мастерской Александру</strong>. Вместе с ними мы изучили наполнение мастерской и спешим поделиться этим знанием с вами.</p><p><img src="'+c+'" alt=""><img src="'+e+'" alt=""><img src="'+n+'" alt=""><img src="'+i+'" alt=""><img src="'+m+'" alt=""><img src="'+l+'" alt=""><img src="'+j+'" alt=""><img src="'+G+'" alt=""><img src="'+I+'" alt=""><img src="'+M+'" alt=""><img src="'+d+'" alt=""><img src="'+f+'" alt=""></p><p>Напротив двери в комнату отдыха в зале загрузки есть еще одна дверь, ведущая в <strong>макетную мастерскую Urban Garden</strong>. Небольшое помещение отлично подходит для работы с моделями и макетами. В конце комнаты стоит большое черное устройство, как-то связанное с 3D-печатью. К сожалению, точнее понять не удалось, попробуем выяснить его точное назначение в другой раз. На полках можно увидеть образцы печати вполне достойного качества. <strong>Широкоформатный принтер</strong> и <strong>терморежущий станочек Styrocut</strong> — хороший комплект для изготовления пластиковых деталей, а большие окна и мощная вытяжка создают приятную и безопасную атмосферу для кропотливой макетной работы.</p><p><img src="'+b+'" alt=""><img src="'+u+'" alt=""><img src="'+h+'" alt=""><img src="'+k+'" alt=""><img src="'+q+'" alt=""><img src="'+v+'" alt=""><img src="'+x+'" alt=""></p><p>Прямо из зоны загрузки в основную рабочую зону мастерской ведёт длинный коридор, из которого можно попасть в <strong>кузницу</strong> и в <strong>монтажную комнату</strong> с дверью на замке. Ограничение доступа в это помещение не удивительно: здесь располагается основное сварочное оборудование. Помимо нескольких <strong>сварочных инверторов</strong>, в том числе и для <strong>аргонодуговой сварки</strong>, в монтажной можно обнаружить <strong>сверлильный, точильный  и шлифовальный станки</strong>, <strong>циркулярную пилу</strong>, <strong>УШМ</strong> и другой ручной инструмент. В помещении для лучшей вентиляции устроены две вытяжки.</p><p><img src="'+y+'" alt=""><img src="'+P+'" alt=""><img src="'+T+'" alt=""><img src="'+w+'" alt=""><img src="'+B+'" alt=""><img src="'+S+'" alt=""><img src="'+z+'" alt=""><img src="'+D+'" alt=""><img src="'+J+'" alt=""><img src="'+A+'" alt=""><img src="'+C+'" alt=""><img src="'+U+'" alt=""><img src="'+F+'" alt=""></p><p>Напротив монтажной, отделенная от коридора перегородкой, расположилась <strong>кузница</strong>. Ковка и прочая формовка металла в основном производятся здесь. На момент нашего визита кузница находилась на стадии переоборудования и подробно ознакомиться с оборудованием этого зала нам в этот раз не удалось. Однако вскоре мы внимательно изучили кузницу на дне открытых ворот, где было <a href="/workshop/tour/pro-dvizhenie-open/">продемонстрировано множество возможностей мастерской в действии</a>.</p><p><img src="'+O+'" alt=""><img src="'+R+'" alt=""><img src="'+$+'" alt=""><img src="'+E+'" alt=""><img src="'+N+'" alt=""><img src="'+V+'" alt=""></p><p>Главный коридор ведёт нас в большой слесарный цех, где разместились сразу три &quot;конфедерата&quot;. Слева от входа находится уголок <a href="https://vk.com/custombicycles" class="external-link" target="_blank" rel="noreferrer">велоателье <strong>Custo Bicycles</strong></a> — творческая площадка мастера <a href="https://vk.com/id383417" class="external-link" target="_blank" rel="noreferrer">Никиты Сергеева</a>. Здесь разрабатываются и создаются не просто велосипеды, а настоящие произведения искусства, сочетающие высокие технологии и тонкий подход к деталям. Ателье берется за проекты любой сложности и доводит каждый до идеала. <a href="https://vk.com/album-50140018_196634048" class="external-link" target="_blank" rel="noreferrer">Электровелосипед &quot;Ракета&quot;</a> — знаковая работа, дающая понять возможности команды Custo Bicycles.  Сказывается наличие всего необходимого оборудования и свободное владение важными технологиями, такими как, например, <strong>гибка и сварка легких алюминиевых труб</strong> и других конструктивных элементов. Сборка рам и велосипедов производится на специальном <strong>стапеле</strong>, а мощный <strong>токарный станок</strong> позволяет изготавливать уникальные элементы конструкции. Каждому конфедерату выделяется несколько верстаков, полок, ящиков для удобной работы и хранения всего необходимого.</p><p><img src="'+Y+'" alt=""><img src="'+K+'" alt=""><img src="'+L+'" alt=""><img src="'+Q+'" alt=""><img src="'+W+'" alt=""><img src="'+X+'" alt=""><img src="'+Z+'" alt=""><img src="'+H+'" alt=""><img src="'+ss+'" alt=""><img src="'+ts+'" alt=""><img src="'+os+'" alt=""></p><p>Напротив окна всю территорию вдоль стены заняла мастерская <strong><a href="http://copperbrass.ru/" class="external-link" target="_blank" rel="noreferrer">CopperBrass</a></strong> братьев <strong>Дмитрия и Олега Зубковых</strong>, производящая множество всевозможных изделий из металла и не только. Команда мастерской прошла долгий путь в поисках своего собственного стиля, освоив огромное количество производственных процессов. Мощные <strong>кузнечные молоты, прессы</strong> и другие станки позволяют использовать в дизайн-проектах мастерской практически любые металлы и прочие материалы. Отсюда выходят уникальные светильники, стильные камины и всевозможные элементы интерьера от дверных ручек до скульптур и прочих архитектурных деталей.</p><p><img src="'+rs+'" alt=""><img src="'+as+'" alt=""><img src="'+_s+'" alt=""><img src="'+ps+'" alt=""><img src="'+gs+'" alt=""><img src="'+cs+'" alt=""><img src="'+es+'" alt=""><img src="'+ns+'" alt=""><img src="'+is+'" alt=""><img src="'+ms+'" alt=""></p><p>Здесь же размещается <strong>лаборатория индивидуального камина FireBAU</strong>, где художник <strong>Евгений Миленький</strong> создаёт настоящие произведения каминного искусства. Возможности мастерских проекта Про.Движение и высокая квалификация мастера позволяют браться за самые сложные проекты и неизменно получать потрясающие результаты.</p><p><img src="'+ls+'" alt=""><img src="'+js+'" alt=""></p><p>В дальнем углу этого большого цеха разместилось <strong>&quot;Объединение практикующих архитекторов&quot; ОПАrch</strong>, занимающееся созданием архитектурных макетов, необычной мебели и прочих предметов интерьера, преимущественно из дерева. Молодая команда смело экспериментирует с формой, используя для производства сложное ЧПУ оборудование, находя неожиданные решения для вполне обыденных задач.</p><p><img src="'+Gs+'" alt=""><img src="'+Is+'" alt=""><img src="'+Ms+'" alt=""><img src="'+ds+'" alt=""><img src="'+fs+'" alt=""><img src="'+bs+'" alt=""><img src="'+us+'" alt=""><img src="'+hs+'" alt=""><img src="'+ks+'" alt=""><img src="'+qs+'" alt=""><img src="'+vs+'" alt=""><img src="'+xs+'" alt=""><img src="'+ys+'" alt=""><img src="'+Ps+'" alt=""></p><p>Это еще далеко не всё. Из длинного коридора, соединяющего просторные цеха мастерской можно попасть в <strong>офис архитектурного бюро А2</strong>, руководит которым основатель проекта Про.Движение <strong>Пётр Виноградов</strong>. Небольшое помещение эффектно увеличили за счёт сварной стальной надстройки второго этажа, где и расположено рабочее место руководителя. Наверх ведёт необычная крутая лестница, разработанная и созданная здесь же в мастерской.</p><p>В самом конце коридора находится комната, где постепенно расквартировывается <strong>проект ИСБМ</strong> — производственно-конструкторское бюро, берущееся за задачи любой сложности в области 3D-печати, фрезеровки на серьезных ЧПУ станках, а также в раскрое различных листовых материалов. Мастера высокой квалификации используют сложное и дорогостоящее оборудование.</p><p><img src="'+Ts+'" alt=""><img src="'+ws+'" alt=""><img src="'+Bs+'" alt=""><img src="'+Ss+'" alt=""></p><p>Если здесь повернуть налево, то попадаешь в просторный зал, где разместилась мастерская еще одного конфедерата — <strong>ателье CusMos</strong>, занимающееся проектированием и непосредственным воплощением смелых кастом-проектов. Это всевозможные переделки и реконструкция старых и новых мотоциклов, мопедов, квадроциклов. Диапазон возможностей огромен: от простого тех. обслуживания до тотальной переделки практически любых транспортных средств — в планах ателье постройка уникальных автомобилей в стиле <strong>hot rod</strong>. В мастерской чисто и светло, на пяти мотоподъемниках сверкают хромированными деталями мощные мотоциклы. Отчасти помещение напоминает шоу-рум, но размещенный вдоль стен многочисленный инструмент позволяет осознать масштабы работ, которые здесь производятся. Можно увидеть внушительный <strong>токарный</strong>, а также <strong>сверлильные и точильные станки</strong>, окруженные верстаками с ящиками заполненными всеми необходимыми слесарными приспособлениями.</p><p><img src="'+zs+'" alt=""><img src="'+Ds+'" alt=""><img src="'+Js+'" alt=""><img src="'+As+'" alt=""><img src="'+Cs+'" alt=""><img src="'+Us+'" alt=""><img src="'+Fs+'" alt=""></p><p>Несмотря на внушительное наполнение, мастерские проекта Про.Движение на Кристалле еще только в начале своего пути. У команды проекта большие планы по развитию пространства и его возможностей. В частности в скором времени планируется постройка полноценной <strong>покрасочной комнаты</strong> для работы с распыляемыми пульверизатором и порошковыми красками. Кстати, приглашают новых конфедератов <strong>со</strong> своей покрасочной комнатой на льготных основаниях. В складском помещении, располагающемся в пристройке к основному зданию мастерской, активно строится удобная <strong>система хранения</strong>, в которой каждому резиденту и конфедерату будет отведено своё место. Скоро завершится оформление кузницы, а <strong>25 апреля в мастерских пройдет <a href="/practice/event/den-otkrytyh-vorot/" title="День открытых ворот">&quot;День открытых ворот&quot;</a></strong>, где любой желающий сможет внимательно изучить всё имеющееся оборудование, познакомиться с резидентами и конфедератами мастерской, а также <a href="/workshop/tour/pro-dvizhenie-open/">увидеть их виртуозное владение станками и инструментами вживую</a>.</p><p>Проект Про.Движение работает, как и другие подобные мастерские, <strong>в смешанном режиме</strong>. Мастерская <strong>принимает заказы</strong> на изготовление изделий практических любых масштабов и любой сложности из металла и других материалов. Сотрудники проекта готовы взяться за ваш проект еще на стадии сырой идеи и совместными усилиями довести его до реализации. Возможны и более открытые формы взаимодействия.</p><p>Есть возможность совместной разработки и <strong>воплощения идеи вместе с мастером</strong>, наблюдая за работой и по мере сил включаясь в процесс. В ходе практической реализации задумки можно овладеть большим количеством новых навыков и умений. Также можно просто <strong>&quot;арендовать&quot; время мастера</strong> для помощи и руководства в ходе самостоятельного воплощения собственных проектов в рамках аренды рабочего места в коворкинге.</p><p><img src="'+Os+'" alt=""><img src="'+Rs+'" alt=""><img src="'+$s+'" alt=""></p><p><img src="'+Es+'" alt=""></p><p>Однако наиболее эффективным способом в деле обучения являются, конечно, мастер-классы и курсы, регулярно проводимые мастерами и сотрудниками мастерской. Так <strong>&quot;Курс кузнечного дела&quot;, состоящий из 8 занятий,</strong> позволит не только воплотить свой творческий проект, но и получить целостное представление об этом сложном и полном тайн деле. Для лучшего усвоения материала и обеспечения полной безопасности процесса, курсы и мастер-классы проводятся <strong>для малых групп</strong> участников от 2 до 6 человек. Также есть программа <strong>набора мастерами учеников</strong> для вдумчивого обучения и передачи навыков и умений во всей их полноте. Несмотря на масштабность проекта, Про.Движение сохраняет индивидуальный подход к желающим влиться в коллектив мастеров.</p><p><strong>Конфедерация</strong> — объединение независимых мастерских под одной крышей — уже приносит множество плюсов всем её участникам. Мастерская серьезно расширяет спектр возможных к реализации заказов, при необходимости <strong>перенаправляя их соответствующим конфедератам</strong> и пользуясь увеличенным парком общего оборудования. Конфедераты совместно решают общие вопросы от наполнения баллонов для аргонодуговой сварки и закупки материалов до доработки и ремонта имеющихся станков и <strong>помощи советом и делом в работе над проектами</strong>. Все общественные ресурсы от питьевой воды до подключения к Интернету финансируются совместно всеми конфедератами. Также общими силами производится ремонт и развитие пространства.</p><p>Команда проекта Про.Движение настроена не останавливаться на достигнутом. Здесь активно вынашиваются планы развития как сугубо производственной, так и образовательной сфер. В планах повышение качества и количества курсов и мастер-классов, получение соответствующих лицензий и формирование четкой образовательной программы. Сам проект отнюдь не ограничен стенами мастерской и продолжает участвовать во всевозможных выездных событиях и совместных проектах. Строятся планы дальнейшей фестивальной деятельности.</p><p>Мы будем активно следить за развитием проекта и мастерских. Конфедерация творческих проектов — отличный формат для объединения разрозненных усилий и достижения синергетических эффектов открытого сотрудничества. А формат открытой мастерской с широкой образовательной программой — прекрасный способ отыскать и развить скрытый творческий потенциал современных горожан. Верным путём идём, это радует.</p><p><img src="'+Ns+'" alt=""><img src="'+Vs+'" alt=""><img src="'+Ys+'" alt=""></p>',36),Qs=[Ls];function Ws(Xs,Zs,Hs,st,tt,ot){return t(),o("div",null,Qs)}const _t=s(Ks,[["render",Ws]]);export{at as __pageData,_t as default};
