import{_ as s,a as t,o,H as r}from"./chunks/framework.Cao6T-Ti.js";const _="/assets/IMG_9825.B97VqfMO.jpg",p="/assets/IMG_9826.l-_YtGb6.jpg",g="/assets/IMG_9828.B18b7MS0.jpg",a="/assets/Planirovka.DBZ5yMfV.jpg",c="/assets/omT6PIFcfn8.yAS8Hnxe.jpg",n="/assets/hg_XAZhjW74.u4FEVTgF.jpg",i="/assets/bEMR-y82aQU.DmHlkUrq.jpg",m="/assets/IMG_9874.D5OjF0Z4.jpg",e="/assets/IMG_9831.BIIlmJlp.jpg",l="/assets/IMG_9829.xaEV5fRC.jpg",j="/assets/IMG_9832.DNEauy9k.jpg",I="/assets/IMG_9833.Df5RLv88.jpg",M="/assets/IMG_9834.DfLrUmnX.jpg",G="/assets/IMG_9840.BObfw6Nc.jpg",u="/assets/IMG_9843.BsceyvC8.jpg",h="/assets/IMG_9875.DlMxWCOZ.jpg",D="/assets/IMG_9844.6hinQrlQ.jpg",d="/assets/IMG_9845.DGI2il_j.jpg",q="/assets/IMG_9841.DN7Zy9sa.jpg",C="/assets/IMG_9842.4LJXmRcW.jpg",B="/assets/IMG_9846.DmNU23Zt.jpg",f="/assets/IMG_9848.ChmbL347.jpg",k="/assets/IMG_9849.BjTl3Yp2.jpg",v="/assets/IMG_9847.o0WVPFby.jpg",b="/assets/IMG_9904.CbDc9_Gb.jpg",y="/assets/IMG_9850.ByUyx_YF.jpg",x="/assets/IMG_9851.iKDKyzGW.jpg",P="/assets/IMG_9852.ZEgQM3_P.jpg",L="/assets/IMG_9853.B68P27_s.jpg",U="/assets/IMG_9854.DNpIZ4qz.jpg",Y="/assets/IMG_9855.CgPPi9aw.jpg",w="/assets/IMG_9856.B2-v_Atb.jpg",F="/assets/IMG_9857.zmg3udB8.jpg",R="/assets/IMG_9858.CQCTnRe6.jpg",S="/assets/IMG_9905.BbDUsYYo.jpg",z="/assets/IMG_9906.EBX9XjQI.jpg",E="/assets/IMG_9886.C7TuNdrH.jpg",T="/assets/IMG_9887.BLJSOC46.jpg",N="/assets/IMG_9888.C0uQjeEo.jpg",O="/assets/IMG_9889.BU3WueCZ.jpg",V="/assets/IMG_9890.CR3DnlsU.jpg",Q="/assets/IMG_9891.D5QgCwHq.jpg",J="/assets/IMG_9876.Db1wDFWB.jpg",Z="/assets/IMG_9877.BIZSOp1E.jpg",H="/assets/IMG_9878.D5vesFh-.jpg",A="/assets/IMG_9879.CyzhLQco.jpg",W="/assets/IMG_9880.Bc2RcEfE.jpg",K="/assets/IMG_9881.KBYhqQvC.jpg",X="/assets/IMG_9882._mWvgnho.jpg",$="/assets/IMG_9883.BYzvL9UU.jpg",ss="/assets/IMG_9884.CgRq8xbk.jpg",ts="/assets/IMG_9885.Lwc9Nqx_.jpg",os="/assets/IMG_9868.DLyWfQEL.jpg",rs="/assets/Q54yi8F7DRw.CcPEuCuV.jpg",_s="/assets/r94klGlk7Jk.C2KlnmIs.jpg",ps="/assets/KjTFcPySKPk.BUf8F-8v.jpg",gs="/assets/Men2OSaFvJ0.DhfF64_z.jpg",as="/assets/IMG_9892.ptJdsUD9.jpg",cs="/assets/IMG_9893.CVaBVySt.jpg",ns="/assets/IMG_9894.CrzEfq4m.jpg",is="/assets/IMG_9895.DW0SRwRr.jpg",ms="/assets/IMG_9896.BQ_yrPVG.jpg",es="/assets/IMG_9897.CAOlR46c.jpg",ls="/assets/IMG_9907.DXd3R5N4.jpg",js="/assets/IMG_9898.DcZyhCMu.jpg",Is="/assets/IMG_9835.BYkXodaL.jpg",Ms="/assets/IMG_9838.DuLFSDbJ.jpg",Gs="/assets/IMG_9839.DqKragoP.jpg",us="/assets/IMG_9911.BbOaRGdq.jpg",hs="/assets/IMG_9866.zPnsARLg.jpg",Ds="/assets/IMG_9865.rF9q3KmG.jpg",ds="/assets/IMG_9870.p4p5DxeM.jpg",qs="/assets/IMG_9863.DG-O9Fii.jpg",Cs="/assets/IMG_9864.BUkZYY_3.jpg",Bs="/assets/IMG_9859.Cxxd4-aH.jpg",fs="/assets/IMG_9860.SbLq8a79.jpg",ks="/assets/IMG_9861.D0qHnc6P.jpg",vs="/assets/IMG_9862.CpM976cH.jpg",bs="/assets/IMG_9867.lGwNYxHw.jpg",ys="/assets/IMG_9869.DrxYY_uy.jpg",xs="/assets/IMG_9909.DCNT2SY9.jpg",Ps="/assets/IMG_9901.CLuuNdf6.jpg",Ls="/assets/IMG_9902.D-cPL0-m.jpg",Us="/assets/ilMdhzPJJ0.DsmCmx9m.jpg",Ys="/assets/ORgUGsLagY.IbODbgF_.jpg",ws="/assets/0_GUbIa839w.lY1_NqpH.jpg",Fs="/assets/6Prt8_BmeLY.DgBXiE3c.jpg",Rs="/assets/8Bju_ADxh90.DTIHi5dh.jpg",Ss="/assets/b5ko5OxPiTI.VR1L1imD.jpg",zs="/assets/D0JjGJMuU9Y.CmzaSgmz.jpg",Es="/assets/IMG_9912.96wDwVLq.jpg",Ts="/assets/IMG_9910.BjJK0v4t.jpg",Ns="/assets/IMG_9899.D7Y-6lcU.jpg",Os="/assets/IMG_9903.2bh1sevo.jpg",Vs="/assets/IMG_9873.v5CcMZMT.jpg",Qs="/assets/IMG_9872.DEd9nINV.jpg",Js="/assets/IMG_9871.DcOQv0To.jpg",_t=JSON.parse('{"title":"Мастерские проекта Про.Движение","description":"","frontmatter":{"title":"Мастерские проекта Про.Движение","date":"2015-04-15","cover":"cover.jpg"},"headers":[],"relativePath":"workshop/tour/pro-dvizhenie/index.md","filePath":"workshop/tour/pro-dvizhenie/index.md"}'),Zs={name:"workshop/tour/pro-dvizhenie/index.md"},Hs=r('<p>Мы уже рассказали, где в Москве можно поработать с деревом, настало время расширить спектр доступных материалов. На этот раз мы побываем в <a href="/workshop/archive/pro-dvizhenie/" title="Мастерские проекта Про.Движение">мастерских проекта Про.Движение</a>, основным приоритетом которых является обработка металла, как мелкая так и крупная.</p><p>Существуя в виде мобильного фестивального проекта в течение более 5 лет, проект Про.Движение решил &quot;осесть&quot; совсем недавно. Идея <a href="/theory/repost/svobodnye-masterskie/" title="Проект открытой архитектурной мастерской “Свободные мастерские”">&quot;Свободных мастерских&quot;</a> сформировалась в недрах команды примерно к концу 2013 года, а сама мастерская на территории бывшего завода &quot;Кристалл&quot; была открыта в ноябре 2014 года. За это время проект не только освоился и обустроил рабочее пространство, но и успел превратиться в целую Конфедерацию, объединив под одной крышей сразу несколько независимых мастеров и мастерских.</p><p><img src="'+_+'" alt=""><img src="'+p+'" alt=""><img src="'+g+'" alt=""></p><p>Водочный завод &quot;Кристалл&quot; прекратил свою работу в конце 2012 года, а окончательно производство переехало в подмосковное Корыстово в апреле 2013 года. Сегодня &quot;Кристалл&quot; — это объект активного <strong>редевелопмента</strong>, т.е. переустройства промышленного объекта в современный городской квартал. Производственное оборудование давно вывезено, в цехах уже не пахнет спиртом. Помещения завода активно заселяются всевозможными творческими проектами. Несмотря на это, рудименты строгого производственного контроля здесь еще сохраняются. В частности, для того, чтобы попасть внутрь вам понадобится записаться у охраны в список на проход в мастерские Про.Движение. Лучше предупредить о своём посещении заранее, чтобы вас могли встретить. Это особенно важно если вы приехали в первый раз, т.к. территория завода внушительная и заблудиться там не так уж и сложно. Чтобы этого не произошло, сразу после выхода из проходной поворачивайте направо, проходите через арку, затем еще раз поворачиваете направо. Далее нужно будет повернуть налево и пройти оставшиеся 50 метров до одноэтажного здания с большими черными воротами. Скорее всего ворота окажутся открытыми, заходим!</p><p><img src="'+a+'" alt=""></p><p>Мы сразу попадаем в <strong>зону загрузки</strong>. Здесь нас встречают два внушительных станка, один из которых режет листовой металл, а другой позволяет сгибать листы под заданным углом. Здесь происходит первичная обработка поступающих в мастерскую материалов. Повернув в дверь налево, отсюда можно попасть в <strong>комнату отдыха</strong>, оборудованную индивидуальными шкафчиками для каждого резидента, диваном, столами и стульями. Здесь работает вай-фай, можно попить воды, угоститься чаем с печеньками. Тут мы встретили замечательных сотрудниц мастерской: <strong>директора по маркетингу Асю и администратора мастерской Александру</strong>. Вместе с ними мы изучили наполнение мастерской и спешим поделиться этим знанием с вами.</p><p><img src="'+c+'" alt=""><img src="'+n+'" alt=""><img src="'+i+'" alt=""><img src="'+m+'" alt=""><img src="'+e+'" alt=""><img src="'+l+'" alt=""><img src="'+j+'" alt=""><img src="'+I+'" alt=""><img src="'+M+'" alt=""><img src="'+G+'" alt=""><img src="'+u+'" alt=""><img src="'+h+'" alt=""></p><p>Напротив двери в комнату отдыха в зале загрузки есть еще одна дверь, ведущая в <strong>макетную мастерскую Urban Garden</strong>. Небольшое помещение отлично подходит для работы с моделями и макетами. В конце комнаты стоит большое черное устройство, как-то связанное с 3D-печатью. К сожалению, точнее понять не удалось, попробуем выяснить его точное назначение в другой раз. На полках можно увидеть образцы печати вполне достойного качества. <strong>Широкоформатный принтер</strong> и <strong>терморежущий станочек Styrocut</strong> — хороший комплект для изготовления пластиковых деталей, а большие окна и мощная вытяжка создают приятную и безопасную атмосферу для кропотливой макетной работы.</p><p><img src="'+D+'" alt=""><img src="'+d+'" alt=""><img src="'+q+'" alt=""><img src="'+C+'" alt=""><img src="'+B+'" alt=""><img src="'+f+'" alt=""><img src="'+k+'" alt=""></p><p>Прямо из зоны загрузки в основную рабочую зону мастерской ведёт длинный коридор, из которого можно попасть в <strong>кузницу</strong> и в <strong>монтажную комнату</strong> с дверью на замке. Ограничение доступа в это помещение не удивительно: здесь располагается основное сварочное оборудование. Помимо нескольких <strong>сварочных инверторов</strong>, в том числе и для <strong>аргонодуговой сварки</strong>, в монтажной можно обнаружить <strong>сверлильный, точильный  и шлифовальный станки</strong>, <strong>циркулярную пилу</strong>, <strong>УШМ</strong> и другой ручной инструмент. В помещении для лучшей вентиляции устроены две вытяжки.</p><p><img src="'+v+'" alt=""><img src="'+b+'" alt=""><img src="'+y+'" alt=""><img src="'+x+'" alt=""><img src="'+P+'" alt=""><img src="'+L+'" alt=""><img src="'+U+'" alt=""><img src="'+Y+'" alt=""><img src="'+w+'" alt=""><img src="'+F+'" alt=""><img src="'+R+'" alt=""><img src="'+S+'" alt=""><img src="'+z+'" alt=""></p><p>Напротив монтажной, отделенная от коридора перегородкой, расположилась <strong>кузница</strong>. Ковка и прочая формовка металла в основном производятся здесь. На момент нашего визита кузница находилась на стадии переоборудования и подробно ознакомиться с оборудованием этого зала нам в этот раз не удалось. Однако вскоре мы внимательно изучили кузницу на дне открытых ворот, где было <a href="/workshop/tour/pro-dvizhenie-open/">продемонстрировано множество возможностей мастерской в действии</a>.</p><p><img src="'+E+'" alt=""><img src="'+T+'" alt=""><img src="'+N+'" alt=""><img src="'+O+'" alt=""><img src="'+V+'" alt=""><img src="'+Q+'" alt=""></p><p>Главный коридор ведёт нас в большой слесарный цех, где разместились сразу три &quot;конфедерата&quot;. Слева от входа находится уголок <a href="https://vk.com/custombicycles" class="external-link" target="_blank" rel="noreferrer">велоателье <strong>Custo Bicycles</strong></a> — творческая площадка мастера <a href="https://vk.com/id383417" class="external-link" target="_blank" rel="noreferrer">Никиты Сергеева</a>. Здесь разрабатываются и создаются не просто велосипеды, а настоящие произведения искусства, сочетающие высокие технологии и тонкий подход к деталям. Ателье берется за проекты любой сложности и доводит каждый до идеала. <a href="https://vk.com/album-50140018_196634048" class="external-link" target="_blank" rel="noreferrer">Электровелосипед &quot;Ракета&quot;</a> — знаковая работа, дающая понять возможности команды Custo Bicycles.  Сказывается наличие всего необходимого оборудования и свободное владение важными технологиями, такими как, например, <strong>гибка и сварка легких алюминиевых труб</strong> и других конструктивных элементов. Сборка рам и велосипедов производится на специальном <strong>стапеле</strong>, а мощный <strong>токарный станок</strong> позволяет изготавливать уникальные элементы конструкции. Каждому конфедерату выделяется несколько верстаков, полок, ящиков для удобной работы и хранения всего необходимого.</p><p><img src="'+J+'" alt=""><img src="'+Z+'" alt=""><img src="'+H+'" alt=""><img src="'+A+'" alt=""><img src="'+W+'" alt=""><img src="'+K+'" alt=""><img src="'+X+'" alt=""><img src="'+$+'" alt=""><img src="'+ss+'" alt=""><img src="'+ts+'" alt=""><img src="'+os+'" alt=""></p><p>Напротив окна всю территорию вдоль стены заняла мастерская <strong><a href="http://copperbrass.ru/" class="external-link" target="_blank" rel="noreferrer">CopperBrass</a></strong> братьев <strong>Дмитрия и Олега Зубковых</strong>, производящая множество всевозможных изделий из металла и не только. Команда мастерской прошла долгий путь в поисках своего собственного стиля, освоив огромное количество производственных процессов. Мощные <strong>кузнечные молоты, прессы</strong> и другие станки позволяют использовать в дизайн-проектах мастерской практически любые металлы и прочие материалы. Отсюда выходят уникальные светильники, стильные камины и всевозможные элементы интерьера от дверных ручек до скульптур и прочих архитектурных деталей.</p><p><img src="'+rs+'" alt=""><img src="'+_s+'" alt=""><img src="'+ps+'" alt=""><img src="'+gs+'" alt=""><img src="'+as+'" alt=""><img src="'+cs+'" alt=""><img src="'+ns+'" alt=""><img src="'+is+'" alt=""><img src="'+ms+'" alt=""><img src="'+es+'" alt=""></p><p>Здесь же размещается <strong>лаборатория индивидуального камина FireBAU</strong>, где художник <strong>Евгений Миленький</strong> создаёт настоящие произведения каминного искусства. Возможности мастерских проекта Про.Движение и высокая квалификация мастера позволяют браться за самые сложные проекты и неизменно получать потрясающие результаты.</p><p><img src="'+ls+'" alt=""><img src="'+js+'" alt=""></p><p>В дальнем углу этого большого цеха разместилось <strong>&quot;Объединение практикующих архитекторов&quot; ОПАrch</strong>, занимающееся созданием архитектурных макетов, необычной мебели и прочих предметов интерьера, преимущественно из дерева. Молодая команда смело экспериментирует с формой, используя для производства сложное ЧПУ оборудование, находя неожиданные решения для вполне обыденных задач.</p><p><img src="'+Is+'" alt=""><img src="'+Ms+'" alt=""><img src="'+Gs+'" alt=""><img src="'+us+'" alt=""><img src="'+hs+'" alt=""><img src="'+Ds+'" alt=""><img src="'+ds+'" alt=""><img src="'+qs+'" alt=""><img src="'+Cs+'" alt=""><img src="'+Bs+'" alt=""><img src="'+fs+'" alt=""><img src="'+ks+'" alt=""><img src="'+vs+'" alt=""><img src="'+bs+'" alt=""></p><p>Это еще далеко не всё. Из длинного коридора, соединяющего просторные цеха мастерской можно попасть в <strong>офис архитектурного бюро А2</strong>, руководит которым основатель проекта Про.Движение <strong>Пётр Виноградов</strong>. Небольшое помещение эффектно увеличили за счёт сварной стальной надстройки второго этажа, где и расположено рабочее место руководителя. Наверх ведёт необычная крутая лестница, разработанная и созданная здесь же в мастерской.</p><p>В самом конце коридора находится комната, где постепенно расквартировывается <strong>проект ИСБМ</strong> — производственно-конструкторское бюро, берущееся за задачи любой сложности в области 3D-печати, фрезеровки на серьезных ЧПУ станках, а также в раскрое различных листовых материалов. Мастера высокой квалификации используют сложное и дорогостоящее оборудование.</p><p><img src="'+ys+'" alt=""><img src="'+xs+'" alt=""><img src="'+Ps+'" alt=""><img src="'+Ls+'" alt=""></p><p>Если здесь повернуть налево, то попадаешь в просторный зал, где разместилась мастерская еще одного конфедерата — <strong>ателье CusMos</strong>, занимающееся проектированием и непосредственным воплощением смелых кастом-проектов. Это всевозможные переделки и реконструкция старых и новых мотоциклов, мопедов, квадроциклов. Диапазон возможностей огромен: от простого тех. обслуживания до тотальной переделки практически любых транспортных средств — в планах ателье постройка уникальных автомобилей в стиле <strong>hot rod</strong>. В мастерской чисто и светло, на пяти мотоподъемниках сверкают хромированными деталями мощные мотоциклы. Отчасти помещение напоминает шоу-рум, но размещенный вдоль стен многочисленный инструмент позволяет осознать масштабы работ, которые здесь производятся. Можно увидеть внушительный <strong>токарный</strong>, а также <strong>сверлильные и точильные станки</strong>, окруженные верстаками с ящиками заполненными всеми необходимыми слесарными приспособлениями.</p><p><img src="'+Us+'" alt=""><img src="'+Ys+'" alt=""><img src="'+ws+'" alt=""><img src="'+Fs+'" alt=""><img src="'+Rs+'" alt=""><img src="'+Ss+'" alt=""><img src="'+zs+'" alt=""></p><p>Несмотря на внушительное наполнение, мастерские проекта Про.Движение на Кристалле еще только в начале своего пути. У команды проекта большие планы по развитию пространства и его возможностей. В частности в скором времени планируется постройка полноценной <strong>покрасочной комнаты</strong> для работы с распыляемыми пульверизатором и порошковыми красками. Кстати, приглашают новых конфедератов <strong>со</strong> своей покрасочной комнатой на льготных основаниях. В складском помещении, располагающемся в пристройке к основному зданию мастерской, активно строится удобная <strong>система хранения</strong>, в которой каждому резиденту и конфедерату будет отведено своё место. Скоро завершится оформление кузницы, а <strong>25 апреля в мастерских пройдет <a href="/practice/event/den-otkrytyh-vorot/" title="День открытых ворот">&quot;День открытых ворот&quot;</a></strong>, где любой желающий сможет внимательно изучить всё имеющееся оборудование, познакомиться с резидентами и конфедератами мастерской, а также <a href="/workshop/tour/pro-dvizhenie-open/">увидеть их виртуозное владение станками и инструментами вживую</a>.</p><p>Проект Про.Движение работает, как и другие подобные мастерские, <strong>в смешанном режиме</strong>. Мастерская <strong>принимает заказы</strong> на изготовление изделий практических любых масштабов и любой сложности из металла и других материалов. Сотрудники проекта готовы взяться за ваш проект еще на стадии сырой идеи и совместными усилиями довести его до реализации. Возможны и более открытые формы взаимодействия.</p><p>Есть возможность совместной разработки и <strong>воплощения идеи вместе с мастером</strong>, наблюдая за работой и по мере сил включаясь в процесс. В ходе практической реализации задумки можно овладеть большим количеством новых навыков и умений. Также можно просто <strong>&quot;арендовать&quot; время мастера</strong> для помощи и руководства в ходе самостоятельного воплощения собственных проектов в рамках аренды рабочего места в коворкинге.</p><p><img src="'+Es+'" alt=""><img src="'+Ts+'" alt=""><img src="'+Ns+'" alt=""></p><p><img src="'+Os+'" alt=""></p><p>Однако наиболее эффективным способом в деле обучения являются, конечно, мастер-классы и курсы, регулярно проводимые мастерами и сотрудниками мастерской. Так <strong>&quot;Курс кузнечного дела&quot;, состоящий из 8 занятий,</strong> позволит не только воплотить свой творческий проект, но и получить целостное представление об этом сложном и полном тайн деле. Для лучшего усвоения материала и обеспечения полной безопасности процесса, курсы и мастер-классы проводятся <strong>для малых групп</strong> участников от 2 до 6 человек. Также есть программа <strong>набора мастерами учеников</strong> для вдумчивого обучения и передачи навыков и умений во всей их полноте. Несмотря на масштабность проекта, Про.Движение сохраняет индивидуальный подход к желающим влиться в коллектив мастеров.</p><p><strong>Конфедерация</strong> — объединение независимых мастерских под одной крышей — уже приносит множество плюсов всем её участникам. Мастерская серьезно расширяет спектр возможных к реализации заказов, при необходимости <strong>перенаправляя их соответствующим конфедератам</strong> и пользуясь увеличенным парком общего оборудования. Конфедераты совместно решают общие вопросы от наполнения баллонов для аргонодуговой сварки и закупки материалов до доработки и ремонта имеющихся станков и <strong>помощи советом и делом в работе над проектами</strong>. Все общественные ресурсы от питьевой воды до подключения к Интернету финансируются совместно всеми конфедератами. Также общими силами производится ремонт и развитие пространства.</p><p>Команда проекта Про.Движение настроена не останавливаться на достигнутом. Здесь активно вынашиваются планы развития как сугубо производственной, так и образовательной сфер. В планах повышение качества и количества курсов и мастер-классов, получение соответствующих лицензий и формирование четкой образовательной программы. Сам проект отнюдь не ограничен стенами мастерской и продолжает участвовать во всевозможных выездных событиях и совместных проектах. Строятся планы дальнейшей фестивальной деятельности.</p><p>Мы будем активно следить за развитием проекта и мастерских. Конфедерация творческих проектов — отличный формат для объединения разрозненных усилий и достижения синергетических эффектов открытого сотрудничества. А формат открытой мастерской с широкой образовательной программой — прекрасный способ отыскать и развить скрытый творческий потенциал современных горожан. Верным путём идём, это радует.</p><p><img src="'+Vs+'" alt=""><img src="'+Qs+'" alt=""><img src="'+Js+'" alt=""></p>',36),As=[Hs];function Ws(Ks,Xs,$s,st,tt,ot){return o(),t("div",null,As)}const pt=s(Zs,[["render",Ws]]);export{_t as __pageData,pt as default};
