import{_ as s,o as t,c as r,e as o}from"./chunks/framework.78e651f5.js";const a="/assets/les-water.7bcfd87b.jpg",g="/assets/les.542f935a.jpg",n="/assets/water-moon.2df72db6.jpg",e="/assets/water2.7bd9a4d5.jpg",p="/assets/les-kite-1.0093b6ab.jpg",c="/assets/tusa-odin.ccaf872a.jpg",i="/assets/tusa16waf.45f27e8d.jpg",_="/assets/tusa.e0c65970.jpg",l="/assets/tusa100.72adddf1.jpg",m="/assets/yogi-budd2.285eecda.jpg",d="/assets/camp111.fdba8a94.jpg",f="/assets/yogi12.7326a1c4.jpg",b="/assets/dom.ff813ca5.jpg",j="/assets/dom3.d44986df.jpg",h="/assets/dom12.e2d769c1.jpg",u="/assets/IMG_1912.199e5e05.jpg",k="/assets/IMG_1919.64d2300d.jpg",q="/assets/IMG_1978.8d98dcbb.jpg",M="/assets/IMG_2062.31ec5233.jpg",I="/assets/IMG_2066.e943b6a4.jpg",G="/assets/IMG_2067.b6839f66.jpg",x="/assets/stroy-ed.ca68a179.jpg",P="/assets/gen.cc728ebc.jpg",v="/assets/IMG_1947.98a36822.jpg",w="/assets/IMG_1982.3ad98e8b.jpg",y="/assets/IMG_1818.8184d9d1.jpg",D="/assets/IMG_2000.82faacda.jpg",F="/assets/IMG_1907.8b20346e.jpg",T="/assets/IMG_1847.26f98751.jpg",O="/assets/IMG_1983.824606e3.jpg",A="/assets/IMG_1985.af1815c9.jpg",S="/assets/IMG_1986.c35c5610.jpg",E="/assets/IMG_1988.777ef64d.jpg",V="/assets/IMG_1989.8bf0c4f0.jpg",$="/assets/IMG_1994.58b025e8.jpg",N="/assets/IMG_1996.cde7b331.jpg",W="/assets/IMG_2068.8a822ce7.jpg",z="/assets/IMG_2069.c231ce3a.jpg",B="/assets/IMG_1925.21ae9680.jpg",C="/assets/les-sam.ac5ccf8f.jpg",J="/assets/note-sklad.f4b5c2a8.jpg",K="/assets/IMG_2004.880189e0.jpg",R="/assets/IMG_2006.237e65ac.jpg",H="/assets/IMG_2007.9c358855.jpg",L="/assets/IMG_2012.06ba1f03.jpg",Q="/assets/IMG_2013.0a7925a0.jpg",U="/assets/IMG_2015.3c749e7d.jpg",X="/assets/IMG_2018.a96522ae.jpg",Y="/assets/IMG_2021.ef624576.jpg",Z="/assets/IMG_2022.cb3819da.jpg",ss="/assets/IMG_2023.c4ebf070.jpg",ts="/assets/IMG_2054.01ee448b.jpg",rs="/assets/IMG_2024.ed2b8274.jpg",os="/assets/IMG_2026.50098195.jpg",as="/assets/IMG_2028.7b693307.jpg",gs="/assets/IMG_2031.a5219afe.jpg",ns="/assets/IMG_2034.b90d1315.jpg",es="/assets/tusa15.b127787a.jpg",ps="/assets/vol-in.1f818081.jpg",cs="/assets/vol.69768dbf.jpg",is="/assets/note-kitchen.f6c420e8.jpg",_s="/assets/stroy00.61acccff.jpg",ls="/assets/eat-14.1426f806.jpg",ms="/assets/eat2.7eb488f1.jpg",ds="/assets/eat3.f7f15a42.jpg",fs="/assets/tusa-8.50230d82.jpg",bs="/assets/tusa2.24c37825.jpg",js="/assets/tusa13.aed765dd.jpg",hs="/assets/IMG_1892.a9490a5e.jpg",us="/assets/art-mass.de7a76a3.jpg",ks="/assets/art1-1.3eabaf54.jpg",qs="/assets/art1.a1f8eddb.jpg",Ms="/assets/art5.1e500785.jpg",Is="/assets/art1000.d329ea91.jpg",Gs="/assets/old3.dce3afa1.jpg",xs="/assets/Tusovochnyi-vese-lyi-bezalkogolnyi-subbotnik.69a88d24.jpg",Ps="/assets/freeDOM-2014.3fd498a0.jpg",vs="/assets/yogi.b4a2fba0.jpg",ws="/assets/IMG_1905.61fda129.jpg",ys="/assets/IMG_1906.0882ed6d.jpg",Ds="/assets/sport4.5ac28b24.jpg",Fs="/assets/sport6.132b0e09.jpg",Ts="/assets/tusa1.f2698d0f.jpg",Os="/assets/IMG_2053.bf500e8f.jpg",As="/assets/kite-fly.6ca3e2f2.jpg",Ss="/assets/kite1.93b44fae.jpg",Es="/assets/serg1.eb26f910.jpg",Vs="/assets/serg.03690e47.jpg",$s="/assets/water.e12fffd1.jpg",Us=JSON.parse('{"title":"Благость и безумие волонтёрства  в сёрф-лагере на Горьковском море","description":"","frontmatter":{"title":"Благость и безумие волонтёрства  в сёрф-лагере на Горьковском море","date":"2016-09-09","cover":"0_cover.jpg"},"headers":[],"relativePath":"workshop/tour/surf2free/index.md","filePath":"workshop/tour/surf2free/index.md"}'),Ns={name:"workshop/tour/surf2free/index.md"},Ws=o('<p>Живописный берег широко разлившейся Волги в полутора часах езды от Нижнего Новгорода. Многие горожане регулярно выбираются на базы отдыха и дикие пляжи на этом побережье. Сосновый лес, песчаный пляж — прекрасные условия для активного загородного отдыха. Летом здесь можно не только загорать и купаться: огромное водохранилище наполняется любителями самых разных видов катания и водного спорта. Виндсёрферы и кайтеры съезжаются сюда со всей страны и даже мира, а плавный уклон дна отлично подходит для обучения новичков в этих дисциплинах. Зимой всё Горьковское море замерзает, открывая свои просторы для любителей сноукайтинга и лыж.</p><p><img src="'+a+'" alt=""><img src="'+g+'" alt=""><img src="'+n+'" alt=""><img src="'+e+'" alt=""><img src="'+p+'" alt=""></p><p>Здесь на лесистом берегу моря рядом с обычной базой отдыха «Буревестник» уже восьмой сезон существует необычный <a href="http://www.surf2free.ru/" class="external-link" target="_blank" rel="noreferrer">сёрф-лагерь «FreeDOM»</a> — <strong>место, где расцветают люди</strong>. Прозрачно устроенный, успешно функционирующий волонтёрский проект — довольно редкое для современной российской культуры явление. Существующая уже <strong>8 лет</strong> автономная школа виндсёрфинга и кайтинга, построенная полностью руками друзей и волонтёров проекта и поддерживаемая ежегодно обновляющейся командой участников — яркий пример совместного творчества. Мы уже больше года следим за их публикациями в сети, и решили в 2016 году обязательно побывать там сами, желательно между многолюдными фестивалями, и хоть немного изучить этот замечательный проект изнутри. За это лето в лагере прошёл очередной архитектурный фестиваль <a href="https://vk.com/woodenwind" class="external-link" target="_blank" rel="noreferrer">«Деревянный ветер»</a>, в шестой раз прогремел масштабный фестиваль стихий <a href="http://wafest.ru/" class="external-link" target="_blank" rel="noreferrer">«WAFEst»</a>, состоялся четвёртый фестиваль йоги <a href="http://nyamafest.ru/" class="external-link" target="_blank" rel="noreferrer">«Нияма»</a>, проводился международный спортивный лагерь <a href="https://vk.com/summerpole" class="external-link" target="_blank" rel="noreferrer">«Pole Dance»</a>, а мы всё никак не могли найти время на поездку. Нам удалось добраться до этого замечательного места только к самому закрытию сезона, где всего за пару дней пребывания успели погрузиться в умиротворённую атмосферу пространства свободы, немного лучше познакомиться с проектом, его основателем и командой стойких волонтёров.</p><p><img src="'+c+'" alt=""><img src="'+i+'" alt=""><img src="'+_+'" alt=""><img src="'+l+'" alt=""><img src="'+m+'" alt=""><img src="'+d+'" alt=""><img src="'+f+'" alt=""></p><p>Многие приезжают сюда отдохнуть на несколько дней, некоторые остаются подольше. Ветер, достаточный для катания на виндсёрфе здесь не редок, а большинство кайтеров хорошо умеет дожидаться необходимой силы порывов. Кто-то решается остаться на берегу водохранилища на всё лето, включившись в ряды волонтёров лагеря, а кто-то даже проводит здесь зиму. Но не только вода и ветер притягивают сюда людей. Для многих сёрф-лагерь стал средоточием новой культуры отдыха и жизни на природе в гармонии с окружающим миром. Это не просто природный уголок свободы от городской суеты, это пространство свободы самопознания и самореализации. Каждый может найти здесь свой уникальный баланс работы и отдыха, отодвигающий вопросы денег на второй план, и дающий возможность сконцентрироваться на самом процессе жизни в лесу.</p><p>Крепкая философская основа проекта, <a href="https://vk.com/topic-9624588_31981325" class="external-link" target="_blank" rel="noreferrer">опубликованная как ориентир для всех, кому интересно принять в нём участие,</a> подкупает своей прямотой и ясностью. Объединённые высокими идеалами и ценностями, участники проекта становятся не просто коллегами по проекту, но попутчиками в этом долгом путешествии к самореализации. Ясность мыслей и глубина осознания — плоды многолетнего саморазвития и социальных экспериментов основателя этого пространства — <strong>Сергея Шилина</strong>. Каждое лето несёт с собой массу нового яркого опыта взаимодействия с первозданными природными стихиями, со слаженной волонтёрской командой, а также с невероятным количеством интересных личностей в формате фестивалей саморазвития. Каждый новый сезон становится новым экспериментом над внутренним устройством лагеря.  Теперь лагерь - место без алкоголя и курения, разговоры здесь всё короче, по делу и без мата. Для всей команды ранние подъемы и регулярная зарядка, прозрачная система принятия решений и распределения обязанностей, простор для приложения собственных сил в сочетании с воспитанием высокой командной и личной дисциплины. Нас больше всего заинтересовал опыт обживания леса и жизнеобеспечения в нём, опыт постепенного очищения культуры лагеря от пагубных привычек общества, а также опыт успешной организации масштабной волонтёрской службы. Что-то мы уже знали по публикациям Сергея в социальных сетях, что-то мы узнали из личного общения с ним и немногими волонтёрами в спокойной атмосфере межсезонья. Итак, обо всём по порядку.</p><h2 id="жизнь-в-лесу" tabindex="-1">Жизнь в лесу <a class="header-anchor" href="#жизнь-в-лесу" aria-label="Permalink to &quot;Жизнь в лесу&quot;">​</a></h2><p>На третий год существования лагеря пришло решение о постройке первого полноценного круглогодичного дома. Несмотря на то, что денег было только на половину здания, средства на его постройку найти удалось. В самом конце декабря сруб с крышей был построен и Сергей пропилил бензопилой первое овальное окно в первом отапливаемом жилище сёрф-лагеря. Несколько лет работы в сфере строительства по-новому раскрылись в его новом призвании. <strong>Фигурное двухслойное утеплённое окно из прозрачного листового пластика</strong> с тех пор стало визитной карточкой архитектуры лагеря. Эта же технология используется в причудливой формы окнах четырёх треугольных <strong>сказочных домиков</strong>, большом окне <strong>купольного дома</strong> и окнах в двухэтажной восьмигранной бревенчатой <strong>«Башне»</strong>. Многие крыши сделаны частично или полностью из прозрачного поликарбоната, а круглый образовательный павильон «<strong>Стовосьмой»</strong> (такова его площадь в квадратных метрах) сделан прозрачным практически весь. Летом в нём, конечно, жарко как в теплице, поэтому приходится часть окон просто снимать, открывая зал для сквозного проветривания.</p><p><img src="'+b+'" alt=""><img src="'+j+'" alt=""><img src="'+h+'" alt=""><img src="'+u+'" alt=""><img src="'+k+'" alt=""><img src="'+q+'" alt=""><img src="'+M+'" alt=""><img src="'+I+'" alt=""><img src="'+G+'" alt=""></p><p>Несмотря на долгое постепенное развитие проекта, все многообразные постройки здесь объединены общими пропорциями и технологическими подходами, что не удивительно, ведь большинство построек здесь построено руками главного мастера лагеря — Эдуарда или, проще, <strong>Эдика</strong>.</p><p><img src="'+x+'" alt=""></p><p>Он уже больше <strong>6 лет</strong> в проекте: очищался и рос вместе с ним, отказавшись сначала от алкоголя, а затем и от курения, что помогло обнаружить массу свободного времени и новых сил на постройку нескольких отапливаемых домов общей вместимостью <strong>50 человек</strong>, <strong>5</strong> крытых площадок площадью <strong>500 кв. м.</strong>, бани**, 2** сцен на воде — большая часть этих конструкций возведена им лично или под его чутким руководством и надзором. Эдик предпочитает работать в одиночку, редко берясь учить волонтёров хитростям плотничества, что не мешает ему выполнять функции целой строительной команды.</p><p>Несмотря на значительное расширение, cёрф-лагерь по-прежнему обеспечивается полностью автономно. Все домики надёжно утеплены и снабжены внушительными <strong>печками системы «Бренеран»</strong> (их еще называют «Булерьян»), которые в холода топятся дровами из собранной в лесу сухостойной древесины. Техническая и питьевая вода получаются из <strong>двух собственных скважин</strong>, а электричество вырабатывается установленным под навесом в овраге <strong>газовым генератором</strong>. Такая автономность накладывает и свои ограничения: в обычные дни «<strong>генчик»</strong> заводят здесь по расписанию, всего на два-три часа в день, как правило — после заката, <strong>с 20 до 23</strong> <strong>часов</strong>. За это время нужно успеть сделать все энергоёмкие задачи. Бывает, оказывается проще найти способ обойтись и вовсе без электричества, что ещё больше учит жить в гармонии с природой. Здесь легко рано заснуть после выключения везде света, больше радости приносят ранние подъемы и уютные вечера при свечах. Так больше места остаётся для живой музыки, создаваемой людьми, и не перестающей удивлять природой. Здесь вечерами смотрят любимый сериал живописных «морских» закатов, умеют ценить тишину и не понаслышке знакомы с умиротворяющей силой первозданных стихий. Чтобы смягчить суровость условий, тут заряжают от генератора и используют в перерывах между его запусками <strong>большие свинцовые аккумуляторы.</strong> Регулярно размышляя о солнечной и ветровой электрогенерации, порой задумываются и о подключении через соседнюю турбазу к местной энергосети.</p><p><img src="'+P+'" alt=""><img src="'+v+'" alt=""><img src="'+w+'" alt=""><img src="'+y+'" alt=""><img src="'+D+'" alt=""><img src="'+F+'" alt=""></p><p>Когда пространство развивается из простого палаточного лагеря, можно внимательно отбирать и внедрять только самые необходимые элементы цивилизации, не принося с собой присущих ей перекосов и излишеств. Порой удивительно, как легко можно обходиться без чего-то, ставшего таким привычным в быту. Теперь здесь хорошо ловит <strong>3G</strong>, есть свой энергонезависимый <strong>Wi-Fi</strong>, а ведь еще в 2014 году <strong>появление горячего душа</strong> в лагере отмечалось как настоящий праздник. Инфраструктура лагеря, несмотря на свою скромность, не только достойно выдерживает приезды даже нескольких тысяч людей, как бывает на ставшем действительно популярным фестивале стихий «WAFEst», но и удовлетворяет запросам взыскательных спортивных ассоциаций, художественных и архитектурных объединений.</p><p><img src="'+T+'" alt=""><img src="'+O+'" alt=""><img src="'+A+'" alt=""><img src="'+S+'" alt=""><img src="'+E+'" alt=""><img src="'+V+'" alt=""><img src="'+$+'" alt=""><img src="'+N+'" alt=""></p><p>Одно из самых ярко разрисованных зданий с длинными столами и скамейками рядом — горячо любимая всеми волонтёрами <strong>кухня с верандой</strong> и <strong>окном для раздачи</strong>. В разгар сезона здесь посменно трудится отдельная волонтёрская команда, обеспечивая <strong>трехразовое вегетарианское питание</strong> для команды и гостей лагеря. Хорошо организованное пространство со всем необходимым для полноценного кулинарного творчества позволяет накормить десятки, сотни и даже тысячи людей. Здесь есть все самое нужное: большие кастрюли и казаны на десятки литров, специально сваренные газовые горелки, плиты, подказанники, множество ножей и половников, всевозможные специи, а также блендер, включение которого, правда, может лишить света весь лагерь, из-за чего его использование иногда даже приходится координировать с технической службой по рации. Из глубокой скважины закачивается в куб и поступает в краны <strong>чистая питьевая вода</strong>. Запасы круп, специй и других сухих и долгоживущих продуктов хранятся здесь на стеллажах, а в условиях нестабильного электроснабжения роль холодильника для овощей и фруктов выполняет вместительный <strong>погреб</strong>. Даже во время людных фестивалей, столовая старается не использовать пластиковую посуду и выдаёт еду в многоразовых тарелках с приборами, которые нужно самостоятельно помыть и вернуть в оборот. И пусть порой это приводит к небольшим неудобствам и задержкам, зато сберегает ресурсы природы и спасает от её загрязнения.</p><p><img src="'+W+'" alt=""><img src="'+z+'" alt=""><img src="'+B+'" alt=""><img src="'+C+'" alt=""></p><p>Песчаная почва соснового леса не слишком плодородна, но что-то в ней вырастить все же удаётся. Из года в год между протоптанными дорожками высаживается <strong>газонная трава</strong>, а неподалёку от медпункта расположился небольшой <strong>огород</strong>, снабжающий кухню ароматными травами. <strong>Базилик, эстрагон, укроп</strong> и другая зелень даёт кое-какой урожай и у обитателей лагеря появляется всё больше смелых идей о повышении автономности его пропитания. Весной повара готовят блюда из свежей <strong>сныти</strong>, летом в лесу можно насобирать <strong>черники</strong>, а осенью <strong>грибов</strong> в нём столько, что сюда стекаются со своими корзинками грибники со всей окрестности. Если приложить к местной агрономии и продуктовым заготовкам побольше сил и внимания, лагерь вполне сможет обеспечить себя и овощами собственного производства. Тем более, что постройка под названием «<strong>Теплица»</strong> на солнечном берегу водохранилища уже есть, осталось только обтянуть её плёнкой и организовать внутри плодородные грядки.</p><p><img src="'+J+'" alt=""></p><p>Ещё одно важное пространство лагеря — наполненный всем необходимым для лесной жизни <strong>склад</strong>. В просторном здании с прозрачной крышей стоит несколько больших стеллажей. В одном углу здесь хранятся палатки и тенты, матрасы и коврики, спальники и постельное бельё, в другом можно найти множество полезных материалов: шланги и верёвки, провода и лампочки, саморезы и уголки, краски и кисти. Здесь есть несколько электролобзиков, пара болгарок, циркулярная пила, перфоратор, шлифмашинки, несколько бензопил, много строительных степлеров и, конечно, шуруповёртов. В ведении Эдика найдётся даже небольшой фуганок-рейсмус. На складе есть <strong>стойка раздачи</strong> с открывающимся окном, через которое во время подготовки и проведения фестивалей волонтёрам выдаётся под роспись все необходимое для работы. Отдельного помещения для творчества здесь пока не предусмотрено: рабочие столы стоят неподалёку на улице, а от непогоды можно укрыться на одной из многочисленных крытых площадок лагеря.</p><p><img src="'+K+'" alt=""><img src="'+R+'" alt=""><img src="'+H+'" alt=""><img src="'+L+'" alt=""><img src="'+Q+'" alt=""><img src="'+U+'" alt=""><img src="'+X+'" alt=""><img src="'+Y+'" alt=""><img src="'+Z+'" alt=""><img src="'+ss+'" alt=""><img src="'+ts+'" alt=""></p><p>Нам удалось на деле опробовать творческую инфраструктуру лагеря: нашлось отличное задание изготовить большую <strong>меловую доску</strong>, чтобы повесить над камином в Башне. Так удобнее вести таблицы со спортивными достижениями ребят, да и вообще с грифельной доской намного проще доносить друг другу сложные вещи. На одной из сцен мы забрали большой <strong>лист твёрдого ламината</strong>, который периодически свозит сюда местная ламинатная фабрика. <strong>Рулетка, линейка, карандаш</strong> и <strong>электролобзик</strong> нашлись на складе. Мы вымеряли место под доску, нанесли разметку на лист и дождались очередного включения генератора чтобы разом выпилить заготовку нужного размера. Акриловая краска легко соскабливается с прочной пластиковой поверхности листа и нам пришлось найти иное решение. На полках нашлась зелёная <strong>алкидная эмаль</strong> для внешних работ. Вонючая, липкая, плохо смешивающаяся с цементом — нельзя сказать, что она идеально подходила для наших задач, но шанс все-таки был. Мы <strong>загрунтовали</strong> поверхность доски тонким слоем эмали и оставили сохнуть <strong>на пару часов</strong>. Затем намешали <strong>краску с цементом</strong> для основного грифельного слоя. Цемент создаёт шершавую, но прочную текстуру, на которую отлично ложится мел. Алкидная эмаль с цементом становится густой и очень вязкой: было непросто нанести её равномерно небольшим <strong>валиком</strong>, но нам это все-таки удалось. Через сутки сушки под навесом на улице она вполне высохла на отлип, но всё ещё довольно сильно пахла, чтобы заносить её в помещение. Высохшую доску волонтёры лагеря вешали уже после нашего отъезда и мы были рады получить от них вести о том, что она идеально подошла для их задач и уже на следующий день полностью покрылась диаграммами и схемами новых идей Сергея об организации внутренней жизни лагеря.</p><p><img src="'+rs+'" alt=""><img src="'+os+'" alt=""><img src="'+as+'" alt=""><img src="'+gs+'" alt=""><img src="'+ns+'" alt=""></p><h2 id="волонтерство" tabindex="-1">Волонтёрство <a class="header-anchor" href="#волонтерство" aria-label="Permalink to &quot;Волонтёрство&quot;">​</a></h2><blockquote><p>Когда само дей­ствие пе­ре­стает иметь зна­че­ние, а все вни­ма­ние со­сре­до­то­чено на со­сто­я­нии, из ко­то­рого оно со­вер­ша­ется, ко­гда ты це­ли­ком в деле, ко­гда вкла­ды­ва­ешь в него душу, лю­бовь, то сам про­цесс ра­боты на­чи­нает на­пол­нять тебя энер­гией, те­перь ты на­хо­дишься в на­сто­я­щем, и из этого со­сто­я­ния по­лу­ча­ется очень кра­си­вый ре­зуль­тат, несу­щий ра­до­сть и пользу всем во­круг.</p></blockquote><p>Для тех, кто склонен сильно разделять работу и отдых, волонтёрство бывает трудно понять: казалось бы, вместо того, чтобы беззаботно расслабляться, человек всё лето посвящает не оплачиваемой работе. Некоторые даже иногда считают, что добровольчество мало чем отличается от древнего рабства. Однако, если отбросить разделение времени на работу и отдых и деньги как его инструмент, обнаруживается глубина и важность такого рода взаимоотношений между людьми и их начинаниями. Кто-то может придумать проект, но не иметь возможности воплотить его в одиночку, а кто-то только ищет возможности применить имеющиеся или освоить новые навыки не в теории, а на практике. Нелюбимая работа ради денег требует отдыха и денежных трат, а занятие любимым делом дарит сил и служит источником радости.</p><blockquote><p>Для тех, кто способен изменить своё отношение к работе, входя в медитативное состояние, уже не будет ничего невозможного, трудности превратятся в возможности, и опыт этот, встраиваясь в жизнь, способен изменить очень многое... больше, чем можно представить.</p></blockquote><p>В таком ключе <strong>пятичасовой рабочий день</strong> не противопоставляется предоставленному жилью, питанию, возможности кататься на сёрфе и интересным мастер-классам, а становится идеальным дополнением картинки, позволяющим превзойти разделение работы и отдыха и, хотя бы на время, забыть о деньгах. Вместе с неплохими условиями жизни на лоне природы волонтёры получают колоссальный опыт слаженной командной и индивидуальной работы, а также глубокое и насыщенное общение с близкими по духу людьми в располагающей атмосфере взаимного уважения.</p><blockquote><p>Вначале идет гонка за удовольствиями - глубокое погружение в иллюзии, потом, когда это надоедает, наступает бессмысленность - тяжелое бремя, когда все перестает иметь значение, но внимание человека всё еще на себе самом... а когда отступает и бессмысленность, сквозь благодарность к сущему появляется ощущение неслучайности себя, и вот тогда всё встает на свои места.</p><p>Я увидел, что творчество, проекты, следующие мощнейшей формуле «что я могу дать?» и есть по сути проявленная благодарность. И именно такие проекты помогают человеку понять себя, проявить свою душу... в этом их красота.</p></blockquote><p>В действительности классическое волонтёрство — лишь малая часть широкого спектра возможностей взаимодействия людей с проектом. Можно приехать кататься на сёрфе или оплачивать только жильё и питание в качестве «жителя», а можно вовлечься в развитие лагеря, получая пропорциональные вкладу скидки на оплату обитания. Таким образом каждый, при желании, может найти свой уникальный баланс усилий и отдыха в лагере. Но на «бесплатном» волонтёрстве возможности участника проекта только начинают раскрываться. Целеустремлённого человека ждут перспективы не только повышения до куратора одной из волонтёрских служб, но и включения в ядро команды проекта в качестве оплачиваемого сотрудника или как руководителя отдельной ветви организации. Волонтёрство — школа жизни и для многих такой опыт может послужить важным инструментом для личной трансформации. Попробовать себя в новых начинаниях, проникнуться любовью и взаимным доверием, сонастроиться с ритмами природы и побыть в обществе единомышленников — вот лишь некоторые из глубоких мотивов всех тех, кто решается пройти эту школу. Раскрывая себя и свои, подчас глубоко скрытые, силы, многие покидают проект чтобы реализовать их в собственных начинаниях, точь в точь как выпускники. И здесь всегда рады их видеть снова: для таких друзей проекта предполагаются существенные скидки на услуги сёрф-лагеря.</p><h3 id="отбор" tabindex="-1">Отбор <a class="header-anchor" href="#отбор" aria-label="Permalink to &quot;Отбор&quot;">​</a></h3><p>Начиная со скамеек и качелей, сёрф-столов и навесов, лагерь строился силами волонтёров и увлечённых друзей. Поначалу творчество, как и прочая организация, было довеском к тусовке, но со временем система сотрудничества лагеря стала принимать все более ясные очертания. Начала формироваться система отбора участников проекта. С 2011 года начали опубликоваться <strong>вакансии</strong> инструкторов, аниматоров, администратора, поваров, барменов, рекламщика, блоггера и менеджеров, с предложением им бесплатного проживания, питания, обучения катанию и проката оборудования плюс общения и отдыха в интересной компании. После недельного оплачиваемого претендентом <strong>испытательного срока</strong> он мог быть принят в команду волонтёров проекта. Уже тогда ключевым принципом ведения проекта стала <strong>прозрачность</strong>, в том числе в условиях сотрудничества. Активное участие предполагало возможное получение различных <strong>бонусов</strong>, а продолжительные плодотворные усилия с самого начала вели к включению в <strong>ядро команды</strong>.</p><p>Каждый сезон собирает новую команду, что позволяет постоянно совершенствовать систему отбора кадров. Уже в 2012 году пошаговая схема участия состояла из 4 основных шагов: <strong>заявки</strong> в свободной форме, <strong>собеседования</strong> в городе, двухнедельного <strong>испытательного срока</strong> и <strong>работы</strong> в проекте.</p><p>Проект проходил и целый ряд кризисов в управлении волонтёрской командой. Начинавшись всего с нескольких увлечённых человек, теперь во вне-фестивальное время она представляет собой группу из <strong>30—40 волонтёров</strong>, разделяющих ответственность за множество различных направлений работы, которая значительно усиливается дополнительным набором добровольцев на время подготовки и проведения многочисленных фестивалей. Когда в 2012 году стало заметно, что затраты на содержание некоторых волонтёров превышают реальную пользу от их пребывания, на некоторое время вводилась <strong>система предоплаты</strong> волонтёрами проживания и питания как гостями. Волонтёр оплачивал день пребывания в лагере, получал <strong>задание</strong>, выполнял его и, сдавая <strong>результаты</strong>, возвращал свои <strong>деньги обратно</strong>. Ничего не делание делало волонтёра клиентом, а активное участие предполагало включение в команду <strong>супер-волонтёров</strong>, освобождённых от плат.</p><p>К 2016 году система отбора добровольцев получила ещё более четкую форму. Основной фильтр теперь — опубликованная <a href="https://vk.com/topic-91708749_33925928" class="external-link" target="_blank" rel="noreferrer">суть сезона</a>, помогающая человеку понять свою синхронность с ним, а также философские основы, согласие с которыми раскрывает глубину и пользу участия в волонтёрском движении. Сформированные ценности проекта — идеальная антенна для поиска именно тех, кто созрел для добровольного сотрудничества. Заявка волонтёра теперь предполагает ответы на следующие вопросы:</p><ol><li><em>Зачем вам этот проект?</em></li><li><em>В паре обзацев расскажите о себе.</em></li><li><em>Когда и на сколько готовы приехать?</em></li><li><em>Перечислите навыки, которые могут быть полезны.</em></li><li><em>Краткая жизненная позиция.</em></li></ol><p>Далее каждый претендент рассматривается персонально. Кто-то сразу получает <strong>отказ</strong>, кого-то сразу <strong>зовут приехать</strong>, но обычно дальше следуют <strong>дополнительные вопросы</strong>, в основном философского плана, необходимые не только чтобы лучше изучить кандидата, но и чтобы он сам лучше понял себя, свою мотивацию и истинные внутренние цели.</p><p><strong>Собеседование</strong> теперь проводится непосредственно в лагере, обычно в специальный <strong>массовый заезд</strong> волонтёров на 1—2 дня. Есть возможность пройти его и <strong>по скайпу</strong>. После некоторого размышления, претендент может получить приглашение пройти 7—10-дневный <strong>испытательный срок</strong>, по результатам которого может быть принят в одну из команд-семей, отвечающих за отдельные направления работы лагеря. Из <strong>120 заявок</strong>, присланных в этом году, были одобрены около 30, а испытательный срок успешно прошли только <strong>20 новых участников</strong>.</p><p><img src="'+es+'" alt=""><img src="'+ps+'" alt=""><img src="'+cs+'" alt=""></p><h3 id="команды" tabindex="-1">Команды <a class="header-anchor" href="#команды" aria-label="Permalink to &quot;Команды&quot;">​</a></h3><p>Автономная школа винд- и кайтсёрфинга в лесу — довольно сложное начинание. Объем задач, стоящих перед таким большим лагерем настолько велик, что обойтись спонтанными усилиями не получится. Здесь требуется прозрачная <strong>система распределения и координации коллективных усилий</strong>. Поначалу многие направления работы пространства представляли собой усилия одного человека или просто вакансии, ожидающие своих работников. Каждый человек, приходящий в проект, сообщает о том, чем он может быть полезен лагерю и его обитателям, что становится основным ориентиром при подборе задач для его волонтёрства. Кто-то вливается в уже существующие направления, кто-то привносит свою уникальную лепту в развитие пространства, а кто-то пробует себя по-очереди во всех сферах, познавая разные грани мира и себя. Есть те, кто лишь иногда бывает в лагере и выполняет задачи удалённо, из города, но многие приезжают из самых разных городов страны и ближнего зарубежья именно для того, чтобы всё лето провести в палатках и свободных домиках в лесу.</p><p>Каждая служба опирается на инициативу того, кто берёт на себя ответственность за её функционирование. Некоторые задачи можно решить в одиночку, но в большинстве направлений требуется командная работа, разделение функций и ответственности, координирование с остальными обитателями пространства. В сёрф-лагере за эти 8 сезонов не только сформировались отдельные службы по всем основным направлениям, но и внутри них успела сложиться некоторая преемственность поколений, когда наиболее опытные волонтёры становятся <strong>кураторами направлений</strong> и затем, вырастая в проекте или покидая его, передают последователям не только технические приёмы, но и навыки координации волонтёрской команды. Такая проверенная годами культура волонтёрства позволяет эффективно масштабировать её в дни фестивалей, когда одних кураторов работает <strong>несколько десятков</strong>, а количество добровольцев превышает <strong>150 человек</strong>.</p><h4 id="служба-чистоты-«шауча»" tabindex="-1">Служба чистоты «Шауча» <a class="header-anchor" href="#служба-чистоты-«шауча»" aria-label="Permalink to &quot;Служба чистоты «Шауча»&quot;">​</a></h4><p><img src="'+is+'" alt=""></p><p>Одна из важнейших ценностей сёрф-лагеря — «шауча», т.е. <strong>чистота</strong>. Внутренняя чистота начинается с чистоты внешней: каждый здесь чтит эту мудрость, соблюдая аккуратность и порядок во всех своих начинаниях. Волонтёры регулярно проводят рейды по очистке нескольких гектаров “дикого” побережья, куда летом приезжают на отдых горожане и куда в дни проведения крупных фестивалей расширяется территория лагеря. Но «шауча» — это не просто сбор мусора, это ещё и чистота ума как способность быть здесь и сейчас, замечать дисгармонию и прикладывать усилия к достижению гармонии, это такая <strong>практика</strong>. Большинство волонтёров начинает свой путь в сёрф-лагере именно со службы Шауча, отвечающей за поддержание порядка в лагере. Десятки лесных туалетов, душей, мусорных урн, дюжина зданий — широкий простор для практики постижения гармонии.</p><h4 id="техническая-служба" tabindex="-1">Техническая служба <a class="header-anchor" href="#техническая-служба" aria-label="Permalink to &quot;Техническая служба&quot;">​</a></h4><p><img src="'+_s+'" alt=""></p><p>Несмотря на свой теперешний размах, сёрф-лагерь по-прежнему является автономным лесным поселением. Из двух скважин насосами набирается в кубы питьевая и техническая вода, пара генераторов даёт свет, есть нагреватели для горячего душа, более десятка дровяных печей, сотни метров проводов и трубопроводов, несколько бензопил и целый парк электроинструментов. Всё это хозяйство выстаивалось годами, усложняясь и разрастаясь по обширной территории лагеря. Теперь всё это хозяйство находится в ведении технической службы, которая при приближении крупных фестивалей значительно усиливается и разделяется на несколько отдельных сфер ответственности вроде обслуживания звукового и светового оборудования, водоснабжения и прочих сфер технического обеспечения жизнедеятельности людей в лесу. Многие задачи в этой сфере достаточно ответственны и доверять энтузиазму волонтёров бывает можно только на свой страх и риск. Люди, как правило, справляются с возложенным доверием, но бывают и ошибки. Так, например, изъян в конструкции построенной волонтёрами бани несколько лет назад привёл к серьезному пожару, полностью её уничтожившему. Благо, лес и его обитателей удалось спасти, но теперь в техническую службу стараются набирать тех, кто имеет достаточно опыта в работе с <strong>техникой</strong>, <strong>строительством</strong> или <strong>инженерными коммуникациями</strong>.</p><h4 id="кухня" tabindex="-1">Кухня <a class="header-anchor" href="#кухня" aria-label="Permalink to &quot;Кухня&quot;">​</a></h4><p><img src="'+ls+'" alt=""><img src="'+ms+'" alt=""><img src="'+ds+'" alt=""></p><p>Команда кухни состоит из <strong>главного повара</strong>, который также является волонтёром, и его <strong>помощников</strong>, количество которых варьируется в зависимости от загруженности лагеря гостями и наличием или отсутствием мероприятий и фестивалей. Волонтёры кухни чаще всего включены в деятельность только этой части лагеря, не отвлекаясь на задачи других волонтёрских служб. Приготовление трехразового вегетарианского питания происходит в <strong>три смены</strong>, каждая из которых является полноценным завершенным действием, возвращающим кухню в первоначальное состояние после бурного кулинарного процесса. Смена включает в себя: трудоёмкую подготовку необходимых для блюда продуктов, приготовление еды и полноценную уборку после смены. Грамотная организация деятельности кухни позволяет при большом количестве посетителей работать в <strong>три команды</strong>, каждая из которых отвечает за отдельный прием пищи в течении дня. Во время фестивалей помимо основной столовой на территории лагеря частично силами волонтёров организуются чайные и небольшие кафе, а когда в межсезонье команда кухни разъезжается, за приготовление пищи отвечают все по желанию и по очереди.</p><h4 id="администрация" tabindex="-1">Администрация <a class="header-anchor" href="#администрация" aria-label="Permalink to &quot;Администрация&quot;">​</a></h4><p>Предоставление услуг потоку гостей требует постоянного общения с ними, тщательного учёта <strong>бронирований, клиентов, оплат</strong>. Нужно организовывать взаимодействие лагеря с внешним миром, будь то вывоз мусора или стирка белья и палаток, закупки продуктов и оборудования и прочие заказы и услуги. Кто-то хочет отметить в сёрф-лагере День рождения, кто-то приезжает с другого конца страны учиться сёрфингу, а как насыщенно время подготовки к фестивалям! Работы администратору всегда хватает. К этой службе также, наверное, можно отнести работу <strong>маркетолога</strong>, продвигающего услуги сёрф-лагеря и его события среди близких по духу целевых групп. Освещать в социальных сетях яркую и насыщенную жизнь сёрф-лагеря в социальных сетях может доставлять настоящее удовольствие, особенно в команде с <strong>фотографом/видеографом</strong> и <strong>журналистом-текстовиком</strong>. Конечно, не на каждый сезон набирается полная команда и нередко волонтёрам приходится сочетать сразу несколько ролей, но для многих это становится не тяготой, а прекрасным средством для личностного роста.</p><h4 id="серф-и-веселье" tabindex="-1">Сёрф и веселье <a class="header-anchor" href="#серф-и-веселье" aria-label="Permalink to &quot;Сёрф и веселье&quot;">​</a></h4><p>Какой сёрф-лагерь без сёрферов? Сотни людей приезжают сюда каждый год чтобы слиться в танце с природными стихиями. Среди них есть как вполне владеющие техникой виндсёрферы и кайтеры, так и желающие поучиться. Некоторые приезжают со своим оборудованием, но для многих пространство «FreeDOM» становится дверьми, через которые они могут войти в этот чарующий мир воды и ветра. Научиться кататься на доске с парусом не так уж и сложно и при правильном подходе обучение занимает не более нескольких дней. После того, как человек научится поднимать парус с воды и ловить в него ветер, для него открывается целый мир различных манёвров, техник и приёмов, осваивать которые можно годами. На каждом из этапов, но особенно на начальных, сложно обойтись без помощи умелого <strong>инструктора</strong>. Ещё до выхода в «море» важно изучить и понять особенности взаимодействия с непредсказуемыми стихиями воды и ветра, управления парусом и технику безопасности. Интересно, что для того, чтобы поставить человека на виндсёрф, не нужны годы опыта и всего за <strong>две недели и 10 тысяч рублей</strong> любой желающий может обучиться здесь на сёрф-инструктора и обучать новичков базовой теории и технике хождения под парусом. Управление воздушным змеем в кайтсёрфинге — задача посложнее и для работы инструктором нужно уже иметь достаточно опыта катания и знаний о свойствах и повадках ветра.</p><p><img src="'+fs+'" alt=""><img src="'+bs+'" alt=""><img src="'+js+'" alt=""><img src="'+hs+'" alt=""></p><p>Катание по «морской» глади под парусом или кайтом сильнейшим образом заряжает человека силой и позитивной энергией и поэтому сёрферы нередко берут на себя ещё одну важную роль <strong>активаторов-аниматоров</strong>, постоянно придумывающих новые забавы и развлечения для гостей и обитателей пространства. Благодаря энергичности и находчивости таких ребят в лагере всегда кипит жизнь, мастер-классы, конкурсы, а улыбки и искренний смех дают всем сил и уверенности двигаться дальше.</p><h4 id="служба-релаксации" tabindex="-1">Служба релаксации <a class="header-anchor" href="#служба-релаксации" aria-label="Permalink to &quot;Служба релаксации&quot;">​</a></h4><p>Спорт, труд, веселье — отличные способы расшевелиться, взбодриться и активизироваться и многим бывает трудно потом расслабиться, успокоиться и дать телу и уму отдохнуть. Тишина и красота природы умиротворяет и помогает ощутить гармонию, но не всегда этого бывает достаточно. В этом году уже несколько человек взяли на себя важную задачу по восстановлению сил уставших за трудовой день волонтёров и потрепанных стихией сёрферов. <strong>Массаж</strong> и <strong>растирания</strong> в комплексе с баней и купанием помогают расслабиться по-настоящему, снимая напряжения и блоки, чем расширяя потенциал человека.</p><h4 id="остальные" tabindex="-1">Остальные <a class="header-anchor" href="#остальные" aria-label="Permalink to &quot;Остальные&quot;">​</a></h4><p>Конечно, на этом список специфических ролей и задач не заканчивается. В отдельном «<strong>Передоме»</strong>, небольшом здании в форме перевёрнутого домика, располагается медпункт — база обитания <strong>квалифицированного врача</strong>, всегда готового оказать помощь при самых различных недомоганиях. Да, бывает, что и массаж не помогает и требуются средства посерьёзнее.</p><p><img src="'+us+'" alt=""><img src="'+ks+'" alt=""><img src="'+qs+'" alt=""><img src="'+Ms+'" alt=""><img src="'+Is+'" alt=""></p><p>Все постройки и предметы мебели лагеря представляют собой настоящие произведения искусства, расписанные яркими орнаментами и глубокими художественными образами. Лагерь высоко ценит труд <strong>художников, дизайнеров</strong> и <strong>оформителей</strong>, способных создавать насыщенные визуальные образы красками на поверхностях или мышкой на экране, а также наполнять пространство лагеря теплом, душевностью и уютом. Для творческих личностей, кстати, допускается специальный <strong>индивидуальный подход к дисциплине</strong>. Особенно активизируется художественное оформительство перед фестивалями, когда появляется множество новых объектов и поверхностей, требуются новые таблички и указатели, а пространство лагеря выходит далеко за пределы его обычной территории.</p><p>Летом с дикого пляжа и окрестных турбаз и поселков, бывает, заносит самых разных персонажей с самыми разными намерениями. На случай проявлений агрессии или непонимания человеком границ дозволенного на территории лагеря есть <strong>охранник</strong>, имеющий все данные для применения силы, но способный решать вопросы мирным путём. Во время крупных событий таких «мирных воинов» требуется, конечно, больше.</p><h2 id="развитие-через-очищение" tabindex="-1">Развитие через очищение <a class="header-anchor" href="#развитие-через-очищение" aria-label="Permalink to &quot;Развитие через очищение&quot;">​</a></h2><p>В далёком <strong>2008 году</strong> одна шумная и весёлая компания любителей экстремального спорта, во многом благодаря усилиям &quot;придумщика&quot; Сергея Шилина, нашла возможность обосноваться на восточном берегу Горьковского моря, разбив там на лето первый сёрф-лагерь. Палатки и навесы, гамаки, котелки и всего пара комплектов досок — школа виндсёрфа поначалу была похожа на затянувшийся на всё лето пикник. Так начался процесс воплощения идей Сергея о <em>&quot;пространстве свободы&quot;</em>.</p><p><img src="'+Gs+'" alt=""></p><p>Первое время сёрф-лагерь, называвшийся тогда <strong>«Позитив»</strong> или даже <strong>«Полная Гавайя»</strong>, регулярно гремел вечеринками и представлял собой пространство довольно простого понимания смысла слова «<strong>свобода»</strong>. Свободные от городской суеты, на стыке всех стихий, команда лагеря и его немногочисленные волонтёры и гости освобождались, в основном, <strong>от комплексов и социальных догм</strong>. В это время Сергей уже начал сонастраиваться с пространством лагеря и осознавать пути развития его изначальных идей. Подолгу пребывая на природе, помногу испытывая себя в катании по воде силой ветра, он всё больше проникался идеями очищения ума и развития в себе благодарности к силам природы. В это время пространство тоже сонастраивалось с Сергеем и культура лагеря также начала активно преображаться. Стали лучше видны цели проекта — создать центр оздоровления тела и сознания, сонастройки со стихиями и умиротворёнными людьми, проводить в нем разнообразные фестивали спорта, творчества и саморазвития.</p><blockquote><p>Сёрф-лагерь — пространство свободы FreeDOM, где свобода понимается не как возможность «делать что хочешь», а как «свобода от оков и блоков, что мешают изменить свою жизнь к лучшему, свобода от масок в общении с другими людьми, свобода от страхов научиться чему-то новому, свобода идти навстречу своей мечте.»</p></blockquote><p><img src="'+xs+'" alt="tusovochnyi-vese-lyi-bezalkogolnyi-subbotnik"></p><p>Лесной лагерь неразрывно связан с природными стихиями и живёт в едином ритме со сменой сезонов, ежегодно обновляясь и продолжая развиваться. Чистый ум правильно воспринимает информацию и быстро извлекает из неё полезный опыт, необходимый для саморазвития. Чистый ум намного лучше справляется с задачами и помогает преодолеть любые трудности. Вектор на очищение сознания отражается и в очищении сути самого проекта. Очищаясь изнутри, Сергей начал предпринимать по-началу робкие, но всё более решительные шаги по преодолению вредных привязанностей и шаблонов. <strong>С 2010 года</strong> он начал проводить первые безалкогольные встречи, субботники и Дни рождения, а уже <strong>в середине сезона 2012 года</strong> культура лагеря претерпела первое значительное превращение.</p><blockquote><p>Мы одни из тех немногих в нашем социуме «ненормальных», которые, работая в сфере отдыха и развлечений, намерены не привлекать в проект алкоголь, траву и прочий хлам, засоряющий мозги. Нам это больше не нужно! Да были времена трэша... и они были по своему прекрасны, но проект развивается, а вместе с ним и команда его создающая…</p></blockquote><p>Отказ от алкоголя серьезно трансформировал команду проекта: некоторые отшатнулись на какое-то время, а многие так и не решились столь радикально изменить свою жизнь.</p><blockquote><p>Да, было непросто трансформировать проект из веселого места с пляжными вечеринками в чистое пространство, но спустя всего год мы увидели, что Сёрфлагерь только выиграл и пошли дальше.</p></blockquote><p>Оказалось, что всегда есть куда идти дальше по дороге саморазвития. Затем настал черёд очищения пищи и кухня лагеря стала <strong>полностью вегетарианской</strong>. Команда проекта тоже прошла очередную очистку. Кто-то навсегда уходил из коллектива, но оставались именно те, кто искренне предан глубоким и важным идеям проекта. Стремление к самосовершенствованию привело Сергея и его команду к ранним подъемам, ежедневным тренировкам и практическому усвоению вечных и неизменных ценностей. На первый план вышло саморазвитие, спокойствие, красота, тишина.</p><p><img src="'+Ps+'" alt="freedom-2014"></p><blockquote><p>Оказалось, что проект говорит миру очень важные слова… Он говорит о чести, доброте и справедливости, он говорит о благодарности и красоте… Он говорит, что страх - это всего лишь иллюзия, и что прожить серую жизнь, полную тревог и мелких забот - это неблагодарность к своей бесконечной душе. Он всё чаще стал говорить о любви. Проект говорит всем нам: «Ребята, танцуйте свои жизни красиво!»</p></blockquote><p>Сергей использует долгие зимние вечера как время для медитации и глубоких размышлений о ключевых принципах существования лагеря и людей в нём. Пришедшее в прошлом году новое понимание особенной важности дисциплины в деле саморазвития, ярко отразилось на ритме жизни и продуктивности работы сёрф-лагеря. С каждым годом понимание развития становилось все тоньше и ближе к универсальным вечным истинам. Сонастройка с природой и лучшими человеческими качествами стала ключевым ориентиром в поисках правильного формата совместной жизни и труда.</p><p>Теперь здесь общий подъём в районе <strong>7 утра</strong>, обязательная для всех совместная или самостоятельная <strong>зарядка</strong>, затем, по необходимости, проводится организационная <strong>планёрка</strong> и каждый отправляется заниматься своим делом. В <strong>выходные понедельники</strong> команде даётся возможность поспать немного подольше и немного переключиться от основной работы. Участники команды добровольно участвуют в коллективной спортивной «<strong>штанга-йоге»</strong> — ежедневных совместных силовых тренировках на площадке с перекладиной, брусьями, кольцами, штангой и гирями. Спортивная дисциплина  развивается с помощью «<strong>санкальпы»</strong> — твёрдой решимости достичь установленного результата. Оттачиваясь на тысячах отжиманий и приседаний и часах, проведённых в «планке» и «столбе», воспитываются навыки брать ответственность, держать слово и достигать цели несмотря ни на какие на трудности.</p><p><img src="'+vs+'" alt=""><img src="'+ws+'" alt=""><img src="'+ys+'" alt=""><img src="'+Ds+'" alt=""><img src="'+Fs+'" alt=""></p><blockquote><p>Когда начинаешь совершать действия из состояния любви, то приближаешься к реальности, начинаешь видеть суть вещей. … Достижение этого состояния лежит в плоскости чистоты, где отсекается всё ненужное, наносное, ненастоящее, чтобы внутренний свет человека наконец-то стал виден… для этого и нужны все кажущиеся ограничения: отказ от алкоголя, курения, тяжелой и неестественной пищи, а также постоянная работа над своим телом и мозгом - всё это практика очищения от груза мусора. Так как именно перегрузка хлама в структуре человека заставляет его остановиться на пути развития и начать деградацию, а это и есть бессилие. Освобождение от мусора ведет к облегчению и дает возможность подниматься вверх по ступеням развития, ведущим к ясному восприятию реальности.</p></blockquote><p>Накопленная решительность и целеустремлённость направляются на дальнейшее саморазвитие и очищение: буквально на наших глазах <strong>1 сентября 2016 года</strong> была торжественно сожжена <strong>курилка</strong>, что ознаменовало новый этап очищения пространства — на этот раз от табачного дыма. Теперь команду лагеря ждёт новый период адаптации. Люди, приезжающие поволонтерить в сёрф-лагерь заведомо знают, что здесь они вряд ли смогут потакать своим слабостям, а скорее получат поводы и возможность их побороть или преобразовать.</p><p><img src="'+Ts+'" alt=""></p><p>Важно отметить, что несмотря на кажущиеся ограничения, в сёрф-лагере не чувствуешь себя скованно. Даже самые сложные ситуации пронизаны жизнерадостным юмором. Даже новички среди волонтёров быстро преодолевают смущение перед фигурой основателя и ядром команды, открываясь для живого человеческого общения. По вечерам здесь любят смотреть с чашкой какао на пляже сериал «морской закат» и интересные фильмы с проектора, пространство регулярно наполняется звуками живых музыкальных инструментов и спонтанного пения. Здесь ценят чаепитие и беседы «ни о чём» и всегда готовы к какой-нибудь новой эксцентричной выходке, разрушающей привычные шаблоны и стереотипы. Хорошо, когда люди каждый день видят свежим взглядом, свободным от шаблонов и ожиданий, и с интересом и радостью воспринимают вечно новый опыт единства гармонии всего и всегда.</p><blockquote><p>На глубинном уровне это и есть основная суть проекта - дать некоторые инструменты и указатели, способные помочь развеять иллюзии, снять оковы с ума, открыть глаза и увидеть восхитительную красоту этого мира и своей собственной жизни…</p></blockquote><p><img src="'+Os+'" alt=""></p><hr><p>Сёрф-лагерь «FreeDOM» — не просто очередная школа винд- и кайтсёрфинга, каких на одном только Горьковском море насчитывается не менее трёх. Это удивительно самобытное место с уникальной внутренней культурой. Свободное не только от вредных привычек, но и от излишней эзотерики и мистицизма, это яркое и чистое пространство действительно можно считать пространством свободы. За 8 лет существования лагерь вырос не только физически, но и духовно, наработав сложную культуру взаимоотношений и сотрудничества. Всего за пару дней пребывания в опустевшем лагере мы смогли воочию убедиться в размахе проекта и серьезности намерений его основателя. Занимаясь саморазвитием уже более десятилетия, Сергей Шилин способствует постоянному совершенствованию и своего масштабного начинания. Поначалу он проводил здесь только лето, а зимовать улетал в тёплые страны, но теперь долгие зимние философские вечера и сноукайтинг на замёрзшем водохранилище полюбились ему настолько, что он всё больше времени проводит в этом чудесном лесном поселении. Мы уверены, что опыт таких увлечённых и самоотверженных искателей красоты и истины может быть полезен многим участникам и организаторам самых разнообразных волонтёрских проектов, а знание о таком замечательном месте может вселить надежду и уверенность во всех сомневающихся и напуганных трудностями. Без стремления к мгновенным результатам, с внимательным анализом опыта и постоянной коррекцией направления развития проекта, годы усилий никогда не останутся бесплодными и принесут те результаты, которые как минимум станут отражением вашего внутреннего мира, а, может быть, станут образцом для других смелых и решительных людей. То, что начиналось как затянувшийся пикник, превратилось в образец новой культуры здоровой жизни в гармонии с природой и самим собой. Так создаются места силы, в которые хочется приезжать, оставаться, а, уезжая, возвращаться вновь и вновь.</p><blockquote><p>Нам нравится простота и естественность, нравится жить, а не искать смыслы жизни. В этом и есть суть.</p></blockquote><p>В статье использованы фотографии из <a href="https://vk.com/albums-9624588" class="external-link" target="_blank" rel="noreferrer">альбомов группы сёрф-лагеря в VK</a>.</p><p><img src="'+As+'" alt=""><img src="'+Ss+'" alt=""><img src="'+Es+'" alt=""><img src="'+Vs+'" alt=""><img src="'+$s+'" alt=""></p>',92),zs=[Ws];function Bs(Cs,Js,Ks,Rs,Hs,Ls){return t(),r("div",null,zs)}const Xs=s(Ns,[["render",Bs]]);export{Us as __pageData,Xs as default};
